INSERT INTO `info` VALUES (809, '投稿资讯▏晋商言药：山西品种点评', '最近这几天，天气还是继续下雨，这对一些低杆作物，是一种严重打击，如，生地，黄芩，柴胡几乎泡在水里，目前生地已经无法推断单产了，死苗的死苗，烂根的烂根，然而这几天一些公司厂家以及加工户好像已经反应...', '柴胡');
INSERT INTO `info` VALUES (810, '陕西宝鸡陈仓区柴胡零星产新价格坚挺走动顺畅', '柴胡是耐寒、耐旱、怕涝的药用植物，在生态环境较差的地区反能显示其优势，因此柴胡在全国种植面积分布广泛。甘肃、山西和陕西是柴胡的三大种植产区，陕西省渭南市、安康市、宝鸡市、商洛市等地的柴胡种植面积...', '柴胡');
INSERT INTO `info` VALUES (811, '西北品种点评之柴胡：两极分化日趋严重', '一、产地分布柴胡在全国种植面积分布广泛。甘肃、山西和陕西是柴胡的三大种植产区，甘肃省家种柴胡主产以陇西为中心的甘肃定西北部各县，定西市陇西县通渭、马河镇一带大面积种植。近年来陇南一带（礼县、宕昌县、武...', '柴胡');
INSERT INTO `info` VALUES (812, '2018汛期直击：甘肃遇最强降雨天气部分产地红色预警', '据甘肃省气象局7月11日9时发布：2018年7月9～11日我省出现今年以来最强降水，陇东南最大累积雨量达200～250毫米，强降水主要集中在10日白天到夜间，陇南、天水、平凉、庆阳等市出现暴雨，局地出...', '柴胡');
INSERT INTO `info` VALUES (813, '强监管周期下，柴胡的优胜劣汰', '药典是国家对药品质量标准及检验方法所作的技术规定，是药品生产、供应、使用、检验、管理部门共同遵循的法定依据。', '柴胡');
INSERT INTO `info` VALUES (814, '柴胡质量参差不齐，后市价格能否出现波澜？', '柴胡是药用大宗品种，有着悠久广泛的用药历史，本品为伞形科植物柴胡或狭叶柴胡的干燥根。柴胡具有解热镇痛、抗炎镇静、', '柴胡');
INSERT INTO `info` VALUES (815, '天地网创始人龙兴超先生深入产地一线调研豫西丹参、黄芩长势情况', '天地网三门峡公司地理位置优越，连翘、丹参、黄芩、柴胡这四个大品种的核心主产地均在公司周边方圆几十公里之内，龙兴超先生作为一名业界资深专家，对当地中药材品种进行了分析和指导，从战略高度对三门峡公司如何做...', '柴胡');
INSERT INTO `info` VALUES (816, '流感疫情来势汹涌，能否刺激柴胡行情再走高价？', '今年冬季的流感疫情来势汹涌，让医疗机构忙得应接不暇，各大药店部分清热解毒类用药销量猛增，甚至还有缺货的现象。这也让清热解毒的中药材原料备受关注，而柴胡对普通感冒、流行性感冒、疟疾、肺炎等有较好的退热效...', '柴胡');
INSERT INTO `info` VALUES (817, '中药材大咖考察：暴涨？暴跌？甘肃药材的众生相！', '党参、当归、黄芪作为甘肃药材行情的晴雨表和风向标，引领着陇西药材发展的方向。但黄芩、款冬花、甘草、旱半夏也引领全国价格风向标，近年有句话说的好，得甘肃药材得天下。近期是甘肃各种药材集中产新期，甘肃药材...', '柴胡');
INSERT INTO `info` VALUES (818, '湖北英山苍术当归和柴胡病虫害情况调研', '本期，第3调研小组对凯丰农资服务有限公司、宁夏大地生态有限公司和宁夏中卫市中宁县枸杞产业发展服务局进行调研', '柴胡');
INSERT INTO `info` VALUES (819, '《药材问答》第35期：元胡、柴胡等品种近况概览', '《药材问答》是一档问答形式的线上栏目。每一期，针对广大天地网用户所提出的热点问题，都会由栏目制作人及天地网智库专家及时作出回答，编写制作成集锦或深度文章。——本期焦点：元胡、柴胡近期行情概览。', '柴胡');
INSERT INTO `info` VALUES (820, '聚焦：柴胡产新，种源混乱质量堪忧！', '柴胡，做为经典用药之一，在我国中医药原料来源上地位重要。张仲景就擅用柴胡，大小柴胡汤、柴葛解肌汤、柴胡龙骨牡蛎汤等无不成为后世医家推崇的千古名方；日本汉方中同样把柴胡当作常备药，从我国引进改良出“三岛...', '柴胡');
