INSERT INTO `info` VALUES (882, '部分东北野生品种简评', '东北野生品种近期走势持续向好，以白鲜皮，赤芍，苍术，升麻等为代表，市价涨幅巨大，白鲜皮从16元左右升至24元左右，赤芍从13-14元升至17-18元，苍术从1...', '升麻');
