INSERT INTO `prescript` VALUES (56323, '51297', '龙胆泻肝汤', '柴胡梢 泽泻各3克 车前子 木通各1.5克 生地黄 当归梢 草龙胆各9克', '上锉如麻豆大，都作一服，用水450毫升，煎至150毫升，去滓，空腹时稍热服，便以美膳压之。', '《兰室秘藏》卷下', '清利肝胆湿热。治肝经实火上攻而成喉口热疮；肝经湿热下注所致小便涩痛，阴部热痒及臊臭。', '-', '-', '柴胡', '[\'柴胡\', \'泽泻\', \'车前子\', \'木通\', \'生地黄\', \'当归\', \'龙胆\']');
