INSERT INTO `prescript` VALUES (56340, '51065', '清膈散', '麦门冬（去心）沙参 人参（去芦）金钗 石斛各30克（去根）草龙胆 柴胡（去根）陈皮（去白）黄连（去须）木通各15克', '每服6克，用水150毫升，煎至100毫升，去滓，空腹时温服，一日二次。', '《魏氏家藏方》卷五', '治脾瘅，口中甜腻。', '-', '-', '柴胡', '[\'麦门冬\', \'去心\', \'沙参\', \'人参\', \'去芦\', \'金钗\', \'石斛\', \'各克去\', \'根草\', \'龙胆\', \'柴胡\', \'陈皮\', \'黄连\', \'木通\']');
INSERT INTO `prescript` VALUES (56341, '51048', '神效瓜蒌散', '大瓜蒌半个（黄熟者帚个，连皮子瓤，重重纸包，火煨，捣烂，取一半）白芷4.5克 玄参6克 升麻L5克 归尾6克 桔梗3克 连翘6克 柴胡3克 青皮3克 天花粉4.5克 穿山甲（炒）3克 川芎2.4克 知母3克 木通3克 木鳖子2个 延胡索0.6克', '上锉一剂。水煎，温服。', '《寿世保元》卷七', '治妇人乳肿作痛，欲成痈毒。', '-', '-', '柴胡', '[\'大瓜\', \'半个\', \'黄熟\', \'皮子\', \'重重\', \'纸包\', \'捣烂\', \'一半\', \'白芷\', \'玄参\', \'升麻\', \'克归尾克\', \'桔梗\', \'连翘\', \'柴胡\', \'青皮\', \'天花粉\', \'穿山甲\', \'炒克\', \'川芎\', \'知母\', \'木通\', \'木鳖子\', \'延胡索\']');
INSERT INTO `prescript` VALUES (56342, '51041', '清肝渗湿汤', '川芎 当归 白芍 生地 柴胡 龙胆草 山栀 天花粉 黄芩各3克 泽泻 木通 甘草各1.5克', '用水400毫升，加灯心20根，煎至320毫升，空腹时服。', '《外科正宗》卷三', '治肝经湿热下注，致成囊痈，阴囊红肿，发热掀痛，小便不利。', '-', '-', '柴胡', '[\'川芎\', \'当归\', \'白芍\', \'生地\', \'柴胡\', \'龙胆草\', \'天花粉\', \'黄芩\', \'泽泻\', \'木通\', \'甘草\']');
INSERT INTO `prescript` VALUES (56343, '51037', '国老散', '甘草（炙）银柴胡 秦艽 大乌梅肉（焙干）各60克', '每次1～2克，食后热汤点服。', '《幼幼新书》卷二十引《聚宝方》', '治小儿骨蒸热久，三焦壅滞，不思饮食。', '服药期间，忌食炙煿之物。', '-', '柴胡', '[\'甘草\', \'银柴胡\', \'秦艽\', \'乌梅\', \'焙干\']');
INSERT INTO `prescript` VALUES (56344, '51028', '加减小柴胡汤', '柴胡（去苗）黄芩各30克 地骨皮 人参 知母 半夏（制）茯苓各15克 炙甘草9克 白芍药24克', '每服30克，加生姜，水煎服。', '《云岐子脉诀》', '治恍偬惊悸。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'黄芩\', \'地骨皮\', \'人参\', \'知母\', \'半夏\', \'茯苓\', \'甘草\', \'克白\', \'芍药\']');
INSERT INTO `prescript` VALUES (56345, '51008', '补阴益气煎', '人参3～9克 当归6～9克 山药（酒炒）6～9克 熟地9～15克或30～60克 陈皮3克 炙甘草3克 升麻0.9～1.5克 柴胡3～6克', '用水400毫升，加生姜3～7片，煎至320毫升，空腹时温服。', '《景岳全书》卷五十一', '主劳倦伤阴，精不化气，或阴虚内乏，以致外感不解，寒热痰疟，或防虚便结不通。\r\n火浮子上者，去升麻，无外邪者，去柴胡。', '-', '-', '柴胡', '[\'人参\', \'当归\', \'克山\', \'药酒\', \'熟地\', \'陈皮克\', \'甘草\', \'升麻\', \'柴胡\']');
INSERT INTO `prescript` VALUES (56346, '50998', '栀子清肝汤', '牛蒡子 柴胡 川芎 白芍 石膏 当归 山栀 丹皮各3克 黄芩 黄连 甘草各1.5克', '用水400毫升，煎取320毫升，食后服。', '《外科正宗》卷二', '治肝火风热上攻，遂成鬓疽，痛连颈项、胸乳，太阳等处，或寒热晡甚，胸胁满闷，口苦舌干者。', '-', '-', '柴胡', '[\'牛蒡子\', \'柴胡\', \'川芎\', \'白芍\', \'石膏\', \'当归\', \'丹皮\', \'黄芩\', \'黄连\', \'甘草\']');
INSERT INTO `prescript` VALUES (56347, '50995', '柴胡半夏汤', '柴胡 半夏各3克 黄芩 白术 陈皮 麦门冬各3克 甘草1.5克 姜3片 大枣2枚', '水煎，温服。\r\n小便不利，加茯苓；冬月无汗，加麻黄；三时无汗，加苏叶；冬月有汗，加桂枝；三时有汗，加防风；咽喉痛，加桔梗；喘嗽，去白术，加杏仁、桑白皮；酒热，加黄连；食积，加山楂、神曲；痰伏胁下作痛，加白芥子；痰甚喉中如牵锯，加竹沥、姜汁；痰稠如胶，加金沸草、前胡；胸膈痞闷，加枳壳。', '《医学入门）卷四', '主伤风发热恶寒，头痛无汗而咳嗽，或协热自利，兼治一切痰症，状似伤寒。', '-', '-', '柴胡', '[\'柴胡\', \'半夏\', \'黄芩\', \'白术\', \'陈皮\', \'麦门冬\', \'甘草\', \'姜片\', \'大枣\']');
INSERT INTO `prescript` VALUES (56348, '50980', '发火汤', '柴胡3克 甘草3克 茯神9克 炒枣仁9克 当归9克 陈皮9克 神曲3克 炒栀子3克 白芥子6克 白术6克 广木香末1.5克 远志3克', '水煎服。', '《辨证录》卷四', '主火郁为病，少气，胸、胁、腹背、面目、四肢填塞愤懑，时而呕逆，咽喉肿痛，口舌干苦，胃脘上下忽时作痛，或腹中暴痛，目赤头晕，心热烦闷，懊侬善暴死，汗濡皮毛，痰多稠浊，两颧红赤，身生痱疮。', '-', '-', '柴胡', '[\'柴胡\', \'甘草\', \'茯神\', \'枣仁\', \'当归\', \'陈皮克\', \'神曲\', \'栀子\', \'白芥子\', \'白术\', \'克广\', \'木香\', \'末克\', \'远志\']');
INSERT INTO `prescript` VALUES (56349, '50953', '益智和中汤', '肉桂0.3克 桂枝1.2克 牡丹皮 柴胡 葛根 益智仁 半夏各1.5克 当归身 炙甘草 黄耆 升麻各3克 白芍药4.5克 干姜少许', '水450毫升，煎至150毫升，去滓，空腹时温服。', '《兰室秘藏》卷下', '温中祛寒，益气升阳。治脾胃虚寒，肠辩下血，或血色紫黑，腹部冷痛，得热物熨之则减轻，右关脉弦，按之无力者。', '-', '-', '柴胡', '[\'肉桂\', \'桂枝\', \'牡丹皮\', \'柴胡\', \'葛根\', \'益智仁\', \'半夏\', \'当归\', \'甘草\', \'黄耆\', \'升麻\', \'各克白\', \'芍药\', \'干姜\', \'少许\']');
INSERT INTO `prescript` VALUES (56350, '50950', '升阳补气汤', '厚朴（姜制）1.5克 升麻 羌活 白芍药 独活 防风 甘草（炙）泽泻各3克 生地黄4.5克 柴胡7.5克', '每服15克，加生姜3片，大枣2枚，用水300毫升，煎至150毫升，去滓，空腹时大温服。\r\n腹胀，加厚朴；腹中似硬，加砂仁。', '《内外伤辨》卷中', '治饮食不时，饥饱劳役，胃气不足，脾气下溜，气短无力，不耐寒热，饭后昏闷，怠情困倦，五心烦热。', '-', '-', '柴胡', '[\'厚朴\', \'姜制克\', \'升麻\', \'羌活\', \'芍药\', \'独活\', \'防风\', \'甘草\', \'泽泻\', \'生地黄\', \'柴胡\']');
INSERT INTO `prescript` VALUES (56351, '50909', '泻血汤', '生地黄（酒洗，炒）熟地黄 蒲黄 丹参（酒炒）当归（酒洗，去土）汉防己（酒洗，，炒）柴胡（去芦）甘草梢（炙）羌活各30克 桃仁（去皮）9克（酒浸）', '每服15克，用水220毫升，煎至150毫升，去滓，空腹时温服。', '《兰室秘藏》卷下', '治热入血室，发热日轻夜重者。', '-', '-', '柴胡', '[\'生地黄\', \'酒洗\', \'熟地黄\', \'蒲黄\', \'丹参\', \'当归\', \'酒洗\', \'土汉\', \'防己\', \'酒洗\', \'柴胡\', \'去芦\', \'甘草\', \'羌活\', \'桃仁\', \'去皮\', \'克酒\']');
INSERT INTO `prescript` VALUES (56352, '50907', '消毒散', '青皮（去白）银花 天花粉 柴胡 僵蚕 贝母 当归（酒拌）白芷各6克', '水400毫升，煎至200毫升，空腹时服。', '《外科经验方》', '理气活血，解毒消肿。治吹乳，乳痈，便毒。', '-', '-', '柴胡', '[\'青皮\', \'白银\', \'天花粉\', \'柴胡\', \'僵蚕\', \'贝母\', \'当归\', \'酒拌\', \'白芷\']');
INSERT INTO `prescript` VALUES (56353, '50875', '首乌青蒿鳖甲饮', '赤色鲜首乌24克（打碎）青蒿9克 鳖甲15克 当归15克 柴胡4.5克 青皮6克 草蔻仁3克（研）', '水煎服。\r\n如口干，加知母6克，生甘草2.5克，如恶心，加藿香9克；如热甚烦躁，加石膏15克。', '《慈航集》卷下', '治温疟，阴虚发热，脉弱者。', '-', '-', '柴胡', '[\'赤色\', \'首乌\', \'打碎\', \'青蒿\', \'鳖甲\', \'当归\', \'柴胡\', \'青皮\', \'克草蔻\', \'仁克研\']');
INSERT INTO `prescript` VALUES (56354, '50862', '栀子乌梅汤', '栀子15克 黄芩15克 甘草15克（炙，微赤）柴胡30克 乌梅肉14枚（微炒用）', '每服12克，用水220毫升，加生姜3片，竹叶14片，豉50粒，煎至160毫升，去滓温服。', '《类证活人书》卷十八', '治伤寒后，虚烦不得眠，心中懊依。', '-', '-', '柴胡', '[\'栀子\', \'黄芩\', \'甘草\', \'克炙微\', \'柴胡\', \'乌梅\', \'肉枚\']');
INSERT INTO `prescript` VALUES (56355, '50850', '人参益气汤', '黄耆4克 生甘草 人参各15克 白芍药9克 柴胡7.5克 炙甘草 升麻各6克 五味子140个', '每服用水300毫升，煎至150毫升，去滓，空腹时温服。', '《兰室秘藏》卷下', '治热伤元气，两手指麻木，四肢困倦，怠惰嗜卧。', '-', '-', '柴胡', '[\'黄耆克\', \'生甘草\', \'人参\', \'各克白\', \'芍药\', \'柴胡\', \'甘草\', \'升麻\', \'五味子\']');
INSERT INTO `prescript` VALUES (56356, '50835', '金延香附汤加减', '金铃子10克，香附10克，元胡5克，枳实10克，大腹皮10克，黄连3克，吴萸1.5克，白芍10克，柴胡10克，良姜10克，香缘皮10克，煅瓦楞10克。', '水煎服，每日1剂。', '董建华方', '理气化瘀通络。主肝郁化火，气滞血瘀，久病入络。', '-', '-', '柴胡', '[\'金铃子\', \'香附\', \'克元\', \'胡克\', \'枳实\', \'大腹皮\', \'黄连\', \'吴萸克\', \'白芍\', \'柴胡\', \'克良\', \'姜克香缘\', \'皮克\', \'瓦楞\']');
INSERT INTO `prescript` VALUES (56357, '50828', '栀子乌梅汤', '栀子1钱，黄芩1钱，柴胡2钱，甘草1钱，乌梅2个，人参1钱，麦冬1钱，竹叶14片。', '上药加生姜、大枣，水煎服。', '《伤寒全生集》卷四', '治伤寒愈后，气阴两伤，虚烦不眠者。', '-', '-', '柴胡', '[\'栀子\', \'黄芩\', \'柴胡\', \'甘草\', \'乌梅\', \'人参\', \'麦冬\', \'钱竹\', \'叶片\']');
INSERT INTO `prescript` VALUES (56358, '50798', '再造活血止痛散', '大黄 红花各1.5克 当归 柴胡各6克 花粉 穿山甲各3克 桃仁50粒 甘草2.4克', '水、酒各半，煎，空腹时热服。', '《跌损妙方》', '治跌打损伤，瘀阻胸胁疼痛。', '-', '-', '柴胡', '[\'大黄\', \'红花\', \'当归\', \'柴胡\', \'花粉\', \'穿山甲\', \'桃仁\', \'甘草\']');
INSERT INTO `prescript` VALUES (56359, '50756', '全鳖丸', '知母 贝母（去心）杏仁（浸，去皮）各22.5克 柴胡60克 川芎30克 当归 明阿胶（炒酥）各15克', '每服70丸，米饮下，不拘时候。', '《仁斋直指》卷九', '治劳瘵，虚热嗽喘。', '-', '-', '柴胡', '[\'知母\', \'贝母\', \'去心\', \'杏仁\', \'去皮\', \'柴胡\', \'川芎\', \'当归\', \'阿胶\']');
INSERT INTO `prescript` VALUES (56360, '50753', '知母茯苓汤', '茯苓（去皮）甘草各30克 知母 五味子 人参 薄荷 半夏（洗四次）柴胡 白术 款冬花 桔梗 麦门冬 黄芩各15克 川芎9克 阿胶9克', '每服9克，用水220毫升，加生姜10片，同煎至160毫升，去滓，稍热服。', '《宜明论方》卷九', '治肺痿，喘咳不已，往来寒热，自汗；妇女胎前产后身热盗汗，咳嗽见血，胸胁疼痛。', '-', '-', '柴胡', '[\'茯苓\', \'去皮\', \'甘草\', \'知母\', \'五味子\', \'人参\', \'薄荷\', \'半夏\', \'四次\', \'柴胡\', \'白术\', \'款冬花\', \'桔梗\', \'麦门冬\', \'黄芩\', \'川芎\', \'阿胶\']');
INSERT INTO `prescript` VALUES (56361, '50729', '补中汤', '升麻 柴胡 当归各0.6克 神曲0.9克（炒）泽泻1.2克 大麦芽面 苍术各1.5克 黄耆7.5克 炙甘草2.4克 红花少许 五味子20个', '上药哎咀，分作二服。用水300毫升，煎取150毫升，去滓，空腹时服。', '《兰室秘藏》卷下', '主面黄，汗多，目赤，四肢沉重，食欲不振，腹中时痛，咳嗽，两手寸脉短，右手脉弦细兼涩，关脉虚。', '-', '-', '柴胡', '[\'升麻\', \'柴胡\', \'当归\', \'神曲\', \'泽泻\', \'大麦芽\', \'苍术\', \'黄耆\', \'甘草\', \'红花\', \'少许\', \'五味子\']');
INSERT INTO `prescript` VALUES (56362, '50706', '千金保童丸', '人参15克 白术15克 茯苓（去皮）10克芦荟3克 胡黄连6克 黄连（炒）10克 芜荑仁9克 使君子（去壳）10克 夜明砂（炒）10克 蚵皮2个（炒）龙胆草（去芦）10克 柴胡9克 苍术（米泔水浸，炒）10克 青皮（炒）10克 陈皮10克 砂仁7.5克 木香10克 槟榔10克 三棱（煨）10克 莪术（煨）10克 香附（炒）10克 枳实（麸炒）9克 神曲（炒）15克 山楂（去核）10克 麦芽（炒）15克 萝卜子（炒）15克 水红花子（炒）15克 加阿魏6克', '每服30～50丸，空腹时用米饮送下。', '《古今医鉴》卷十三', '消癣化积，清热杀虫。治小儿癖块。', '服药期间，忌猪肉。宜食鸽子、虾蟆。', '-', '柴胡', '[\'人参\', \'白术\', \'茯苓\', \'去皮\', \'芦荟\', \'胡黄连\', \'黄连\', \'炒克芜\', \'仁克\', \'使君子\', \'夜明砂\', \'炒克\', \'皮个\', \'龙胆草\', \'芦克\', \'柴胡\', \'苍术\', \'米泔水\', \'炒克\', \'青皮\', \'炒克\', \'陈皮克\', \'砂仁\', \'木香\', \'槟榔\', \'三棱\', \'莪术\', \'香附\', \'炒克\', \'枳实\', \'炒克\', \'神曲\', \'炒克\', \'山楂\', \'去核\', \'麦芽\', \'炒克\', \'萝卜\', \'炒克\', \'水红花子\', \'炒克加\', \'阿魏\']');
INSERT INTO `prescript` VALUES (56363, '50697', '导气汤', '黄耆24克 甘草18克 青皮12克 升麻 柴胡 当归梢 泽泻各6克 橘皮3克 红花3克 五味子120个', '每服用水400毫升，煎至200毫升，去滓，空腹时热服。', '《兰室秘藏》卷下', '主两腿麻木沉重。', '-', '-', '柴胡', '[\'黄耆克\', \'甘草\', \'青皮\', \'升麻\', \'柴胡\', \'当归\', \'泽泻\', \'橘皮\', \'红花\', \'五味子\']');
INSERT INTO `prescript` VALUES (56364, '50688', '天麻黄膏汤', '天麻 芍药 神曲（炒）羌活（肢节不痛 去之）茯苓各0.9克 人参 黄连各1.2克 当归1.5克 黄耆 甘草 升麻 葛根· 黄柏 苍术各1.8克 泽泻2.1克 柴胡2.7克', '上药哎咀，作一服。用水450毫升，煎至150毫升，去滓，空腹时温服。', '《兰室秘藏》卷下', '治表有风证，因连日酣饮，其证复来，右口角并眼颇有侧视，及左手左脚腿麻木疼痛。', '-', '-', '柴胡', '[\'天麻\', \'芍药\', \'神曲\', \'羌活\', \'肢节\', \'不痛\', \'茯苓\', \'人参\', \'黄连\', \'当归\', \'黄耆\', \'甘草\', \'升麻\', \'葛根\', \'黄柏\', \'苍术\', \'泽泻\', \'柴胡\']');
INSERT INTO `prescript` VALUES (56365, '50681', '柴胡鳖甲汤', '柴胡（去苗）鳖甲（去裙澜，醋炙令热）各30克 地骨皮45克 知母（焙）30克', '每服9克，用水150毫升，乌梅半个，青蒿少许，同煎至90毫升，去滓，食后，临卧温服。', '《圣济总录》卷八十九', '主虚劳夜多盗汗，面色萎黄，四肢无力，不思饮食，咳嗽不止。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'鳖甲\', \'炙令\', \'地骨皮\', \'知母\', \'焙克\']');
INSERT INTO `prescript` VALUES (56366, '50639', '柴胡半夏汤', '柴胡240克，人参90克 半夏75克 甘草（炙）90克 黄芩90克 白术60克 麦门冬（去心）30克', '每服15克，用水220毫升，入生姜5片，大枣1枚，同煎至180毫升，去滓温服，不拘时。', '《类证活人书》卷十七', '主痰热头痛，手足烦热，肢节倦怠，身体疼痛，嗜卧少力，饮食无味，兼治五饮、痰癣。', '-', '-', '柴胡', '[\'柴胡\', \'人参\', \'半夏\', \'甘草\', \'炙克\', \'黄芩\', \'白术\', \'麦门冬\', \'心克\']');
INSERT INTO `prescript` VALUES (56367, '50624', '温白丸', '紫菀22克 吴茱萸22克 菖蒲15克 柴胡15克 厚朴15克（炙）桔梗15克 皂角22克（去皮、籽，炙）乌头75克（熬）茯苓15克 桂心15克 干姜15克 黄连15克 蜀椒15克（汗）巴豆7.5克（熬）人参15克', '一服2丸，不知，渐增至5丸，以知为度。', '《外台秘要》卷十二引崔氏方', '温里祛寒，消症除癖。治心腹积聚、症癖。', '-', '-', '柴胡', '[\'紫菀\', \'吴茱萸\', \'菖蒲\', \'柴胡\', \'厚朴\', \'桔梗\', \'克皂角克\', \'去皮\', \'乌头\', \'克熬\', \'茯苓\', \'克桂心克\', \'干姜\', \'黄连\', \'克蜀椒克汗\', \'巴豆\', \'克熬\', \'人参\']');
INSERT INTO `prescript` VALUES (56368, '50620', '首乌白芍汤', '制首乌9克 北沙参9克 银柴胡4.5克白茯苓9克 黑驴胶6克（蛤扮炒）生白芍6克炒扁豆6克 扁石斛9克 生苡仁18克 生谷芽15克', '水煎服。', '《镐京直指医方》卷二', '治泄泻日久，肝脾阴伤者。', '-', '-', '柴胡', '[\'首乌\', \'北沙参\', \'银柴胡\', \'白茯苓\', \'黑驴\', \'胶克蛤扮\', \'炒生\', \'白芍\', \'扁豆\', \'克扁\', \'石斛\', \'克生\', \'仁克生\', \'谷芽克\']');
INSERT INTO `prescript` VALUES (56369, '50598', '益气养营汤', '人参 茯苓 陈皮 贝母 香附 当归（酒拌）川芎 黄耆（盐水拌炒）熟地黄（酒拌，芍药（炒）各1.5克 甘草（炙）桔梗（炒）柴胡各0.9克 白术（炒）3克', '加生姜，水煎服。成人倍用。\r\n胸痞，人参、熟地黄各减0.9克；口干，加五味子、麦门冬；往来寒热，加软柴胡、地骨皮；脓清，加人参、黄耆；脓多，加川芎、当归；脓不止，加人参、黄耆、当归；肌肉迟生，加白蔹、官桂。', '《保婴摄要》卷五', '治小儿气血损伤，四肢颈项等处患肿，不问软硬、赤白、痛否，日脯发热，或溃而不敛者。', '-', '-', '柴胡', '[\'人参\', \'茯苓\', \'陈皮\', \'贝母\', \'香附\', \'当归\', \'酒拌\', \'川芎\', \'黄耆\', \'盐水\', \'熟地黄\', \'酒拌\', \'芍药\', \'甘草\', \'桔梗\', \'柴胡\', \'白术\', \'炒克\']');
INSERT INTO `prescript` VALUES (56370, '50584', '小柴胡汤', '柴胡12克 黄芩9克 人参6克 半夏（洗）9克 甘草（炙）5克 生姜（切）9克 大枣（擘）4枚', '上药七味，以水1.2升，煮取600毫升，去滓，再煎取300毫升，分两次温服。\r\n若胸中烦而不呕，去半夏、人参，加栝楼实1枚；若渴，去半夏，人参加至9克，栝楼根12克；若腹中痛者，去黄芩，加芍药9克；若胁下痞梗，去大枣，加牡蛎12克；若心下悸，小便不利者，去黄芩，加茯苓12克；若不渴，外有微热者，去人参，加桂枝6克，温覆微汗愈；若咳者，去人参、大枣、生姜，加五味子5克，干姜5克。', '《伤寒论》', '和解少阳。治伤寒少阳证。往来寒热，胸胁苦满，嘿嘿不欲饮食，心烦喜呕，口苦，咽干，目眩；妇人伤寒，热入血室；疟疾、黄疸与内伤杂病而见少阳证者。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'人参\', \'半夏\', \'洗克\', \'甘草\', \'炙克\', \'生姜\', \'切克\', \'大枣\']');
INSERT INTO `prescript` VALUES (56371, '50569', '和血益气汤', '柴胡 炙草 生甘草 麻黄根各0.9克 酒当归梢1.2克 酒知母 酒汉肪己 羌活各15克 酒生地黄2.1克 升麻3克 杏仁 桃仁各6个 红花少许 酒黄连2.4克 石膏1.8克 酒黄柏3克', '用水600毫升，煎至300毫升，去滓温服。', '《兰室秘藏》卷上', '治口干舌干，小便数，舌上赤脉。', '-', '-', '柴胡', '[\'柴胡\', \'炙草\', \'生甘草\', \'麻黄根\', \'各克酒\', \'当归\', \'梢克酒\', \'知母\', \'酒汉肪己\', \'羌活\', \'各克酒\', \'生地黄\', \'升麻\', \'杏仁\', \'桃仁\', \'各个\', \'红花\', \'少许\', \'黄连\', \'石膏\', \'克酒\', \'黄柏\']');
INSERT INTO `prescript` VALUES (56372, '50563', '开郁至神汤', '人参3克 香附9克 茯苓6克 白术3克 当归6克 白芍15克 陈皮1.5克 甘草1.5克 炒栀子3克 柴胡15克', '水煎服。', '《辨证录》卷四', '治肝胆气郁，胃皖饱闷，胸胁腆胀，膈咽不通，吞酸吐食，见食则喜，食完作楚，头颊疼痛，耳鸣目眩，目不识人。', '-', '-', '柴胡', '[\'人参\', \'香附\', \'茯苓\', \'白术\', \'当归\', \'白芍\', \'陈皮克\', \'甘草\', \'栀子\', \'柴胡\']');
INSERT INTO `prescript` VALUES (56373, '50553', '柴胡加龙骨牡蛎汤', '柴胡12克 龙骨 黄芩 生姜 铅丹 人参 桂枝（去皮）茯苓各4.5克 半夏6克（洗）大黄6克（切）牡蛎4.5克（熬）大枣（擘）6枚', '上药十二味，除大黄外，以水800毫升，煮取400毫升，再纳大黄，更煮一二沸，去滓，每次温服100毫升。', '《伤寒论》', '和解清热，镇惊安神。主伤寒往来寒热，胸胁苦满，烦躁惊狂不安，时有谵语，身重难以转侧，现用于癫痫、神经官能症、美尼尔氏综合征以及高血压病等见有胸满烦惊为主证者。', '-', '-', '柴胡', '[\'柴胡\', \'龙骨\', \'黄芩\', \'生姜\', \'铅丹\', \'人参\', \'桂枝\', \'去皮\', \'茯苓\', \'半夏\', \'克洗\', \'大黄\', \'克切\', \'牡蛎\', \'克熬\', \'大枣\']');
INSERT INTO `prescript` VALUES (56374, '50539', '青蒿饮', '青蒿 甘草（炙）柴胡（去苗）知母（焙）龙骨 麦门冬（去心）各30克 桃枝 柳枝各1握', '上八味，锉如麻豆。每服12克，以童便220毫升，浸经宿，入葱白、薤白各10厘米，切，同煎至160毫升，去滓，食后温服。', '《圣济总录》卷八十七', '治暴急热劳，四肢烦疼，手脚心热，咽干虚渴，饮食减少。', '-', '-', '柴胡', '[\'青蒿\', \'甘草\', \'柴胡\', \'去苗\', \'知母\', \'龙骨\', \'麦门冬\', \'去心\', \'各克桃枝\', \'柳枝\', \'各握\']');
INSERT INTO `prescript` VALUES (56375, '50518', '午时茶', '茅术300克 陈皮300克 柴胡300克 连翘300克 白芷300克 川朴450克 枳实300克 楂肉300克 羌活300克 前胡300克 防风300克 藿香300克 甘草300克 陈茶10千克 桔梗450克 麦芽450克 苏叶450克 建曲300克 川芎300克', '每服9克，加葱、姜少许煎，热服。汗出即效。', '《经验百病内外方》', '治外感风寒，内停食滞，及不服水土，腹泻腹痛。', '-', '-', '柴胡', '[\'茅术克\', \'陈皮克\', \'柴胡\', \'连翘\', \'白芷\', \'克川\', \'朴克\', \'枳实\', \'克楂\', \'肉克\', \'羌活\', \'克前\', \'胡克\', \'防风\', \'藿香\', \'甘草\', \'陈茶\', \'桔梗\', \'麦芽\', \'克苏叶克\', \'建曲克\', \'川芎\']');
INSERT INTO `prescript` VALUES (56376, '50508', '加味温胆汤', '香附7.2克 橘红3.6克 半夏 竹茹 枳实各2.4克 人参 茯苓 柴胡 麦冬 桔梗各1.8克 甘草1.2克', '上药加生姜3片、大枣2枚，水煎服。', '《杂病源流犀烛》卷六', '行气解郁，化痰养心。治失志郁抑，痰涎沃心，怔忡不寐者。', '-', '-', '柴胡', '[\'香附\', \'橘红\', \'半夏\', \'竹茹\', \'枳实\', \'人参\', \'茯苓\', \'柴胡\', \'麦冬\', \'桔梗\', \'甘草\']');
INSERT INTO `prescript` VALUES (56377, '50507', '五苓平胃汤', '柴胡4.5克 黄芩 苍术 半夏各3克 甘草0.9克 白术4.5克 陈皮 茯苓 厚朴 猪苓 泽泻各2.4克 桂枝1.5克', '加生姜、大枣，水煎服。', '《嵩崖尊生》卷九', '治疟疾初起，热多寒少。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'苍术\', \'半夏\', \'甘草\', \'白术\', \'陈皮\', \'茯苓\', \'厚朴\', \'猪苓\', \'泽泻\', \'桂枝\']');
