INSERT INTO `prescript` VALUES (56422, '49882', '消瘿汤', '元参15克，生牡蛎（先煎）30克，浙贝母9克，夏枯草15克，海浮石12克，香附12克，青皮9克，当归18克，海藻24克，昆布24克，柴胡9克，红花12克，半夏12克。', '水煎服，每日服1剂，日服2次。', '王树元方', '理气化痰，活血散瘀。主气滞痰凝。', '-', '-', '柴胡', '[\'参克生\', \'牡蛎\', \'先煎克\', \'浙贝母\', \'夏枯草\', \'海浮石\', \'香附\', \'青皮\', \'当归\', \'海藻\', \'昆布\', \'柴胡\', \'红花\', \'半夏\']');
INSERT INTO `prescript` VALUES (56423, '49877', '人参羌活散', '柴胡（去苗）独活（去芦）羌活（去苗）各60克 人参（去芦）芎藭 枳壳（去瓤，麸炒）茯苓（去皮）甘草（炙）各30克 桔梗 前胡 天麻（酒浸，炙）地甘皮（去土）各15克', '每服3克，用水100毫升，入薄荷少许，前至80毫升，去滓温服，不计时候。', '《太平惠民和剂局方》卷十', '治小儿寒邪湿病，时疫疮疹，头痛体疼，壮热多睡；潮热烦渴，痰实咳嗽。亦治肝经有热，眼目涩痒昏朦。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'独活\', \'去芦\', \'羌活\', \'去苗\', \'人参\', \'去芦\', \'枳壳\', \'茯苓\', \'去皮\', \'甘草\', \'桔梗\', \'天麻\', \'浸炙地\', \'甘皮\']');
INSERT INTO `prescript` VALUES (56424, '49855', '退热清气汤', '柴胡 橘皮 茯苓各3克 半夏 枳壳各2.4克 香附2克 川芎1.5克 砂仁7粒 木香 甘草各1克', '加生姜同煎，温服。', '《医学入门》卷七', '理气和胃。治气逆身热，中脘痞满。', '-', '-', '柴胡', '[\'柴胡\', \'橘皮\', \'茯苓\', \'半夏\', \'枳壳\', \'香附\', \'川芎\', \'砂仁\', \'木香\', \'甘草\']');
INSERT INTO `prescript` VALUES (56425, '49854', '秦艽鳖甲散', '柴胡 鳖甲（去裙襕，酥炙，用九肋者）地骨皮各30克 秦艽 当归 知母各15克', '每次15克，用水200毫升，加青蒿5叶，乌梅1个，煎至140毫升，去滓，临卧、空腹各一服。', '《卫生宝鉴》卷五', '滋阴养血，退热除蒸。治虚劳阴亏血虚，骨蒸壮热，肌肉消瘦，唇红颊赤，困倦盗汗。', '-', '-', '柴胡', '[\'柴胡\', \'鳖甲\', \'肋者\', \'地骨皮\', \'秦艽\', \'当归\', \'知母\']');
INSERT INTO `prescript` VALUES (56426, '49837', '栀子仁汤', '栀子仁30克 柴胡45克（去苗）川升麻60克 黄芩60克 赤芍30克 大青30克 石膏60克 知母30克 甘草15克（炙赤，锉）杏仁60克（汤浸，去皮、尖、双仁，麸炒微黄）', '每服12克，以水200毫升，入生姜4克，豉100粒，煎至120毫升，去滓，不计时候，温服。', '《类证活人书》卷十六', '治阳毒伤寒，壮热，百节疼痛。', '-', '-', '柴胡', '[\'栀子\', \'仁克\', \'柴胡\', \'克去\', \'苗川\', \'升麻\', \'黄芩\', \'赤芍\', \'克大青克\', \'石膏\', \'知母\', \'甘草\', \'克炙赤\', \'杏仁\', \'克汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (56427, '49836', '祛劳汤', '柴胡（去苗）常山（锉）鳖甲（去裙襕，醋浸，炙）知母（切，焙）青蒿（千者）甘草（炙，锉）枳壳（去瓤，麸炒）桂（去粗皮）各30克 木香15克', '每服15克，水220毫升，入柳枝心7枚，葱白3枚，煎至150毫升，去滓，空腹时温服。', '《圣济总录》卷三十五', '治劳疟经久不愈，寒热消瘦。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'常山\', \'鳖甲\', \'知母\', \'切焙\', \'青蒿\', \'千者\', \'甘草\', \'枳壳\', \'炒桂去\', \'粗皮\', \'木香\']');
INSERT INTO `prescript` VALUES (56428, '49820', '加味茯苓汤', '苍术6克 白术9克 茯苓3克 甘草（炙）1.5克 猪苓2.4克 泽泻3克 升麻1.5克 肉桂2.1克 柴胡1.8克 黄芩3克 生草1.5克 当归3克 白芍2.1克', '用水400毫升，煎至200毫升，温服。', '《杏苑生春》卷四', '健脾利湿。治中气不足，水湿壅盛，阳气郁遏，不得上达，以致水泄注下，日夜无度，小便短少，口渴咽干，腹中疼痛，或变成赤白痢疾者。', '-', '-', '柴胡', '[\'苍术\', \'白术\', \'茯苓\', \'甘草\', \'炙克\', \'猪苓\', \'泽泻\', \'升麻\', \'肉桂\', \'柴胡\', \'黄芩\', \'克生\', \'草克\', \'当归\', \'白芍\']');
INSERT INTO `prescript` VALUES (56429, '49813', '补中益气汤', '黄耆 甘草（炙）各1.5克 人参（去芦）0.9克 当归身0.6克（酒焙干或晒干）橘皮（不去白）0.6～0.9克 升麻0.6～0.9克 柴胡0.6～0.9克 白术0.9克', '上药哎咀，都作一服。用水300毫升，煎至150毫升，去滓，空腹时稍热服。', '《脾胃论》卷中', '补中益气，升阳举陷，主脾胃气虚，少气懒言，四肢无力，困倦少食，饮食乏味，不耐劳累，动则气短；或气虚发热，气高而喘，身热而烦，渴喜热饮，其脉洪大，按之无力，皮肤不任风寒，而生寒热头痛；或气虚下陷，久泻脱肛。现用于子宫下垂；胃下垂或其它内脏下垂者。\r\n病甚劳役、热甚者，黄耆加至3克，咳嗽者，去人参；腹中痛者，加白芍药1.5克、炙甘草1.5克；若恶热喜寒而腹痛者；再加黄芩0.6～0.9克；恶寒冷痛，加桂心0.3～0.9克；头痛，加蔓荆子0.6～0.9克；痛甚者加川芎0.6克；顶痛、脑痛，加藁本0.9～1.5克。', '阴虚内热者忌服。', '-', '柴胡', '[\'黄耆\', \'甘草\', \'人参\', \'芦克\', \'当归\', \'身克酒\', \'焙干\', \'晒干\', \'橘皮\', \'不去\', \'升麻\', \'柴胡\', \'白术\']');
INSERT INTO `prescript` VALUES (56430, '49812', '芦荟肥儿丸', '五谷虫（炒）60克 芦荟（生）胡黄连（炒）川黄连（姜炒）各30克 银柴胡（炒）36克 扁豆（炒）山药（炒）各60克 南山楂75克 虾蟆（煅）4个 肉豆蔻（煨）21克 槟榔15克 使君子（炒）75克 神曲（炒）60克 麦芽（炒）48克 鹤虱（炒）24克 芜荑（炒）30克 朱砂（飞）6克 麝香6克', '每服3克，米饮送下。', '《医宗金鉴》卷五十二', '清肝健脾，消积杀虫。治肝疳。面目爪甲皆青，眼生眵泪，隐涩难睁，摇头揉目，合面睡卧，耳疮流脓，腹大青筋，身体羸瘦，燥渴烦急，粪青如苔。', '-', '-', '柴胡', '[\'五谷\', \'炒克\', \'芦荟\', \'胡黄连\', \'炒川\', \'黄连\', \'银柴胡\', \'炒克\', \'扁豆\', \'山药\', \'克南\', \'山楂\', \'虾蟆\', \'煅个\', \'肉豆蔻\', \'槟榔\', \'使君子\', \'炒克\', \'神曲\', \'炒克\', \'麦芽\', \'炒克\', \'鹤虱\', \'炒克芜\', \'炒克\', \'朱砂\', \'飞克\', \'麝香\']');
INSERT INTO `prescript` VALUES (56431, '49810', '三黄补血汤', '牡丹皮 黄耆 升麻各3克 当归 柴胡各4.5克 熟地黄 川芎各6克 生地黄9克 白芍药15克', '每服15克，用水600毫升，煎至300毫升，去滓，空腹时稍热服。', '《兰室秘藏》卷中', '补气养血。治亡血血虚。面赤善惊，六脉俱大，按之空虚者。', '-', '-', '柴胡', '[\'牡丹皮\', \'黄耆\', \'升麻\', \'当归\', \'柴胡\', \'熟地黄\', \'川芎\', \'生地黄\', \'克白\', \'芍药\']');
INSERT INTO `prescript` VALUES (56432, '49800', '秦艽防风汤', '秦艽 防风 当归身 白术各4.5克 炙甘草 泽泻各1.8克 黄柏1.5克 大黄（煨）橘皮各0.9克 柴胡 升麻各0.6克 桃仁30个 红花少许', '上药锉碎，用水450毫升，煎至150毫升，去滓，空腹时稍热服。', '《兰室秘藏》卷下', '治痔漏，每日大便时发疼痛。', '服药期间，忌房事，避风寒，不吃辛热之物。', '-', '柴胡', '[\'秦艽\', \'防风\', \'当归\', \'白术\', \'甘草\', \'泽泻\', \'黄柏\', \'大黄\', \'橘皮\', \'柴胡\', \'升麻\', \'桃仁\', \'红花\', \'少许\']');
INSERT INTO `prescript` VALUES (56433, '49784', '三灵汤', '当归6克 白芍3克 羚羊角4.5克 龙齿6克 石决18克 半夏曲9克 柴胡3克 葛根6克 茯神6克 白术3克 青皮3克', '用冬瓜子9克煎汤代水，煎药服。', '《医醇剩义》卷四', '治肝痹，夜卧则惊，口渴多饮，小便频数，脾虚作胀，腹大如怀。', '-', '-', '柴胡', '[\'当归\', \'白芍\', \'羚羊角\', \'龙齿\', \'克石\', \'决克\', \'半夏\', \'曲克\', \'柴胡\', \'葛根\', \'茯神\', \'白术\', \'青皮\']');
INSERT INTO `prescript` VALUES (56434, '49770', '草豆蔻丸', '泽泻0.3克（小便数减半）柴胡0.6～1.2克（消息胁痛多少用）神曲 姜黄各1.2克 当归身 生甘草 熟甘草 青皮各1.8克 桃仁（汤洗，去皮、尖）2.1克 白僵蚕 吴茱萸（汤洗去苦烈味，焙干）益智仁 黄耆 陈皮 人参各2.4克 半夏3克（汤洗七次）草豆蔻仁4.2克（面裹烧，面熟为度，去皮用仁）麦蘖面（炒黄）4.5克', '每服30～50丸，白开水送下。', '《脾胃论》卷下', '主脾胃气虚，恶风怕寒，耳鸣，腰背疼痛，鼻息不通，不闻香臭，额寒脑痛，目眩，食入反出，心胃疼痛，咽膈不通，四肢厥逆，身体沉重，不能转侧，头项转动不利。', '-', '-', '柴胡', '[\'泽泻\', \'小便\', \'减半\', \'柴胡\', \'消息\', \'胁痛\', \'多少\', \'神曲\', \'姜黄\', \'当归\', \'生甘草\', \'甘草\', \'青皮\', \'桃仁\', \'汤洗\', \'皮尖\', \'白僵蚕\', \'吴茱萸\', \'汤洗\', \'苦烈\', \'焙干\', \'益智仁\', \'黄耆\', \'陈皮\', \'人参\', \'半夏\', \'克汤\', \'七次\', \'草豆蔻\', \'仁克面\', \'面熟\', \'为度\', \'去皮\', \'用仁\', \'麦蘖\', \'黄克\']');
INSERT INTO `prescript` VALUES (56435, '49756', '柴胡散', '柴胡30克（去苗）茵陈15克 犀角屑15克 麦门冬30克（去心）鳖甲60克（涂醋，炙微煮，去裙襕）甘草15克（炙微赤，锉）', '每服12克，以水300毫升，煎至180毫升，去滓，不计时候温服。', '《太平圣惠方》卷五十五', '主劳黄。证见四肢无力，骨节烦疼，或时吐逆，不能不食，鼻中干燥，身热疼闷，渐觉羸瘦，寒热不定。', '-', '-', '柴胡', '[\'柴胡\', \'克去\', \'茵陈\', \'犀角\', \'麦门冬\', \'克去\', \'鳖甲\', \'克涂\', \'甘草\', \'克炙微\']');
