INSERT INTO `prescript` VALUES (56437, '49716', '通气防风汤', '防风 羌活 陈皮 人参 甘草各1.5克 藁本 青皮各0.9克 白豆蔻 黄柏各0.6克 升麻 柴胡 黄耆各3克', '上哎咀，都作一服。用水300毫升，煎至150毫升，空腹时去滓温服。\r\n如面白脱色，气短者，不可服。', '《内外伤辨》卷中', '治风热外乘，肺气郁甚，肩背痛，汗出，小便数而少。', '-', '-', '柴胡', '[\'防风\', \'羌活\', \'陈皮\', \'人参\', \'甘草\', \'藁本\', \'青皮\', \'白豆蔻\', \'黄柏\', \'升麻\', \'柴胡\', \'黄耆\']');
INSERT INTO `prescript` VALUES (56438, '49697', '化斑汤', '黑参6克 鼠粘子3克 柴胡2.4克 荆芥 防风各1.8克 连翘2.1克 木通2.4克 枳壳2.1克 蝉蜕1.5克 生甘草1.2克 灯心20茎 淡竹叶15片', '水煎，温服，一日二三次。', '《张氏医通》卷十五', '化斑透疹。治痘与斑夹出者。', '-', '-', '柴胡', '[\'参克鼠\', \'粘子克\', \'柴胡\', \'荆芥\', \'防风\', \'连翘\', \'木通\', \'枳壳\', \'蝉蜕\', \'生甘草\', \'灯心\', \'淡竹叶\']');
