INSERT INTO `prescript` VALUES (56543, '47820', '柴胡疏肝散', '陈皮（醋炒）柴胡各6克 川芎 枳壳（麸炒）芍药各4.5克 甘草（炙）1，5克 香附4.5克', '用水220毫升，煎至180毫升，空腹时服。', '《景岳全书》卷五十六', '疏肝解郁。主胁肋疼痛，寒热往来。', '-', '-', '柴胡', '[\'陈皮\', \'柴胡\', \'川芎\', \'枳壳\', \'芍药\', \'甘草\', \'炙克\', \'香附\']');
