INSERT INTO `prescript` VALUES (56545, '47793', '柴胡丸', '柴胡（去苗）黄连（去须）知母（焙）赤芍药 龙胆 黄芩（去黑心）地骨皮 麦门冬（去心，掐）茯神（去木）甘草（炙）各30克 槟榔（锉）22克。', '每服20丸 温酒下，不拘时。', '《圣济总录》卷一五○', '主妇人血风劳气，头目昏眩，胸背拘急，四肢酸痛，心躁烦热，气满腹胀，腰膝无力，经候不调。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'黄连\', \'须知\', \'母焙\', \'芍药\', \'龙胆\', \'黄芩\', \'黑心\', \'地骨皮\', \'麦门冬\', \'茯神\', \'甘草\', \'槟榔\', \'锉克\']');
INSERT INTO `prescript` VALUES (56546, '47788', '大柴胡汤', '柴胡15克 枳实9克（炙）生姜15克（切）黄芩9克 芍药9克 半夏9克（洗）大枣12枚（擘）一方有大黄6克', '上七味，用水1.2升，煮取600毫升，去滓再煎，温服200毫升，日三服。', '《伤寒论》', '和解少阳，内泻热结。主少阳、阳明合病，往来寒热，胸胁苦满，呕不止，郁郁微烦，心下痞硬或满痛，大便秘结，或协热下利，舌苔黄，脉弦有力者。现用本方加减治疗急性胰腺炎、急性胆囊炎、胆石症等见有上述证候者。', '-', '-', '柴胡', '[\'柴胡\', \'枳实\', \'生姜\', \'克切\', \'黄芩\', \'芍药\', \'半夏\', \'克洗\', \'大枣\', \'一方\', \'大黄\']');
INSERT INTO `prescript` VALUES (56547, '47779', '大连翘饮', '连翘 瞿麦 滑石 车前子 牛蒡子 赤芍 山栀 木通 当归 防风 黄芩 柴胡 甘草 荆芥 蝉蜕 石膏各1.5克', '用水400毫升，加灯心20根，煎至320毫升，母子同服。', '《外科正宗》卷四', '主小儿丹毒发热，痰涎壅盛，一切诸疮痧疹，颈项生核，或伤风、伤寒、时行发热。', '-', '-', '柴胡', '[\'连翘\', \'瞿麦\', \'滑石\', \'车前子\', \'牛蒡子\', \'赤芍\', \'木通\', \'当归\', \'防风\', \'黄芩\', \'柴胡\', \'甘草\', \'荆芥\', \'蝉蜕\', \'石膏\']');
