INSERT INTO `prescript` VALUES (56561, '47530', '荆防败毒散', '羌活 独活 柴胡 前胡 枳壳 茯苓 防风 荆芥 桔梗 川芎各4.5克 甘草15克', '上药用水300毫升，煎至240毫升，温服。', '《摄生众妙方》卷八', '疏风解表，败毒消肿。治凤寒感冒初起，恶寒发热，头疼身痛，苔白，脉浮者；疮肿初起，见表寒证者。', '-', '-', '柴胡', '[\'羌活\', \'独活\', \'柴胡\', \'枳壳\', \'茯苓\', \'防风\', \'荆芥\', \'桔梗\', \'川芎\', \'甘草\']');
