INSERT INTO `prescript` VALUES (56563, '47509', '夏枯草汤', '夏枯草6克 当归9克 白术 茯苓 桔梗 陈皮 生地 柴胡 甘草 贝母 香附 白芍各3克 白芷 红花各0.9克', '先将夏枯草用水750毫升，煎至500毫升，滤清，同药煎至400毫升，食后服，将药滓同前夏枯草滓，共再煎至300～350毫升，临卧时入酒适量和服。宜食淡味物件。', '《外科正宗》卷二', '治瘰疬、马刀，不问已溃来溃，或已溃日久成漏，形体消瘦，饮食不甘，寒热如疟，渐成劳瘵。', '-', '-', '柴胡', '[\'夏枯草\', \'当归\', \'白术\', \'茯苓\', \'桔梗\', \'陈皮\', \'生地\', \'柴胡\', \'甘草\', \'贝母\', \'香附\', \'白芍\', \'白芷\', \'红花\']');
INSERT INTO `prescript` VALUES (56564, '47495', '当归四逆汤', '当归尾2.1克 附子（炮）官桂 茴香（炒）柴胡各1.5克 芍药1.2克 茯苓 玄胡索 川楝子各0.9克（酒煮）泽泻0.6克', '用水350毫升，煎至150毫升，去滓，空腹时温服。', '《卫生宝鉴》卷十八', '主疝气，脐腹冷痛，牵引腰胯。', '-', '-', '柴胡', '[\'当归\', \'尾克\', \'附子\', \'炮官桂\', \'茴香\', \'柴胡\', \'芍药\', \'茯苓\', \'玄胡索\', \'川楝子\', \'各克酒\', \'泽泻\']');
INSERT INTO `prescript` VALUES (56565, '47469', '柴胡达原饮', '柴胡4.5克 生枳壳4.5克 川朴4.5克 青皮4.5克 炙草2.1克 黄芩4.5克 苦桔梗3克 草果1.8克 槟榔6克 荷叶梗16厘米', '水煎服。', '《重订通俗伤寒论》', '和中化湿，祛痰止疟。主痰疟。痰湿阻于膜原，胸膈痞满，心烦懊憹，头眩口腻，咳痰不爽，间日疟发，舌苔粗如积粉，扪之糙涩者。', '-', '-', '柴胡', '[\'柴胡\', \'克生\', \'枳壳\', \'克川\', \'朴克\', \'青皮\', \'克炙草克\', \'黄芩\', \'克苦\', \'桔梗\', \'草果\', \'槟榔\', \'荷叶\', \'厘米\']');
INSERT INTO `prescript` VALUES (56566, '47452', '乙肝解毒汤', '方1：黄芪30克，虎杖15克，白花蛇舌草15克，露蜂房15克，半枝莲15克，仙鹤草15克，土茯苓15克，仙灵脾15克，鹿衔草15克，柴胡12克，郁金12克，当归12克，生鸡内金12克，桂枝9克，甘草3克。\r\n方2：雄黄30克（色赤如鸡冠者为上品，效高毒性小），生白矾（即酸矾）15克，五灵脂60克，共研细末装胶囊。', '方1煎服，先将诸药用冷水浸泡半小时，沸后再煎5分钟即可。\r\n方2胶囊用汤药或温开水送服均可。每次吞服0.3克（2粒），每日2次。汤药、胶囊均6日一个疗程。', '来春茂方', '清化解毒，活血祛瘀，健脾疏肝，益气湿肾。主湿热邪毒内蕴，肝郁血瘀，脾肾两亏，营卫失调。慢性乙型肝炎。', '-', '-', '柴胡', '[\'黄芪\', \'虎杖\', \'白花蛇\', \'舌草克\', \'露蜂房\', \'半枝\', \'莲克\', \'仙鹤草\', \'土茯苓\', \'仙灵脾\', \'鹿衔草\', \'柴胡\', \'郁金\', \'当归\', \'克生\', \'鸡内金\', \'桂枝\', \'甘草\', \'克方\', \'雄黄\', \'克色\', \'鸡冠\', \'上品\', \'效高\', \'毒性\', \'小生\', \'白矾\', \'即酸矾克\', \'五灵脂\', \'共研\', \'细末装\', \'胶囊\']');
INSERT INTO `prescript` VALUES (56567, '47433', '柴胡复生汤', '藁本 川芎各1克 白芍药1.2克 蔓荆子 羌活 独活 白芷各1克 柴胡1.8克 炙甘草 薄荷 桔梗各1.2克 五味子20粒 苍术 茯苓 黄芩各1.5克', '上药作一服。用水300毫升，煎至150毫升，去滓，食后热服。', '《原机启微》卷下', '疏风祛湿，清热和血。主风湿热邪上攻，目赤羞明，泪多眵少，脑巅沉重，睛珠痛应太阳，眼睫无力，常欲垂闭，不敢久视，久视则瘦疼，翳膜陷下者。', '-', '-', '柴胡', '[\'藁本\', \'川芎\', \'各克白\', \'芍药\', \'蔓荆子\', \'羌活\', \'独活\', \'白芷\', \'柴胡\', \'甘草\', \'薄荷\', \'桔梗\', \'五味子\', \'苍术\', \'茯苓\', \'黄芩\']');
INSERT INTO `prescript` VALUES (56568, '47432', '消肿汤', '黍粘子（炒）黄连各1.5克 当归梢 甘草各3克 瓜蒌根 黄耆各4.5克 生黄芩 柴胡各6克 连翘9克 红花少许', '上哎咀。每服15克，用水300毫升，煎至150毫升，去滓稍热，食后服。', '《兰室秘藏》卷下', '治马刀疮。', '服药期间，忌服酒、湿面。', '-', '柴胡', '[\'粘子\', \'黄连\', \'当归\', \'甘草\', \'各克瓜\', \'黄耆\', \'各克生\', \'黄芩\', \'柴胡\', \'连翘\', \'红花\', \'少许\']');
INSERT INTO `prescript` VALUES (56569, '47430', '清胆化石汤', '柴胡6克，鹅不食草15克，延胡6克，金钱草15克，金铃子10克，黄芩9克，郁金6克，通草3克，蒲公英12克，北茵陈15克。', '水煎服，每日1剂，日服2次。', '翁恭方', '疏肝理气，清热化滞，利胆排石。主肝气郁结，木郁化火。', '-', '-', '柴胡', '[\'柴胡\', \'鹅不食草\', \'克延\', \'胡克\', \'金钱草\', \'金铃子\', \'黄芩\', \'郁金\', \'通草\', \'蒲公英\', \'克北\', \'茵陈\']');
INSERT INTO `prescript` VALUES (56570, '47424', '疏肝散', '黄连（吴茱萸煎水炒）6克 柴胡 当归（酒洗）各4.5克 青皮（去瓤）桃仁（研如泥）枳壳（麸炒）各3克 川芎 白芍（酒炒）各2.1克 红花1.5克', '上药，锉散。水煎，空腹时服。', '《寿世保元》卷五', '疏肝理气，活血通络。治肝经气滞血瘀，左胁下痛者。', '-', '-', '柴胡', '[\'黄连\', \'吴茱萸\', \'炒克\', \'柴胡\', \'当归\', \'酒洗\', \'青皮\', \'桃仁\', \'研如泥\', \'枳壳\', \'川芎\', \'白芍\', \'红花\']');
INSERT INTO `prescript` VALUES (56571, '47421', '除湿补气汤', '升麻18克 苍术12克 酒黄柏 柴胡 黄耆各9克 酒知母 藁本 生甘草 当归各6克 五味子 陈皮各4.5克', '每服15克，用水300毫升，煎至150毫升，去滓，早起空腹时服，待少时再进早饭。', '《兰室秘藏》卷下', '清热燥湿，益气升阳。主湿热内蕴，脾气不足，两腿麻木，沉重无力，多汗喜笑，口中涎下，身重如山，语声不出，寸脉洪大。', '-', '-', '柴胡', '[\'升麻\', \'苍术\', \'克酒\', \'黄柏\', \'柴胡\', \'黄耆\', \'各克酒\', \'知母\', \'藁本\', \'生甘草\', \'当归\', \'五味子\', \'陈皮\']');
