INSERT INTO `prescript` VALUES (56574, '47377', '柴胡饮子', '柴胡 人参 黄芩 甘草 大黄 当归 芍药各15克', '每服9克，用水150毫升，加生姜3片，煎至100毫升，温服。日三服。', '《宣明论方》卷四', '主伤寒发汗不解；或中外诸邪热，口干烦渴；或下后热未除，汗后劳复；或骨蒸肺痿喘嗽，妇人余疾，产后经病。', '-', '-', '柴胡', '[\'柴胡\', \'人参\', \'黄芩\', \'甘草\', \'大黄\', \'当归\', \'芍药\']');
