INSERT INTO `prescript` VALUES (56581, '47319', '柴胡饮', '北柴胡（去芦，净洗）人参（去芦）当归（酒洗）黄芩 赤芍药 甘草（炙）各30克 大黄（生用）桔梗（去芦，锉，炒）北五味（去梗）半夏各15克', '每服6克，用水150毫升，加乌梅1个，生姜2片，煎至100毫升，不拘时温服。', '《证治准绳·幼科》卷三', '主小儿骨蒸疳气，五心烦热，日哺转甚，口干无味，渴多身瘦，胸满痰多，小便黄色，食减神昏。', '-', '-', '柴胡', '[\'柴胡\', \'芦净\', \'人参\', \'去芦\', \'当归\', \'酒洗\', \'黄芩\', \'芍药\', \'甘草\', \'大黄\', \'生用\', \'桔梗\', \'去芦\', \'炒北\', \'五味\', \'半夏\']');
INSERT INTO `prescript` VALUES (56582, '47314', '川芎肉桂汤', '酒汉防己 防风各0.9克 炒神曲 独活各1.5克 川芎、柴胡 肉桂 当归梢 炙甘草 苍术各3克 羌活4.5克 桃仁5个（去皮，尖，研如泥）', '用好酒900毫升，熬至300毫升，去滓稍热，空腹时服。', '《兰室秘藏》卷中', '主感受寒湿，腰痛不能转侧，两胁搐急作痛者。', '-', '-', '柴胡', '[\'酒汉\', \'防己\', \'防风\', \'神曲\', \'独活\', \'川芎\', \'柴胡\', \'肉桂\', \'当归\', \'甘草\', \'苍术\', \'羌活\', \'桃仁\', \'皮尖\', \'研如泥\']');
INSERT INTO `prescript` VALUES (56583, '47309', '四物逍遥汤', '柴胡9克，当归9克，川芎9克，香附9克，延胡索9克，桃仁9克，红花9克，赤芍12克，生地12克，青皮6克。', '水煎服，每日1剂，日服2次。', '朱南荪方', '疏肝解郁，利气调经。主肝郁气滞。', '-', '-', '柴胡', '[\'柴胡\', \'当归\', \'川芎\', \'香附\', \'延胡索\', \'桃仁\', \'红花\', \'赤芍\', \'生地\', \'青皮\']');
INSERT INTO `prescript` VALUES (56584, '47304', '人参败毒散', '柴胡（去苗）甘草（烂）桔梗 人参（去芦）芎藭 茯苓（去皮）枳壳（去瓤，麸炒）前胡（去苗，洗）羌活（去苗）独活（去苗）各900克', '每服6克，用水150毫升，入生姜、薄荷各少许，同煎至100毫升，去滓，不拘时候，寒多则热服，热多则温服。', '《太平惠民和剂局方》卷二', '益气解表，散风祛湿。治伤寒时气，头痛项强，壮热恶寒，身体烦疼，及寒壅咳嗽，鼻塞声重，风痰头痛，呕哕寒热。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'甘草\', \'桔梗\', \'人参\', \'去芦\', \'茯苓\', \'去皮\', \'枳壳\', \'胡去\', \'苗洗\', \'羌活\', \'去苗\', \'独活\', \'去苗\']');
INSERT INTO `prescript` VALUES (56585, '47293', '人参白术汤', '人参 黄芩 柴胡 干葛 栀子仁 甘草（炙）各15克 白术 防风 半夏（泡七次）各?克', '每服12克，加生姜3片，水煎服。', '《丹溪心法》卷三', '治气虚呃逆。', '-', '-', '柴胡', '[\'人参\', \'黄芩\', \'柴胡\', \'干葛\', \'栀子\', \'甘草\', \'白术\', \'防风\', \'半夏\', \'七次\']');
INSERT INTO `prescript` VALUES (56586, '47270', '龙脑鸡苏丸', '柴胡（要真银川者）60克（锉，同木通以沸汤100毫升浸一二宿，绞汁后入膏）木通（锉，同柴胡浸）阿胶（炒微燥）蒲黄（真者，微炒）人参各60克 麦门冬（汤洗，去心，焙干）120克 黄耆（去芦）30克 鸡苏（净叶）500克 甘草（炙）45克 生干地黄末（后入膏）180克', '每服20丸，嚼破热水下，不嚼亦得。虚劳烦热，消渴惊悸，煎人参汤下；咳嗽唾血，鼻衄吐血，将麦门冬（汤浸去心），煎汤下，并食后、临卧服之。惟血崩下血，诸淋疾，皆空腹时服。治淋用车前子汤下。', '《太平惠民和剂局方》卷六', '治肺热咳嗽，鼻衄吐血，血崩下血，血淋、热淋、劳淋、气淋，胃热口臭，肺热喉腥，脾疽口甜，胆疸口苦。', '-', '-', '柴胡', '[\'柴胡\', \'银川\', \'木通\', \'沸汤\', \'一二\', \'宿绞汁\', \'入膏\', \'木通\', \'锉同\', \'柴胡\', \'阿胶\', \'微燥\', \'黄真者\', \'人参\', \'麦门冬汤\', \'洗去\', \'焙干\', \'黄耆\', \'芦克鸡\', \'苏净\', \'叶克\', \'甘草\', \'炙克生\', \'地黄\', \'末后\', \'入膏克\']');
INSERT INTO `prescript` VALUES (56587, '47260', '人参荆茶汤', '荆芥穗60克 芍药 天麻 芎藭 当归（洗，切，焙）京三棱（煨，锉）黄耆（薄切）鳖甲（醋浸，去裙襕，炙）牛膝（去苗，酒浸，切。焙）各30克木香15克 熟干地黄（切，焙）柴胡（去苗）各45克防风（去叉）牡丹皮 大腹皮各22克 枳壳（去瓤，麸炒）90克 半夏（为末，生姜汁作饼，曝干）30克 秦艽（去苗，土）7.5克 人参 石膏（碎，研）白术 羌活（去芦头）款冬花（择）陈橘皮（去白，切，炒）各15克', '每服9克，用水150毫升，加生姜2片、大枣1枚（去核），同煎至100毫升，去滓，空腹、日午或临卧时服。', '《圣济总录》卷十三', '治风消。血气虚损，攻刺疼痛，四肢无力，面色无华，形体羸瘦，胸膈痞闷，或大便多秘。或时泻利。', '-', '-', '柴胡', '[\'荆芥\', \'穗克\', \'芍药\', \'天麻\', \'当归\', \'洗切\', \'焙京\', \'三棱\', \'黄耆\', \'薄切\', \'鳖甲\', \'牛膝\', \'苗酒\', \'浸切\', \'木香\', \'克熟\', \'地黄\', \'切焙\', \'柴胡\', \'去苗\', \'防风\', \'牡丹皮\', \'大腹皮\', \'枳壳\', \'炒克\', \'半夏\', \'末生\', \'姜汁\', \'作饼\', \'曝干克\', \'秦艽\', \'苗土\', \'人参\', \'石膏\', \'碎研\', \'白术\', \'羌活\', \'芦头\', \'款冬花\', \'橘皮\', \'白切\']');
INSERT INTO `prescript` VALUES (56588, '47251', '川芎散', '川芎（洗）柴胡（去苗，洗）各30克 半夏曲 甘草（炙）甘菊 细辛（去叶）人参（去芦）前胡（去苗，洗）防风（去叉股）各15克', '每服12克，用水150毫升，加生姜4片，薄荷5叶，同煎至100毫升，去律温服。', '《普济本事方》卷四', '主风热上壅，鼻塞清涕，眼多眵泪，半边头疼。', '-', '-', '柴胡', '[\'川芎\', \'柴胡\', \'苗洗\', \'半夏\', \'甘草\', \'甘菊\', \'细辛\', \'人参\', \'芦前\', \'胡去\', \'苗洗\', \'防风\']');
INSERT INTO `prescript` VALUES (56589, '47238', '一柴胡饮', '柴胡6克 黄芩5克 芍药6克 生地5克 陈皮5克 甘草2克', '水煎，分二次温服。\r\n内热甚，加连翅6克;外邪甚，加防风3克；邪结在胸痞满者，去生地，加积实6克；热结阳明而渴者，轻加花粉或葛根6克，重加知母、石膏。', '《景岳全书》卷五十一', '治外感四时不正之气，或发热，或寒热，或妇人热入血室，或产后冒风，以致寒热如疟，但外有邪而内兼火者。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'芍药\', \'生地\', \'陈皮克\', \'甘草\']');
INSERT INTO `prescript` VALUES (56590, '47163', '加减黑逍遥散', '生地150克 柴胡15克（盐水炒）白芍45克（醋炒）丹皮45克（炒黑）山药90克（炒）茯苓45克（入乳拌蒸）阿胶90克（蒲灰炒）荆芥灰45克 地榆90克（炒炭）', '用童便调下9克。', '《医略六书》卷二十六', '治溺出纯血，脉弦数濡涩者。（尿血）', '-', '-', '柴胡', '[\'生地\', \'柴胡\', \'盐水\', \'白芍\', \'克醋\', \'丹皮\', \'黑山\', \'药克\', \'茯苓\', \'克入\', \'乳拌\', \'阿胶\', \'蒲灰\', \'荆芥\', \'灰克\', \'地榆\', \'炒炭\']');
INSERT INTO `prescript` VALUES (56591, '47162', '芎苏散', '川芎6克 苏叶 枳壳（麸炒）桔梗 柴胡 半夏（汤泡七次）广陈皮 白茯苓（去皮）各3克 干葛4.5克 甘草（炙）1.5克', '每服9克，加生姜3片、大枣1枚，水煎服。', '《增补内经拾遗方论》卷三引《太平惠民和剂局方》', '治四时感冒，发热恶寒，头疼身痛。', '-', '-', '柴胡', '[\'川芎\', \'克苏叶\', \'枳壳\', \'桔梗\', \'柴胡\', \'半夏\', \'汤泡\', \'七次\', \'广陈皮\', \'白茯苓\', \'去皮\', \'干葛克\', \'甘草\', \'炙克\']');
INSERT INTO `prescript` VALUES (56592, '47151', '清疳解毒汤', '人中黄 川黄连（生）柴胡各1.5克 知母（生）连翘（去心）牛蒡子（炒，研）犀角（镑）黑参 荆芥 防风各3克 石膏（煅）4.5克', '加淡竹叶3克，灯心150厘米，用水400毫升，煎至320毫升，空腹时服。\r\n呕吐，加芦根15克。', '《医宗金鉴》卷六十五', '治疹后余毒上攻，致患走马牙疳，牙根溃烂，随变黑腐，臭秽难闻。', '-', '-', '柴胡', '[\'人中\', \'黄川\', \'黄连\', \'柴胡\', \'知母\', \'连翘\', \'去心\', \'牛蒡子\', \'炒研\', \'犀角\', \'黑参\', \'荆芥\', \'防风\', \'石膏\', \'煅克\']');
INSERT INTO `prescript` VALUES (56593, '47143', '秦艽扶羸汤', '柴胡（去苗）60克 人参（去芦头）鳖甲（米醋炙）秦艽 地骨皮各45克 半夏（汤洗七遍）紫菀茸 甘草各30克 当归（洗，焙）38克', '上药用水400毫升，加生姜5片，乌梅、大枣各1枚，煎至280毫升，去滓，食后服。\r\n热甚者，加青蒿；汗多，加黄耆，去半夏、生姜。', '《杨氏家藏方》卷十', '益气滋阴，退热除蒸。治肺痿骨蒸，已成劳嗽，或寒或热，声嗄不出，体虚自汗，四肢怠惰，饮食减少。', '-', '-', '柴胡', '[\'柴胡\', \'苗克\', \'人参\', \'芦头\', \'鳖甲\', \'米醋\', \'秦艽\', \'地骨皮\', \'半夏\', \'汤洗\', \'七遍\', \'紫菀\', \'甘草\', \'当归\', \'洗焙克\']');
INSERT INTO `prescript` VALUES (56594, '47131', '附子防风散', '白术30克 白茯苓23克 柴胡45克（去苗）五味子30克 干姜23克（炮裂，切）甘草23克（炙微赤，切）附子23克（炮裂，去皮、脐）桂心15克 防风23克（去芦头）', '每服9克，以水150毫升，人生姜4片，煎至90毫升，去滓，不计时候温服。', '《类证活人书》卷十七', '治伤寒阴痉，闭目合面，手足厥逆，筋脉拘急，汗出不止。', '-', '-', '柴胡', '[\'白术\', \'白茯苓\', \'柴胡\', \'克去\', \'五味子\', \'干姜\', \'克炮\', \'裂切\', \'甘草\', \'克炙微\', \'赤切\', \'附子\', \'克炮裂\', \'去皮\', \'脐桂心克\', \'防风\', \'克去\', \'芦头\']');
