INSERT INTO `prescript` VALUES (56598, '47091', '宝珍膏', '生地 茅术 枳壳 五加皮 莪术 桃仁 山奈 当归 川乌 陈皮 乌药 三棱 大黄 首乌 草乌 柴胡 防风 刘寄奴 牙皂 肉桂 羌活 威灵仙 赤芍 南星 香附 荆芥 白芷 海风藤 藁本 续断 良姜 独活 麻黄 甘松 连翘各9克 川芎15克 血余60克 东丹9印克 肉桂 麝香 木香 附子各6克 冰片 樟脑 小茴香 乳香 没药 阿魏 细辛各9克', '制成药膏。贴患处。', '《中医伤科学讲义》', '主风湿性关节痛及跌打损伤。', '-', '-', '柴胡', '[\'生地\', \'茅术\', \'枳壳\', \'五加皮\', \'莪术\', \'桃仁\', \'山奈\', \'当归\', \'川乌\', \'陈皮\', \'乌药\', \'三棱\', \'大黄\', \'首乌\', \'草乌\', \'柴胡\', \'防风\', \'刘寄奴\', \'牙皂\', \'肉桂\', \'羌活\', \'威灵仙\', \'赤芍\', \'南星\', \'香附\', \'荆芥\', \'白芷\', \'海风藤\', \'藁本\', \'续断\', \'良姜\', \'独活\', \'麻黄\', \'甘松\', \'连翘\', \'川芎\', \'克血余克\', \'东丹\', \'印克\', \'肉桂\', \'麝香\', \'香附子\', \'冰片\', \'樟脑\', \'小茴香\', \'乳香\', \'没药\', \'阿魏\', \'细辛\']');
