INSERT INTO `prescript` VALUES (56631, '46618', '柴芩清膈煎', '川柴胡2.4克 生锦纹（酒浸）4.5克 生枳壳4，5克 焦山栀9克 青子芩4.5克 苏薄荷4.5克 苦桔梗3克 青连翘6克 生甘草1.8克 鲜淡竹叶36片', '水煎服。', '《重订通俗伤寒论》', '攻里兼和解。主少阳表邪，内结膈中，膈上如焚，寒热如疟，心烦懊憹，大便不通。', '-', '-', '柴胡', '[\'柴胡\', \'克生\', \'锦纹\', \'浸克生\', \'枳壳\', \'焦山\', \'栀克\', \'青子\', \'克苏\', \'薄荷\', \'克苦\', \'桔梗\', \'克青\', \'连翘\', \'生甘草\', \'克鲜\', \'淡竹叶\']');
INSERT INTO `prescript` VALUES (56632, '46613', '柴芩煎', '柴胡6～9克 黄芩 栀子 泽泻 木通各6克 枳壳4.5克', '用水400毫升，煎至320毫升，温服。\r\n如疟痢并行鲜血、纯血者，加芍药6克，甘草3克；如湿胜气陷者，加防风3克。', '《景岳全书》卷五十一', '解表清里。主伤寒表邪未解，外内俱热，泻利烦渴，喜冷气壮，脉滑数；疟痢并行，内热出血，兼表邪发黄者。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'栀子\', \'泽泻\', \'木通\', \'枳壳\']');
INSERT INTO `prescript` VALUES (56633, '46590', '加减漏芦汤', '漏芦30克，刘寄奴30克，公英30克，地丁30克，双花30克，连翘30克，柴胡13克，海藻15克，元参12克，香附12克，大贝12克，皂刺10克。', '水煎服，每日1剂，日服4次。', '王法昌方', '理气活血，软坚散结，疏肝解郁化痰。主气滞痰凝结。', '-', '-', '柴胡', '[\'漏芦\', \'刘寄奴\', \'克公英克地\', \'丁克\', \'双花克\', \'连翘\', \'柴胡\', \'海藻\', \'克元\', \'参克\', \'香附\', \'克大\', \'贝克\', \'刺克\']');
INSERT INTO `prescript` VALUES (56634, '46558', '归芍二黄汤', '黄芪4.5克，白术3克，苍术3克，当归3克，白芍3克，陈皮3克，熟地15克，生地9克，炙甘草9克，柴胡6克。', '水煎服，每日1剂，日服2次。', '《女科旨要》卷四', '补中健脾。主脾胃虚损。', '-', '-', '柴胡', '[\'黄芪\', \'白术\', \'苍术\', \'当归\', \'白芍\', \'陈皮克\', \'熟地\', \'生地\', \'甘草\', \'柴胡\']');
INSERT INTO `prescript` VALUES (56635, '46552', '参耆汤', '黄耆（蜜炙）6克 人参1.5克 甘草（炙）3克 当归1，克 柴胡1克 升麻1克 苍术（米泔浸）3克 青皮（去瓤）1.5克 神曲（炒）2克 黄柏（酒炒）1克', '水煎，空腹时服。', '《万病回春》卷二', '主脾胃虚弱，元气不足，四肢沉重，食后昏沉。', '-', '-', '柴胡', '[\'黄耆\', \'蜜炙克\', \'人参\', \'甘草\', \'炙克\', \'当归\', \'柴胡\', \'升麻\', \'苍术\', \'浸克\', \'青皮\', \'神曲\', \'炒克\', \'黄柏\', \'炒克\']');
INSERT INTO `prescript` VALUES (56636, '46545', '柴前梅连散', '胡黄连 柴胡 前胡 乌梅各9克', '每次6克，用童便200毫升，猪胆1枚，猪脊髓1条，韭根白1.5克，同煎至150毫升。去滓温服，不拘时候。', '《玉机微义》卷九引《瑞竹堂经验方》', '主骨蒸劳热，久而不痊。', '-', '-', '柴胡', '[\'胡黄连\', \'柴胡\', \'乌梅\']');
INSERT INTO `prescript` VALUES (56637, '46479', '解毒活血汤', '连翘6克 葛根6克 柴胡9克 当归6克 生地15克 赤芍9克 桃仁24克（研）红花15克 枳壳3克 甘草6克', '水煎服。', '《医林改错》卷下', '清热解毒，凉血活血。治瘟毒吐泻初起。现用于麻疹、脑炎、脑膜炎后遗症，灰质炎后遗症等。', '若见汗多，肢冷，眼塌，不可用。', '-', '柴胡', '[\'连翘\', \'葛根\', \'柴胡\', \'当归\', \'生地\', \'赤芍\', \'桃仁\', \'克研\', \'红花\', \'枳壳\', \'甘草\']');
INSERT INTO `prescript` VALUES (56638, '46465', '解毒济生汤', '川芎 当归 黄柏 知母 天花粉 金银花 麦门冬 远志 柴胡 黄芩 犀角 茯神各3克 甘草 红花各1.5克', '水400毫升，煎至320毫升，临服入童便200毫升，随病上下服。\r\n病在手指，加升麻1.5克，足指加牛膝1.5克。', '《外科正宗》卷二', '清热，活血，解毒。治脱疽初起，恶寒体倦，发热作渴，或肿或紫，成麻或痛，四肢倦怠，心志恍惚不宁者。', '-', '-', '柴胡', '[\'川芎\', \'当归\', \'黄柏\', \'知母\', \'天花粉\', \'金银花\', \'麦门冬\', \'远志\', \'柴胡\', \'黄芩\', \'犀角\', \'茯神\', \'甘草\', \'红花\']');
INSERT INTO `prescript` VALUES (56639, '46439', '益胃升阳汤', '柴胡 升麻各1.5克 炙甘草 当归身（酒洗）陈皮各3克 人参（去芦）炒神曲各4.5克 黄耆6克 白术9克 生黄芩少许', '上药哎咀。每服6克，用水600毫升，煎至300毫升，去滓，稍热服，不拘时候。\r\n腹中痛，加白芍药9克，肉桂少许，如渴，加葛根0.6克。', '《兰室秘藏）卷中', '益气升阳，养血调经。治妇人经候不调，经来量多，色黑有块，大便水泄，日二三次，饮食减少，食罢烦心，身体消瘦。', '-', '-', '柴胡', '[\'柴胡\', \'升麻\', \'甘草\', \'当归\', \'身酒\', \'陈皮\', \'人参\', \'去芦\', \'神曲\', \'黄耆克\', \'白术\', \'克生\', \'黄芩\', \'少许\']');
INSERT INTO `prescript` VALUES (56640, '46432', '当归附子汤', '当归0.6克 炒盐0.9克 蝎梢 升麻各1.5克 甘草1.8克 柴胡2.1克 黄柏少许（为引用）附子3克 干姜 良姜各3克', '每服15克，用水750毫升，煎至150毫升，去滓，稍热服。亦可研为细末，酒面糊为丸，每服6克。', '《兰室秘藏》卷中', '主赤白带下，脐下冷痛。', '-', '-', '柴胡', '[\'当归\', \'盐克蝎\', \'升麻\', \'甘草\', \'柴胡\', \'黄柏\', \'少许\', \'引用\', \'附子\', \'干姜\', \'良姜\']');
INSERT INTO `prescript` VALUES (56641, '46421', '安神汤', '生甘草 炙甘草各6克 防风7.5克 柴胡 升麻 酒生地黄 酒知母各15克 黄耆60克 酒黄柏 羌活各30克', '每服15克，用水400毫升，煎至200毫升，加蔓荆子1.5克、川芎0.9克，再煎至150毫升，去滓，临卧热服。', '《兰室秘藏》卷中', '头痛头眩，眼黑。', '-', '-', '柴胡', '[\'生甘草\', \'甘草\', \'防风\', \'柴胡\', \'升麻\', \'生地黄\', \'知母\', \'黄耆克酒\', \'黄柏\', \'羌活\']');
INSERT INTO `prescript` VALUES (56642, '46404', '乳香盏落散', '御米壳（去蒂）120克 柴胡（去苗）桔梗（去芦）甘草（炒）陈皮各30克', '每服9克，用水150毫升，加灯草3克，煎至100毫升，去滓，食后服。', '《卫生宝鉴》卷九', '治偏正头痛，痛不可忍。', '-', '-', '柴胡', '[\'米壳\', \'去蒂克\', \'柴胡\', \'去苗\', \'桔梗\', \'去芦\', \'甘草\', \'陈皮\']');
INSERT INTO `prescript` VALUES (56643, '46390', '疏肝解郁汤', '香附9克 青皮 柴胡 郁金各6克 丹参12克 川芎4.5克 红泽兰12克 延胡 金铃炭各6克', '水煎，温服。', '《中医妇科治疗学》', '疏肝理气，活血调经。治肝郁气滞，经行不畅，色淡红，量少，间有血块，胸胁胀满，有时嗳气，舌苔黄，脉弦者。', '-', '-', '柴胡', '[\'香附\', \'青皮\', \'柴胡\', \'郁金\', \'丹参\', \'川芎\', \'克红\', \'泽兰\', \'克延\', \'金铃\']');
INSERT INTO `prescript` VALUES (56644, '46352', '水解散', '柴胡 知母 瓜蒌 青木香 升麻 茵陈各30克 大黄 栀子仁 石膏 芒消各45克 黄芩 干葛各37.5克 枳壳（炒）芍药各22.5克', '空腹时以冷水和散1匙，调顿服。以利为度。春、夏用冷水，秋、冬用暖水，不退再服。', '《医方类聚》卷四十六引《千金月令》', '治伤寒时疾。', '-', '-', '柴胡', '[\'柴胡\', \'知母\', \'青木香\', \'升麻\', \'茵陈\', \'大黄\', \'栀子\', \'石膏\', \'芒消\', \'黄芩\', \'干葛\', \'枳壳\', \'芍药\']');
INSERT INTO `prescript` VALUES (56645, '46328', '水浸鳖甲汤', '鳖甲（九胁者，去裙襕，醋炙）升麻 柴胡（去苗）人参 白茯苓（去黑皮）槟榔（锉）肉豆蔻（去壳）诃黎勒皮 犀角（镑）青橘皮（汤浸，去白，焙）陈橘皮（汤浸，去白，焙）甘草（炙，锉）缩砂仁 茴香子（炒）陈曲（炒）各15克', '上十五味，粗捣筛，每服9克，用水220毫升，浸二日，煎至160毫升，去滓，空腹时细呷，以食压之。', '《圣济总录》卷八十七', '治气劳羸瘦，四肢疼痛，心腹妨闷，不思饮食。', '-', '-', '柴胡', '[\'鳖甲\', \'九胁者\', \'升麻\', \'柴胡\', \'去苗\', \'人参\', \'白茯苓\', \'黑皮\', \'槟榔\', \'肉豆蔻\', \'黎勒皮\', \'犀角\', \'橘皮\', \'汤浸\', \'橘皮\', \'汤浸\', \'甘草\', \'砂仁\', \'茴香\', \'陈曲\']');
INSERT INTO `prescript` VALUES (56646, '46325', '肝病复原丹', '银柴胡60克 川朴60克 木香30克 香附90克 桃仁60克 当归120克 三棱30克 莪术30克 姜黄90克 延胡索60克 红花30克', '每服1丸，日服二次。', '《赵心波儿科临床经验选编》', '治肝炎缠绵不愈，胸闷腹胀，肝脾肿大，恶心纳差，胁痛。', '-', '-', '柴胡', '[\'银柴胡\', \'克川\', \'朴克\', \'木香\', \'香附\', \'桃仁\', \'当归\', \'三棱\', \'莪术\', \'姜黄\', \'延胡索\', \'红花\']');
INSERT INTO `prescript` VALUES (56647, '46310', '归芍甘麦汤', '当归6克 杭芍12克 白术9克 柴胡6克 茯神9克 甘草3克 小麦30克（或麦芽18克）大枣3枚', '水煎，温服，不拘时。', '《中医妇科治疗学》', '舒肝解郁。治产后肝经郁热，乳汁自出，面色苍黄，间有潮红，心烦易怒，头晕胁胀，苔黄，脉弦数。', '-', '-', '柴胡', '[\'当归\', \'克杭\', \'芍克\', \'白术\', \'柴胡\', \'茯神\', \'甘草\', \'小麦\', \'麦芽\', \'大枣\']');
INSERT INTO `prescript` VALUES (56648, '46291', '地骨皮散', '地骨皮 茯苓各15克 柴胡 黄芩 生地黄 知母各30克 石膏60克 羌活 麻黄各22.5克', '每服30克，加生姜少许煎服。\r\n有汗者，去羌活、麻黄。', '《丹溪心法》卷一', '主阳毒火炽，浑身壮热，脉长而滑，心烦口渴。', '-', '-', '柴胡', '[\'地骨皮\', \'茯苓\', \'柴胡\', \'黄芩\', \'生地黄\', \'知母\', \'石膏\', \'羌活\', \'麻黄\']');
INSERT INTO `prescript` VALUES (56649, '46241', '贝母汤', '贝母（去心，姜制）黄芩（生，去皮）干姜（生）陈皮（去白）五味子（拣）各30克 桑白皮（蜜炙）半夏（汤浸）柴胡（去苗）桂心（不见火）各15克 木香 炙甘草各7.5克', '每服15克，加杏仁（去皮、尖）7个，生姜5片，用水220毫升，煎至160毫升，去滓热服。', '《普济本事方》卷三', '主咳嗽日久不愈。', '-', '-', '柴胡', '[\'贝母\', \'去心\', \'姜制\', \'黄芩\', \'去皮\', \'干姜\', \'生陈皮\', \'五味子\', \'桑白皮\', \'半夏\', \'汤浸\', \'柴胡\', \'苗桂心\', \'不见\', \'火各克\', \'木香\', \'甘草\']');
INSERT INTO `prescript` VALUES (56650, '46182', '化木汤', '白术60克 附子3克 肉桂3克 柴胡3克 杜若根30克', '水煎服。盖被发汗。', '《辨证录》卷九', '治木肾。睾丸作痛，后反不痛者。', '-', '-', '柴胡', '[\'白术\', \'附子\', \'肉桂\', \'柴胡\', \'克杜若\', \'根克\']');
INSERT INTO `prescript` VALUES (56651, '46164', '柴胡抑肝汤', '柴胡7.5克 赤芍 牡丹皮各4.5克 青皮6克 连翘 生地各1.5克 地骨皮 香附 苍术 山栀各3克 川芎2.1克 甘草0.9克 神曲2.4克', '水煎，空腹时服。', '《医学入门》卷八', '疏肝解郁，凉血退热。主寡居独阴，寒热类疟者。', '-', '-', '柴胡', '[\'柴胡\', \'赤芍\', \'牡丹皮\', \'青皮\', \'连翘\', \'生地\', \'地骨皮\', \'香附\', \'苍术\', \'川芎\', \'甘草\', \'神曲\']');
INSERT INTO `prescript` VALUES (56652, '46152', '明目地黄丸', '熟地黄（焙干）120克 生地黄（酒洗）山药 泽泻 山茱萸（去核，酒洗）牡丹皮（酒洗）柴胡 茯神（乳蒸，晒干）当归身（酒洗）五味子（烘干）各60克', '每服9克，空腹时用淡盐汤送下。', '《审视瑶函》卷五', '治肾虚目暗不明。', '服药期间忌食萝卜。', '-', '柴胡', '[\'熟地黄\', \'焙干\', \'生地黄\', \'酒洗\', \'山药\', \'泽泻\', \'山茱萸\', \'去核\', \'酒洗\', \'牡丹皮\', \'酒洗\', \'柴胡\', \'茯神\', \'乳蒸\', \'晒干\', \'当归\', \'身酒\', \'五味子\', \'烘干\']');
INSERT INTO `prescript` VALUES (56653, '46067', '鳖血丸', '人参15克 川芎 芜荑 北柴胡各30克 使君子21个 胡黄连 川黄连各60克', '每服20丸，食前熟水下。', '《世医得效方》卷十二', '主疳劳。', '-', '-', '柴胡', '[\'人参\', \'川芎\', \'芜荑北\', \'柴胡\', \'使君子\', \'胡黄连\', \'黄连\']');
INSERT INTO `prescript` VALUES (56654, '46064', '青蒿散', '青蒿（春夏用叶，秋冬用子，童便浸）1握 大鳖甲（炙黄，醋淬五七次，去腥）白术（湿纸裹，煨熟）地骨皮 白茯苓 粉草（炙）拣参（去芦头）瓜蒌根 北柴胡（去芦）桑白皮（蜜炙）各19克', '每服9克，用水150毫升，煎至100毫升，去滓温服，不拘时候。', '《世医得效方》卷九', '治骨蒸虚劳，憎寒壮热。', '-', '-', '柴胡', '[\'青蒿\', \'春夏\', \'用叶\', \'秋冬\', \'用子\', \'童便\', \'浸握\', \'鳖甲\', \'黄醋\', \'七次\', \'去腥\', \'白术\', \'湿纸\', \'地骨皮\', \'白茯苓\', \'粉草\', \'参去\', \'芦头瓜\', \'根北\', \'柴胡\', \'去芦\', \'桑白皮\']');
INSERT INTO `prescript` VALUES (56655, '46060', '柴苓饮', '柴胡6～9克 猪苓 茯苓 泽泻各6克 白术6～9克 肉桂3～9克', '用水220毫升煎服。\r\n如寒邪胜者，加生姜3～5片，如汗出热不退者，加芍药3～6克。', '《景岳全书》卷五十一', '解表利湿。主风湿发黄，发热身痛，脉紧，表里俱病，小便不利，中寒泄泻。', '-', '-', '柴胡', '[\'柴胡\', \'猪苓\', \'茯苓\', \'泽泻\', \'白术\', \'肉桂\']');
INSERT INTO `prescript` VALUES (56656, '46050', '柴平汤', '银柴胡6克 黄芩4.5克 人参（去芦）半夏（汤泡七次）各3克 甘草1.5克 陈皮3.6克 苍术（泔浸）4.5克 厚朴（姜制）3克', '用水400毫升，加生姜3片，红枣2枚，煎至320毫升，来发先服。\r\n疟发于午前为阳，属气虚，加白术（土炒）2.4克，白茯苓（去皮）2.1克；发于午后为阴，属血虚，加当归（酒浸）2.7克，川芎2.1克；发于午前，延及午后，此气血两虚，上四味俱加；食积，加神曲（炒）2.4克，麦芽（炒）2.1克，山楂3克，枳实（麸炒）3克。', '《增补内经拾遗方论》卷三引《宦邸便方》', '和解少阳，祛温和胃。主湿疟，一身尽痛，手足沉重，寒多热少，脉濡。', '-', '-', '柴胡', '[\'银柴胡\', \'黄芩\', \'人参\', \'去芦\', \'半夏\', \'汤泡\', \'七次\', \'甘草\', \'陈皮克\', \'苍术\', \'泔浸克\', \'厚朴\', \'姜制克\']');
INSERT INTO `prescript` VALUES (56657, '46039', '大温中饮', '熟地9～21克 冬白术9～15克 当归9～15克（如泄泻者不宜用，或以山药代之）人参6～15克（甚者30克，或不用亦可）炙甘草3克 柴胡6～12克麻黄3～9克 肉桂3～6克 干姜（炒熟）3～6克（或用煨生姜3-7片亦可）', '用水400毫升，煎至280毫升，去浮末，温服，或略盖取微汗。\r\n如气虚，加黄耆6～9克：寒甚阳虚者，加制附子3～6克，头痛，加川芎或白芷、细辛；阳虚气陷，加升麻，肚腹泄泻，宜少减柴胡，加防风、细辛亦可。', '《景岳全书》卷五十一', '温中补虚，解表祛邪。主阳虚伤寒及一切四时劳倦寒疫阴暑之气，身虽炽热，时犹畏寒，即在夏月亦欲衣被覆盖，或喜热汤，或兼呕恶泄泻，但六脉无力，肩脊怯寒；邪气不能外达者。', '-', '-', '柴胡', '[\'熟地\', \'克冬\', \'白术\', \'当归\', \'克如\', \'泄泻\', \'不宜\', \'山药\', \'代之\', \'人参\', \'甚者\', \'不用\', \'亦可\', \'甘草\', \'柴胡\', \'麻黄\', \'肉桂\', \'干姜\', \'炒熟\', \'生姜\', \'亦可\']');
INSERT INTO `prescript` VALUES (56658, '45959', '半解汤', '白芍30克 柴胡6克 当归9克 川芎15克 甘草3克 蔓荆子3克 半夏3克', '水煎服。', '《辨证录》卷二', '疏肝解郁，活血祛风。主治郁气不宣，风邪外袭，半边头痛，多在左边者。', '-', '-', '柴胡', '[\'白芍\', \'柴胡\', \'当归\', \'川芎\', \'甘草\', \'蔓荆子\', \'半夏\']');
INSERT INTO `prescript` VALUES (56659, '45935', '羌活饮子', '羌活、独活、川芎、柴胡（去苗）、前胡、细辛、白蒺藜（炒.去刺）、麦门冬（去心）、山药、紫苏叶、黄蓍（蜜炙）、升麻，各二钱。乌梅（去核）、枳壳（麸炒）、防风、蔓荆子、藁本（去土）、荆芥穗、甘草（炙）、桑皮（炙），各半两。干葛，一两。', '每服三钱，水一盏半，生姜三片，薄荷五叶，同煎至八分，去滓，食后温服。', '明方贤着《奇效良方》', '治风毒，上攻头面，发热颊赤，唇焦眼涩，鼻出热气，项背拘急，兼治肝元虚风等疾。', '-', '-', '柴胡', '[\'羌活\', \'独活\', \'川芎\', \'柴胡\', \'苗前\', \'细辛\', \'蒺藜\', \'麦门冬\', \'去心\', \'山药\', \'紫苏叶\', \'蓍蜜\', \'升麻\', \'各二钱\', \'乌梅\', \'去核\', \'枳壳\', \'防风\', \'蔓荆子\', \'藁本\', \'土荆芥\', \'甘草\', \'炙桑皮\', \'半两\', \'干葛\', \'一两\']');
INSERT INTO `prescript` VALUES (56660, '45894', '柴胡梅连散', '柴胡 人参 黄芩 甘草 胡黄连 当归 芍药各15克', '每服9克，用童便150毫升，乌梅1个，猪胆汁10毫升，猪髓1条，韭根1.5克，水200毫升，同煎至245毫升，去滓温服，不拘时。', '《金匮翼》卷三', '主骨蒸劳热，久而不愈。', '-', '-', '柴胡', '[\'柴胡\', \'人参\', \'黄芩\', \'甘草\', \'胡黄连\', \'当归\', \'芍药\']');
INSERT INTO `prescript` VALUES (56661, '45801', '柴陈煎', '柴胡6～9克 陈皮4.5克 半夏6克茯苓6克 甘草3克 生姜3～7片', '用水220毫升，煎至160毫升，空腹时温服。\r\n如寒盛者，加细辛2.1～2.4克；如风盛气滞者，加苏叶4.5克；如冬月寒甚，加麻黄4.5克；气逆多嗽者，加杏仁3克；痞满气滞者，加白芥子1.5～2.1克。', '《景岳全书》卷五十一', '解表发汗，化痰止咳。主伤风兼寒，咳嗽发热，痞满多痰者。', '-', '-', '柴胡', '[\'柴胡\', \'陈皮克\', \'半夏\', \'茯苓\', \'甘草\', \'生姜\']');
INSERT INTO `prescript` VALUES (56662, '45756', '人参鳖甲圆', '杏仁（汤浸.去皮.尖.炒）、人参、当归（洗.焙）、赤芍药、甘草（炙）、柴胡（去苗）、桔梗（去芦），各一两；地骨皮、宣黄连（去须）、胡黄连，各一分；肉桂（去粗皮）、木香，各半两；麝香（别研）半分，鳖甲一枚（可重二两者.醋炙黄色为度）。\r\n上为细末，用青蒿一斤，研烂，绞取汁，童子小便五升，酒五升，同熬至二升以来，次入真酥三两，白沙蜜三两，再熬成膏，冷，方下众药末，搜和令匀，圆如梧桐子大。', '每服五十圆，温酒送下，不拘时候。', '《宋太平惠民和剂局方》', '治妇人一切虚损，肌肉瘦瘁，盗汗心忪，咳嗽上气，经脉不调，或作寒热，不思饮食。', '-', '-', '柴胡', '[\'杏仁\', \'汤浸\', \'皮尖\', \'人参\', \'当归\', \'芍药\', \'甘草\', \'柴胡\', \'去苗\', \'桔梗\', \'去芦\', \'一两\', \'地骨皮\', \'黄连\', \'胡黄连\', \'一分\', \'肉桂\', \'粗皮\', \'木香\', \'半两\', \'麝香\', \'别研\', \'半分\', \'鳖甲\', \'一枚\', \'可重\', \'两者\', \'黄色\', \'为度\', \'细末用\', \'青蒿\', \'一斤\', \'研烂\', \'取汁\', \'童子\', \'小便\', \'五升\', \'酒五升\', \'同熬\', \'至二升\', \'以来\', \'次入\', \'三两\', \'白沙\', \'三两\', \'成膏\', \'冷方\', \'下众\', \'药末\', \'搜和令匀\', \'梧桐子\']');
INSERT INTO `prescript` VALUES (56663, '45727', '补虚款冬花汤', '款冬花22.5克 人参15克 升麻15克 桔梗22.5克（炒）杏仁（汤浸，去皮、尖、双仁，炒）30克 白茯苓（去黑皮）22.5克 甘草（炙，锉）4克 干姜（炮）7.5克 柴胡（去苗）45克 天门冬（去心，焙）15克 鳖甲（去裙襕，醋炙）30克 黄耆（细锉）15克 桑根白皮（锉，炒）22.5克 肉苁蓉（酒浸，去皴皮，炙）30克', '每服15克，用水225毫升，煎至180毫升，去滓，食后温服，一日三次。', '《圣济总录》卷八十六', '主肺劳痰嗽，日渐羸瘦。', '-', '-', '柴胡', '[\'款冬花\', \'人参\', \'升麻\', \'桔梗\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁\', \'炒克\', \'白茯苓\', \'黑皮\', \'甘草\', \'干姜\', \'炮克\', \'柴胡\', \'苗克\', \'天门冬\', \'心焙克\', \'鳖甲\', \'炙克\', \'黄耆\', \'锉克桑根\', \'白皮\', \'炒克\', \'肉苁蓉\', \'酒浸\', \'皮炙克\']');
INSERT INTO `prescript` VALUES (56664, '45721', '圆明膏', '诃子皮（湿纸裹煨）甘草各6克 当归身9克 柴胡 生地黄 麻黄（去节，捣开）黄连各15克', '上七味，先以水400毫升，煎麻黄至200毫升 掠去沫，外六味各哎咀如豆大，筛去末，入在内同熬，滴水中不散为度，入熟蜜少许，再熬，勤点眼。', '《兰室秘藏》卷上', '收睛明目。治劳心过度，饮食失节，目生内障及瞳子散大。', '-', '-', '柴胡', '[\'诃子\', \'皮湿纸\', \'甘草\', \'当归\', \'身克\', \'柴胡\', \'生地黄\', \'麻黄\', \'去节\', \'捣开\', \'黄连\']');
INSERT INTO `prescript` VALUES (56665, '45720', '和肝汤', '柴胡 栀子 丹皮 苍术 天花粉各6克 白芍15克 茯苓 生地各9克 甘草3克 陈皮1.5克 川芎3克', '水煎服。', '《辨证录》卷三', '治火郁于上、中二焦，上身疼痛，腰以下不痛者。', '-', '-', '柴胡', '[\'柴胡\', \'栀子\', \'丹皮\', \'苍术\', \'天花粉\', \'白芍\', \'茯苓\', \'生地\', \'甘草\', \'陈皮克\', \'川芎\']');
INSERT INTO `prescript` VALUES (56666, '45673', '七熬丸', '大黄45克 前胡（一作柴胡）芒消（熬）各150克 葶苈 蜀椒（并熬）各7.5克 生姜 芎藭各22.5克 茯苓18克 杏仁11克 （熬）挑仁20枚（熬）虻虫（熬）水蛭（熬）各13.5克', '空腹时服7丸，米饮送下，每日三次。不效，量加一倍。', '《备急干金要方》卷四', '治瘀滞日久，月经不利，手足烦热，腹满，心烦，默默不欲寤者。', '-', '-', '柴胡', '[\'大黄\', \'克前\', \'胡一作\', \'柴胡\', \'消熬\', \'各克葶\', \'生姜\', \'茯苓\', \'杏仁\', \'克熬\', \'挑仁枚\', \'水蛭\']');
INSERT INTO `prescript` VALUES (56667, '45632', '得生丸', '益母草600g 当归200g 白芍200g 柴胡100g 木香50g 川芎50g', '口服，一次1丸，一日2次。', '《中国药典》2015版', '养血化瘀，调经止痛。用于血瘀气滞，月经不调，经期腹痛，症瘕痞块。', '孕妇忌服。', '以上六味，粉碎成细粉，过筛，混匀。每100g粉末加炼蜜190～210g制成大蜜丸，即得。', '柴胡', '[\'益母草\', \'当归\', \'白芍\', \'柴胡\', \'木香\', \'川芎\']');
INSERT INTO `prescript` VALUES (56668, '45624', '菊花清燥汤', '甘菊花6克 当归 生地 白芍（酒炒）川芎 知母 贝母（去心，研）地骨皮 麦冬（去心）各3克 柴胡 黄芩 升麻 犀角（镑）甘草（生）各1.5克', '上药加竹叶20片，灯心70厘米，用水400毫升，煎至320毫升，食后温服。', '《医宗金鉴》卷六十八', '清热解毒，泻火凉血。治石榴疽。色红焮肿，坚硬疼痛，破翻如榴，寒热如疟。', '-', '-', '柴胡', '[\'甘菊花\', \'当归\', \'生地\', \'白芍\', \'川芎\', \'知母\', \'贝母\', \'心研\', \'地骨皮\', \'麦冬\', \'去心\', \'柴胡\', \'黄芩\', \'升麻\', \'犀角\', \'甘草\', \'生各克\']');
INSERT INTO `prescript` VALUES (56669, '45572', '乌梅丸', '乌梅30克（微炒）鳖甲60克（涂醋，炙令黄，去裙襕）川升麻30克 柴胡45克（去苗）甘草20克（生用）麦门冬30克（去心，焙）虎头骨60克（涂酥，炙令黄）天灵盖30克（涂酥，炙令黄）川大黄30克（锉碎，微炒）桃仁30克（汤浸，去皮、尖、双仁，麸炒微黄）', '每于空腹时以粥饮下20丸。', '《太平圣惠方》卷五十二', '治久疟，往来寒热，经年不愈，形体瘦弱；亦治劳疟。', '-', '-', '柴胡', '[\'乌梅\', \'克微\', \'鳖甲\', \'克涂\', \'炙令\', \'黄去\', \'升麻\', \'柴胡\', \'克去\', \'甘草\', \'克生用\', \'麦门冬\', \'克去\', \'心焙虎\', \'头骨\', \'克涂\', \'炙令\', \'天灵盖\', \'克涂\', \'炙令\', \'黄川\', \'大黄\', \'桃仁\', \'克汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (56670, '45537', '羌活胜风汤', '白术1.5克 枳壳 羌活 川芎 白芷 独活 防风 前胡 桔梗 薄荷各1.2克 荆芥 甘草各0.9克 柴胡2.1克 黄芩1.5克', '上作一服。用水300毫升，煎取150毫升，去滓热服。', '《原机启微》卷下', '治风热上扰，眵多眊躁，紧涩羞明，赤脉贯睛，头痛鼻塞，肿胀涕泪，脑巅沉重，眉骨酸疼，外翳如云雾、丝缕、秤星、螺盖。', '-', '-', '柴胡', '[\'白术\', \'枳壳\', \'羌活\', \'川芎\', \'白芷\', \'独活\', \'防风\', \'桔梗\', \'薄荷\', \'荆芥\', \'甘草\', \'柴胡\', \'黄芩\']');
INSERT INTO `prescript` VALUES (56671, '45522', '后辛汤', '柴胡 陈皮 栀子皮（姜汁炒）枳壳各3克 郁金 当归 茯苓 合欢花各6克 蒺藜12克 佛手1.5克', '水煎服。', '《医醇剩义》卷四', '治胆胀，胁下痛胀，口中苦，善太息。', '-', '-', '柴胡', '[\'柴胡\', \'陈皮\', \'栀子\', \'姜汁\', \'枳壳\', \'郁金\', \'当归\', \'茯苓\', \'合欢花\', \'蒺藜\', \'佛手\']');
INSERT INTO `prescript` VALUES (56672, '45502', '柴胡桂枝干姜汤', '柴胡24克 桂枝9克 干姜3克 栝楼根12克 黄芩9克 牡蛎6克（熬）甘草6克（炙）', '以水1.2升，煮取600毫升，去滓，再煎取300毫升，温服150毫升，日二服。初服微烦，复服汗出便愈。', '《伤寒论》', '和解散寒，生津敛阴。主伤寒少阳证，往来寒热，寒重热轻，胸胁满微结，小便不利，渴而不呕，但头汗出，心烦；牡疟寒多热少，或但寒不热。', '-', '-', '柴胡', '[\'柴胡\', \'桂枝\', \'干姜\', \'栝楼\', \'根克\', \'黄芩\', \'牡蛎\', \'克熬\', \'甘草\']');
INSERT INTO `prescript` VALUES (56673, '45401', '柴胡归芎汤', '柴胡 川芎 白芍青皮（去瓤）枳壳（麸炒）各4.5克 甘草 香附子 当归 龙胆草 木香 砂仁', '上锉一剂。加生姜1片，水煎，温服。', '《寿世保元》卷五', '疏肝泻火。主肝火内郁，左右胁俱痛。', '-', '-', '柴胡', '[\'柴胡\', \'川芎\', \'白芍\', \'青皮\', \'枳壳\', \'甘草\', \'香附子\', \'当归\', \'龙胆草\', \'木香\', \'砂仁\']');
INSERT INTO `prescript` VALUES (56674, '45386', '四顺清凉饮子', '当归身 龙胆草（酒洗，炒）黄芩 桑皮（蜜制）车前子 生地黄 赤芍 枳壳各2.4克 炙甘草0.9克 熟大黄 防风 川芎 川黄连（炒）木贼草 羌活 柴胡各1.8克', '用水400毫升，煎至300毫升，去滓，食远服。', '《审视瑶函》卷三', '治凝脂翳症。生于风轮上，初起如星，色白，中有凹陷，如针刺伤，后渐渐长大，变为黄色。', '-', '-', '柴胡', '[\'当归\', \'龙胆草\', \'酒洗\', \'黄芩\', \'桑皮\', \'蜜制\', \'车前子\', \'生地黄\', \'赤芍\', \'枳壳\', \'甘草\', \'克熟\', \'大黄\', \'防风\', \'川芎\', \'黄连\', \'木贼\', \'羌活\', \'柴胡\']');
INSERT INTO `prescript` VALUES (56675, '45375', '地骨皮饮', '柴胡（去芦）地骨皮各90克 知母 甘草（炙）鳖甲（醋炙黄）黄芩 人参各7.5克 赤茯苓15克', '每服6～9克，加生姜1片、乌梅1个，水煎服。', '《奇效良方》卷六十四', '主小儿骨蒸，潮热往来，心膈烦悸，及热病后低热不退。', '-', '-', '柴胡', '[\'柴胡\', \'去芦\', \'地骨皮\', \'知母\', \'甘草\', \'鳖甲\', \'黄芩\', \'人参\', \'茯苓\']');
INSERT INTO `prescript` VALUES (56676, '45326', '苍术汤', '防风 黄柏各3克 柴胡6克 苍术9克', '上药都作一服。用水500毫升，煎至150毫升，去滓，空腹时服。', '《兰室秘藏》卷中', '清化湿热，祛风止痛。主湿热下注，腰腿疼痛。', '-', '-', '柴胡', '[\'防风\', \'黄柏\', \'柴胡\', \'苍术\']');
INSERT INTO `prescript` VALUES (56677, '45317', '柴葛解肌汤', '柴胡3.6克 葛根4.5克 赤芍3克 甘草1.5克 黄芩4.5克 知母3克 贝母3克 生地黄6克 丹皮4.5克', '水煎服。\r\n心烦，加淡竹叶10片，谵语，加石膏9克。', '《医学心悟》卷二', '解肌清热主外感温邪，内有郁热，发热头痛，不恶寒而口渴者。', '-', '-', '柴胡', '[\'柴胡\', \'葛根\', \'赤芍\', \'甘草\', \'黄芩\', \'知母\', \'贝母\', \'生地黄\', \'丹皮\']');
INSERT INTO `prescript` VALUES (56678, '45252', '栝楼散', '栝楼1枚（干者）柴胡15克（去苗）甘草15克（炙微赤，锉）款冬花15克 芦根15克（锉）贝母15克（煨令微黄）', '每服15克，以水350毫升，入生姜4克，煎至175毫升，去滓，不计时候温服。', '《太平圣惠方》卷五十五', '治肺黄。眼目色白，头面微肿，鼻衄不止，多涕增寒，偏身赤疹，壮热上气，腹胀胸满。', '-', '-', '柴胡', '[\'栝楼\', \'干者\', \'柴胡\', \'克去\', \'甘草\', \'克炙微\', \'款冬花\', \'芦根\', \'贝母\', \'微黄\']');
INSERT INTO `prescript` VALUES (56679, '45243', '人参羌活散', '柴胡（去苗）、独活（去芦）、羌活（去苗），各二两；人参（去芦）、川芎、枳壳（去瓤.麸炒）、茯苓（去皮）、甘草（炙），各一两；桔梗、前胡、天麻（酒浸.炙）、地骨皮（去土），各半两。', '每服一钱，水七分盏，入薄荷少许，煎至五分，去滓，温服，不计时候。', '《宋太平惠民和剂局方》', '治小儿寒邪温病，时疫疮疹，头痛体疼，壮热多睡，及治潮热烦渴，痰实咳嗽。', '-', '上为散。', '柴胡', '[\'柴胡\', \'去苗\', \'独活\', \'去芦\', \'羌活\', \'二两\', \'人参\', \'去芦\', \'川芎\', \'枳壳\', \'茯苓\', \'去皮\', \'甘草\', \'一两\', \'桔梗\', \'天麻\', \'酒浸\', \'地骨皮\', \'半两\']');
INSERT INTO `prescript` VALUES (56680, '45241', '栝楼牛蒡汤', '栝楼仁 牛蒡子（炒，研）花粉 黄芩 生栀子（研）连翘（去心）皂刺 金银花 甘草（生）陈皮各3克 青皮 柴胡各1.5克', '用水400毫升，煎至320毫升，入煮酒适量和匀，空腹时服。', '《医宗金鉴》卷六十六', '理气疏肝，清热解毒，消肿排脓。治肝气郁结，热毒壅滞，致成乳疽、乳痈，初起憎寒壮热者。', '-', '-', '柴胡', '[\'栝楼\', \'牛蒡子\', \'炒研\', \'花粉\', \'黄芩\', \'栀子\', \'连翘\', \'心皂\', \'金银花\', \'甘草\', \'生陈皮\', \'青皮\', \'柴胡\']');
INSERT INTO `prescript` VALUES (56681, '45239', '大柴胡汤', '枳实（去瓤.炒）半两，柴胡（去芦）半斤，大黄二两，半夏（汤洗七次.切.焙）二两，半赤芍药、黄芩，各三两。', '每服三大钱，以水一盏半，入生姜五片，枣一枚，煎至一中盏，滤去滓，温服，食后、临卧。', '《宋太平惠民和剂局方》', '伤寒十馀日，邪气结在里，寒热往来，大便秘涩，腹满胀痛，语言谵妄，心中痞硬，饮食不下；或不大便五、六日，绕脐刺痛，时发烦躁，及汗后如疟，日晚发热，兼脏腑实，脉有力者，可服。', '此药治伤寒内热里实，若身体疼痛，是表证末解，不可服之。', '上五味，为粗末，入半夏拌匀。', '柴胡', '[\'枳实\', \'半两\', \'柴胡\', \'去芦\', \'半斤\', \'大黄\', \'二两\', \'半夏\', \'汤洗\', \'七次\', \'切焙二\', \'两半\', \'芍药\', \'黄芩\', \'三两\']');
INSERT INTO `prescript` VALUES (56682, '45225', '柴葛桂枝汤', '嫩桂枝3克 杭白芍4.5克 北柴胡 粉干葛各3克 炙甘草2.4克', '上药加老生姜3克、大红枣5枚，净水浓煎，热服。', '《幼幼集成》卷三', '主小儿伤风，自汗发热。', '-', '-', '柴胡', '[\'桂枝\', \'克杭\', \'白芍\', \'克北\', \'柴胡\', \'粉干葛\', \'甘草\']');
INSERT INTO `prescript` VALUES (56683, '45204', '镇肝圆', '蔓荆子（去白皮）、地肤子、人参、茺蔚子、决明子、白茯苓（去皮）、远志（去心）、防风（去芦.叉），各一两；青葙子、地骨皮、柴胡（去芦）、山药、车前子、柏子仁（炒）、玄参、甘菊、甘草（炙），各半两；细辛（去苗）一分。', '每服二十圆，米饮下，食后，日二服。', '《宋太平惠民和剂局方》', '治肝经不足，内受风热，上攻眼目，昏暗痒痛，隐涩难开，堆眵多泪，怕日羞&rsquo;明，时发肿赤，或生障翳，并宜服之。', '-', '上为末，蜜水煮糊，圆如梧桐子大。', '柴胡', '[\'蔓荆子\', \'白皮\', \'地肤子\', \'人参\', \'茺蔚子\', \'决明子\', \'白茯苓\', \'去皮\', \'远志\', \'去心\', \'防风\', \'芦叉\', \'一两\', \'青葙子\', \'地骨皮\', \'柴胡\', \'芦山\', \'车前子\', \'柏子仁\', \'玄参\', \'甘菊\', \'甘草\', \'半两\', \'细辛\', \'去苗\', \'一分\']');
INSERT INTO `prescript` VALUES (56684, '45176', '牛黄清心圆', '白芍药、麦门冬（去心）、黄芩、当归（去苗）、防风（去苗）、白术，各一两半。柴胡、桔梗、芎、白茯苓（去皮）、杏仁（去皮.尖.双仁.麸炒黄.别研），各一两二钱半。神曲（研）、蒲黄（炒）、人参（去芦），各二两半。羚羊角末、麝香（研）、龙脑（研），各一两。肉桂（去粗皮）、大豆黄卷（碎炒）、阿胶（碎炒），各一两七钱半。白干姜（炮）七钱半，牛黄（研）一两二钱，犀角末二两，雄黄（研飞）八钱，干山药，七两。甘草（炒）五两，金箔（一千二百箔，内四百箔为衣），大枣一百枚（蒸熟.去皮.核.研成膏）。', '每服一圆，温水化下，食后服之。\r\n小儿惊，即酌度多少，以竹叶汤温温化下。', '《宋太平惠民和剂局方》', '治诸风缓纵不随，语言蹇涩，心怔健忘，恍惚去来，头目眩冒，胸中烦郁，痰涎壅塞，精神昏愦。又治心气不足，神志不定，惊恐怕怖，悲忧惨戚，虚烦少睡，喜怒无时，或发狂颠，神情昏乱。', '-', '上除枣、杏仁、金箔、二角末及牛黄、麝香、雄黄、龙脑四味外，为细末。入馀药和匀，用炼蜜与枣膏为圆，每两作一十圆，用金箔为衣。', '柴胡', '[\'芍药\', \'麦门冬\', \'去心\', \'黄芩\', \'当归\', \'去苗\', \'防风\', \'去苗\', \'白术\', \'各一\', \'两半\', \'柴胡\', \'桔梗\', \'白茯苓\', \'去皮\', \'杏仁\', \'皮尖\', \'双仁麸\', \'黄别研\', \'一两\', \'二钱\', \'神曲\', \'研蒲\', \'人参\', \'去芦\', \'各二\', \'两半\', \'羚羊角\', \'麝香\', \'龙脑\', \'研各\', \'一两\', \'肉桂\', \'粗皮\', \'大豆黄卷\', \'阿胶\', \'一两\', \'七钱\', \'半白\', \'干姜\', \'炮七钱\', \'牛黄\', \'一两\', \'二钱\', \'犀角\', \'二两\', \'雄黄\', \'研飞\', \'八钱\', \'山药\', \'七两\', \'甘草\', \'五两\', \'金箔\', \'一千二百\', \'箔内\', \'四百\', \'大枣\', \'一百枚\', \'蒸熟\', \'去皮\', \'研成\']');
INSERT INTO `prescript` VALUES (56685, '45153', '圣散子', '厚朴（去粗皮.姜汁炙）、白术、防风（去芦头）、吴茱萸（汤洗七次）、泽泻、附子（炮裂.去皮.脐.一说去土）、高良姜、猪苓（去皮）、藿香（去枝.土）、苍术、麻黄（去根.节）、细辛（去苗）、芍药、独活（去芦）、半夏（汤洗七次.姜汁制）、茯苓（去皮）、柴胡（去芦）、枳壳（去瓤.麸炒），各半两。甘草（炙）一两，草豆蔻仁（十个，去皮），石菖蒲半两。', '每服四钱，水一盏半，煎取一盏，去滓，热服，不计时候，取遍身微汗即愈。时气不和，空腹饮之，以辟邪疫。', '《宋太平惠民和剂局方》', '治伤寒、时行疫疠、风温、湿温，一切不问阴阳两感，表里未辨，或外热内寒，或内热外寒，头项腰脊拘急疼痛，发热恶寒，肢节疼重，呕逆喘咳，鼻塞声重；及食饮生冷，伤在胃，胸膈满闷，腹胁胀痛，心下结痞，手足逆冷，肠鸣泄泻，水谷不消，时自汗出，小便不利，并宜服之。', '-', '上为粗散。', '柴胡', '[\'厚朴\', \'粗皮\', \'姜汁\', \'白术\', \'防风\', \'芦头\', \'吴茱萸\', \'汤洗\', \'七次\', \'泽泻\', \'附子\', \'炮裂\', \'去皮\', \'高良姜\', \'猪苓\', \'去皮\', \'藿香\', \'枝土\', \'苍术\', \'麻黄\', \'根节\', \'细辛\', \'去苗\', \'芍药\', \'独活\', \'去芦\', \'半夏\', \'汤洗\', \'七次\', \'姜汁\', \'茯苓\', \'去皮\', \'柴胡\', \'去芦\', \'枳壳\', \'半两\', \'甘草\', \'一两\', \'草豆蔻\', \'十个\', \'去皮\', \'石菖蒲\', \'半两\']');
INSERT INTO `prescript` VALUES (56686, '45132', '当归川芎散', '当归 川芎 柴胡 白术 芍药各3克 山栀（炒）3.6克 牡丹皮 茯苓各2.4克 蔓荆子甘草各1.5克', '水煎服。', '《证治准绳·类方》卷八', '养血清肝，疏风散热。主手足少阳经血虚疮证；或风热耳内痒痛生疮出水；或头目不清，寒热少食；或妇女月经不调，胸脯不利，腹胁疼痛。', '-', '-', '柴胡', '[\'当归\', \'川芎\', \'柴胡\', \'白术\', \'芍药\', \'克山\', \'炒克\', \'牡丹皮\', \'茯苓\', \'蔓荆子\', \'甘草\']');
INSERT INTO `prescript` VALUES (56687, '45126', '逍遥丸（水丸）', '柴胡100g 当归100g 白芍100g 白术（炒）100g 茯苓100g 炙甘草80g 薄荷20g', '口服，一次6～9g，一日1～2次。', '《中国药典》2015版', '疏肝健脾，养血调经。用于肝气不舒，胸胁胀痛，头晕目眩，食欲减退，月经不调。', '-', '上七味，粉碎成细粉，过筛，混匀。另取生姜100g，分次加水煎煮，滤过。取上述粉末，用煎液泛丸，干燥，即得。', '柴胡', '[\'柴胡\', \'当归\', \'白芍\', \'白术\', \'茯苓\', \'甘草\', \'薄荷\']');
INSERT INTO `prescript` VALUES (56688, '45110', '拨云散', '羌活、防风、柴胡、甘草（炒），各一斤。', '每服二钱，水一盏半，煎至七分，食后、临睡时服，薄荷茶调，菊花苗汤下亦得。', '《宋太平惠民和剂局方》', '治男子、妇人风毒上攻，眼目昏暗，翳膜遮障，怕日羞明，多生热泪，隐涩难开，眶痒赤痛，睑眦红烂，瘀肉侵睛，但是一切风毒眼疾，并皆治之。', '忌腌藏、酱、湿面、发风、毒物等。', '上为末。', '柴胡', '[\'羌活\', \'防风\', \'柴胡\', \'甘草\', \'一斤\']');
INSERT INTO `prescript` VALUES (56689, '45101', '逍遥丸（大蜜丸）', '柴胡100g 当归100g 白芍100g 白术（炒）100g 茯苓100g 炙甘草80g 薄荷20g', '口服，一次1丸，一日2次。', '《中国药典》2015版', '疏肝健脾，养血调经。用于肝气不舒，胸胁胀痛，头晕目眩，食欲减退，月经不调。', '-', '以上七味，粉碎成细粉，过筛，混匀。每100g粉末加炼蜜135～145g制成大蜜丸，即得。', '柴胡', '[\'柴胡\', \'当归\', \'白芍\', \'白术\', \'茯苓\', \'甘草\', \'薄荷\']');
INSERT INTO `prescript` VALUES (56690, '45058', '逍遥散', '甘草（微炙赤）半两，当归（去苗.微炒）、茯苓（去皮.白者）、芍药（白）、白术、柴胡（去苗），各一两。', '每服二钱，水一大盏，烧生姜一块切破，薄荷少许，同煎至七分，去渣热服，不拘时候。', '《宋太平惠民和剂局方》', '治血虚劳倦，五心烦热，肢体疼痛，头目昏重，心忪颊赤，口燥咽干，发热盗汗，减食嗜卧，及血热相搏，月水不调，脐腹胀痛，寒热如疟。\r\n又疗室女血弱阴虚，荣卫不和，痰嗽潮热，肌体羸瘦，渐成骨蒸。', '-', '上为粗末。', '柴胡', '[\'甘草\', \'微炙赤\', \'半两\', \'当归\', \'苗微\', \'茯苓\', \'去皮\', \'白者\', \'芍药\', \'白白\', \'柴胡\', \'一两\']');
INSERT INTO `prescript` VALUES (56691, '45057', '茵陈蒿大黄汤', '茵陈蒿、山栀仁、柴胡、黄檗（蜜炙）、龙胆草，各半两。黄芩（去黑心）、升麻、大黄（炒），各一两。', '每服五钱，水一盏半，煎至一盏，去滓，早晚食后温服。', '明方贤着《奇效良方》', '治伤寒发黄，面目悉黄，小便赤，宜服。', '-', '-', '柴胡', '[\'茵陈蒿\', \'山栀仁\', \'柴胡\', \'黄檗\', \'龙胆草\', \'半两\', \'黄芩\', \'黑心\', \'升麻\', \'大黄\', \'一两\']');
INSERT INTO `prescript` VALUES (56692, '45034', '黄芩贝母汤', '黄芩6克 柴胡 玄参 桔梗 杏仁 芍药 贝母各9克 五味子3克', '以水450毫升，煎至100毫升，热服。', '《医学摘粹》卷三', '治鼻孔发热生疮。', '-', '-', '柴胡', '[\'黄芩\', \'柴胡\', \'玄参\', \'桔梗\', \'杏仁\', \'芍药\', \'贝母\', \'五味子\']');
INSERT INTO `prescript` VALUES (56693, '45029', '疏肝利胆汤', '柴胡12克，白芍20克，枳壳10克，木香10克，元胡12克，川楝子15克，茵陈30克，大黄6克（后下），金钱草30克，麦芽30克，甘草6克。', '水煎服，每日2剂，日服2次。', '《卜用方》', '疏肝利胆，理气除湿。主肝胆气滞，湿热蕴结。', '-', '-', '柴胡', '[\'柴胡\', \'白芍\', \'枳壳\', \'木香\', \'克元\', \'胡克\', \'川楝子\', \'茵陈\', \'大黄\', \'克后\', \'金钱草\', \'麦芽\', \'甘草\']');
INSERT INTO `prescript` VALUES (56694, '45002', '黄芩厚朴汤', '黄芩4.5克 白芍药（炒）厚朴（姜制）枳壳（炒）陈皮 葛根各3克 甘草（炙）1.5克 柴胡2.1克', '加生姜3片，水煎服。', '《古今医彻》卷一', '治协热下利。', '-', '-', '柴胡', '[\'黄芩\', \'克白\', \'芍药\', \'厚朴\', \'姜制\', \'枳壳\', \'陈皮\', \'葛根\', \'甘草\', \'炙克\', \'柴胡\']');
INSERT INTO `prescript` VALUES (56695, '44989', '清肺饮子', '白芍药1.5克 人参 升麻 柴胡各1.2克 天门冬 麦门冬（去心）各0。9克 陈皮0.75克 甘草（生）黄芩 黄柏 甘草（炙）各0.6克', '上药哎咀，作一服。用水300毫升，煎至150毫升，去滓，食后温服。\r\n汗多，加黄耆1.5克。', '《卫生宝鉴》卷八', '治暑热伤气耗阴。', '-', '-', '柴胡', '[\'芍药\', \'人参\', \'升麻\', \'柴胡\', \'天门冬\', \'麦门冬\', \'去心\', \'陈皮克\', \'甘草\', \'黄芩\', \'黄柏\', \'甘草\']');
INSERT INTO `prescript` VALUES (56696, '44969', '柴葛解肌汤（程氏）', '柴胡6g 葛根6g 黄芩6g 赤芍6g 甘草3g 知母5g 生地9g 丹皮3g 贝母6g', '水煎服。心烦加淡竹叶十片（3克）；谵语加石膏三钱（12克）。', '《医学心悟》', '外感风热，里热亦盛证。不恶寒而口渴，舌苔黄，脉浮数。', '-', '-', '柴胡', '[\'柴胡\', \'葛根\', \'黄芩\', \'赤芍\', \'甘草\', \'知母\', \'生地\', \'丹皮\', \'贝母\']');
INSERT INTO `prescript` VALUES (56697, '44959', '人参轻骨散', '贝母（去心）、白茯苓（焙）、半夏（煮），各一两。枳壳（去瓤.炒）二两半，苍术（浸一宿）六两，人参、白术（焙）、白芷（不见火）、陈皮（去白）、秦艽、赤芍药，各二两。川芎、当归（去芦.焙）、肉桂（去粗皮）、干姜（炮），各一两半。柴胡（去芦）、麻黄（去根.节），各三两。桔梗（去芦）、甘草、厚朴，各四两（姜汁浸）。', '每服三钱，水一盏，生姜三片，同煎至七分，通口稍热服。\r\n身体倦怠加乌梅一个，咳嗽加枣二枚，同煎，不拘时。', '《宋太平惠民和剂局方》', '解利四时伤寒，头痛壮热，项背拘急，骨节烦疼，憎寒恶风，肢体困倦，大便不调，小便赤涩，呕逆烦渴；或伤风感寒，头痛体热，鼻塞声重，咳嗽痰涎；及山岚瘴气，时行疫疠，潮热往来，及疗五劳七伤，中气滞，心腹痞闷，停痰呕逆，冷气奔冲，攻注刺痛。又治妇人血气撮痛，经候不调，并宜服之。', '-', '上为细末。', '柴胡', '[\'贝母\', \'去心\', \'白茯苓\', \'半夏\', \'一两\', \'枳壳\', \'两半\', \'苍术\', \'六两\', \'人参\', \'白术\', \'白芷\', \'不见\', \'陈皮\', \'秦艽\', \'芍药\', \'二两\', \'川芎\', \'当归\', \'芦焙\', \'肉桂\', \'粗皮\', \'干姜\', \'两半\', \'柴胡\', \'去芦\', \'麻黄\', \'根节\', \'三两\', \'桔梗\', \'去芦\', \'甘草\', \'厚朴\', \'四两\', \'姜汁\']');
INSERT INTO `prescript` VALUES (56698, '44954', '柴胡羚角汤', '鳖血柴胡6克 归尾6克 杜红花3克碧玉散9克（包煎）羚角片9克（先煎）桃仁9粒 小青皮4.5克 炒川甲3克 吉林人参3克 醋炒生锦纹9克', '水煎，临服调入牛黄膏3克。', '《重订通俗伤寒论》', '和解偏重破积主妇人温病发热，经水适断，热入血室，昼日明了，夜则谵语，甚则昏厥，舌干口臭，便闭溺短者。', '-', '-', '柴胡', '[\'柴胡\', \'克归尾克\', \'红花\', \'碧玉\', \'散克包\', \'羚角\', \'片克先\', \'桃仁\', \'粒小\', \'青皮\', \'炒川甲克\', \'吉林\', \'人参\', \'克醋\', \'生锦纹克\']');
INSERT INTO `prescript` VALUES (56699, '44940', '柴胡加芒消汤', '柴胡8克 黄芩3克 人参3克 甘草3克（炙）生姜3克（切）半夏3克 大枣4枚 芒消6克', '上药除芒消外，以水800毫升，煮取400毫升，去津，内芒消，更煮微沸，分二次温服，不解更作。', '《伤寒论》', '和解少阳，润燥去结。主伤寒少阳证未解，阳明燥结，胸胁满而呕。日晡潮热者。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'人参\', \'甘草\', \'生姜\', \'克切\', \'半夏\', \'大枣\', \'消克\']');
INSERT INTO `prescript` VALUES (56700, '44920', '柴胡连翘汤', '中桂0.9克 当归梢4.5克 黍粘子6克 炙甘草 酒黄柏 生地黄各9克 柴胡 黄芩（炒）酒知母 连翘各15克 瞿麦穗18克', '每服15克，用水300毫升，煎至150毫升，去滓稍热，食后服之。', '《兰室秘藏》卷下', '主马刀疮。', '-', '-', '柴胡', '[\'中桂克\', \'当归\', \'梢克\', \'粘子克\', \'甘草\', \'黄柏\', \'生地黄\', \'柴胡\', \'黄芩\', \'炒酒\', \'知母\', \'连翘\', \'麦穗\']');
INSERT INTO `prescript` VALUES (56701, '44904', '人参荆芥散', '荆芥穗、羚羊角（镑）、酸枣仁（微炒）、生干地黄、枳壳（麸炒.去瓤.称）、人参、鳖甲（醋浸.去裙.炙黄）、肉桂（去粗皮）、白术、柴胡，各七两半；甘草、川芎、赤芍药、牡丹皮、当归、防风（去苗.叉），各五两。', '每服三钱，水一盏半，生姜三片，煎至八分，去渣热服，不拘时，日二服。', '《宋太平惠民和剂局方》', '治妇人血风劳气，身体疼痛，头昏目涩，心怔烦倦，寒热盗汗，颊赤口干，痰嗽胸满，精神不爽；或月水不调，倂癖块硬，疼痛发歇；或时呕逆，饮食不进，或因产将理失节，淹延瘦瘁，乍起乍卧，甚即着床。\r\n常服除一切风虚劳冷宿病。', '有孕不宜服。', '上为粗末。', '柴胡', '[\'荆芥\', \'羚羊角\', \'酸枣仁\', \'生干\', \'地黄\', \'枳壳\', \'人参\', \'鳖甲\', \'肉桂\', \'粗皮\', \'白术\', \'柴胡\', \'各七\', \'两半\', \'甘草\', \'川芎\', \'芍药\', \'牡丹皮\', \'当归\', \'防风\', \'苗叉\', \'五两\']');
INSERT INTO `prescript` VALUES (56702, '44823', '加味温胆汤', '枳实（麸炒）、半夏（汤泡）、白茯苓（去皮）、橘红、人参，各一钱半。竹茹、香柎、麦门冬、柴胡、桔梗，各一钱。甘草（半钱）。', '上作一服，水二钟，生姜三片，红枣二枚，煎至一钟，不拘时服。', '明方贤着《奇效良方》', '治心胆虚怯，处事易惊，梦寐不安，气[]生涎，涎与气搏，变生诸证，或短气困乏或复自汗，四肢浮肿，饮食无味，心虚烦闷，坐卧不安。', '-', '-', '柴胡', '[\'枳实\', \'半夏\', \'汤泡\', \'白茯苓\', \'去皮\', \'橘红\', \'人参\', \'各一钱\', \'竹茹\', \'麦门冬\', \'柴胡\', \'桔梗\', \'各一钱\', \'甘草\', \'半钱\']');
INSERT INTO `prescript` VALUES (56703, '44797', '黄连羊肝丸', '黄连20g 胡黄连40g 黄芩40g 黄柏20g 龙胆20g 柴胡40g 青皮（醋炒）40g 木贼40g 密蒙花40g 茺蔚子40g 决明子（炒）40g 石决明（煅）40g 夜明砂40g 鲜羊肝160g', '口服，一次1丸，一日1～2次。', '《中国药典》2015版', '泻火明目。用于肝火旺盛，目赤肿痛，视物昏暗，羞明流泪，胬肉攀睛。', '-', '以上十四味，将鲜羊肝切碎、蒸熟、干燥，与其余黄连等十三味掺匀，粉碎成细粉，过筛，混匀。每100g粉末加炼蜜120～150g制成大蜜丸，即得。', '柴胡', '[\'黄连\', \'胡黄连\', \'黄芩\', \'黄柏\', \'龙胆\', \'柴胡\', \'青皮\', \'木贼\', \'密蒙花\', \'茺蔚子\', \'决明子\', \'石决明\', \'夜明砂\', \'羊肝\']');
INSERT INTO `prescript` VALUES (56704, '44795', '青蒿散', '青蒿60克 龙胆27克（去芦头）栀子仁23克 知母23克 黄连30克（去须）鳖甲60克（涂醋，炙令黄，去裙襕）黄耆30克（锉）桑根白皮30克（锉）地骨皮15克 白术30克 甘草15克（炙微赤）柴胡45克（去苗）', '每服12克，以水300毫升，入生姜4克，煎至180毫升，去滓，不计时候温服。', '《太平圣惠方》卷七十', '治妇人骨蒸劳热，四肢烦疼，日渐羸瘦。', '-', '-', '柴胡', '[\'青蒿\', \'龙胆\', \'克去\', \'芦头\', \'栀子\', \'仁克\', \'知母\', \'黄连\', \'克去\', \'鳖甲\', \'克涂\', \'炙令\', \'黄去\', \'黄耆\', \'桑根\', \'白皮\', \'地骨皮\', \'白术\', \'甘草\', \'克炙微\', \'柴胡\', \'克去\']');
INSERT INTO `prescript` VALUES (56705, '44785', '云母膏', '蜀椒（去目及闭口者.微炒出汗甘）、白芷、没药（研）、赤芍药、肉桂（去粗皮）、当归，各半两；盐花（研）一十四两，麒麟竭（研）、菖蒲、白芨、川芎、草龙胆、木香、白敛、防风（去芦.叉）、厚朴（去粗皮.姜汁制）、麝香（研）、桔梗、柴胡（去芦头）、松脂、人参、苍术（泔浸一宿）、黄芩、夜合（用皮）、乳香、附子（去皮.脐）、茯苓（去皮）、高良姜，各半两；硝石（研如粉）、甘草、云母（光明白薄者.研粉），各四两；桑白皮、水银（候膏凝如人体热.以生绢袋盛水银.以手弹如针头大.铺在膏上.谓之养药母）、柏叶（不用近道者）、槐叶、', '治发背，先以败蒲一斤，用水三升，煎五十沸，如人体温，将蒲水洗疮，拭干贴药，一两分为三服，温酒下，未成脓者立瘥，于外贴之，奶痈外贴。\r\n瘰倁骨疽，毒穿至骨，用药一两，分作三服，温酒下，甚者即泻出恶物，兼外贴，瘥。\r\n肠痈，以药半两分为五服，甘草汤下，末成脓者当时消，已有脓者，随药下脓出，后每日酒下五圆，如梧桐子大，待脓止即住服。\r\n风眼，贴两太阳穴。\r\n壁镜咬、蜘蛛咬，外贴，留疮口。\r\n发脑、发髭鬓、发眉、发耳、脐痈、牙痈、牙疼，并外贴包裹，即当时痛止。\r\n箭头所伤，箭头在内，外贴，每日吃少许烂绿豆，箭头自', '《宋太平惠民和剂局方》', '治一切疮肿伤折等病。', '已上主疗，只忌羊血，馀无所忌。', '上除云母、硝石、麒麟竭、没药、麝香、乳香、黄丹、盐花八味别研外，并如豆大，用上件清油，于瓷器中浸所药七日，以物封闭后，用文火煎，不住手搅，三上火，三下火。每上，候匝匝沸，乃下火，候沸定再上，如此三次，候白芷、附子之类黄色为度，勿令焦黑，以绵或新布绞去滓，却入铛中、再上火熬。后下黄丹与别研药八味，以柳篦不住手搅，直至膏凝，良久色变再上熬，仍滴少许水中，凝结不黏手为度。先炙一瓷器，热即倾药在内，候如人体温热，弹水银在上，每用膏药，即先刮去水银。', '柴胡', '[\'蜀椒\', \'目及\', \'闭口\', \'出汗\', \'白芷\', \'没药\', \'研赤\', \'芍药\', \'肉桂\', \'粗皮\', \'当归\', \'半两\', \'盐花\', \'研一\', \'十四两\', \'麒麟\', \'竭研\', \'菖蒲\', \'白芨\', \'川芎\', \'龙胆\', \'木香\', \'白敛\', \'防风\', \'芦叉\', \'厚朴\', \'粗皮\', \'姜汁\', \'麝香\', \'桔梗\', \'柴胡\', \'芦头\', \'松脂\', \'人参\', \'苍术\', \'泔浸\', \'一宿\', \'黄芩\', \'合用\', \'皮乳\', \'香附子\', \'去皮\', \'茯苓\', \'去皮\', \'高良姜\', \'半两\', \'硝石\', \'研如粉\', \'甘草\', \'云母\', \'明白\', \'薄者\', \'研粉\', \'四两\', \'桑白皮\', \'水银\', \'候膏\', \'凝如\', \'人体\', \'热以\', \'生绢袋\', \'水银\', \'手弹\', \'针头\', \'大铺\', \'谓之\', \'养药\', \'母柏叶\', \'不用\', \'近道者\', \'槐叶\']');
INSERT INTO `prescript` VALUES (56706, '44768', '秘传降气汤', '桑白皮（炒二两），骨碎补（去毛.炒）、草果仁（去皮.煨）、五加皮（酒浸半日.炒黄）、半夏（生为末.生姜自然汁为饼.再碎.炒）、桔梗、诃子（炮.去核），各半两。甘草（炒）、枳壳（去瓤.麸炒）、陈皮（去白.炒黄）、柴胡（去芦）、地骨皮（炒黄），各一两。', '每服二钱，紫苏三叶，姜钱三片，水一盏，同煎至七分，食后，通口服。\r\n常服调顺荣卫，通利三焦，开膈化痰，和五脏。\r\n痰嗽，加半夏僸煎。\r\n心肺虚，加人参、茯苓煎。\r\n上膈热，加北黄芩煎。\r\n下部大段虚，加少许炮附子煎，如使附子，多加生姜。\r\n妇人血虚，加当归煎。', '《宋太平惠民和剂局方》', '治男子、妇人上热下虚之疾。凡饮食过度，致伤脾胃，酒色无节，耗损肾元，水土交攻，阴阳关膈，遂使气不升降，上热则头目昏眩，痰实呕逆，胸膈不快，咽喉干燥，饮食无味；下弱则腰脚无力，大便秘涩，里急后重，脐腹冷痛。\r\n治以凉，则脾气怯弱，肠鸣下利；治以温，则上焦壅热，口舌生疮，及脚气上攻，与久痢不瘥。宜先服此药，却以所主药治之，无不效者。', '-', '上为粗散和匀，再就蒸一伏时，晒干。', '柴胡', '[\'桑白皮\', \'二两\', \'骨碎补\', \'果仁\', \'去皮\', \'五加皮\', \'酒浸\', \'半日\', \'半夏\', \'生为\', \'生姜\', \'自然\', \'桔梗\', \'诃子\', \'去核\', \'半两\', \'甘草\', \'枳壳\', \'陈皮\', \'柴胡\', \'去芦\', \'地骨皮\', \'一两\']');
INSERT INTO `prescript` VALUES (56707, '44766', '蓝青散', '蓝青15克 寒水石30克 石膏30克 犀角屑30克 柴胡30克（去苗）知母15克 杏仁15克（汤浸，去皮、尖、双仁，麸炒微黄）黄芩30克 栀子仁15克 甘草15克（炙微赤，锉）赤芍药23克 羚羊角屑23克', '每服3克，以水100毫升，煎至50毫升，去滓，入竹沥、蜜、生蓝等汁共60毫升，更煎三二沸，放温，不计时候，量儿大小，分减服之。', '《太平圣惠方》卷九十一', '治小儿丹毒赤肿，身体壮热如火。', '-', '-', '柴胡', '[\'蓝青\', \'寒水石\', \'石膏\', \'犀角\', \'柴胡\', \'克去\', \'知母\', \'杏仁\', \'克汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'黄芩\', \'栀子\', \'仁克\', \'甘草\', \'克炙微\', \'芍药\', \'羚羊角\']');
INSERT INTO `prescript` VALUES (56708, '44747', '补经固真汤', '白葵花（去萼，研烂）1。2克 甘草（炙）郁李仁（去皮、尖，研泥）柴胡各3克 干姜（细末）人参各6克 生黄芩（细研）3克 陈皮（留皮）1.5克', '上药除黄芩外，以水450毫升，煎至260毫升，再入黄芩，同煎至150毫升，去滓，早晨空腹时热服，少时以早饭压之。', '《兰室秘藏》卷中', '主妇人始病血崩，日久血少，复亡其阳，白带下流不止，诸药不效者。', '-', '-', '柴胡', '[\'葵花\', \'研烂克\', \'甘草\', \'郁李仁\', \'皮尖\', \'研泥\', \'柴胡\', \'干姜\', \'细末\', \'人参\', \'各克生\', \'黄芩\', \'细研克\', \'陈皮\', \'留皮克\']');
INSERT INTO `prescript` VALUES (56709, '44740', '金露圆', '生干地黄（焙）、贝母（去心）、紫菀（洗.去苗.焙）、柴胡（去芦.焙）、干姜（炮）、桂心（不见火）、人参（洗.去芦.切.焙）、防风（去芦.焙）、枳壳（汤浸.去瓤.麸炒）、蜀椒（去目.炒出汗）、桔梗（洗.去芦.焙）、吴茱萸（汤浸七遍）、甘草（炙）、川芎（洗.去芦.焙）、菖蒲（米泔浸一宿）、白茯苓（去黑皮.焙）、厚朴（去粗皮.姜汁制）、鳖甲（米醋炙黄）、甘松（净洗），各一两。草乌头（炮）、黄连（洗.焙），各二两。\r\n巴豆（去心、膜，用醋煮三十沸，焙干，取一两，不去油，煮时须亲自数三十沸，便倾出焙干，若沸过则药无', '每服五圆，小儿两圆。\r\n心中痰患，姜汤下。\r\n心痛酸，石榴皮汤下。\r\n口疮，蜜汤下。\r\n头痛，石膏汤葱茶下。\r\n一切脾气，橘皮汤下，水泻、气泻，煮陈皮饮下。\r\n赤痢，甘草汤下。\r\n白痢，干姜汤下。\r\n赤白痢，甘草干姜汤下。\r\n胸膈噎闷，通草汤下。\r\n妇人血气，当归酒下，如不饮酒，当归煎汤下亦得。\r\n疝气、岚气、小肠气及下坠，附子汤下。\r\n常服及应急诸般疾患，只米饮、茶、酒、熟水任下。\r\n伤冷腹痛，酒食所伤，酒疸、黄疸，结气痞塞，鹤膝，并用盐汤、盐酒下。', '《宋太平惠民和剂局方》', '治腹内积聚症块，久患大如杯及黄瘦宿水，朝暮咳嗽，积年冷气，时复腹下盘痛绞结，冲心及两胁，彻背连心，痛气不息，气绕脐下，状如虫咬不可忍。\r\n又治十种水气，反胃吐食呕逆，饮食多噎，五般痔俈，气走注风，有似虫行，手足烦热，夜卧不安，睡语无度。\r\n又治小儿惊疳，妇人五邪，梦与鬼交，沉重不思饮食，昏昏如梦，不晓人事，欲死俱多，或歌或哭不定，月候不调，心中如狂，身体羸瘦，莫辨其状。\r\n但服此药，万无失一，是病皆疗，更不细述。', '-', '上为细末，以面糊圆，如梧桐子大。', '柴胡', '[\'生干\', \'地黄\', \'贝母\', \'去心\', \'紫菀\', \'洗去\', \'柴胡\', \'去芦\', \'焙干\', \'姜炮\', \'桂心\', \'不见\', \'人参\', \'洗去\', \'芦切\', \'防风\', \'芦焙\', \'枳壳\', \'汤浸\', \'出汗\', \'桔梗\', \'洗去\', \'芦焙\', \'吴茱萸\', \'汤浸\', \'七遍\', \'甘草\', \'川芎\', \'洗去\', \'芦焙\', \'菖蒲\', \'泔浸\', \'一宿\', \'白茯苓\', \'黑皮\', \'厚朴\', \'粗皮\', \'姜汁\', \'鳖甲\', \'米醋\', \'黄甘松净\', \'一两\', \'乌头\', \'黄连\', \'二两\', \'巴豆\', \'心膜\', \'三十\', \'焙干\', \'一两\', \'不去\', \'时须\', \'亲自\', \'三十\', \'沸便\', \'倾出\', \'焙干\', \'若沸过\']');
INSERT INTO `prescript` VALUES (56710, '44734', '柴胡舒肝丸', '茯苓100g 枳壳（炒）50g 豆蔻40g 白芍（酒炒）50g 甘草50g 香附（醋制）75g 陈皮50g 桔梗50g 厚朴（姜制）50g 山楂（炒）50g 防风50g 六神曲（炒）50g 柴胡75g 黄芩50g 薄荷50g 紫苏梗75g 木香25g 槟榔（炒）75g 三棱（醋制）50g 大黄（酒炒）50g 青皮（炒）50g 当归50g 姜半夏75g 乌药50g 莪术（制）50g', '口服，一次1丸，一日2次。', '《中国药典》2015版', '舒肝理气，消胀止痛。用于肝气不舒，胸胁痞闷，食滞不清，呕吐酸水。', '-', '以上二十五味，混合，粉碎成细粉，过筛，混匀。每100g粉末加炼蜜180～190g制成大蜜丸，即得。', '柴胡', '[\'茯苓\', \'枳壳\', \'豆蔻\', \'白芍\', \'甘草\', \'香附\', \'醋制\', \'陈皮\', \'桔梗\', \'厚朴\', \'姜制\', \'山楂\', \'防风\', \'神曲\', \'柴胡\', \'黄芩\', \'薄荷\', \'紫苏梗\', \'木香\', \'槟榔\', \'三棱\', \'制大\', \'黄酒\', \'青皮\', \'当归\', \'半夏\', \'乌药\', \'莪术\']');
INSERT INTO `prescript` VALUES (56711, '44680', '秦艽鳖甲散', '荆芥（去梗）、贝母（去心）、天仙藤、前胡（去芦）、青皮（去白）、柴胡（去芦）、甘草（炙）、陈皮（去白）、秦艽（去芦.洗）、鳖甲（去裙.醋炙），各一两；干葛二两（焙），白芷、肉桂（去粗皮）、羌活，各半两。', '每服二钱，水一盏，生姜三片，同煎至八分，稍热服，不拘时候，酒调亦得。', '《宋太平惠民和剂局方》', '治男子、妇人气血劳伤，四肢倦怠，肌体消弱，骨节烦疼，头昏颊赤，肢体枯槁，面色萎黄，唇焦口干，五心烦热，痰涎咳嗽，腰背引痛，乍起乍卧，梦寐不宁，神情恍惚，时有盗汗，口苦无味，不美饮食；及治山岚瘴气，寒热往来，并能治之。\r\n常服养气血，调荣卫，解倦怠。', '-', '上为细末。', '柴胡', '[\'荆芥\', \'贝母\', \'去心\', \'天仙藤\', \'胡去\', \'青皮\', \'柴胡\', \'去芦\', \'甘草\', \'陈皮\', \'秦艽\', \'芦洗\', \'鳖甲\', \'一两\', \'干葛\', \'二两\', \'白芷\', \'肉桂\', \'粗皮\', \'羌活\', \'半两\']');
INSERT INTO `prescript` VALUES (56712, '44675', '蓝叶散', '白芷 柴胡 知母 杏仁（去皮）川芎 赤芍药 生地黄 川升麻 干葛 生甘草各7.5克 烂石膏 栀子仁各3.75克 蓝叶（日干）7.5克', '每次4.5克，水煎服。\r\n热甚，加黄芩、玄参。', '《仁斋直指》卷二十四', '治诸丹发热赤肿。', '-', '-', '柴胡', '[\'白芷\', \'柴胡\', \'知母\', \'杏仁\', \'去皮\', \'川芎\', \'芍药\', \'生地黄\', \'升麻\', \'干葛\', \'生甘草\', \'各克烂\', \'石膏\', \'栀子\', \'蓝叶日\', \'干克\']');
INSERT INTO `prescript` VALUES (56713, '44671', '安坤赞育丸', '香附（醋制）96g 鹿茸24g 阿胶24g 紫河车20g 白芍16g 当归16g 牛膝14g 川牛膝14g 北沙参12g 没药（醋制）12g 天冬11.5g 补骨脂（盐制）11g 龙眼肉10g 茯苓8g 黄柏8g 龟甲8g 锁阳8g 杜仲（盐制）8g 秦艽8g 鳖甲（醋制）8g 艾叶（炭）8g 白薇8g 延胡索（醋制）8g 山茱萸（酒制）8g 鹿尾7.5g 枸杞子6g 鸡冠花6g 黄芪6g 乳香（醋制）6g 赤石脂（煅）6g 鹿角胶6g 菟丝子4g 肉苁蓉（酒制）6g 鸡血藤4g 桑寄生4g 琥珀4g 甘', '口服，一次1丸，一日2次。', '《中国药典》2015版', '补气养血，调经止带。用于气血两亏，肝肾不足，形瘦虚赢，神倦体疲，面黄浮肿，心悸失眠，腰酸腿软，午后低烧，骨蒸潮热，月经不调，崩漏带下，产后虚弱，瘀血腹痛，大便溏泻。', '孕妇遵医嘱服用。', '以上六十三味，白术、砂仁、地黄、酸枣仁、沉香、陈皮、续断、川芎、橘红、黄芩、泽泻、远志、青蒿、藁本、肉豆蔻、柴胡、红花、紫苏叶、木香、丹参、熟地黄粉碎成粗粉；其余香附等四十二味，置罐中，加黄酒400g，加盖封闭，放高压罐内加热或隔水炖至酒尽，取出，与上述粗粉掺匀，干燥，粉碎成细粉，过筛，混匀。每100g粉末加炼蜜110～130g制成大蜜丸，即得。', '柴胡', '[\'香附\', \'醋制\', \'鹿茸\', \'阿胶\', \'紫河车\', \'白芍\', \'当归\', \'牛膝\', \'川牛膝\', \'北沙参\', \'没药\', \'醋制\', \'天冬\', \'补骨脂\', \'盐制\', \'龙眼肉\', \'茯苓\', \'黄柏\', \'龟甲\', \'锁阳\', \'杜仲\', \'盐制\', \'秦艽\', \'鳖甲\', \'醋制\', \'艾叶\', \'炭白薇\', \'延胡索\', \'醋制\', \'山茱萸\', \'酒制\', \'鹿尾\', \'枸杞子\', \'鸡冠花\', \'黄芪\', \'乳香\', \'醋制\', \'赤石脂\', \'鹿角胶\', \'菟丝子\', \'肉苁蓉\', \'酒制\', \'鸡血藤\', \'桑寄生\', \'琥珀\']');
INSERT INTO `prescript` VALUES (56714, '44664', '上二黄丸', '黄芩60克 黄连（去须，酒浸）30克 升麻 柴胡各9克 甘草6克', '每服50～70丸，空腹用温开水送下。', '《内外伤辨》卷下', '治热食伤中，胸脘痞闷，兀兀欲吐，烦乱不安。', '-', '-', '柴胡', '[\'黄芩\', \'黄连\', \'浸克\', \'升麻\', \'柴胡\', \'甘草\']');
INSERT INTO `prescript` VALUES (56715, '44649', '沉香鳖甲散', '沉香（不见火）、人参（去芦）、木香（不见火）、巴戟（去心）、牛膝（去芦.酒浸）、黄蓍（去芦）、白茯苓（焙）、柴胡、荆芥（去梗）、半夏（姜汁浸二宿.炒）、川当归（去芦）、秦艽（去芦），各半两；附子（炮.去皮.脐）、肉桂（去粗皮）、鳖甲（醋浸.去裙.炙黄），各一两；羌活、熟干地黄（净洗.酒洒.蒸.焙），各七钱半；肉豆蔻四个。', '每服二钱，水一盏，葱白二寸，生姜三片，枣子二枚，擘破，同煎至七分，空心，食前。', '《宋太平惠民和剂局方》', '治男子、妇人五劳七伤，气血虚损，腰背拘急，手足沉重，百节酸疼，面色黑黄，肢体倦怠，行动喘乏，胸膈不快，咳嗽痰涎，夜多异梦，盗汗失精，嗜卧少力，肌肉瘦瘁，不思饮食，日渐羸弱，一切劳伤，诸虚百损，并能治之。', '-', '上为细末。', '柴胡', '[\'沉香\', \'不见\', \'人参\', \'芦木\', \'不见\', \'火巴戟\', \'牛膝\', \'芦酒\', \'白茯苓\', \'柴胡\', \'荆芥\', \'半夏\', \'姜汁\', \'浸二宿\', \'炒川\', \'当归\', \'去芦\', \'秦艽\', \'去芦\', \'半两\', \'附子\', \'去皮\', \'肉桂\', \'粗皮\', \'鳖甲\', \'一两\', \'羌活\', \'熟干\', \'地黄\', \'净洗\', \'各七钱\', \'肉豆蔻\', \'四个\']');
INSERT INTO `prescript` VALUES (56716, '44613', '樗根白皮丸', '樗根白皮21克 白术 枳实（面炒）茯苓 柴胡 升麻各6克 黄柏（盐水炒）知母（盐水炒）牡蛎（煅）各9克 韭子（炒）30克 芍药（炒）15克', '每服50丸，空腹时用盐汤送下。', '《古今医鉴》卷八', '主湿热伤脾之遗精。', '-', '-', '柴胡', '[\'白皮\', \'白术\', \'枳实\', \'茯苓\', \'柴胡\', \'升麻\', \'黄柏\', \'盐水\', \'知母\', \'盐水\', \'牡蛎\', \'韭子\', \'炒克\', \'芍药\', \'炒克\']');
INSERT INTO `prescript` VALUES (56717, '44601', '午时茶颗粒', '苍术30g 柴胡30g 羌活30g 防风30g 白芷30g 川芎30g 广藿香30g 前胡30g 连翘30g 陈皮30g 山楂30g 枳实30g 麦芽（炒）45g 甘草30g 桔梗45g 六神曲（炒）30g 紫苏叶45g 厚朴45g 红茶960g', '开水冲服，一次6g，一日1～2次。', '《中国药典》2015版', '解表和中。用于感受风寒，内伤食积，寒热吐泻。', '-', '以上十九味，苍术、柴胡、羌活、防风、白芷、川芎、广藿香、连翘、前胡、陈皮、枳实、紫苏叶、厚朴提取挥发油，蒸馏后的水溶液另器收集；药渣与山渣等六味，加水煎煮二次，第一次2小时，第二次1小时，合并煎液，滤过，滤液静置，取上清液与上述水溶液合并，浓缩至适量，加乙醇等量使沉淀，取上清液浓缩成稠膏，加蔗糖粉适量，制成颗粒，干燥，放冷，喷加上述苍术等挥发油，混匀，制成600g，即得。', '柴胡', '[\'苍术\', \'柴胡\', \'羌活\', \'防风\', \'白芷\', \'川芎\', \'广藿香\', \'连翘\', \'陈皮\', \'山楂\', \'枳实\', \'麦芽\', \'甘草\', \'桔梗\', \'神曲\', \'紫苏叶\', \'厚朴\', \'红茶\']');
INSERT INTO `prescript` VALUES (56718, '44590', '平肝舒络丸', '柴胡45g 青皮（醋炙）30g 陈皮45g 佛手45g 乌药45g 香附（醋炙）45g 木香45g 檀香45g 丁香30g 沉香150g 广藿香45g 砂仁45g 豆蔻45g 厚朴（姜炙）45g 枳壳（去瓤麸炒）45g 羌活45g 白芷45g 铁丝威灵仙（酒炙）45g 细辛45g 木瓜45g 防风45g 钩藤45g 僵蚕（麸炒）45g 胆南星（酒炙）75g 天竺黄30g 桑寄生45g 何首乌（黑豆酒炙）45g 牛膝45g 川芎30g 熟地黄45g 龟甲（沙烫醋淬）45g 延胡索（醋炙）45g 乳香（醋炙）', '温黄酒或温开水送服，一次1丸，一日2次。', '《中国药典》2015版', '平肝疏络，活血祛风。用于肝气郁结，经络不疏引起的胸胁胀痛，肩背串痛，手足麻木，筋脉拘挛。', '-', '以上四十三味，除羚羊角粉外，朱砂飞水成极细粉，冰片研细；其余柴胡等四十味粉碎成细粉，与上述粉末配研，过筛，混匀。每100g粉末加炼蜜140～160g制成大蜜丸，即得。', '柴胡', '[\'柴胡\', \'青皮\', \'陈皮\', \'佛手\', \'乌药\', \'香附\', \'木香\', \'檀香\', \'丁香\', \'沉香\', \'广藿香\', \'砂仁\', \'豆蔻\', \'厚朴\', \'枳壳\', \'羌活\', \'白芷\', \'铁丝\', \'威灵仙\', \'细辛\', \'木瓜\', \'防风\', \'钩藤\', \'僵蚕\', \'胆南星\', \'天竺黄\', \'桑寄生\', \'何首乌\', \'黑豆\', \'牛膝\', \'川芎\', \'熟地黄\', \'龟甲\', \'沙烫\', \'延胡索\', \'乳香\']');
INSERT INTO `prescript` VALUES (56719, '44585', '地榆败毒散', '西党参9克 羌活9克 独活9克 柴胡9克 前胡9克 甘草9克 茯苓9克 枳壳6克（炒用）抚芎6克 桔梗6克 生地榆30克 紫竹根1把 生姜3片', '水煎服。', '《外科医镜》', '主中狂犬毒。', '-', '-', '柴胡', '[\'党参\', \'羌活\', \'独活\', \'柴胡\', \'克前\', \'胡克\', \'甘草\', \'茯苓\', \'枳壳\', \'桔梗\', \'克生\', \'地榆\', \'紫竹\', \'根把\', \'生姜\']');
INSERT INTO `prescript` VALUES (56720, '44551', '七宣圆', '柴胡（去苗.洗）、枳实、木香、诃黎勒皮，各五两；桃仁（去皮.尖）、甘草，各六两；大黄（面裹.煨）十五两。', '每服二十圆，米饮下，食后临卧服，稍增至四、五十圆，取宣利为度。觉病势退，服五补圆。不问男女老少，并可服饵，量虚实加减。', '《宋太平惠民和剂局方》', '疗风气结聚，宿食不消，兼砂石、皮毛在腹中，及积年腰脚疼痛，冷如冰石，脚气冲心，烦愦闷乱，头旋暗倒，肩背重痛，心腹胀满，胸膈闭塞，风毒肿气，连及头面，大便或秘，小便时涩，脾胃气痞，不能饮食，脚气转筋，掣痛挛急，心神恍惚，眠卧不安等疾。', '-', '上为末，炼蜜圆如梧桐子大。', '柴胡', '[\'柴胡\', \'苗洗\', \'枳实\', \'木香\', \'黎勒皮\', \'五两\', \'桃仁\', \'皮尖\', \'甘草\', \'六两\', \'大黄\', \'面裹\', \'十五两\']');
INSERT INTO `prescript` VALUES (56721, '44538', '乌鸡白凤丸', '乌鸡（去毛爪肠）640g 鹿角胶128g 鳖甲（制）64g 牡蛎（煅）48g 桑螵蛸48g 人参128g 黄芪32g 当归144g 白芍128g 香附（醋制）128g 天冬64g 甘草32g 地黄256g 熟地黄256g 川芎64g 银柴胡26g 丹参128g 山药128g 芡实（炒）64g 鹿角霜48g', '口服，水蜜丸一次6g，小蜜丸一次9g，大蜜丸一次1丸，一日2次。', '《中国药典》2015版', '补气养血，调经止带。用于气血两虚，身体瘦弱，腰膝酸软，月经不调，崩漏带下。', '-', '上二十味，熟地黄、地黄、川芎、鹿角霜、银柴胡、芡实、山药、丹参八味粉碎成粗粉，其余乌鸡等十二味，分别酌予碎断，置罐中，另加黄酒1500g，加盖封闭，隔水炖至酒尽，取出，与上述粗粉掺匀，低温干燥，再粉碎成细粉，过筛，混匀。每100g粉末加炼蜜30～40g与适量的水，泛丸，干燥，制成水蜜丸；或加炼蜜90～120g制成小蜜丸或大蜜丸，即得。', '柴胡', '[\'乌鸡\', \'爪肠\', \'鹿角胶\', \'鳖甲\', \'牡蛎\', \'桑螵蛸\', \'人参\', \'黄芪\', \'当归\', \'白芍\', \'香附\', \'醋制\', \'天冬\', \'甘草\', \'地黄\', \'熟地黄\', \'川芎\', \'银柴胡\', \'丹参\', \'山药\', \'芡实\', \'鹿角霜\']');
INSERT INTO `prescript` VALUES (56722, '44509', '加减散花去癫汤', '生白芍30克 当归 麦冬各15克 焦栀 元参 辰茯神 杜牛膝各9克 川柴胡6克 生甘草 白芥子 鲜石菖蒲各6克 当门子0.15克（冲）', '水煎服。', '《重订通俗伤寒论》', '治悲苦发狂。', '-', '-', '柴胡', '[\'白芍\', \'当归\', \'麦冬\', \'各克焦\', \'栀元\', \'参辰\', \'茯神\', \'牛膝\', \'各克川\', \'柴胡\', \'生甘草\', \'白芥子\', \'石菖蒲\', \'各克当\', \'门子\', \'克冲\']');
INSERT INTO `prescript` VALUES (56723, '44478', '柴胡升麻汤', '柴胡（去苗）、干葛、荆芥（去梗）、赤芍药、石膏，各一钱半。前胡（去苗）、升麻、桑白皮、黄芩，各一钱。', '上作一服，水二钟，生姜三片，豆豉二十粒，煎至一钟，不拘时服。', '明方贤着《奇效良方》', '治时行瘟疫，壮热恶风，头痛体疼，鼻塞咽干，咳嗽涕唾稠黏。', '-', '-', '柴胡', '[\'柴胡\', \'苗干葛\', \'荆芥\', \'芍药\', \'石膏\', \'各一钱\', \'半前\', \'胡去\', \'升麻\', \'桑白皮\', \'黄芩\', \'各一钱\']');
INSERT INTO `prescript` VALUES (56724, '44477', '麻黄桂枝汤', '柴胡（三钱） 芍药（二钱半） 桂枝（去皮.一钱半） 甘草（炙.一钱半） 黄芩（一钱半） 半夏（一钱三分） 人参（一钱）', '上作一服，水二钟，生姜五片，大枣二枚，煎至一钟，食远服。', '明方贤着《奇效良方》', '治伤寒六七日，发热微有恶寒，表证未解者。', '-', '-', '柴胡', '[\'柴胡\', \'三钱\', \'芍药\', \'二钱\', \'桂枝\', \'去皮\', \'一钱\', \'甘草\', \'一钱\', \'黄芩\', \'一钱\', \'半夏\', \'一钱\', \'三分\', \'人参\', \'一钱\']');
INSERT INTO `prescript` VALUES (56725, '44444', '感冒清热颗粒', '荆芥穗200g 薄荷60g 防风100g 柴胡100g 紫苏叶60g 葛根100g 桔梗60g 苦杏仁80g 白芷60g 苦地丁200g 芦根160g', '开水冲服，一次1袋，一日2次。', '《中国药典》2015版', '疏风散寒，解表清热。用于风寒感冒，头痛发热，恶寒身痛，鼻流清涕，咳嗽咽干。', '-', '上十一味，取荆芥穗、薄荷、紫苏叶提取挥发油，蒸馏后的水溶液另器收集；药渣与其余防风等八味加水煎煮二次，每次1.5小时，合并煎液，滤过，滤液与上述水溶液合并。合并液浓缩成相对密度为1.35～1.38（55℃）的清膏，取清膏，加蔗糖、糊精及乙醇适量，制成颗粒，干燥，加入上述挥发油，混匀，制成1600g；或将合并液浓缩成相对密度为1.32～1.35（55℃）的清膏，加入辅料适量，混匀，制成无糖颗粒，干燥，加入上述挥发油，混匀，制成800g；或将合并液减压浓缩至相对密度为1.08～1.10（55℃）的药液，喷雾干', '柴胡', '[\'荆芥\', \'薄荷\', \'防风\', \'柴胡\', \'紫苏叶\', \'葛根\', \'桔梗\', \'苦杏仁\', \'白芷\', \'苦地丁\', \'芦根\']');
INSERT INTO `prescript` VALUES (56726, '44439', '加味逍遥丸', '柴胡300g 当归300g 白芍300g 白术（麸炒）300g 茯苓300g 甘草240g 牡丹皮450g 桅子（姜炙）450g 薄荷60g', '口服，一次6g，一日2次。', '《中国药典》2015版', '舒肝清热，健脾养血。用于肝郁血虚，肝脾不和，两胁胀痛，头晕目眩，倦怠食少，月经不调，脐腹胀痛。', '切忌气恼劳碌；忌食生冷油腻。', '以上九味，粉碎成细粉，过筛，混匀。另取生姜100g，煎液泛丸，干燥，即得。', '柴胡', '[\'柴胡\', \'当归\', \'白芍\', \'白术\', \'茯苓\', \'甘草\', \'牡丹皮\', \'桅子\', \'薄荷\']');
INSERT INTO `prescript` VALUES (56727, '44398', '温白圆', '川乌（炮.去皮.脐）二两半，柴胡（去芦）、桔梗、吴茱萸（汤洗七次.焙干.炒）、菖蒲、紫菀（去苗.叶及土）、黄连（去须）、干姜（炮）、肉桂（去粗皮）、茯苓（去皮）、蜀椒（去目及闭口.炒出汗）、人参、厚朴（去粗皮.姜汁制）、皂荚（去皮.子.炙）、巴豆（去皮.心.膜.出油.炒.研），各半两。', '每服三圆，生姜汤下，食后或临卧服，渐加至五、七圆。', '《宋太平惠民和剂局方》', '治心腹积聚，久症癖块，大如杯碗，黄疸宿食，朝起呕吐，支满上气，时时腹胀，心下坚结，上来抢心，傍攻两胁。十种水病，八种痞塞，翻胃吐逆，饮食噎塞，五种淋疾，九种心痛，积年食不消化，或疟疾连年不瘥。\r\n及疗一切诸风，身体顽痹，不知痛痒，或半身不遂，或眉发堕落。\r\n及疗七十二种风，三十六种遁尸疰忤，及癫。\r\n或妇人诸疾，断续不生，带下淋沥，五邪失心，愁忧思虑，意思不乐，饮食无味，月水不调。\r\n及腹中一切诸疾，有似怀孕，连年累月，羸瘦困弊，或歌或哭，如鬼所使，但服此药，无不除愈。', '-', '上为细末，入巴豆匀，炼蜜为圆，如梧桐子大。', '柴胡', '[\'川乌\', \'去皮\', \'脐二\', \'两半\', \'柴胡\', \'去芦\', \'桔梗\', \'吴茱萸\', \'汤洗\', \'七次\', \'焙干\', \'菖蒲\', \'紫菀\', \'苗叶及\', \'黄连\', \'干姜\', \'肉桂\', \'粗皮\', \'茯苓\', \'去皮\', \'蜀椒\', \'目及\', \'闭口\', \'出汗\', \'人参\', \'厚朴\', \'粗皮\', \'姜汁\', \'皂荚\', \'皮子\', \'巴豆\', \'去皮\', \'心膜\', \'油炒\', \'研各\', \'半两\']');
INSERT INTO `prescript` VALUES (56728, '44394', '大山蓣圆', '白术、麦门冬（去心）、白芍药、杏仁（去皮.尖.麸炒黄）、防风（去芦.叉）、川芎，各一两半；大豆黄卷（炒）、熟干地黄、肉桂（去粗皮.僸.炒）、当归（酒浸），各二两半；桔梗、白茯苓（去皮）、柴胡，各一两二钱半；干姜（炮）七钱半，甘草（炙）七两，大枣一百个（蒸熟.去皮.核），阿胶（炒）、人参，各一两七钱半；白蔹半两，山蓣七两半。', '每服一圆，温酒或米饮化下，嚼服亦得，食前。', '《宋太平惠民和剂局方》', '治诸虚百损，五劳七伤，肢体沉重，骨节酸疼，心中烦悸，唇口干燥，面体少色，情思不乐，咳嗽喘乏，伤血动气，夜多异梦，盗汗失精，腰背强痛，脐腹弦急，嗜卧少起，喜惊多忘，饮食减少，肌肉瘦瘁。\r\n又治风虚，头目眩运，心神不宁，及病后气不复常，渐成劳损。\r\n久服补诸不足，愈风气百疾。\r\n常服养真气，益精补髓，活血驻颜。', '-', '上为末，炼蜜与蒸枣同和圆，如弹子大。', '柴胡', '[\'白术\', \'麦门冬\', \'心白\', \'芍药\', \'杏仁\', \'皮尖\', \'防风\', \'芦叉\', \'川芎\', \'各一\', \'两半\', \'大豆黄卷\', \'炒熟\', \'地黄\', \'肉桂\', \'当归\', \'酒浸\', \'各二\', \'两半\', \'桔梗\', \'白茯苓\', \'去皮\', \'柴胡\', \'一两\', \'二钱\', \'干姜\', \'炮七钱\', \'甘草\', \'七两\', \'大枣\', \'一百个\', \'蒸熟\', \'去皮\', \'阿胶\', \'人参\', \'一两\', \'七钱\', \'白蔹\', \'半两\', \'山蓣\', \'两半\']');
INSERT INTO `prescript` VALUES (56729, '44384', '人参败毒散', '柴胡（去苗）、甘草、桔梗、人参（去芦）、川芎、茯苓（去皮）、枳壳（去瓤.麸炒）、前胡（去苗.洗）、羌活（去苗）、独活（去苗），右十味，各三十两。', '每服二钱，水一盏，入生姜、薄荷各少许，同煎七分，去滓，不拘时候，寒多则热服，热多则温服。', '《宋太平惠民和剂局方》', '治伤寒时气，头痛项强，壮热恶寒，身体烦疼，及寒壅咳嗽，鼻塞声重，风痰头痛，呕哕寒热，并皆治之。', '-', '为粗末。', '柴胡', '[\'柴胡\', \'去苗\', \'甘草\', \'桔梗\', \'人参\', \'去芦\', \'川芎\', \'茯苓\', \'去皮\', \'枳壳\', \'胡去\', \'苗洗\', \'羌活\', \'去苗\', \'独活\', \'苗右十味\', \'三十两\']');
INSERT INTO `prescript` VALUES (56730, '44381', '大柴胡汤', '柴胡（二钱半） 大黄（三钱） 枳实（一钱） 赤芍药（二钱） 黄芩（二钱） 半夏（二钱）', '上作一服，水二钟，生姜五片，红枣一枚，煎至一钟，食远服。', '明方贤着《奇效良方》', '治伤寒十余日不解，邪热结在里，身热烦燥，言语谵妄，大便不通，绕脐刺痛。', '-', '-', '柴胡', '[\'柴胡\', \'二钱\', \'大黄\', \'三钱\', \'枳实\', \'一钱\', \'芍药\', \'二钱\', \'黄芩\', \'二钱\', \'半夏\', \'二钱\']');
INSERT INTO `prescript` VALUES (56731, '44374', '柴胡石膏散', '赤芍药、柴胡（去苗）、前胡（去苗）、石膏（煅）、干葛，各五十两。升麻二十五两，黄芩、桑白皮，各三十七两半。荆芥穗（去土）三十七两。', '每服二钱，水一盏，入生姜三片，豉十馀粒，同煎七分，去滓，稍热服。\r\n小儿分作三服，更量大小加减，不计时候。', '《宋太平惠民和剂局方》', '治时行瘟疫，壮热恶风，头痛体疼，鼻塞咽干，心胸如满，寒热往来，痰实咳嗽，涕唾稠粘。', '-', '上为粗末。', '柴胡', '[\'芍药\', \'柴胡\', \'苗前\', \'胡去\', \'石膏\', \'干葛\', \'五十两\', \'升麻\', \'二十五两\', \'黄芩\', \'桑白皮\', \'三十七\', \'两半\', \'荆芥\', \'三十七\']');
INSERT INTO `prescript` VALUES (56732, '44368', '人参黄蓍散', '天门冬（去心）三十两，半夏（汤洗七次.姜汁制）、知母、桑白皮（炒）、赤芍药、黄蓍、紫菀、甘草，各十五两；白茯苓（去皮）、柴胡（去苗）、秦艽（去土）、生干地黄、地骨皮，各二十两；人参、桔梗，各十两；鳖甲（去裙.醋炙）一两。', '每服二大钱，以水一盏，煎至七分，去滓，温服，食后。', '《宋太平惠民和剂局方》', '治虚劳客热，肌肉消瘦，四肢倦怠，五心烦热，口燥咽干，颊赤心忪，日晚潮热，夜有盗汗，胸胁不利，减食多渴，咳唾稠黏，时有脓血。', '-', '上为粗末。', '柴胡', '[\'天门冬\', \'去心\', \'三十两\', \'半夏\', \'汤洗\', \'七次\', \'姜汁\', \'知母\', \'桑白皮\', \'芍药\', \'紫菀\', \'甘草\', \'十五两\', \'白茯苓\', \'去皮\', \'柴胡\', \'去苗\', \'秦艽\', \'土生\', \'地黄\', \'地骨皮\', \'二十两\', \'人参\', \'桔梗\', \'十两\', \'鳖甲\', \'一两\']');
INSERT INTO `prescript` VALUES (56733, '44364', '柴胡六合汤', '熟地黄9克 川芎3克 芍药9克 当归9克 柴胡3克 黄芩3克', '水煎服。', '《医垒元戎》', '主妊娠伤寒，邪入少阳，胸胁满痛，脉弦者。', '-', '-', '柴胡', '[\'熟地黄\', \'川芎\', \'芍药\', \'当归\', \'柴胡\', \'黄芩\']');
INSERT INTO `prescript` VALUES (56734, '44362', '小柴胡汤', '柴胡（三钱） 人参（二钱） 黄芩（二钱） 半夏（一钱半） 甘草（一钱）', '上作一服，水二钟，生姜三片，红枣二枚，煎至一钟，不拘时服。', '明方贤着《奇效良方》', '治伤寒，寒热如疟，胸膈满痛，小便不利，大便涩。', '-', '-', '柴胡', '[\'柴胡\', \'三钱\', \'人参\', \'二钱\', \'黄芩\', \'二钱\', \'半夏\', \'一钱\', \'甘草\', \'一钱\']');
INSERT INTO `prescript` VALUES (56735, '44324', '小柴胡汤', '半夏（汤洗七次.焙干）二两半，柴胡（去芦）半斤，人参、甘草（炙）、黄芩，各三两。', '每服三大钱，水一盏半，生姜五片，枣一个，擘破，同煎至七分，去滓，稍热服，不拘时。小儿分作二服，量大小加减。', '《宋太平惠民和剂局方》', '治伤寒、温热病，身热恶风，颈项强急，胸满胁痛，呕哕烦渴，寒热往来，身面皆黄，小便不利，大便秘硬，或过经未解，或潮热不除；及瘥后劳复，发热疼痛；妇人伤风，头痛烦热；经血适断，寒热如疟，发作有时；及产后伤风，头痛烦热，并宜服之。', '-', '上为粗末。', '柴胡', '[\'半夏\', \'汤洗\', \'七次\', \'焙干\', \'两半\', \'柴胡\', \'去芦\', \'半斤\', \'人参\', \'甘草\', \'黄芩\', \'三两\']');
INSERT INTO `prescript` VALUES (56736, '44254', '八风九州岛岛岛岛汤', '麻黄（去节.四两）。甘草（炙）、干姜、附子（炮）、防风、独活，各三两。石膏（绵裹.搥碎）、茯苓、白术、川芎、柴胡、当归、人参、细辛，各二两。杏仁（去皮尖.四十九粒）。', '分为三服，一日令尽。若病人嬴瘦者，用水煎服，药讫厚覆，当汗出，微微去上衣，汗解以粉粉之。', '明方贤着《奇效良方》', '治男子妇人寒冷，不自爱护，当风解衣，汗出卧冷湿地，半身不遂，或俯仰屈伸难，周身淫淫，四肢不收，状如风狂，饮食损少，手足苦冷，或不随。', '忌生菜海藻菘菜酢桃李猪肉雀肉。', '上咀，以水一斗，清酒五升，渍三夜，煮取四升。', '柴胡', '[\'麻黄\', \'去节\', \'四两\', \'甘草\', \'干姜\', \'附子\', \'防风\', \'独活\', \'三两\', \'石膏\', \'绵裹\', \'茯苓\', \'白术\', \'川芎\', \'柴胡\', \'当归\', \'人参\', \'细辛\', \'二两\', \'杏仁\', \'皮尖\', \'四十九\']');
INSERT INTO `prescript` VALUES (56737, '44205', '清瘟解毒丸', '大青叶100g 连翘75g 玄参100g 天花粉100g 桔梗75g 牛蒡子（炒）100g 羌活75g 防风50g 葛根100g 柴胡50g 黄芩100g 白芷50g 川芎50g 赤芍50g 甘草25g 淡竹叶100g', '口服，一次2丸，一日2次；小儿酌减。', '《中国药典》2015版', '清瘟解毒。用于外感时疫，憎寒壮热，头痛无汗，口渴咽干，痄腮，大头瘟。', '-', '以上十六味，粉碎成细粉，过筛，混匀。每100g粉末加炼蜜150～170g制成大蜜丸，即得。', '柴胡', '[\'大青叶\', \'连翘\', \'玄参\', \'天花粉\', \'桔梗\', \'牛蒡子\', \'羌活\', \'防风\', \'葛根\', \'柴胡\', \'黄芩\', \'白芷\', \'川芎\', \'赤芍\', \'甘草\', \'淡竹叶\']');
INSERT INTO `prescript` VALUES (56738, '44191', '神仙百解散', '山茵陈、柴胡（去芦）、前胡（生姜制.炒）、人参、羌活、独活、甘草、苍术（米泔浸.炒）、干葛、白芍药、升麻、防风（去苗）、藿香（去梗）、白术、半夏（姜汁炙），各一两。\r\n立春已后不加减。\r\n立夏已后加柴胡一分，赤茯苓、当归，各半两。\r\n立秋已后减柴胡一分，不用当归、茯苓，只加干姜（炮）、肉桂（去粗皮），各一分，麻黄（去节）半两。\r\n立冬已后并无加减。\r\n* 一方无当归，有黄芩（去芦）半两。', '每服三钱，水一盏半，姜三片，枣二个，煎至一盏，热服，不计时候，并进二服。\r\n如要表散，加葱白三寸，淡豆豉三十粒，同煎服，以衣被盖覆，汗出而愈。', '《宋太平惠民和剂局方》', '治伤寒遍身疼痛，百节拘急，头目昏痛，肢体劳倦，壮热憎寒，神志不爽；感冒瘟疫瘴气。常服辟瘟疫，治劳倦。', '-', '上为细末。', '柴胡', '[\'茵陈\', \'柴胡\', \'芦前\', \'生姜\', \'人参\', \'羌活\', \'独活\', \'甘草\', \'苍术\', \'泔浸\', \'干葛白\', \'芍药\', \'升麻\', \'防风\', \'去苗\', \'藿香\', \'白术\', \'半夏\', \'姜汁\', \'两立\', \'加减\', \'立夏\', \'后加\', \'柴胡\', \'一分\', \'茯苓\', \'当归\', \'各半\', \'两立\', \'柴胡\', \'一分\', \'不用\', \'当归\', \'茯苓\', \'只加\', \'干姜\', \'肉桂\', \'粗皮\', \'一分\', \'麻黄\', \'去节\', \'半两\', \'立冬\', \'已后\', \'加减\', \'一方\', \'当归\', \'黄芩\', \'去芦\', \'半两\']');
INSERT INTO `prescript` VALUES (56739, '44163', '天仙藤散', '天仙藤、青蒿子（炒）、桑白皮（炒）、香附子（炒）、荆芥穗、前胡（生姜汁制.炒）、柴胡、桔梗、麻黄（去根.节）、苍术（炒）、干葛、陈皮，各十斤。茴香（炒）、秦艽、川芎、白芍药、黄半夏（为粗末.姜汁炙）、羌活，各二斤半。甘草（炒）、肉桂（去粗皮）、白芷、厚朴（去粗皮.姜汁炒），各五斤。', '每服三大钱，水一盏半，入生姜、乌梅、枣子，煎至七分，去滓，温服。并两滓作一服煎。', '《宋太平惠民和剂局方》', '治男子、妇人五劳七伤，感冷冒寒气弱体虚，多倦少力。常服壮筋骨，肢体轻健，进食。', '-', '上二十四味，为粗末。', '柴胡', '[\'天仙藤\', \'青蒿\', \'桑白皮\', \'香附子\', \'荆芥\', \'穗前\', \'胡生\', \'姜汁\', \'柴胡\', \'桔梗\', \'麻黄\', \'根节\', \'苍术\', \'干葛\', \'陈皮\', \'十斤\', \'茴香\', \'秦艽\', \'川芎\', \'芍药\', \'半夏\', \'粗末\', \'姜汁\', \'羌活\', \'二斤\', \'甘草\', \'肉桂\', \'粗皮\', \'白芷\', \'厚朴\', \'粗皮\', \'姜汁\', \'五斤\']');
INSERT INTO `prescript` VALUES (56740, '44157', '柴胡散', '石膏、黄芩、甘草、赤芍药、葛根，各一两；麻黄（去根.节）、柴胡（去苗），各半两。', '三岁小儿每服一钱，水一小盏，入生姜少许，葱白三寸，豉二十粒，同煎至五分，滤去滓，温服，不拘时候，汗出为效，量儿大小加减。', '《宋太平惠民和剂局方》', '治小儿伤寒壮热，头痛体疼，口干烦渴。', '-', '上捣罗为散。', '柴胡', '[\'石膏\', \'黄芩\', \'甘草\', \'芍药\', \'葛根\', \'一两\', \'麻黄\', \'根节\', \'柴胡\', \'半两\']');
INSERT INTO `prescript` VALUES (56741, '44156', '春泽汤', '泽泻（三钱） 猪苓（二钱） 茯苓（二钱） 白术（二钱） 桂心（一钱） 人参（一钱半） 柴胡（一钱） 麦门冬（一钱半）', '每服七钱，水一钟半，灯心二十茎，煎至一钟，食远服。', '明方贤着《奇效良方》', '治伏暑发热，烦渴引饮，小便不利，兼治伤寒阴阳不分，疑二之间，最宜服之。', '-', '-', '柴胡', '[\'泽泻\', \'三钱\', \'猪苓\', \'二钱\', \'茯苓\', \'二钱\', \'白术\', \'二钱桂心\', \'一钱\', \'人参\', \'一钱\', \'柴胡\', \'一钱\', \'麦门冬\', \'一钱\']');
INSERT INTO `prescript` VALUES (56742, '44090', '救苦化坚丸', '黄耆3克 人参1克 炙甘草1.5克 真漏芦 升麻各3克 葛根1.5克 连翘3克 牡丹皮1克 当归身 生地黄 熟地黄各1克 白芍药1克 肉桂0.6克 柴胡2.4克 黍粘子1克 羌活3克 独活 防风各1.5克 昆布0.6克 京三棱（煨）0.6克 广茂（煨）1克 益智仁0.6克 大麦蘖面3克 神曲末（炒黄色）0，6克 黄连（去须）1克 黄柏（炒）1克 厚朴9克（姜制）', '每服9克，白汤下。', '《兰室秘藏》卷下', '治瘰疬，马刀挟瘿，从耳下或耳后下颈至肩上，或入缺盆中。', '-', '-', '柴胡', '[\'黄耆克\', \'人参\', \'甘草\', \'克真\', \'漏芦\', \'升麻\', \'葛根\', \'连翘\', \'牡丹皮\', \'当归\', \'生地黄\', \'熟地黄\', \'各克白\', \'芍药\', \'肉桂\', \'柴胡\', \'粘子克\', \'羌活\', \'独活\', \'防风\', \'昆布\', \'克京\', \'三棱\', \'克广茂\', \'益智仁\', \'大麦\', \'面克\', \'神曲\', \'黄色\', \'黄连\', \'须克\', \'黄柏\', \'炒克\', \'厚朴\', \'姜制\']');
INSERT INTO `prescript` VALUES (56743, '44085', '柴胡升麻汤', '柴胡（去芦）、前胡（去芦）、干葛、石膏（煅）、赤芍药，各十两。升麻五两，荆芥（去梗）七两半，黄芩（去粗皮）、桑白皮，各六两半。', '每服三大钱，水一盏半，生姜三片，豉十馀粒，同煎一盏，去滓，稍热服，不拘时。小儿更量大小加减。', '《宋太平惠民和剂局方》', '治时行瘟疫，壮热恶风，头痛体疼，鼻塞咽干，心胸烦满，寒热往来，痰盛咳嗽，涕唾稠粘。', '-', '上俰咀。', '柴胡', '[\'柴胡\', \'芦前\', \'胡去\', \'芦干葛\', \'石膏\', \'芍药\', \'十两\', \'升麻\', \'五两\', \'荆芥\', \'两半\', \'黄芩\', \'粗皮\', \'桑白皮\', \'各六\', \'两半\']');
INSERT INTO `prescript` VALUES (56744, '44069', '护肝片', '柴胡250g 茵陈250g 板蓝根250g 五味子300g 猪胆粉20g 绿豆128g', '口服，一次4片，一日3次。', '《中国药典》2015版', '疏肝理气，健脾消食。具有降低转氨酶作用。用于慢性肝炎及早期肝硬化等。', '-', '上六味，绿豆粉碎成细粉；柴胡、茵陈、板蓝根加水煎煮二次，每次2小时，滤过，合并滤液，静置48小时，取上清液，减压浓缩成相对密度为1.30（80℃）的清膏，与绿豆粉101g混合，减压干燥，粉碎成细粉；五味子粉碎成粗粉，用75％乙醇回流提取三次，第一次3小时，第二次2小时，第三次1小时，合并提取液，静置24小时，取上清液，回收乙醇，浓缩至相对密度为1.28（80℃），与绿豆粉27g混匀，减压干燥，粉碎成细粉；取猪胆粉，与上述细粉混匀，制颗粒，干燥，压制成1000片，包糖衣，即得。', '柴胡', '[\'柴胡\', \'茵陈\', \'板蓝根\', \'五味子\', \'猪胆粉\', \'绿豆\']');
INSERT INTO `prescript` VALUES (56745, '44067', '舒肝和胃丸', '香附（醋制）45g 白芍45g 佛手150g 木香45g 郁金45g 白术（炒）60g 陈皮75g 柴胡15g 广藿香30g 炙甘草15g 菜菔子45g 槟榔（炒焦）45g 乌药45g', '口服，水蜜丸一次9g，大蜜丸一次2丸，一日2次。', '《中国药典》2015版', '舒肝解郁，和胃止痛。用于两胁胀满，食欲不振，打嗝呕吐，胃脘疼痛，大便失调。', '-', '上十三味，粉碎成细粉，过筛，混匀。每100g粉末用炼蜜70～85g加适量的水泛丸，干燥，制成水蜜丸；或加炼蜜120～130g制成大蜜丸，即得。', '柴胡', '[\'香附\', \'醋制\', \'白芍\', \'佛手\', \'木香\', \'郁金\', \'白术\', \'陈皮\', \'柴胡\', \'广藿香\', \'甘草\', \'菜菔子\', \'槟榔\', \'炒焦\', \'乌药\']');
INSERT INTO `prescript` VALUES (56746, '44058', '四逆散', '甘草（炙）、柴胡、枳实、芍药，各三钱。', '上作一服，水二钟，煎至一钟，食远服。', '明方贤着《奇效良方》', '少阴病四逆，其人或咳或悸，小便不利，腹中痛或泄利下重者。', '-', '-', '柴胡', '[\'甘草\', \'柴胡\', \'枳实\', \'芍药\', \'各三钱\']');
INSERT INTO `prescript` VALUES (56747, '44002', '黄蓍鳖甲散', '人参、肉桂（去粗皮）、苦梗，各一两六钱半；生干地黄（洗.焙干）三两三钱，半夏（煮）、紫菀（去芦）、知母、赤芍药、黄蓍、甘草、桑白皮，各二两半；天门冬（去心.焙）、鳖甲（去裙.醋炙），各五两；秦艽（去芦）、白茯苓（焙）、地骨皮（去土）、柴胡（去芦），各三两三钱。', '每服二大钱，水一盏，煎至七分，去滓温服，食后。', '《宋太平惠民和剂局方》', '治虚劳客热，肌肉消瘦，四肢倦怠，五心烦热，口燥咽干，颊赤心忪，日晚潮热，夜有盗汗，胸胁不利，减食多渴，咳唾稠粘，时有脓血。', '-', '上为粗末。', '柴胡', '[\'人参\', \'肉桂\', \'粗皮苦\', \'一两\', \'六钱\', \'半生\', \'地黄\', \'焙干\', \'两三\', \'半夏\', \'紫菀\', \'去芦\', \'知母\', \'芍药\', \'甘草\', \'桑白皮\', \'各二\', \'两半\', \'天门冬\', \'鳖甲\', \'五两\', \'秦艽\', \'去芦\', \'白茯苓\', \'地骨皮\', \'柴胡\', \'去芦\', \'两三\']');
INSERT INTO `prescript` VALUES (56748, '43987', '清燥汤', '苍术（米泔浸.一钱半） 五味子（十四枚） 黄蓍（一钱） 黄连（一钱） 白术（一钱） 橘皮（一钱） 白茯苓（去皮.一钱） 当归（一钱） 人参（七分） 生地黄（七分） 麦门冬（去心.半钱） 神曲（炒.半钱） 猪苓（半钱） 黄蘖（酒炒.半钱） 甘草（半钱） 泽泻（半钱） 柴胡（三分） 升麻（三分）', '上作一服，水二钟，煎至一钟，食前服。', '明方贤着《奇效良方》', '六七月之间，湿令大行，子能令母实而热旺，湿热相合，而刑庚踏肠，故寒凉救之，燥金受湿热之邪，寒水生化之源，源绝则肾亏，痿厥之病大作，腰以下痿软，瘫痪不能行走，不正，两足欹侧，以此药主之。', '-', '-', '柴胡', '[\'苍术\', \'泔浸\', \'一钱\', \'五味子\', \'十四\', \'一钱\', \'黄连\', \'一钱\', \'白术\', \'一钱\', \'橘皮\', \'一钱\', \'白茯苓\', \'去皮\', \'一钱\', \'当归\', \'一钱\', \'人参\', \'七分\', \'生地黄\', \'七分\', \'麦门冬\', \'心半钱\', \'神曲\', \'炒半钱\', \'猪苓\', \'半钱\', \'黄蘖\', \'半钱\', \'甘草\', \'半钱\', \'泽泻\', \'半钱\', \'柴胡\', \'三分\', \'升麻\', \'三分\']');
INSERT INTO `prescript` VALUES (56749, '43977', '龙胆泻肝丸', '龙胆120g 柴胡120g 黄芩60g 栀子（炒）60g 泽泻120g 关木通60g 车前子（盐炒）60g 当归（酒炒）60g 地黄120g 炙甘草60g', '口服，一次3～6g,一日2次。', '《中国药典》2015版', '清肝胆，利湿热。用于肝胆湿热，头晕目赤，耳鸣耳聋，耳肿疼痛，胁痛口苦，尿赤涩痛，湿热带下。', '孕妇慎用。', '上十味，粉碎成细粉，过筛，混匀，用水泛丸，干燥，即得。', '柴胡', '[\'龙胆\', \'柴胡\', \'黄芩\', \'栀子\', \'泽泻\', \'关木通\', \'车前子\', \'当归\', \'地黄\', \'甘草\']');
INSERT INTO `prescript` VALUES (56750, '43972', '人参败毒散', '柴胡（去芦.二钱），川芎（一钱半），前胡（去芦）、甘草（炙）、人参（去芦）、桔梗（去芦）、羌活（去苗）、独活（去苗）、茯苓（去皮）、枳壳（麸炒），各一钱。薄荷少许。', '上作一服，水二钟，生姜五片，煎至一钟，不拘时服。', '明方贤着《奇效良方》', '治伤寒头痛，壮热恶寒及风痰咳嗽，鼻塞重重，身体疼痛。', '-', '-', '柴胡', '[\'柴胡\', \'芦二钱\', \'川芎\', \'一钱\', \'半前\', \'胡去\', \'甘草\', \'人参\', \'去芦\', \'桔梗\', \'去芦\', \'羌活\', \'去苗\', \'独活\', \'去苗\', \'茯苓\', \'去皮\', \'枳壳\', \'一钱\', \'薄荷\', \'少许\']');
INSERT INTO `prescript` VALUES (56751, '43969', '十味芎苏散', '川芎（二钱），紫苏、桔梗（去芦）、柴胡（去苗）、茯苓（去皮）、半夏、枳壳、陈皮，各一钱。干葛（一钱半），甘草（炙.半钱）。', '上作一服，水二钟，生姜三片，红枣二枚，煎至一钟，不拘时服。', '唐孙思邈《千金翼方》', '治四时伤寒，发热头痛。', '-', '-', '柴胡', '[\'川芎\', \'二钱\', \'紫苏\', \'桔梗\', \'芦柴\', \'胡去\', \'茯苓\', \'去皮\', \'半夏\', \'枳壳\', \'陈皮\', \'各一钱\', \'干葛一钱\', \'甘草\', \'炙半钱\']');
INSERT INTO `prescript` VALUES (56752, '43797', '除湿羌活汤', '羌活4.5克 防风 升麻 柴胡各3克 藁本 苍术（米泔浸）各6克', '上锉一剂。加生姜3片，水煎服。', '《寿世保元》卷二', '祛风胜湿。主风湿犯表，发热，一身尽痛，脉浮而缓。', '-', '-', '柴胡', '[\'羌活\', \'防风\', \'升麻\', \'柴胡\', \'藁本\', \'苍术\', \'泔浸\']');
INSERT INTO `prescript` VALUES (56753, '43784', '附子汤', '附子（炮裂.去皮脐）、干姜（炮）、甘草（炙）、防风（去叉）、独活（去芦），各一两半。石膏（碎）、白茯苓（去皮）、白术、川芎、柴胡（去苗）、当归（酒浸.切焙）、人参，各一两。杏仁（去皮尖.炒研）二十枚。细辛，一两。', '每服五钱匕，水酒共一盏半，煎至一盏，去滓温服，日三。嬴瘦者只用水煎服。', '明方贤着《奇效良方》', '治风亸曳，手足不随，身体不能俯仰。', '-', '上挫如麻豆。', '柴胡', '[\'附子\', \'炮裂\', \'去皮\', \'干姜\', \'甘草\', \'防风\', \'独活\', \'去芦\', \'两半\', \'石膏\', \'白茯苓\', \'去皮\', \'白术\', \'川芎\', \'柴胡\', \'去苗\', \'当归\', \'浸切\', \'人参\', \'一两\', \'杏仁\', \'皮尖\', \'炒研\', \'二十枚\', \'细辛\', \'一两\']');
INSERT INTO `prescript` VALUES (56754, '43731', '龙虎丹', '黑牵牛、藿香叶（生）、天麻（去苗）、牛膝（去苗.酒浸.切.焙.微炒）、硫黄（结沙）、天竺黄（生研）、细辛（去苗.洗）、半夏（汤洗七次.生姜汁制）、附子（炮.去皮.脐）、何首乌（去粗皮）、羌活（去苗.洗.焙）、独活（去苗）、柴胡（去苗）、川芎（洗）、桔梗（生），各二两。寒水石（烧通赤.研.飞）一斤，茴香（淘.去土.焙）、甘松（洗去土.焙）、肉桂（去粗皮）、五灵脂（生）、白芷（生）、菊花（去土）、川乌（炮.去皮.脐）、白僵蚕（去丝.嘴.炒）、缩砂仁（生），各五两。牙硝（研）、木香（生）、水银（与硫黄用慢火结成', '每服一圆，用薄荷酒嚼下。日进一服，重即两服。\r\n产后惊风，乱道见物，朱砂酒磨下；产后身多虚肿，血风，频增昏沉，身如针刺，发随梳落，面黄心逆，并煎当归酒嚼下，日进两服；若治伤寒，炒葱、豉，酒嚼下一、二服，盖覆出汗立愈；小儿惊风，薄荷酒化下少许；大人急风，口噤失音等，薄荷酒化灌之，常服茶、酒任下，不拘时候服。', '《宋太平惠民和剂局方》', '治丈夫、妇人新得、久患急、缓风，半身不遂，手脚筋衰；及风毒攻注，遍身疮疥，头风多饶白屑，毒风面上生疮，刺风状如针刺，风急倒作声，顽风不认痛痒，倁风颈生斑驳，暗风头旋眼黑，卂风面生赤点，肝风鼻闷眼俽瞩，偏风口眼斜，节风肢节断续，脾风心多呕逆，酒风行步不前，肺风鼻塞项疼，胆风令人不睡，气风肉似虫行，肾风耳内蝉鸣，阴间湿痒。', '-', '上为细末，炼蜜为剂，如鸡头大。', '柴胡', '[\'牵牛\', \'藿香\', \'叶生\', \'天麻\', \'去苗\', \'牛膝\', \'苗酒\', \'浸切\', \'硫黄\', \'结沙\', \'天竺黄\', \'生研\', \'细辛\', \'苗洗\', \'半夏\', \'汤洗\', \'次生\', \'姜汁\', \'附子\', \'去皮\', \'何首乌\', \'粗皮\', \'羌活\', \'苗洗\', \'独活\', \'去苗\', \'柴胡\', \'去苗\', \'川芎\', \'桔梗\', \'二两\', \'寒水石\', \'烧通\', \'研飞\', \'一斤\', \'茴香\', \'淘去\', \'土焙\', \'甘松\', \'肉桂\', \'粗皮\', \'五灵脂\', \'白芷\', \'菊花\', \'川乌\', \'去皮\', \'白僵蚕\', \'炒缩\', \'砂仁\', \'五两\', \'牙硝研\', \'木香\', \'水银\', \'硫黄\', \'慢火\', \'结成\']');
INSERT INTO `prescript` VALUES (56755, '43723', '栀子仁汤', '栀子仁、赤芍药、大青、知母、升麻、杏仁，各一钱。黄芩（一钱半），石膏（二钱半），柴胡（二钱），甘草（半钱）。', '上作一服，水二钟，生姜五片，豆豉五十粒，煎至一钟，不拘时服。', '明方贤着《奇效良方》', '治发斑烦躁，面赤咽痛，潮热。', '-', '-', '柴胡', '[\'栀子\', \'仁赤\', \'芍药\', \'大青\', \'知母\', \'升麻\', \'杏仁\', \'各一钱\', \'黄芩\', \'一钱\', \'石膏\', \'二钱\', \'柴胡\', \'二钱\', \'甘草\', \'半钱\']');
INSERT INTO `prescript` VALUES (56756, '43702', '耳聋左慈丸', '磁石（煅）20g 熟地黄160g 山茱萸（制）80g 牡丹皮60g 山药80g 茯苓60g 泽泻60g 竹叶柴胡20g', '口服，水蜜丸一次6g，小蜜丸一次9g，大蜜丸一次1丸，一日2次。', '《中国药典》2015版', '滋肾平肝。用于肝肾阴虚，耳鸣耳聋，头晕目眩。', '-', '以上八味，粉碎成细粉，过筛，混匀。每100g粉末用炼蜜35～50g加适量的水泛丸，干燥，制成水蜜丸，或加炼蜜90～110g制成小蜜丸或大蜜丸，即得。', '柴胡', '[\'磁石\', \'熟地黄\', \'山茱萸\', \'牡丹皮\', \'山药\', \'茯苓\', \'泽泻\', \'竹叶\', \'柴胡\']');
INSERT INTO `prescript` VALUES (56757, '43651', '栀子乌梅汤', '栀子、黄芩，各二钱。柴胡（三钱），甘草（一钱），乌梅（三个.去核）。', '上作一服，水二钟，生姜三片，竹叶十四片，豆豉三十粒，煎至一钟，不拘时服。', '明方贤着《奇效良方》', '治伤寒瘥后不得眠。', '-', '-', '柴胡', '[\'栀子\', \'黄芩\', \'各二钱\', \'柴胡\', \'三钱\', \'甘草\', \'一钱\', \'乌梅\', \'三个\', \'去核\']');
INSERT INTO `prescript` VALUES (56758, '43637', '养心延龄益寿丹', '茯神15克 柏子仁12克（炒）丹参12克 酒白芍12克 丹皮12克 全当归15克（酒炒）川芎6克 干生地12克（酒洗）醋柴胡9克 香附米12克（炙）栀子9克（炒）酒条芩9克 陈皮9克 野于术6克（炒）枳壳12克（炒）酸枣仁12克（炒）', '每服9克，温开水送下。', '《慈禧光绪医方选议》', '养心安神，补肾滋阴，调肝理脾。治心肾俱亏，肝脾不调，身体瘦弱，心烦躁汗，夜寐不实，梦魇惊怖，腰膝酸疼，纳谷不香。', '-', '-', '柴胡', '[\'茯神\', \'柏子仁\', \'丹参\', \'克酒\', \'白芍\', \'丹皮\', \'克全\', \'当归\', \'克酒\', \'川芎\', \'克干\', \'生地\', \'克酒\', \'柴胡\', \'香附\', \'米克\', \'栀子\', \'条芩克\', \'陈皮克野\', \'于术克\', \'枳壳\', \'酸枣仁\']');
INSERT INTO `prescript` VALUES (56759, '43607', '生发饮', '熟地12克，首乌15克，当归10克，白芍12克，川芎9克，桃仁10克，红花10克，赤芍9克，丹皮9克，香附6克，柴胡6克，白芷6克，葱白3寸。', '水煎服，每日1剂，日服3次。', '常建林方', '养血活血，祛瘀通络。主血虚失荣，瘀血阻滞。', '-', '-', '柴胡', '[\'熟地\', \'首乌\', \'当归\', \'白芍\', \'川芎\', \'桃仁\', \'红花\', \'赤芍\', \'丹皮\', \'香附\', \'柴胡\', \'白芷\', \'葱白\']');
INSERT INTO `prescript` VALUES (56760, '43581', '桃奴散', '桃奴5枚 甘草7.5克（炙微赤，锉）杏仁20枚（汤浸，去皮、尖，双仁，麸炒微黄）麝香3克 桔梗（去芦头）赤芍药 黄芩 柴胡（去苗）川升麻 川大黄（锉，微炒）鬼臼（去毛）各15克', '每服3克，以水200毫升，煎至l00毫升，去滓，不计时候温服。以利为度。量儿大小，以意加减。', '《太平圣惠方》卷八十三', '治小儿中恶，心腹紧胀疼痛，颜色青黑，大便不通。', '-', '-', '柴胡', '[\'桃奴\', \'甘草\', \'克炙微\', \'杏仁\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'麝香\', \'桔梗\', \'芦头\', \'芍药\', \'黄芩\', \'柴胡\', \'苗川\', \'升麻\', \'大黄\', \'鬼臼\']');
INSERT INTO `prescript` VALUES (56761, '43574', '人参清肌散', '人参6克 白术4.5克 白茯苓9克（去皮）当归6克 赤芍6克 柴胡2.4克 半夏6克 葛粉6克 甘草2.4克', '加生姜、大枣，水煎服。', '《寿世保元》卷四', '治气虚，无汗潮热者。', '-', '-', '柴胡', '[\'人参\', \'白术\', \'白茯苓\', \'去皮\', \'当归\', \'赤芍\', \'柴胡\', \'半夏\', \'葛粉\', \'甘草\']');
INSERT INTO `prescript` VALUES (56762, '43565', '柴胡牡蛎汤', '柴胡10克，龙胆草6克，生牡蛎15克。', '水煎服，每日1剂，日服2次。', '余国俊方', '疏肝达郁，清降胆火。主肝胆郁热。', '-', '-', '柴胡', '[\'柴胡\', \'龙胆草\', \'克生\', \'牡蛎\']');
INSERT INTO `prescript` VALUES (56763, '43559', '升阳燥湿汤', '高良姜3克，干姜3克，郁李仁3克，甘草3克，陈皮1.5克，黄芪1.5克，白葵花0.9克，柴胡0.9克，升麻0.9克。', '水煎服，每日1剂，日服2次。', '《仁术便览》卷四', '补气升阳，燥湿散寒。主胞宫寒湿。', '-', '-', '柴胡', '[\'高良姜\', \'干姜\', \'郁李仁\', \'甘草\', \'陈皮克\', \'黄芪\', \'克白\', \'葵花\', \'柴胡\', \'升麻\']');
INSERT INTO `prescript` VALUES (56764, '43553', '补中益气丸', '炙黄芪200g 党参60g 炙甘草100g 白术（炒）60g 当归60g 升麻60g 柴胡60g 陈皮60g', '口服，水丸一次6g，小蜜丸一次9g，大蜜丸一次1丸，一日2～3次。', '《中国药典》2015版', '补中益气，升阳举陷。用于脾胃虚弱、中气下陷证引起的体倦乏力、食少腹胀、久泻、脱肛、子宫脱垂。', '-', '上八味，粉碎成细粉，过筛，混匀。另取生姜20g、大枣40g加水煎煮二次，滤过。取上述细粉，用煎液泛丸，干燥，制成水丸；或将生姜和大枣的煎液浓缩，每100g粉末加炼蜜100～120g及生姜和大枣的浓缩煎液,制成小蜜丸；或每100g粉末加炼蜜100～120g制成大蜜丸，即得。', '柴胡', '[\'黄芪\', \'党参\', \'甘草\', \'白术\', \'当归\', \'升麻\', \'柴胡\', \'陈皮\']');
INSERT INTO `prescript` VALUES (56765, '43542', '青丸子', '干葛 柴胡各30克 天竺黄15克 辰砂60克 甘草 薄荷各7.5克 全蝎7个 青黛15克', '每次0.3～0.6克，薄荷汤化下。', '《普济方》卷三八四引《鲍氏方》', '治小儿发热风壅。', '-', '-', '柴胡', '[\'干葛\', \'柴胡\', \'天竺黄\', \'辰砂\', \'甘草\', \'薄荷\', \'全蝎\', \'青黛克\']');
INSERT INTO `prescript` VALUES (56766, '43531', '宽气汤', '柴胡 乌药 秦艽 甘草 酒蒸大黄各3克 白芍30克 茯苓6克 当归9克 天麻 防风各0.9克 天花粉6克', '水煎服。', '《辨证录》卷二', '理气疏肝，泻火熄风。治体强嗜酒，偶因发怒，致成口眼歪斜，其脉洪大有力者。', '-', '-', '柴胡', '[\'柴胡\', \'乌药\', \'秦艽\', \'甘草\', \'酒蒸\', \'大黄\', \'白芍\', \'茯苓\', \'当归\', \'天麻\', \'防风\', \'天花粉\']');
INSERT INTO `prescript` VALUES (56767, '43523', '木香化滞汤', '半夏30克 草豆蔻仁 甘草（炙）各15克 柴胡12克 木香 橘皮各9克 枳实（麸炒，去瓤）当归梢各6克 红花1.5克', '每服1.5克，用水400毫升，加生姜5片，煎至150毫升，去滓，空腹时稍热服。', '《内外伤辨》卷下', '治情志不遂，食滞中脘，腹部微痛，心下痞满，不思饮食，食之不散。', '服药期间，忌酒及湿面。', '-', '柴胡', '[\'半夏\', \'草豆蔻\', \'甘草\', \'柴胡\', \'木香\', \'橘皮\', \'枳实\', \'当归\', \'红花\']');
INSERT INTO `prescript` VALUES (56768, '43511', '龙胆泻肝汤加减', '龙胆草9克，焦栀9克，柴胡9克，黄芩9克，生地9克，泽泻12克，当归12克，木通9克，甘草6克，银花50克，连翘15克，板兰根15克。', '水煎服，每日1剂，日服2次。', '齐强方', '清肝泻火，佐以解毒消炎。主肝胆火盛，上攻于目。', '-', '-', '柴胡', '[\'龙胆草\', \'克焦\', \'栀克\', \'柴胡\', \'黄芩\', \'生地\', \'泽泻\', \'当归\', \'木通\', \'甘草\', \'银花\', \'连翘\', \'克板\', \'兰根\']');
INSERT INTO `prescript` VALUES (56769, '43475', '清凉甘露饮', '犀角 银柴胡 茵陈 石斛 枳壳 麦门冬 甘草 生地 黄芩 知母 枇杷叶各3克', '用水400毫升，加淡竹叶20片，灯心20根，煎至320毫升，食后服。', '《外科正宗》卷四', '清热养阴。治情志不遂，过食膏粱厚味，痰火内蕴，致患茧唇，唇部高突坚硬，或损破流血。', '-', '-', '柴胡', '[\'犀角\', \'银柴胡\', \'茵陈\', \'石斛\', \'枳壳\', \'麦门冬\', \'甘草\', \'生地\', \'黄芩\', \'知母\', \'枇杷叶\']');
INSERT INTO `prescript` VALUES (56770, '43431', '椒桂汤', '川椒18克（炒黑）桂枝18克 良姜9克 柴胡18克 小茴香12克 广皮9克 吴茱萸9克（泡淡）青皮9克', '用急流水1.6升，煮成600毫升，温服200毫升。覆被令微汗，佳。不汗，再服200毫升，接饮生姜汤，促之得汗。次早又服200毫升，不必覆被再令汗。', '《温病条辨》卷三', '温中散寒，行气止痛。治暴感寒湿成疝，寒热往来，脉弦反数，舌白滑，或无苔，不渴，当脐痛，或胁下痛。', '-', '-', '柴胡', '[\'川椒克\', \'桂枝\', \'克良\', \'姜克\', \'柴胡\', \'小茴香\', \'克广皮克\', \'吴茱萸\', \'克泡\', \'淡青\', \'皮克\']');
INSERT INTO `prescript` VALUES (56771, '43421', '宣志汤', '茯苓15克 菖蒲3克 甘草3克 白术9克 生枣仁15克 远志3克 柴胡3克 当归9克 人参3克 山药15克 巴戟天9克', '水煎服。二剂心志舒，再服二剂见效。', '《辨证录》卷九', '解郁通阳。治阳痿。志意郁闷，以致阳事不举，或举而不坚。', '-', '-', '柴胡', '[\'茯苓\', \'菖蒲\', \'甘草\', \'白术\', \'克生\', \'枣仁\', \'远志\', \'柴胡\', \'当归\', \'人参\', \'山药\', \'巴戟天\']');
INSERT INTO `prescript` VALUES (56772, '43420', '养目汤', '熟地30克 白芍15克 麦冬15克 当归30克 葳蕤15克 山茱萸12克 北五味3克 甘草3克 甘菊花6克 柴胡1.5克', '水煎服。', '《辨证录》卷三', '补肾养肝，祛风明目。治目病由于肝肾不足，误作实火治后；目不痛而色淡红，羞明恶日。', '-', '-', '柴胡', '[\'熟地\', \'白芍\', \'麦冬\', \'当归\', \'葳蕤\', \'山茱萸\', \'克北\', \'五味\', \'甘草\', \'甘菊花\', \'柴胡\']');
INSERT INTO `prescript` VALUES (56773, '43413', '内托消毒散', '人参9克 黄耆30克 防风9克 白芷9克 川芎9克 当归9克 桔梗6克 连翘12克 升麻9克 柴胡6克 金银花15克 甘草节6克', '水、酒各150毫升，煎至150毫升，徐徐温服。', '《证治准绳·疡医》卷三', '治发颐有脓不消者。', '-', '-', '柴胡', '[\'人参\', \'黄耆克\', \'防风\', \'白芷\', \'川芎\', \'当归\', \'桔梗\', \'连翘\', \'升麻\', \'柴胡\', \'金银花\', \'甘草\', \'节克\']');
INSERT INTO `prescript` VALUES (56774, '43399', '升阳胜湿汤', '柴胡3克，羌活6克，苍术6克，黄芪6克，防风4.5克，升麻4.5克，独活4.5克，当归9克，藁本3克，甘草3克。', '水煎服，每日1剂，日服2次。', '《胎产要决》卷上', '疏风祛湿。主痰湿下注。', '-', '-', '柴胡', '[\'柴胡\', \'羌活\', \'苍术\', \'黄芪\', \'防风\', \'升麻\', \'独活\', \'当归\', \'藁本\', \'甘草\']');
INSERT INTO `prescript` VALUES (56775, '43366', '醉仙汤', '羌活 防风 柴胡 苍术 白芷 川芎 当归 生地 黄柏（盐水炒）牛膝 杜仲（盐水炒）香附（醋炒）白茯苓各3克', '上药用醇黄酒250毫升、水250毫升，煎至250毫升，加食盐0.21克，搅匀沮服，如能饮者，再饮酒200毫升，使血行可速愈也。\r\n如小儿患者，上药各用2.1克。有云翳，加白蒺藜、木贼；羞明，加天麻。', '《眼科阐微》卷三', '治时行害眼，并暴发赤肿疼痛，凶狠如蟹晴者。', '-', '-', '柴胡', '[\'羌活\', \'防风\', \'柴胡\', \'苍术\', \'白芷\', \'川芎\', \'当归\', \'生地\', \'黄柏\', \'盐水\', \'牛膝\', \'杜仲\', \'盐水\', \'香附\', \'白茯苓\']');
INSERT INTO `prescript` VALUES (56776, '43353', '完带汤', '白术（土炒）30克 山药（炒）30克 人参6克 白芍（酒炒）15克 车前子（酒炒）9克 苍术（制）9克 甘草3克 陈皮1.5克 黑芥穗1.5克 柴胡1.8克', '水煎服。', '《傅青主女科》卷上', '健脾燥湿，疏肝理气。治脾虚肝郁，湿浊下注，带下色白或淡黄，清稀无臭，倦怠便溏，面色咣白，舌淡苔白，脉缓或濡弱者。', '-', '-', '柴胡', '[\'白术\', \'炒克\', \'山药\', \'炒克\', \'人参\', \'白芍\', \'炒克\', \'车前子\', \'炒克\', \'苍术\', \'制克\', \'甘草\', \'陈皮克\', \'芥穗克\', \'柴胡\']');
INSERT INTO `prescript` VALUES (56777, '43327', '加味逍遥散', '甘草3克，当归3克，白芍3克，白术3克，茯苓3克，柴胡3克，桂皮2.1克，山栀2.1克。', '上为粗末，水煎服。', '《杂病源流犀烛》卷二十七', '舒肝解郁，清热散结。主肝郁脾虚。', '-', '-', '柴胡', '[\'甘草\', \'当归\', \'白芍\', \'白术\', \'茯苓\', \'柴胡\', \'桂皮\', \'克山\', \'栀克\']');
INSERT INTO `prescript` VALUES (56778, '43323', '四物消风饮', '生地黄12克 归身 赤芍各6克 荆芥 薄荷 蝉蜕各4.5克 柴胡 川芎 黄芩各3.6克 生甘草3克', '水煎服。', '《外科证治全书》卷五', '治素体血虚，风热外客，皮肤游风，瘾疹瘙痒；及劳伤冒风，身热口燥。', '-', '-', '柴胡', '[\'生地黄\', \'克归身\', \'赤芍\', \'荆芥\', \'薄荷\', \'蝉蜕\', \'柴胡\', \'川芎\', \'黄芩\', \'生甘草\']');
INSERT INTO `prescript` VALUES (56779, '43315', '煎麦散', '大鳖甲60克（醋煮三五十沸后，净去裙襕，另用好醋煮令香）银州柴胡60克（去苗）大川乌头30克（炮裂，去皮、脐）元参90克 干漆30克（炒）干葛30克 秦艽60克（去土）人参30克 茯苓30克', '每服6克，先用小麦3～7粒，煎汤取150毫升，去麦，再入诸药，同煎至105毫升，食后或临卧时温服。', '《博济方》卷一', '调补营卫。治荣卫不调，夜多盗汗，四肢烦疼，饮食减少，肌瘦面黄。', '-', '-', '柴胡', '[\'鳖甲\', \'克醋\', \'三五十\', \'沸后\', \'令香\', \'银州\', \'柴胡\', \'克去\', \'大川\', \'乌头\', \'克炮裂\', \'去皮\', \'脐元\', \'参克\', \'干漆\', \'干葛克\', \'秦艽\', \'克去\', \'人参\', \'茯苓\']');
INSERT INTO `prescript` VALUES (56780, '43241', '舒肝溃坚汤', '夏枯草 僵蚕（炒）各6克 香附子（酒炒）石决明（煅）各4.5克 当归 白芍（醋炒）陈皮 柴胡 抚芎 穿山甲（炒）各3克 红花 片子姜黄 甘草（生）各1.5克', '上药加灯心1.5米为引，用水600毫升，煎成200毫升，空腹时热服。\r\n便燥者，加乳香3克；便溏者，加煅牡蛎3克。', '《医宗金鉴》卷六十四', '治瘰疬，石疽。', '-', '-', '柴胡', '[\'夏枯草\', \'僵蚕\', \'香附子\', \'石决明\', \'当归\', \'白芍\', \'陈皮\', \'柴胡\', \'抚芎\', \'穿山甲\', \'红花\', \'片子\', \'姜黄\', \'甘草\', \'生各克\']');
INSERT INTO `prescript` VALUES (56781, '43216', '秦艽散', '秦艽30克（去苗）柴胡30克（去苗）枳壳22克（麸炒微黄，去瓤）桑根白皮22克（锉）麦门冬30克（去心）葛根22克（锉）', '每服12克，以水300毫升，入生姜4克，芦根16.5厘米，煎至180毫升，去滓，不计时候，频频温服。', '《太平圣惠方》卷十', '治伤寒汗后，余热不除，四肢拘急，胸膈不利，呕逆，不思饮食。', '-', '-', '柴胡', '[\'秦艽\', \'克去\', \'柴胡\', \'克去\', \'枳壳\', \'克麸\', \'微黄\', \'桑根\', \'白皮\', \'麦门冬\', \'克去\', \'葛根\']');
INSERT INTO `prescript` VALUES (56782, '43211', '阿魏消瘤汤', '阿魏1.5克，柴胡1.5克，甘草1.5克，当归尾4.5克，赤芍4.5克，桔梗3克。', '水煎服，每日1剂，日服2次。', '张文明方', '理血消瘤。主瘀血阻滞。', '-', '-', '柴胡', '[\'阿魏\', \'柴胡\', \'甘草\', \'当归\', \'尾克\', \'赤芍\', \'桔梗\']');
INSERT INTO `prescript` VALUES (56783, '43210', '调中益气汤', '黄耆3克 人参（去芦头，有嗽者去之）甘草 苍术各1.5克 柴胡 橘皮（如腹中气不得运转，更加3克）升麻各0.6克 木香0.3～0.6克', '水600毫升，煎至500毫升，去滓，空腹时带热服之。宁心绝思，药必神效。', '《脾胃论》卷中', '益气健脾，和中祛湿。主肠胃虚弱，湿阻气滞，脘腹胀满，不思饮食，身体倦怠，大便泄泻，肢节烦疼者。', '-', '-', '柴胡', '[\'黄耆克\', \'人参\', \'芦头\', \'有嗽\', \'甘草\', \'苍术\', \'柴胡\', \'橘皮\', \'腹中\', \'不得\', \'运转\', \'更加\', \'升麻\', \'木香\']');
INSERT INTO `prescript` VALUES (56784, '43179', '清上泻火汤', '荆芥穗 川芎各0.6克 蔓荆子 当归身 苍术各0.9克 酒黄连 生地黄 藁本 生甘草各1.5克 升麻 防风各2.1克 酒黄柏 炙甘草 黄耆各3克 酒黄芩 酒知母各4.5克 羌活9克 柴胡15克 细辛少许 红花少许', '上药锉碎，分作二服。每服用水300毫升，煎至150毫升，去滓，食后稍热服。', '《兰室秘藏》卷中', '治热厥头痛，得寒则止，遇热则作。', '-', '-', '柴胡', '[\'荆芥\', \'川芎\', \'蔓荆子\', \'当归\', \'苍术\', \'各克酒\', \'黄连\', \'生地黄\', \'藁本\', \'生甘草\', \'升麻\', \'防风\', \'各克酒\', \'黄柏\', \'甘草\', \'黄耆\', \'各克酒\', \'黄芩\', \'知母\', \'羌活\', \'柴胡\', \'细辛\', \'少许\', \'红花\', \'少许\']');
