INSERT INTO `prescript` VALUES (56786, '43164', '加味补中益气汤', '黄耆（炒）2钱，人参1钱，白术（去芦，炒）2钱，白茯苓2钱，陈皮8分，柴胡4分，升麻3分，白芍（酒炒）1钱5分，当归（酒炒）3钱，萝卜子（炒）3钱，厚朴（姜炒）1钱，甘草（炙）8分，枳实（麸炒）8分。', '加生姜，水煎服。', '《寿世保元》卷三', '脾胃虚弱，治失其宜，元气虚惫，脾胃伤损，肿胀尤甚。', '-', '-', '柴胡', '[\'黄耆\', \'人参\', \'白术\', \'去芦\', \'白茯苓\', \'陈皮分\', \'柴胡\', \'升麻\', \'白芍\', \'钱分\', \'当归\', \'萝卜\', \'厚朴\', \'甘草\', \'炙分\', \'枳实\', \'炒分\']');
INSERT INTO `prescript` VALUES (56787, '43146', '黄连启心汤', '人参3克 白术 丹皮各9克 黄连 玄参各6克 甘草3克 桂枝1克 半夏1.5克 柴胡1克', '水煎服。', '《辨证录》卷五', '治五志厥阳之火太盛，遏抑于心包之内，致患关格，吐逆不得饮食，大、小便不通。', '-', '-', '柴胡', '[\'人参\', \'白术\', \'丹皮\', \'黄连\', \'玄参\', \'甘草\', \'桂枝\', \'半夏\', \'柴胡\']');
