INSERT INTO `prescript` VALUES (56793, '43059', '中满分消汤', '川乌 泽泻 黄连 人参 青皮 当归 生姜 麻黄 柴胡 干姜 荜澄茄各0.6克 益智仁 半夏 茯苓 木香 升麻各0.9克 黄耆 吴茱萸 厚朴 草豆蔻仁 黄柏各15克', '用水600毫升，煎至300毫升，空腹时热服。', '《兰室秘藏》卷上', '治中满寒胀，寒疝，腹中冷，心下痞，大小便不通，四肢逆冷，食入反出，虚烦躁动，下肢不收。', '服药期间，忌酒、湿面、生冷、油腻等物，戒房事。', '-', '柴胡', '[\'川乌\', \'泽泻\', \'黄连\', \'人参\', \'青皮\', \'当归\', \'生姜\', \'麻黄\', \'柴胡\', \'干姜\', \'荜澄茄\', \'益智仁\', \'半夏\', \'茯苓\', \'木香\', \'升麻\', \'黄耆\', \'吴茱萸\', \'厚朴\', \'草豆蔻\', \'黄柏\']');
INSERT INTO `prescript` VALUES (56794, '43043', '助阳和血补气汤', '香白芷0.6克 蔓荆子0.9克 炙甘草 当归身（酒洗）柴胡各1.5克 升麻 防风各2.1克 黄耆3克', '上药哎咀，作一服。用水230毫升，煎至150毫升，去滓，临卧时热服。', '《脾胃论》卷下', '治热邪上壅，眼中自睛红，多眵泪，无疼痛而隐涩难开，昏花不明，由于服苦寒药太过者。', '服药期间，须忌风寒及食冷物。', '-', '柴胡', '[\'白芷\', \'蔓荆子\', \'甘草\', \'当归\', \'身酒\', \'柴胡\', \'升麻\', \'防风\', \'黄耆克\']');
INSERT INTO `prescript` VALUES (56795, '43042', '加味败毒散', '羌活 独活 前胡 柴胡 当归 川芎 枳壳（去瓤）桔梗 茯苓 人参各15克 薄荷 甘草 白术 防风 荆芥 苍术（米泔水浸）赤芍 生地黄各1.5克', '加生姜、大枣，水煎，温服。', '《寿世保元》卷四', '疏风祛湿，凉血解毒。治风热客于肌肤，气滞血凝，发为瘾疹；感冒风湿，以致发斑者。', '-', '-', '柴胡', '[\'羌活\', \'独活\', \'柴胡\', \'当归\', \'川芎\', \'枳壳\', \'桔梗\', \'茯苓\', \'人参\', \'薄荷\', \'甘草\', \'白术\', \'防风\', \'荆芥\', \'苍术\', \'米泔水\', \'赤芍\', \'生地黄\']');
INSERT INTO `prescript` VALUES (56796, '43035', '全体神膏', '当归60克 生地60克 续断30克 牛膝30克 甘草15克 地榆30克 茜草30克 小蓟30克 木瓜30克 杏仁9克 人参30克 皂角6克 川芎30克 刘寄奴30克 桑木枝120克 红花60克 白术30克 黄誉30克 柴胡9克 荆芥9克', '用时烊化摊膏，膏重约30克，外贴患处。若骨折，掺胜金丹贴之。', '《辨证录》卷十三', '治骨折，跌打损伤。', '-', '-', '柴胡', '[\'当归\', \'生地\', \'续断\', \'牛膝\', \'甘草\', \'地榆\', \'茜草\', \'小蓟\', \'木瓜\', \'杏仁\', \'人参\', \'克皂角克\', \'川芎\', \'刘寄奴\', \'克桑木枝克\', \'红花\', \'白术\', \'黄誉克\', \'柴胡\', \'荆芥\']');
