INSERT INTO `prescript` VALUES (56803, '42938', '加味补中益气汤', '黄芩1钱，黄耆1钱，柴胡1钱，半夏8分，芍药8分，人参8分，白术8分，当归8分，甘草5分，升麻3分，陈皮6分。', '上作1服。水2钟，加生姜3片，煎8分，空心服。有汗及寒重，加桂枝5分，倍黄耆；热盛，倍柴胡、黄芩；渴，加麦门冬、天花粉。', '《万氏家抄方》卷二', '平素不足，兼以劳役内伤，挟感寒暑，致患疟疾，寒热交作，肢体倦软，乏力少气。', '-', '-', '柴胡', '[\'黄芩\', \'黄耆\', \'柴胡\', \'半夏\', \'芍药\', \'人参\', \'白术\', \'当归\', \'甘草\', \'升麻\', \'陈皮分\']');
