INSERT INTO `prescript` VALUES (56822, '42484', '滋阴降火汤', '当归3克 川芎1.5克 白芍药（薄荷汁炒）黄芩各2.1克 生地黄（姜汁炒）黄柏（蜜水炒）知母（酒炒）各2.4克 柴胡2.1克 熟地黄2.4克 麦门冬2.4克', '上药，加生姜1片，大枣1枚，水煎服。别以附子为末，唾津调贴涌泉穴。\r\n气虚，加人参、黄耆各2.4克；咳嗽，加阿胶、杏仁各2.1克，五味子0.9克；咯唾衄血，加牡丹皮2.4克，藕节自然汁10毫升，犀角末1.5克。', '《医便》卷二', '治阴虚火动，起于九泉者。', '-', '-', '柴胡', '[\'当归\', \'川芎\', \'克白\', \'芍药\', \'薄荷\', \'黄芩\', \'生地黄\', \'姜汁\', \'黄柏\', \'蜜水\', \'知母\', \'柴胡\', \'熟地黄\', \'麦门冬\']');
INSERT INTO `prescript` VALUES (56823, '42456', '养血平肝散', '当归（酒浸）6克，炒白芍6克，香附（炒黑）6克，青皮（醋炒）2.4克，柴胡2.4克，川芎2.4克，生地黄2.4克，甘草1.5克。', '水煎，食前服。', '《济阴纲目》卷二', '养血舒肝，调经止血。主肝气失调。', '-', '-', '柴胡', '[\'当归\', \'浸克\', \'白芍\', \'香附\', \'黑克\', \'青皮\', \'炒克\', \'柴胡\', \'川芎\', \'生地黄\', \'甘草\']');
INSERT INTO `prescript` VALUES (56824, '42406', '升阳举经汤', '肉桂（去皮，盛夏不用）白芍药 红花各1.5克 细辛1.8克 人参 熟地黄 川芎各3克 独活 黑附子（炮裂，去皮、脐）炙甘草各5克 羌活 藁本 防风各6克 白术 当归 黄耆 柴胡各9克 桃仁（汤浸，去皮、尖）10个', '每服9克，用水450毫升，煎至150毫升。空腹时热服。', '《兰室秘藏》卷中', '治妇人经水不止，右尺脉按之空虚，属气血俱脱者。', '-', '-', '柴胡', '[\'肉桂\', \'去皮\', \'盛夏\', \'不用\', \'芍药\', \'红花\', \'细辛\', \'人参\', \'熟地黄\', \'川芎\', \'独活\', \'附子\', \'炮裂\', \'去皮\', \'甘草\', \'羌活\', \'藁本\', \'防风\', \'白术\', \'当归\', \'黄耆\', \'柴胡\', \'桃仁\', \'汤浸\', \'皮尖\']');
INSERT INTO `prescript` VALUES (56825, '42398', '复方少腹化瘀汤', '红藤60克 牛膝25克 桃仁 红花 当归各15克 延胡索 赤芍 香附各10克 炮姜 桂枝 川楝子 小茴香各6克 柴胡3克', '水煎服，每日一剂，每剂二煎，分二次。', '《急腹症方药新解》', '活血化瘀，温经止痛。治阑尾包块、索条，腹部手术后切口炎性浸润，腹腔内炎性包块。', '-', '-', '柴胡', '[\'红藤\', \'牛膝\', \'桃仁\', \'红花\', \'当归\', \'延胡索\', \'赤芍\', \'香附\', \'各克炮\', \'桂枝\', \'川楝子\', \'小茴香\', \'柴胡\']');
INSERT INTO `prescript` VALUES (56826, '42371', '乳核饮', '柴胡12克，白芍12克，香附12克，郁金12克，青皮9克，丹参9克，三棱9克，生牡蛎30克（先煎），白花蛇舌草15克，夏枯草30克，黄芪15克。', '水煎服，每日1剂，日服2次。', '吴熙方', '疏肝理气，活血化瘀，消痰散结。主气滞血瘀，气阻痰凝。（乳腺增生）', '-', '-', '柴胡', '[\'柴胡\', \'白芍\', \'香附\', \'郁金\', \'青皮\', \'丹参\', \'三棱\', \'克生\', \'牡蛎\', \'克先\', \'白花蛇\', \'舌草克\', \'夏枯草\', \'黄芪\']');
INSERT INTO `prescript` VALUES (56827, '42360', '柴胡蚤休汤', '炒柴胡10克，蚤休15克，茯苓10克，赤芍10克，白芍10克，茜草10克，当归10克，郁金10克，制香附10克，黄芩15克，莪术15克，全瓜蒌20克，生鳖甲20克，虎杖20克，甘草10克。', '水煎服，每日1剂，日服2次。', '《浙江省中医院方》', '疏肝理气，活血化瘀。主气滞血瘀。', '-', '-', '柴胡', '[\'柴胡\', \'克蚤\', \'休克\', \'茯苓\', \'赤芍\', \'白芍\', \'茜草\', \'当归\', \'郁金\', \'克制\', \'香附\', \'黄芩\', \'莪术\', \'全瓜\', \'克生\', \'鳖甲\', \'虎杖\', \'甘草\']');
INSERT INTO `prescript` VALUES (56828, '42358', '升阳散火汤', '升麻 葛根 独活 羌活 白芍药 人参 各15克 甘草（炙）柴胡各9克 防风7.5克 甘草（生）6克', '每服15克，用水300毫升，煎至150毫升，去滓，大温服，不拘时。', '《内外伤辨》卷中', '升阳散火。治血虚或胃虚过食冷物，阳气郁遏于脾，肌肤灼热，或骨蒸潮热，扪之烙手。', '服药期间，忌食寒凉之物。', '-', '柴胡', '[\'升麻\', \'葛根\', \'独活\', \'羌活\', \'芍药\', \'人参\', \'甘草\', \'柴胡\', \'防风\', \'甘草\', \'生克\']');
INSERT INTO `prescript` VALUES (56829, '42341', '柴金汤', '柴胡12克，滑石12克，鸡内金12克，白芍9克，黄芩9克，半复9克，枳实6克，川军6克，生姜15克，大枣4枚，金钱草24克。', '水煎服，每日1剂，日服2次。', '岳美中方', '疏肝泻热，行气利胆。主肝经热甚，胆热犯胃。', '-', '-', '柴胡', '[\'柴胡\', \'滑石\', \'鸡内金\', \'白芍\', \'黄芩\', \'克半复克\', \'枳实\', \'川军\', \'生姜\', \'大枣\', \'金钱草\']');
INSERT INTO `prescript` VALUES (56830, '42337', '清骨散', '北柴胡 生地黄各60克 人参 防风 熟地黄 秦艽 赤茯苓各30克 胡黄连15克 薄荷叶23克', '每服12克，用水150毫升，煎至100毫升，温服。', '《永类钤方》卷十三', '治气阴两虚，热邪内伏，五心烦热，欲成劳瘵。', '-', '-', '柴胡', '[\'柴胡\', \'生地黄\', \'人参\', \'防风\', \'熟地黄\', \'秦艽\', \'茯苓\', \'胡黄连\', \'薄荷叶\']');
INSERT INTO `prescript` VALUES (56831, '42316', '芍药清肝散', '白术0.9克 甘草（炙）0.75克 川芎0.9克 防风0.9克 荆芥0.75克 桔梗0.9克 羌活0.9克 芍药0.75克 柴胡0.6克 前胡0.75克薄荷0.75克 黄芩0.75克。山栀0.6克 知母0.6克 滑石0，9克 石膏0.9克 大黄1.2克 芒消1克', '上为粗末，作一服。用水200毫升，煎至100毫升，食后热服。\r\n大便不硬者，去大黄、芒消。', '《原机启微》卷下', '治风热上攻，眼目昏糊，眵多眊燥，紧涩羞明，赤脉贯睛，大便秘结。', '-', '-', '柴胡', '[\'白术\', \'甘草\', \'炙克\', \'川芎\', \'防风\', \'荆芥\', \'桔梗\', \'羌活\', \'芍药\', \'柴胡\', \'克前\', \'胡克\', \'薄荷\', \'黄芩\', \'克山\', \'栀克\', \'知母\', \'滑石\', \'石膏\', \'大黄\', \'克芒\', \'消克\']');
INSERT INTO `prescript` VALUES (56832, '42303', '加减千金固胎饮', '人参1钱，生于术3钱，云苓2钱，炙甘草5分，酒炒黄芩1钱，枳壳1钱5分（炒），青皮1钱5分，柴胡5分。', '用煨姜3钱，大枣3个，荷叶蒂3个为引，河井水煎。头煎在疟前服，二煎接服之。如恶心，加灶心土5钱、乌梅2个；如腹胀，加砂仁3钱，如热多，加青蒿3钱；如腰痛，加续断3钱；如痰多，加川贝母1钱5分、橘红8分；如大便结，加鲜首乌5钱。当归5钱；如作泻，加鹿角霜3钱；如腹痛，加煨广木香1钱5分；如泻热，加酒炒川连3分；如胎气下坠，加黄耆5钱（炙）、菟丝子2钱；如子气上逆，加砂仁3钱、葡萄干3钱。', '《慈航集》卷下', '孕妇人虚，疟疾寒热不止。', '愈后饮食宜清淡，调理4-5日，庶疟不复。', '-', '柴胡', '[\'人参\', \'生于\', \'钱云苓\', \'甘草\', \'分酒\', \'黄芩\', \'枳壳\', \'钱分\', \'青皮\', \'钱分\', \'柴胡\']');
INSERT INTO `prescript` VALUES (56833, '42297', '四柴胡饮', '柴胡3～9克 炙甘草3克 生姜3～7片 当归6～9克（泻者少用）人参6～9克或15～21克', '用水400毫升，煎至200毫升，温服。\r\n朐膈滞闷者，加陈皮3克。', '《景岳全书》卷五十一', '扶正解表。治元气不足，或忍饥劳倦，而外感风寒，或六脉紧数微细，正不胜邪者。', '-', '-', '柴胡', '[\'柴胡\', \'甘草\', \'生姜\', \'当归\', \'克泻者\', \'少用\', \'人参\']');
INSERT INTO `prescript` VALUES (56834, '42296', '加味益气汤', '黄耆1钱，人参1钱，白术7分，陈皮7分，当归7分，柴胡1钱，升麻3分，黄柏（酒炒）7分，羌活1钱半，防风5分，甘草5分。', '加生姜3片，水煎，热服。冬月，加细辛3分；如热甚，脉滞有力，加黄芩（酒炒）3分。', '《回春》卷二', '素体怯弱，兼之疲劳，感冒伤寒，头痛发热者。', '-', '-', '柴胡', '[\'黄耆\', \'人参\', \'白术\', \'陈皮分\', \'当归\', \'柴胡\', \'升麻\', \'黄柏\', \'炒分\', \'羌活\', \'钱半\', \'防风\', \'甘草\']');
INSERT INTO `prescript` VALUES (56835, '42284', '来苏散', '柴胡（去芦）、甘草（炙）、干姜，各二两。肉桂（去粗皮.不见火）、桔梗、防风、荆芥穗、五如皮，各一两。芍药（半两），麻黄（去节）、陈皮（去白），各一两半。黄芪（蜜水浸一宿.炙）一分。', '每服二钱，水一盏，生姜三片，同煎至八分，热服，不拘时候。', '《宋太平惠民和剂局方》', '解利四时温疫，伤寒，身体壮热，头痛憎寒，项脊拘急，浑身疼痛，烦渴闷乱，大小便涩，嗜卧少力，全不思饮食，及诸气疾，五劳七伤，山岚瘴疟，寒热往来等疾，并皆治之。\r\n常服和顺三焦，辟瘴气，进饮食。', '-', '上为细末。', '柴胡', '[\'柴胡\', \'去芦\', \'甘草\', \'干姜\', \'二两\', \'肉桂\', \'粗皮\', \'不见\', \'桔梗\', \'防风\', \'荆芥\', \'五如皮\', \'一两\', \'芍药\', \'半两\', \'麻黄\', \'去节\', \'陈皮\', \'两半\', \'黄芪\', \'水浸\', \'一宿\', \'一分\']');
INSERT INTO `prescript` VALUES (56836, '42279', '牛黄清心丸', '羚羊角（镑.另研.一两） 麝香（另研.一两） 龙脑（另研.一两） 人参（去芦.二两半） 蒲黄（二两半） 白茯苓（去皮.二钱半） 芎藭（一两六钱半） 柴胡（一两六钱半） 杏仁（另研.一两六钱半） 桔梗（一两六钱半） 防风（去芦.一两半） 白术（一两半） 白芍药（一两半） 麦门冬（一两半） 黄芩（一两半） 神曲（一两半） 当归（一两半） 阿胶（炒.一两七钱半） 大豆黄卷（一两七钱半） 肉桂（一两七钱半） 干姜（七钱半） 牛黄（另研.一两二钱） 犀角（另研.二两） 雄黄（另研 八钱） 金箔（一千二百箔） 甘草（', '每服一丸，食后温水下。', '明方贤着《奇效良方》', '治诸风缓纵不随，言语蹇涩，痰涎壅盛，心怔健忘，或发癫狂，并皆治之。', '-', '上除大枣、杏仁、金箔、羚羊角、犀角、麝香、龙脑、雄黄另研，余药别研为细末，入羚羊角等七味。入内再研和匀。将大枣煮熟去皮核，捣烂如泥。同炼蜜为丸，每一两作十丸，金箔为衣。', '柴胡', '[\'羚羊角\', \'另研\', \'一两\', \'麝香\', \'另研\', \'一两\', \'龙脑\', \'另研\', \'一两\', \'人参\', \'芦二\', \'两半\', \'蒲黄二\', \'两半\', \'白茯苓\', \'去皮\', \'二钱\', \'一两\', \'六钱\', \'柴胡\', \'一两\', \'六钱\', \'杏仁\', \'另研\', \'一两\', \'六钱\', \'桔梗\', \'一两\', \'六钱\', \'防风\', \'芦一\', \'两半\', \'白术\', \'两半\', \'芍药\', \'两半\', \'麦门冬\', \'两半\', \'黄芩\', \'两半\', \'神曲\', \'两半\', \'当归\', \'两半\', \'阿胶\', \'一两\', \'七钱\', \'大豆黄卷\', \'一两\', \'七钱\', \'肉桂\', \'一两\', \'七钱\', \'干姜\', \'七钱\', \'牛黄\', \'另研\', \'一两\', \'二钱\', \'犀角\', \'另研\', \'二两\', \'雄黄\', \'另研\', \'八钱\', \'金箔\', \'一千二百\', \'甘草\']');
INSERT INTO `prescript` VALUES (56837, '42264', '虎骨散', '虎骨（酥炙黄）、乌梅肉、赤茯苓（去皮）、肉苁蓉（酒浸.切焙）、人参、甘草（炙）、芍药、鳖甲（去裙襕.醋浸炙）、白术、豆豉（慢火炒）、紫苑（去土）、黄蓍（焙）、常山（炒）、枳壳（去穰.麸炒）、犀角（镑）、知母，以上各一两。当归、升麻、柴胡、桔梗（炒）、前胡（去芦）、桂心（去皮）、木香、天灵盖（酥炙），各三分。桃仁（汤浸.去皮.炒.四十九粒）。', '上为细末，每服二钱匕，温酒调下，空心临卧各一。若为丸，即炼蜜和丸，如梧桐子大，每服二十丸至三十丸，温酒下。', '明方贤着《奇效良方》', '治白虎风，走注疼痛，发歇不定，积年不效者。', '-', '-', '柴胡', '[\'虎骨\', \'乌梅\', \'茯苓\', \'去皮\', \'肉苁蓉\', \'浸切\', \'人参\', \'甘草\', \'芍药\', \'鳖甲\', \'白术\', \'豆豉\', \'慢火\', \'紫苑\', \'土黄\', \'常山\', \'枳壳\', \'犀角\', \'知母\', \'以上\', \'一两\', \'当归\', \'升麻\', \'柴胡\', \'桔梗\', \'炒前\', \'胡去\', \'芦桂心\', \'去皮\', \'木香\', \'天灵盖\', \'三分\', \'桃仁\', \'汤浸\', \'去皮\', \'四十九\']');
INSERT INTO `prescript` VALUES (56838, '42255', '芦荟消疳饮', '芦荟 银柴胡 胡黄连 川黄连 牛蒡子 玄参 桔梗 山栀 石膏 薄荷 羚羊角各1.5克 甘草 升麻各0.9克', '上药用水400毫升，淡竹叶10片，煎至210毫升，食后服。', '《外科正宗》卷四', '清热除疳。治小儿走马牙疳，身热气粗，牙龈腐烂，气味作臭，以及穿腮破唇者。', '-', '-', '柴胡', '[\'芦荟\', \'银柴胡\', \'胡黄连\', \'黄连\', \'牛蒡子\', \'玄参\', \'桔梗\', \'石膏\', \'薄荷\', \'羚羊角\', \'甘草\', \'升麻\']');
INSERT INTO `prescript` VALUES (56839, '42232', '血府逐瘀汤', '当归 生地各9克 桃仁12克 红花9克 枳壳 赤芍各6克 柴胡3克 甘草3克 桔梗4.5克 川芎4.5克 牛膝10克', '水煎服。', '《医林改错》卷上', '活血祛瘀，行气止痛。治上焦瘀血，头痛胸痛，胸闷呃逆，失眠不寐，心悸怔忡，瘀血发热，舌质暗红，边有瘀斑或瘀点，唇暗或两目暗黑，脉涩或弦紧3妇人血瘀经闭不行，痛经，肌肤甲错，日晡潮热；以及脱疽、白疙，眼科云雾移睛、青盲等目疾。现用于高血压、精神分裂症、脑震荡后遗症、慢性粒细胞性白血病、血栓性静脉炎、色素沉着、性功能低下、更年期综合征、顽固性头痛、顽固性低热、眼底出血等属瘀血内阻，日久不愈者。', '-', '-', '柴胡', '[\'当归\', \'生地\', \'桃仁\', \'红花\', \'枳壳\', \'赤芍\', \'柴胡\', \'甘草\', \'桔梗\', \'川芎\', \'牛膝\']');
INSERT INTO `prescript` VALUES (56840, '42225', '清骨散', '银柴胡4.5克 胡黄连 秦艽 鳖甲（醋炙）地骨皮 青蒿 知母各3克 甘草1.5克', '用水400毫升，煎至320毫升，空腹时服。\r\n血虚甚，加当归、芍药、生地；嗽多，加阿胶、麦门冬、五味子。', '《证治准绳·类方》卷一', '清骨退蒸，滋阴潜阳。治虚劳阴虚火旺，骨蒸劳热，身体羸瘦，脉细数。', '-', '-', '柴胡', '[\'银柴胡\', \'胡黄连\', \'秦艽\', \'鳖甲\', \'地骨皮\', \'青蒿\', \'知母\', \'甘草\']');
INSERT INTO `prescript` VALUES (56841, '42194', '圆明内障升麻汤', '干姜3克 五味子6克 白葆苓9克 防风15克 白芍药18克 柴胡21克 人参 炙甘草 当归身（酒洗）白术 升麻 葛根各30克 黄耆 羌活各45克', '每服15～21克，用水450毫升，煎至300毫升，入黄芩、黄连各6克，同煎数沸，去滓，煎至150毫升，空腹时热服。', '《兰室秘藏》卷上', '治内障初觉，视觉微昏，眼前有黑花，神水变淡绿色；继则视物为二，神水变淡白色；日久则失明，神水变纯白色。', '-', '-', '柴胡', '[\'干姜\', \'五味子\', \'克白葆苓\', \'防风\', \'克白\', \'芍药\', \'柴胡\', \'人参\', \'甘草\', \'当归\', \'身酒\', \'白术\', \'升麻\', \'葛根\', \'黄耆\', \'羌活\']');
INSERT INTO `prescript` VALUES (56842, '42151', '复睛丸', '当归 蝉蜕 槟榔 夏枯草 胡黄连 黄誊 白蒺藜各30克 羌活 独活 防风 细辛 枳壳 白芍 赤芍 川芎 柴胡 青皮 陈皮 甘草各12克 茯苓9克 白芷6克 木贼20克 蛇蜕 藁本各6克', '每服6克。', '《银海指南》卷三', '治一切目疾。', '-', '-', '柴胡', '[\'当归\', \'蝉蜕\', \'槟榔\', \'夏枯草\', \'胡黄连\', \'誊白\', \'蒺藜\', \'羌活\', \'独活\', \'防风\', \'细辛\', \'枳壳\', \'白芍\', \'赤芍\', \'川芎\', \'柴胡\', \'青皮\', \'陈皮\', \'甘草\', \'茯苓\', \'白芷\', \'木贼\', \'蛇蜕\', \'藁本\']');
