INSERT INTO `prescript` VALUES (56844, '42129', '加味益气汤', '人参2钱，白术5钱，甘草1钱，茯苓3钱，陈皮5分，半夏1钱，柴胡1钱。', '水煎服。', '《辨证录》卷九', '贪眠乐卧，终日徜徉枕席之上，脾气内伤，遂至风邪袭之，身痛背疼，发热恶风。', '-', '-', '柴胡', '[\'人参\', \'白术\', \'甘草\', \'茯苓\', \'陈皮分\', \'半夏\', \'柴胡\']');
INSERT INTO `prescript` VALUES (56845, '42080', '柴芩活血散结汤', '柴胡10克，黄芩10克，川芎10克，当归10克，郁金10克，青皮10克，红花10克，忍冬藤30克，丹参30克，赤芍15克，橘核15克，鸡血藤75克。', '水煎服，每日1剂，日服2次。', '宋爱武方', '清热疏肝，活血散结。主湿热蕴结，气滞血瘀。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'川芎\', \'当归\', \'郁金\', \'青皮\', \'红花\', \'忍冬藤\', \'丹参\', \'赤芍\', \'橘核\', \'鸡血藤\']');
INSERT INTO `prescript` VALUES (56846, '42074', '连翘散坚汤', '柴胡36克 草龙胆（酒洗四次）土瓜根（酒制）各30克 黄芩（酒炒二次）21克 当归梢 生黄芩 广茂 京三棱（同广茂酒炒）连翘 芍药各15克 炙甘草9克 黄连（酒炒二次）苍术各6克', '每服百余丸；一半哎咀，每服15克，用水300毫升，先浸多半日，煎至150毫升，去滓，临卧热服，去枕仰卧，每口作十次咽之，留一口送下丸药。服毕即卧，更以龙泉散涂之。', '《兰室秘藏》卷下', '治马刀。耳下或至缺盆，或肩上生疮，坚硬如石，动之无根；或生两胁，或已流脓，作疮未破。', '-', '-', '柴胡', '[\'柴胡\', \'克草\', \'龙胆\', \'酒洗\', \'四次\', \'土瓜\', \'根酒制\', \'黄芩\', \'二次\', \'当归\', \'梢生\', \'黄芩\', \'广茂京\', \'三棱\', \'同广茂酒\', \'连翘\', \'芍药\', \'甘草\', \'黄连\', \'二次\', \'苍术\']');
INSERT INTO `prescript` VALUES (56847, '42066', '疏肝和胃饮', '当归10克，白芍15克，柴胡10克，枳实6克，瓜蒌10克，薤白10克，半夏10克，陈皮5克，甘草3克，蒲公英10克，煅瓦楞10克。', '水煎服，每日1剂，日服2次。', '谭日强方', '疏肝和胃，制酸止痛。主肝胃不和。', '-', '-', '柴胡', '[\'当归\', \'白芍\', \'柴胡\', \'枳实\', \'克瓜\', \'薤白\', \'半夏\', \'陈皮克\', \'甘草\', \'蒲公英\', \'瓦楞\']');
INSERT INTO `prescript` VALUES (56848, '42045', '升阳除湿汤', '当归（酒洗）独活各1.5克 蔓荆子2.1克 防风 炙甘草 升麻 藁本各3克 柴胡 羌活 苍术 黄耆各4.5克', '用水900毫升，煎至300毫升，去滓，空腹时热服，待少时，以早饭压之。', '《兰室秘藏》卷中', '治妇人饮食劳倦，心火乘脾，漏下恶血，月事不调；或暴崩不止，多下水浆之物，怠惰嗜卧，四肢不收，困倦乏力，其脉缓而弦急，按之洪大。', '-', '-', '柴胡', '[\'当归\', \'酒洗\', \'独活\', \'蔓荆子\', \'防风\', \'甘草\', \'升麻\', \'藁本\', \'柴胡\', \'羌活\', \'苍术\', \'黄耆\']');
INSERT INTO `prescript` VALUES (56849, '42036', '治急性外耳道湿疹方', '苦参6克，苍术10克，盐黄柏10克，白藓皮10克，地肤子10克，蒲公英10克，车前子（包煎）15克，当归10克，柴胡6克。', '水煎服，每日1剂，日服2次。', '葛英华方', '清热燥湿，祛风止痒。主肝经湿热，兼挟风邪。', '-', '-', '柴胡', '[\'苦参\', \'苍术\', \'克盐\', \'黄柏\', \'克白藓\', \'皮克\', \'地肤子\', \'蒲公英\', \'车前子\', \'包煎克\', \'当归\', \'柴胡\']');
INSERT INTO `prescript` VALUES (56850, '42023', '丁香茱萸汤', '黄柏0.9克 炙甘草 丁香 柴胡 橘皮各1.5克 升麻2.1克 吴茱萸 苍术 人参各3克 当归身4.5克 草豆蔻仁 黄耆各6克', '每服15克，用水600毫升，煎至300毫升。去滓，空腹稍热服。', '《兰室秘藏》卷中', '主脾胃虚寒，呕吐哕者。', '-', '-', '柴胡', '[\'黄柏\', \'甘草\', \'丁香\', \'柴胡\', \'橘皮\', \'升麻\', \'吴茱萸\', \'苍术\', \'人参\', \'当归\', \'身克\', \'草豆蔻\', \'黄耆\']');
INSERT INTO `prescript` VALUES (56851, '41974', '草豆蔻散', '草豆蔻（去皮）30克，人参30克，柴胡30克，白术30克，陈皮45克，炙甘草15克。', '上为散，每服12克，加生姜3克，大枣3枚，水煎去滓温服。不拘时候。', '《太平圣惠方》卷七十五', '补气健脾，疏肝行气。主脾胃气虚。妊娠腹胀。', '-', '-', '柴胡', '[\'草豆蔻\', \'去皮\', \'人参\', \'柴胡\', \'白术\', \'陈皮克\', \'甘草\']');
INSERT INTO `prescript` VALUES (56852, '41967', '升阳除湿汤', '苍术3克 柴胡 羌活 防风 升麻 神曲 泽泻 猪苓各1.5克 炙甘草 陈皮 麦蘖面各0.9克', '上都作一服。用水300毫升，煎至150毫升，去滓，空腹时服。\r\n胃寒肠鸣，加益智仁、半夏各1。5克，生姜3片，大枣1枚。', '《兰室秘藏》卷下', '治脾虚湿盛，不思饮食，泄泻无度，小便黄少，四肢困倦。', '-', '-', '柴胡', '[\'苍术\', \'柴胡\', \'羌活\', \'防风\', \'升麻\', \'神曲\', \'泽泻\', \'猪苓\', \'甘草\', \'陈皮\', \'麦蘖\']');
INSERT INTO `prescript` VALUES (56853, '41963', '回阳汤', '干姜（炮）2钱，附子（炮）2钱，人参3钱，白术3钱，黄耆3钱，当归2钱，陈皮2钱，甘草（炙）2钱，柴胡5分，升麻5分。', '托里回阳汤（《保婴撮要》卷十五）、回阳酒（《疡科选粹》卷二）。', '《外科枢要》卷四', '脾肾虚寒，疮属纯阴，或药损元气，不肿痛，不腐溃；或腹痛泄泻，呕吐厥逆，或阳脱陷。', '-', '-', '柴胡', '[\'干姜\', \'炮钱\', \'附子\', \'炮钱\', \'人参\', \'白术\', \'黄耆\', \'当归\', \'陈皮\', \'甘草\', \'柴胡\', \'升麻\']');
INSERT INTO `prescript` VALUES (56854, '41924', '温肾汤', '柴胡 麻黄根各1.8克 白茯苓 白术 酒黄柏 猪苓 升麻各3克 苍术 防风各4.5克 泽泻9克', '上药分作二服，每服用水600毫升，煎至300毫升，去滓，空腹时稍热服。', '《兰室秘藏》卷下', '治面色萎黄，身黄，脚痿弱无力，阴汗。', '-', '-', '柴胡', '[\'柴胡\', \'麻黄根\', \'白茯苓\', \'白术\', \'黄柏\', \'猪苓\', \'升麻\', \'苍术\', \'防风\', \'泽泻\']');
