INSERT INTO `prescript` VALUES (56856, '41907', '理郁升陷汤', '生黄耆18克 知母9克 当归身9克 桂枝尖4.5克 柴胡4.5克 乳香（不去油）9克 没药（不去油）9克', '水煎服。\r\n胁下撑胀，或兼痒者，加龙骨、牡蛎（皆不用煅）各15克；少腹下坠，加升麻3克。', '《医学衷中参西录》上册', '治胸中大气下陷，又兼气分郁结，经络湮瘀者。', '-', '-', '柴胡', '[\'生黄\', \'耆克\', \'知母\', \'当归\', \'身克\', \'桂枝\', \'尖克\', \'柴胡\', \'乳香\', \'不去\', \'油克\', \'没药\', \'不去\', \'油克\']');
INSERT INTO `prescript` VALUES (56857, '41899', '清凉甘露饮', '茵陈3克，银柴胡3克，石斛3克，枳壳3克，麦冬3克，甘草3克，生地3克，黄芩3克，知母3克，枇杷叶3克，淡竹叶20片，灯心草20片。', '水煎服，每日1剂，日服2次，食后服。', '《外科正宗》', '清心解毒。主热毒上蒸。', '-', '-', '柴胡', '[\'茵陈\', \'银柴胡\', \'石斛\', \'枳壳\', \'麦冬\', \'甘草\', \'生地\', \'黄芩\', \'知母\', \'枇杷叶\', \'淡竹叶\', \'灯心草\']');
