INSERT INTO `prescript` VALUES (56861, '41850', '归柴饮', '当归30克 柴胡15克 炙甘草2.4克', '用水300毫升煎服。或加生姜3～5片，或加陈皮3克，或加人参。\r\n如大便多溏者，以冬术代当归亦佳。', '《景岳全书》卷五十一', '治营虚不能作汗，及真阴不足，外感寒邪难解者。', '-', '-', '柴胡', '[\'当归\', \'柴胡\', \'甘草\']');
