INSERT INTO `prescript` VALUES (56885, '41465', '加减龙荟丸', '当归1两（酒洗），龙胆草1两（酒洗），栀子仁1两（炒），黄芩1两，大黄5钱（酒蒸），芦荟5钱，青黛5钱，木香2钱半，柴胡5钱，青皮1两，胆星3钱，麝香5分。', '针砂酒：针砂一两，穿山甲末一钱，拌针砂养一昼夜，播出山甲，以酒一碗，将针砂浸三四日，噙酒口内，外用磁石一块，绵裹塞耳。', '《古今医鉴》卷九', '聪耳泻火。主', '忌怒，戒色。', '-', '柴胡', '[\'当归\', \'两酒\', \'龙胆草\', \'两酒\', \'栀子\', \'黄芩\', \'大黄\', \'钱酒\', \'芦荟\', \'青黛钱\', \'木香\', \'钱半\', \'柴胡\', \'青皮\', \'两胆星\', \'麝香\']');
