INSERT INTO `prescript` VALUES (56905, '41230', '通泻利咽汤', '生大黄（后下06-10克，软柴胡6-9克，淡黄芩6-9克，金银花10-15克，连翘壳10-15克，射干10克，夏枯草10克，蒲公英10-15克。', '水煎服，每日1剂，日服2次。', '《福建中医药》', '清热解毒，通腑利咽，排脓生肌。主肺胃积热，复感风温时邪。', '-', '-', '柴胡', '[\'大黄\', \'后下\', \'克软\', \'柴胡\', \'克淡\', \'黄芩\', \'金银花\', \'连翘\', \'射干\', \'夏枯草\', \'蒲公英\']');
INSERT INTO `prescript` VALUES (56906, '41195', '疏肝利胆汤', '柴胡10克，黄芩8克，海金砂15克，金钱草15克，鸡内金10克，川郁金8克，炒金铃子10克，白芍10克，炒枳实10克，赤茯苓15克，车前子10克。', '水煎服，每日1剂，日服2次。', '李培生方', '疏肝利胆，清热除湿，理气和营，止痛散结。主肝胆湿热蕴结。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'海金砂\', \'金钱草\', \'鸡内金\', \'克川\', \'郁金\', \'金铃子\', \'白芍\', \'枳实\', \'克赤\', \'茯苓\', \'车前子\']');
INSERT INTO `prescript` VALUES (56907, '41192', '益阴肾气丸', '泽泻 茯苓各7.5克 生地黄（酒洗，晒干）牡丹皮 山莱萸 当归梢（酒洗）五味子 干山药 柴胡各15克 熟地黄60克', '每服50丸，空腹时用淡盐汤送下。', '《兰室秘藏》卷上', '补肾明目。治肾水不足，神水宽大，视物初觉昏暗，渐睹空中有黑花，物成二体，久则光不收，及内障神水淡绿色咸淡白色。', '-', '-', '柴胡', '[\'泽泻\', \'茯苓\', \'生地黄\', \'酒洗\', \'晒干\', \'牡丹皮\', \'山莱萸\', \'当归\', \'五味子\', \'山药\', \'柴胡\', \'熟地黄\']');
INSERT INTO `prescript` VALUES (56908, '41175', '逍遥五黄汤', '当归（酒洗）1.5克 白芍（酒洗）3克 白术（土炒）3克 白茯苓（去皮）3克 柴胡（酒炒）2.4克 薄荷0.6克 生地（姜炒）3克 黄芩（酒炒）3克 黄连（姜炒）3克 黄柏（酒炒）3克 知母（生）4.5克 煮耆（盐水炒）3克 神曲（炒）2.4克 甘草（炙）1.2克 香附（童便制）3克 地骨皮（酒炒）3克', '上锉一剂。加煨姜3片，乌梅半个，水煎，温服。', '《古今医鉴》卷十一', '治妇人午后发热，汗出后热退。', '-', '-', '柴胡', '[\'当归\', \'洗克\', \'白芍\', \'洗克\', \'白术\', \'炒克\', \'白茯苓\', \'去皮\', \'柴胡\', \'炒克\', \'薄荷\', \'生地\', \'炒克\', \'黄芩\', \'炒克\', \'黄连\', \'炒克\', \'黄柏\', \'炒克\', \'知母\', \'生克\', \'盐水\', \'炒克\', \'神曲\', \'炒克\', \'甘草\', \'炙克\', \'香附\', \'童便\', \'制克\', \'地骨皮\', \'炒克\']');
INSERT INTO `prescript` VALUES (56909, '41165', '舒肝破瘀通脉汤', '紫丹参15克，白芍9克，赤芍9克，银柴胡9克，羌活9克，防风9克，木贼9克，蝉蜕9克，当归9克，白术9克，云苓9克，甘草3克。', '水煎服，每日1剂，日服2次。', '庞赞襄方', '舒肝破瘀通脉。主肝气郁滞，气结血凝。（视网膜静脉阻塞）', '-', '-', '柴胡', '[\'丹参\', \'白芍\', \'赤芍\', \'银柴胡\', \'羌活\', \'防风\', \'木贼\', \'蝉蜕\', \'当归\', \'白术\', \'克云苓\', \'甘草\']');
INSERT INTO `prescript` VALUES (56910, '41146', '露蜂房汤', '露蜂房10克，山茨菇10克，郁金10克，青皮10克，贝母12克，柴胡10克，橘叶10克，香附12克，夏枯草25克。', '水煎服，每日1剂，日服2次。', '郭子光方', '疏肝豁痰，软坚散结。主气郁痰结。（乳腺小叶增生）', '-', '-', '柴胡', '[\'露蜂房\', \'克山\', \'茨菇\', \'郁金\', \'青皮\', \'贝母\', \'柴胡\', \'克橘\', \'叶克\', \'香附\', \'夏枯草\']');
INSERT INTO `prescript` VALUES (56911, '41072', '加减平疟养脾丸', '黄耆（炙）1钱，人参1钱，白术1分，当归1分，白茯苓1分，半夏曲1分，黄芩1分，陈皮1分，常山1分，鳖甲（九肋者，醋炙）1分，使君子肉1分，柴胡7分，草果仁7分，厚朴（姜汁炒）7分，神曲（炒）7分，肉桂5分，青皮6分，炙甘草5分。', '陈米汤送下。', '《育婴秘诀》卷二', '补脾平肝。主久疟发搐，病在肝脾者。', '-', '-', '柴胡', '[\'黄耆\', \'人参\', \'白术\', \'当归\', \'白茯苓\', \'半夏\', \'曲分\', \'黄芩\', \'陈皮分\', \'常山\', \'鳖甲\', \'九肋者\', \'炙分\', \'使君子\', \'肉分\', \'柴胡\', \'分草\', \'果仁\', \'厚朴\', \'姜汁\', \'炒分\', \'神曲\', \'炒分\', \'肉桂\', \'青皮\', \'甘草\']');
INSERT INTO `prescript` VALUES (56912, '41060', '加味益气汤', '黄耆（蜜炒）1钱，人参1钱，白术（去芦）1钱，陈皮1钱，当归1钱，升麻5分，柴胡5分，木香5分，香附8分，青皮（去瓤）8分，川芎8分，桂枝少许，甘草3分。', '加生姜、大枣，水煎服。', '《回春》卷四', '气虚，浑身发麻。', '-', '-', '柴胡', '[\'黄耆\', \'人参\', \'白术\', \'芦钱\', \'陈皮\', \'当归\', \'升麻\', \'柴胡\', \'木香\', \'香附\', \'青皮\', \'川芎\', \'桂枝\', \'少许\', \'甘草\']');
INSERT INTO `prescript` VALUES (56913, '41049', '加减平阳汤', '葛根3钱，云苓5钱，甜白术5钱，麦冬5钱，川贝母3钱（去心，研），生石膏3钱，柴胡1钱，枳壳1钱5分，橘红1钱5分。', '如体虚者，加人参1钱，贫人无力者，以上党参1两代之；如恶心，加广藿香3钱；如胃口饱闷作胀，加草蔻仁2钱，槟榔1钱5分；如寒多，加煨姜3钱，大枣3枚；如内热口渴，加知母2钱；如皮外热，加青蒿2钱；如作泻，加炒白芍5钱，车前子3钱。', '《慈航集》卷下', '疟疾。初病令人先寒洒浙，洒浙寒甚，发热头痛鼻于，渴欲饮水，目眴眴不得眠，甚则烦躁谵语，畏火，厌听人声喧哗，寒久乃热，热退汗出，喜见日月光火气，心乃快然，善饥而能食。', '-', '-', '柴胡', '[\'葛根\', \'钱云苓\', \'白术\', \'麦冬\', \'川贝母\', \'心研\', \'生石膏\', \'柴胡\', \'枳壳\', \'钱分\', \'橘红\', \'钱分\']');
INSERT INTO `prescript` VALUES (56914, '41045', '加减补阴益气汤', '生地5钱，人参1钱半，黄耆3钱（蜜炙），柴胡5分（盐醋炒黑），白芍1钱半（醋炒），升麻3分（盐醋炒），阿胶3钱（蒲黄灰炒），山药3钱（炒），血余3钱（炒灰），赤石脂3钱（醋炒）。', '水煎，去滓温服。', '《医略六书》卷二十六', '交接出血，脉软数者。', '-', '-', '柴胡', '[\'生地\', \'人参\', \'钱半\', \'黄耆\', \'钱蜜\', \'柴胡\', \'分盐\', \'白芍\', \'钱半醋\', \'升麻\', \'分盐\', \'阿胶\', \'黄灰\', \'山药\', \'余钱\', \'赤石脂\']');
INSERT INTO `prescript` VALUES (56915, '41015', '加减柴芩汤', '柴胡8分，黄芩1钱，半夏1钱，青皮1钱，草果1钱，苍术5分，槟榔5分，川芎3分。', '水1碗，酒1碗，加生姜3片，葱白2根，煎1碗温服。微汗。', '《医林绳墨大全》卷一', '疟发3-4日。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'半夏\', \'青皮\', \'草果\', \'苍术\', \'槟榔\', \'川芎\']');
INSERT INTO `prescript` VALUES (56916, '41003', '散痛饮', '乌药4.5克，玄胡素4.5克，杜仲4.5克，桃仁4.5克，青皮3克，柴胡3克，穿山甲3克，牛膝3克，红花3克，甘草1克，生姜3片。', '水煎服，每日1剂，日服2次。', '《丹台玉案》卷五', '疏肝通络，活血止痛。主瘀血内阻。（男子性交疼痛）', '-', '-', '柴胡', '[\'乌药\', \'克玄\', \'胡素克\', \'杜仲\', \'桃仁\', \'青皮\', \'柴胡\', \'穿山甲\', \'牛膝\', \'红花\', \'甘草\', \'生姜\']');
INSERT INTO `prescript` VALUES (56917, '40933', '柴葛蓝草汤', '板蓝根10-30克，葛根10-30克，白花蛇舌草10-20克，柴胡6-10克，连翘6-15克，浙贝3-12克，射干3-10克，荆芥3-10克。', '水煎服，每日1剂，日服2次。', '《中医杂志》', '清热解毒，利咽消肿。主风热乳蛾。', '-', '-', '柴胡', '[\'板蓝根\', \'葛根\', \'白花蛇\', \'舌草\', \'柴胡\', \'连翘\', \'浙贝\', \'射干\', \'荆芥\']');
INSERT INTO `prescript` VALUES (56918, '40916', '退斑汤', '生地12克，熟地12克，当归12克，柴胡9克，香附9克，茯苓9克，川芎9克，白僵蚕9克，白术9克，白芷9克，白藓皮15克，白附子6克，甘草6克。', '水煎服，每日1剂，日服3次。', '姜兆俊方', '疏肝解郁，养血健脾。主情志抑郁，渐伤肝脾，肝郁化火，火燥淤滞。', '-', '-', '柴胡', '[\'生地\', \'熟地\', \'当归\', \'柴胡\', \'香附\', \'茯苓\', \'川芎\', \'白僵蚕\', \'白术\', \'白芷\', \'克白藓\', \'皮克\', \'白附子\', \'甘草\']');
INSERT INTO `prescript` VALUES (56919, '40905', '升清流气饮', '升麻3克，青皮6克，黄芪10克，木香3克，苏叶10克，乌药6克，柴胡3克，大腹皮10克，川芎3克，蔓荆子6克，菖蒲3克。', '水煎服，每日1剂，日服2次。', '干祖望方', '调理气机，升清开窍。主气滞窍闭。', '-', '-', '柴胡', '[\'升麻\', \'青皮\', \'黄芪\', \'木香\', \'克苏叶克\', \'乌药\', \'柴胡\', \'大腹皮\', \'川芎\', \'蔓荆子\', \'菖蒲\']');
INSERT INTO `prescript` VALUES (56920, '40821', '化瘀丸', '柴胡30g，薄荷30g，栀子30g，归尾30g，红花30g，赤芍30g。', '早、晚各服1丸。', '《中西医结合皮肤病学》', '疏肝活血，化瘤消斑。主肝郁血瘀证。面部色素沉着，四肢皮肤有紫红色结节性皮疹，慢性紫红斑片，胁胀，月经提前，经血有血块，痛经等，脉弦滑，舌薄黄苔，可有瘀斑。常用于黄褐斑，女子颜面黑皮病，银屑病、酒渣、痤疮、多发性神经纤维瘤等。', '-', '-', '柴胡', '[\'柴胡\', \'薄荷\', \'栀子\', \'归尾\', \'红花\', \'赤芍\']');
INSERT INTO `prescript` VALUES (56921, '40820', '抑肝散', '软柴胡 甘草各1.5克 川芎2.5克 当归 白术（炒）茯苓 钩藤钩各3克', '上药水煎，子母同服。', '《保婴撮要》卷一', '抑肝健脾，清热解痉。治肝经虚热发搐，或痰热咬牙，或惊悸寒热，或木乘土而呕吐痰饮，腹胀少食，睡卧不安。', '-', '-', '柴胡', '[\'柴胡\', \'甘草\', \'川芎\', \'当归\', \'白术\', \'茯苓\', \'钩藤\']');
INSERT INTO `prescript` VALUES (56922, '40814', '加味二陈汤', '甘草8分，半夏1钱，茯苓1钱，陈皮1钱5分，南星、枳实、黄芩、白术、黄连、瓜蒌仁、桔梗、杏仁、山楂、柴胡（少佐）、贝母、金沸草、姜汁、竹沥。', '年力壮盛者，先吐去痰，后服此药。', '《玉案》卷二', '食积夹痰，憎寒恶风，自汗，胸膈满闷，气上攻冲，头不昏痛，项不强，无热者。', '-', '-', '柴胡', '[\'甘草\', \'半夏\', \'茯苓\', \'陈皮\', \'钱分\', \'南星\', \'枳实\', \'黄芩\', \'白术\', \'黄连\', \'桔梗\', \'杏仁\', \'山楂\', \'柴胡\', \'少佐\', \'贝母\', \'金沸草\', \'姜汁\', \'竹沥\']');
INSERT INTO `prescript` VALUES (56923, '40802', '加味温胆汤', '人参2钱半，橘红1钱，茯苓1钱，黄连（酒炒）1钱，软苗柴胡1钱，当归身1钱，川芎1钱，白芍药1钱，生地黄1钱，酸枣仁1钱，半夏7分，甘草5分，竹茹1团，生姜3片。', '水2钟，煎至1钟，去滓温服。', '《准绳·伤寒》卷五', '虚烦身振不得眠。', '-', '-', '柴胡', '[\'人参\', \'钱半\', \'橘红\', \'茯苓\', \'黄连\', \'软苗\', \'柴胡\', \'当归\', \'身钱\', \'川芎\', \'钱白\', \'芍药\', \'生地黄\', \'酸枣仁\', \'半夏\', \'甘草\', \'竹茹\', \'生姜\']');
INSERT INTO `prescript` VALUES (56924, '40791', '归葵汤', '柴胡2分，生甘草3分，蔓荆子3分，连翘3分，生地黄3分，当归身3分，红葵花3分，人参3分，黄耆5分，酒黄芩5分，防风5分，羌活5分，升麻1钱。', '连翘饮子（原书同卷）、连翘饮（《内科摘要》卷下）。', '《兰室秘藏》卷上', '目中溜火，恶日与火，隐涩难开，小角紧，视物昏花，迎风有泪。', '-', '-', '柴胡', '[\'柴胡\', \'生甘草\', \'蔓荆子\', \'连翘\', \'生地黄\', \'当归\', \'身分\', \'葵花\', \'人参\', \'分黄耆\', \'分酒\', \'黄芩\', \'防风\', \'羌活\', \'升麻\']');
INSERT INTO `prescript` VALUES (56925, '40790', '活血散风汤', '当归12克，益母草9克，泽兰9克，白芷9克，荆芥穗6克，羌活6克，川芎4.5克，柴胡4.5克，柴胡4.5克，蝉蜕3克。', '水煎服，每日1剂，日服3次。', '《美容护肤中医八法》', '活血散风。主风邪伤于营卫，气血失和。', '-', '-', '柴胡', '[\'当归\', \'益母草\', \'泽兰\', \'白芷\', \'荆芥\', \'穗克\', \'羌活\', \'川芎\', \'柴胡\', \'柴胡\', \'蝉蜕\']');
INSERT INTO `prescript` VALUES (56926, '40782', '安厥汤', '人参3钱，玄参1两，茯苓3钱，白薇1钱，麦冬5钱，生地5钱，天花粉3钱，炒栀子3钱，白芍1两，柴胡5分，甘草1钱。', '水煎服。', '《辨证录》卷五', '阴血不归于阳气之中所致阳厥，日间忽然发热，一时厥去，手足冰凉，语言惶惑，痰迷心窍，头晕眼昏。', '-', '-', '柴胡', '[\'人参\', \'玄参\', \'茯苓\', \'钱白薇\', \'麦冬\', \'生地\', \'天花粉\', \'栀子\', \'白芍\', \'柴胡\', \'甘草\']');
INSERT INTO `prescript` VALUES (56927, '40781', '化毒散', '当归1两，白芍药1两，赤芍药1两，知母1两，苎麻根1两，白胶香1两，甘草1两，贝母1两，秦艽1两，生地黄1两，败龟1两，柴胡1两，前胡1两，官桂1两，熟生黄1两。', '上(口父)咀。每服半两，水2盏，加生姜3片，枣子2个，煎至7分，去滓温服。', '《普济方》卷二九一', '瘰疬。', '-', '-', '柴胡', '[\'当归\', \'两白\', \'芍药\', \'两赤\', \'芍药\', \'知母\', \'苎麻根\', \'两白胶\', \'甘草\', \'贝母\', \'秦艽\', \'生地黄\', \'两败\', \'柴胡\', \'两前\', \'胡两\', \'官桂\', \'两熟生\']');
INSERT INTO `prescript` VALUES (56928, '40769', '丹栀龙胆逍遥汤', '柴胡9克，当归9克，白芍9克，茯苓9克，白术9克，薄荷9克，丹皮9克，龙胆草6克，甘草6克，生姜3克。', '水煎服，每日1剂，日服2次。', '《美容护肤中医八法》', '疏肝扶脾，清热凉血。主肝脾不和，火燥郁滞。', '-', '-', '柴胡', '[\'柴胡\', \'当归\', \'白芍\', \'茯苓\', \'白术\', \'薄荷\', \'丹皮\', \'龙胆草\', \'甘草\', \'生姜\']');
