INSERT INTO `prescript` VALUES (56930, '40765', '加味清脾饮', '青皮2钱，柴胡2钱，川朴2钱（捣），黄芩2钱（生），法夏2钱（研），甘草1钱，茯苓2钱，白术2钱（炒），草果2钱（炒，研），槟榔3钱，枳壳3钱（炒），川大黄5钱（生）。', '生姜为引，水煎服。', '《医门八法》卷二', '疟疾初发，作寒作热，舌有苔，中满不欲饮食，甚且发呕。', '-', '-', '柴胡', '[\'青皮\', \'柴胡\', \'钱川朴\', \'黄芩\', \'钱生\', \'法夏钱研\', \'甘草\', \'茯苓\', \'白术\', \'草果\', \'炒研\', \'槟榔\', \'枳壳\', \'炒川\', \'大黄\', \'钱生\']');
INSERT INTO `prescript` VALUES (56931, '40706', '加味小柴胡汤', '柴胡1钱，人参1钱，黄芩（炒）1钱，川芎1钱，白术1钱，黄耆（盐水浸炒）1钱，当归（酒炒）1钱，甘草1钱，黄柏（酒拌炒）1钱，知母（酒拌炒）1钱，半夏5分。', '水2钟，煎8分，食前服。痛甚，加黄连；小便不利，加木通。', '《外科经验方》', '囊痈腐烂，或饮食少思，日晡发热。', '-', '-', '柴胡', '[\'柴胡\', \'人参\', \'黄芩\', \'川芎\', \'白术\', \'黄耆\', \'水浸\', \'当归\', \'甘草\', \'黄柏\', \'酒拌\', \'知母\', \'酒拌\', \'半夏\']');
INSERT INTO `prescript` VALUES (56932, '40700', '加味益气汤', '人参1钱5分，黄耆（蜜炙）1钱5分，白术（蜜炙）1钱5分，甘草（炙）1钱5分，当归2钱，川芎8分，砂仁8分，陈皮8分，酸枣仁（炒）8分，升麻（炒）3分，柴胡3分。', '加生姜3片，大枣2枚，水煎，空心服。', '《叶氏女科》卷二', '养气血。固胎元，预防堕胎。主妇人先经半产，后次有胎，妊娠7月以前。', '-', '-', '柴胡', '[\'人参\', \'钱分\', \'黄耆\', \'蜜炙钱分\', \'白术\', \'蜜炙钱分\', \'甘草\', \'炙钱分\', \'当归\', \'川芎\', \'砂仁\', \'陈皮分\', \'酸枣仁\', \'炒分\', \'升麻\', \'炒分\', \'柴胡\']');
