INSERT INTO `prescript` VALUES (56935, '40670', '加味小柴胡汤', '柴胡3钱，黄芩3钱，知母3钱，潞参3钱，鳖甲3钱（醋炙），清半夏2钱，常山1钱半（酒炒），草果1钱，甘草1钱，酒曲3钱，生姜3钱，大枣2枚（擘开）。', '疟初起者，减潞参、鳖甲；热甚者，加生石膏5-6钱或至1两；寒甚者，再加草果5分或至1钱。', '《衷中参西》上册', '久疟不愈，脉象弦而无力。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'知母\', \'参钱\', \'鳖甲\', \'半夏\', \'常山\', \'钱半酒\', \'草果\', \'甘草\', \'酒曲\', \'生姜\', \'大枣\']');
INSERT INTO `prescript` VALUES (56936, '40644', '加味顺气化痰汤', '人参1钱，白术1钱，白芍药3分，白茯苓3分，半夏3分，陈皮3分，枳实3分，柴胡3分，苏叶3分，黄柏3分，甘草（炙）3分。', '水2钟，加生姜3片，煎至7分，食后温服，滓再煎。', '《扶寿精方》', '痰病。', '-', '-', '柴胡', '[\'人参\', \'白术\', \'钱白\', \'芍药\', \'白茯苓\', \'半夏\', \'陈皮分\', \'枳实\', \'柴胡\', \'分苏叶分\', \'黄柏\', \'甘草\', \'炙分\']');
INSERT INTO `prescript` VALUES (56937, '40629', '清肝利咽汤', '柴胡12克，白芍12克，当归10克，栀子12克，枳实12克，桔梗10克，射干12克，蝉衣9克，苏子15克，半夏12克，前胡10克，白芥子9克，甘草10克。', '水煎服，每日1剂，日服2次。', '宋根信方', '清肝利咽，降逆化痰。主肝气上逆，壅滞咽喉。', '-', '-', '柴胡', '[\'柴胡\', \'白芍\', \'当归\', \'栀子\', \'枳实\', \'桔梗\', \'射干\', \'蝉衣\', \'苏子\', \'半夏\', \'克前\', \'胡克\', \'白芥子\', \'甘草\']');
INSERT INTO `prescript` VALUES (56938, '40617', '地骨皮汤', '地骨皮（去骨）1两，胡黄连1两，鳖甲（涂酥炙黄，去裙襕）半两，柴胡（去苗）半两，犀角（锉，取屑）半两，嫩桃枝（锉）半两，川大黄（炮）半两，知母半两。', '每服1大钱，水1盏，煎至5分，去滓温服，不拘时候。', '《卫生总微》卷十六', '小儿骨蒸体热肌瘦。', '-', '-', '柴胡', '[\'地骨皮\', \'胡黄连\', \'鳖甲\', \'黄去\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'犀角\', \'取屑\', \'半两\', \'嫩桃枝\', \'半两\', \'大黄\', \'半两\', \'知母\', \'半两\']');
INSERT INTO `prescript` VALUES (56939, '40582', '柴葛解肌汤加减', '柴胡15克，葛根15克，白芷10克，桔梗12克，元参15克，理石25克，赤芍12克，生草10克，双花15克，连翘15克，山甲15克，川芎10克，川军5克，茵陈15克，苦参15克，黄柏15克，公英15克，地丁15克。', '水煎服，每日1剂，日服2次。', '陈光发方', '辛凉解肌表邪气。主风寒外袭，营卫失调，风热湿邪客于肌腠，使皮肤失去濡养而致病。', '-', '-', '柴胡', '[\'柴胡\', \'葛根\', \'白芷\', \'桔梗\', \'克元\', \'参克理\', \'石克\', \'赤芍\', \'克生\', \'草克\', \'双花克\', \'连翘\', \'克山\', \'甲克\', \'川芎\', \'川军\', \'茵陈\', \'苦参\', \'黄柏\', \'克公英克地\', \'丁克\']');
INSERT INTO `prescript` VALUES (56940, '40559', '滋生青阳汤', '生地12克 白芍3克 丹皮4.5克 麦冬4.5克（青黛拌）石斛6克 天麻2.4克 甘菊6克 石决明24克 柴胡2.4克（醋炒）桑叶3克 薄荷3克 灵磁石15克（整块同煎）', '水煎服。', '《医醇剩义》卷一', '滋阴潜阳，平肝息风。治肝风。头目眩晕，肢节摇颤，如登云雾，如坐舟中。', '-', '-', '柴胡', '[\'生地\', \'白芍\', \'丹皮\', \'麦冬\', \'克青黛拌\', \'石斛\', \'天麻\', \'甘菊\', \'石决明\', \'柴胡\', \'克醋\', \'桑叶\', \'薄荷\', \'克灵\', \'磁石\', \'整块\']');
INSERT INTO `prescript` VALUES (56941, '40509', '归气救产汤', '人参3钱，熟地5钱，白芍2钱，茯苓1钱，山药5钱，白术5钱，柴胡3分，砂仁1粒。', '水煎服。', '《辨证录》卷十二', '妇人产子之后，肝肾两虚，阴不能入阳，四肢浮肿，寒热往来，气喘咳嗽，胸膈不利，口吐酸水，两胁疼痛。', '-', '-', '柴胡', '[\'人参\', \'熟地\', \'白芍\', \'茯苓\', \'山药\', \'白术\', \'柴胡\', \'砂仁\']');
INSERT INTO `prescript` VALUES (56942, '40502', '甲乙归藏汤', '真珠母8钱，龙齿2钱，柴胡1钱（醋炒），薄荷1钱，生地6钱，归身2钱，白芍1钱5分（酒炒），丹参2钱，柏子仁2钱，夜合花2钱，沉香5分，红枣10枚，夜交藤4钱（切）。', '-', '《医醇剩义》卷一', '身无他苦，饮食如常，惟彻夜不寐，间日轻重，如发疟然，起伏而又延久不愈，左关独弦数，余部平平者。', '-', '-', '柴胡', '[\'珠母\', \'龙齿\', \'柴胡\', \'薄荷\', \'生地\', \'钱归身\', \'白芍\', \'钱分\', \'丹参\', \'柏子仁\', \'夜合\', \'花钱\', \'沉香\', \'红枣\', \'夜交藤钱切\']');
INSERT INTO `prescript` VALUES (56943, '40500', '九仙散', '柴胡（去芦）2两，苍术（米泔水浸一宿，去粗皮，滤干锉片，用火炒至微黄色）2两，赤芍药6钱半，荆芥6钱半，甘草6钱半，麻黄（不去根节）半两，川芎半两，薄荷（和梗）半两，旋覆花（去老梗）3钱。', '上(口父)咀。每服2钱，水1盏，加生姜2片，葱1根，煎7分，不拘时温服。', '《活幼心书》卷下', '诸目疾，不拘岁月远近。', '-', '-', '柴胡', '[\'柴胡\', \'去芦\', \'苍术\', \'米泔水\', \'粗皮滤\', \'片用\', \'黄色\', \'两赤\', \'芍药\', \'钱半\', \'荆芥\', \'钱半\', \'甘草\', \'钱半\', \'麻黄\', \'不去\', \'根节\', \'半两\', \'川芎\', \'半两\', \'薄荷\', \'半两\', \'旋覆花\', \'老梗钱\']');
INSERT INTO `prescript` VALUES (56944, '40482', '二十二味虎睛丸', '虎睛1只（生眼佳，晒干，酒浸令黄色），真珠3钱，蜂房3钱，麻黄2分（去节），钩藤3分，铁精4分，防葵4分，大黄4分，子芩4分，龙齿4分，银屑4分，栀子仁4分，羌活4分，柴胡3分，升麻3分，白鲜皮3分，雷丸（烧令赤）3分，人参3分，细辛1分半，蛇皮5寸（炙），石膏5分，蚱蝉4枚（去翅足，炙）。', '虎晴丸（《普济方》卷三七七）。', '《颅囟经》卷上', '孩子从1岁至大，癫发无时，口出白沫，小便淋沥不利。', '忌生冷、油腻。', '-', '柴胡', '[\'虎睛\', \'只生\', \'眼佳\', \'晒干\', \'浸令\', \'黄色\', \'真珠\', \'蜂房\', \'麻黄\', \'分去\', \'钩藤\', \'分铁\', \'精分\', \'防葵\', \'大黄\', \'分子\', \'龙齿\', \'银屑\', \'栀子\', \'仁分\', \'羌活\', \'柴胡\', \'升麻\', \'白鲜皮\', \'雷丸\', \'烧令\', \'赤分\', \'人参\', \'细辛\', \'分半\', \'蛇皮\', \'石膏\', \'蚱蝉\']');
