INSERT INTO `prescript` VALUES (56946, '40418', '加味逍遥散', '白术8分（炒），白芍8分（炒），当归8分，白茯苓8分，柴胡5分，薄荷5分，陈皮6分，白扁豆1钱（炒），甘草6分，神曲1钱（炒），麦芽8分（炒）。', '兼服健脾肥儿丸。', '《幼科直言》卷四', '小儿脾疳。因乳食不调，饥饱不一，或一切病后，亏损气血，以致时热时冷，或大便非结即泻，面黄肌瘦，肚大夜热。', '-', '-', '柴胡', '[\'白术\', \'白芍\', \'当归\', \'白茯苓\', \'柴胡\', \'薄荷\', \'陈皮分\', \'白扁豆\', \'甘草\', \'神曲\', \'麦芽\']');
INSERT INTO `prescript` VALUES (56947, '40396', '二术柴葛汤', '柴胡1钱半，白术1钱半，苍术（泔浸）1钱，干葛1钱2分，陈皮7分，甘草（炙）5分。', '二术柴胡汤（《医钞类编》卷八）。《医宗必读》本方用法：水钟半，生姜五片，煎八分服。若一日一发及午前发者，邪在阳分，加枯芩、茯苓、半夏各1钱；热甚头痛，再加川芎、软石膏各1钱；口干，加石膏、知母、麦门冬各1钱。若间日或三日发，或午后或夜发者，邪入阴分，加川芎、当归、芍药（酒炒）、熟地黄、知母（酒炒）各1钱，红花（酒洗）、黄柏（酒炒）、升麻各4分，提起阳分，方可截之。若间一日，连发二日，或日夜各发者，气血俱病，加人参、黄耆，白茯苓各1钱以补气，川芎、当归、芍药、熟地黄各1钱以补血。', '方出《明医杂着》卷二，名见《医统》卷三十七', '疟疾。', '-', '-', '柴胡', '[\'柴胡\', \'钱半\', \'白术\', \'钱半\', \'苍术\', \'泔浸\', \'干葛钱分\', \'陈皮分\', \'甘草\', \'炙分\']');
INSERT INTO `prescript` VALUES (56948, '40337', '加减圣效散', '卜子（炒）8钱，砂仁（炒，研）8钱，槟榔8钱，陈皮8钱，延胡8钱，厚朴5钱，防风5钱，苍术5钱，藁本5钱，藿香叶5钱，柴胡5钱，独活5钱，石菖蒲5钱，泽泻5钱，枳壳5钱，细辛5钱，草豆蔻（去壳）10个。', '加减圣功散（《痧症汇要》卷四）。', '《痧书》卷下', '伤寒时疫风湿，阴阳两感，表里未辨，或外热内寒，或外寒内热，肢节拘急，头项腰脊疼痛，发热恶寒，呕逆喘咳，鼻塞声重，及食饮生冷，伤在胃脘，胸膈饱满，肠胁胀痛，心下痞结，手足逆冷，肠鸣泄泻，水谷不消，小溲不利。', '-', '-', '柴胡', '[\'卜子\', \'砂仁\', \'研钱\', \'槟榔\', \'陈皮\', \'钱延\', \'胡钱\', \'厚朴\', \'防风\', \'苍术\', \'藁本\', \'藿香\', \'叶钱\', \'柴胡\', \'独活\', \'石菖蒲\', \'泽泻\', \'枳壳\', \'细辛\', \'草豆蔻\']');
INSERT INTO `prescript` VALUES (56949, '40291', '白术汤', '白术3两，柴胡（去苗）3两，生姜（去皮，薄切，焙干）3两，厚朴（去粗皮，涂生姜汁炙香熟）3两，桂（去粗皮）3两，甘草（炙，锉）1两，槟榔（锉）10枚。', '每服3钱匕，水1盏，煎至7分，去滓温服。微利为度。', '《圣济总录》卷七十一', '痞气，胁肋满闷。', '-', '-', '柴胡', '[\'白术\', \'柴胡\', \'生姜\', \'去皮\', \'薄切\', \'焙干\', \'厚朴\', \'粗皮\', \'涂生\', \'姜汁\', \'炙香熟\', \'两桂去\', \'粗皮\', \'甘草\', \'槟榔\']');
INSERT INTO `prescript` VALUES (56950, '40288', '二十四味大建中汤', '人参（去芦）1两，白茯苓（去皮）1两，桔梗（炒）1两，柴胡（去苗）1两，甘草（炙）1两，陈皮（去瓤）1两，当归（去芦）1两，秦艽（洗净）1两，川芎1两，阿胶（蛤粉炒）1两，半夏（汤泡7次）1两，柏子仁1两，草果子1两，乌药1两，白芍药3钱，黄耆（蜜炙）3钱，鳖甲（米醋炙）3钱，地黄（熟煮）3钱，乌梅肉3钱，五味子3钱，槟榔半钱，地骨皮（去骨），木香1钱（不见火），肉桂1钱半（去粗皮，不见火）。', '上(口父)咀。每服4钱，水1盏半，加生姜3片，枣子2个，煎至8分，去滓，不拘时候服。', '《魏氏家藏方》卷四', '虚劳。寒热往来，日久未愈，不思饮食，肌肉消瘦，口燥咽干者。', '-', '-', '柴胡', '[\'人参\', \'去芦\', \'白茯苓\', \'去皮\', \'桔梗\', \'柴胡\', \'甘草\', \'陈皮\', \'当归\', \'去芦\', \'秦艽\', \'洗净\', \'川芎\', \'阿胶\', \'蛤粉\', \'半夏\', \'汤泡次\', \'柏子仁\', \'两草\', \'果子\', \'乌药\', \'两白\', \'芍药\', \'黄耆\', \'蜜炙钱\', \'鳖甲\', \'米醋\', \'黄熟\', \'乌梅\', \'五味子\', \'槟榔\', \'半钱\', \'地骨皮\', \'木香\', \'不见\', \'肉桂\', \'钱半去\', \'粗皮\', \'不见\']');
INSERT INTO `prescript` VALUES (56951, '40250', '地黄汤', '生干地黄（焙）1两，柴胡（去苗）1两，石膏2两，栀子仁3分，赤小豆（生）3两，木通（锉）3分。', '每服3钱匕，水1盏，加竹叶14片，同煎取6分，去滓温服，不拘时候。', '《圣济总录》卷八十六', '心劳实热，口疮心烦，多笑少力，小便不利。', '-', '-', '柴胡', '[\'生干\', \'地黄\', \'柴胡\', \'石膏\', \'栀子\', \'仁分\', \'赤小豆\', \'生两\', \'木通\', \'锉分\']');
INSERT INTO `prescript` VALUES (56952, '40244', '地骨皮散', '地骨皮3分，百合3分，黄耆三分，赤茯苓3分，人参半两（去芦头），赤芍药3分，枳壳3分（麸炒微黄，去瓤），桑根白皮3分（锉），柴胡1两半（去苗），甘草半两（炙微赤，锉），麦门冬1两半（去心，焙），犀角屑3两，杏仁1两（汤浸，去皮尖双仁，麸炒微黄），鳖甲1两（涂醋，炙令黄，去裙襕），白前3分。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷三十一', '虚劳骨蒸烦热，心神不宁，及小便赤涩，时有咳嗽，四肢羸弱疼痛。', '忌苋菜。', '-', '柴胡', '[\'地骨皮\', \'百合\', \'分黄耆\', \'三分\', \'茯苓\', \'人参\', \'半两\', \'芦头\', \'芍药\', \'枳壳\', \'分麸\', \'微黄\', \'桑根\', \'白皮\', \'柴胡\', \'两半\', \'去苗\', \'甘草\', \'半两\', \'炙微\', \'麦门冬\', \'两半\', \'犀角\', \'杏仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'白前\']');
INSERT INTO `prescript` VALUES (56953, '40232', '加味凉膈散', '大黄2两，朴消2两，甘草2两，连翘4两，栀子仁1两，黄芩1两，薄荷1两，柴胡1两，槟榔1两。', '每服5钱，水煎服。', '《济阳纲目》卷二十三', '瘴疟，迷闷狂妄，哑不能言。', '-', '-', '柴胡', '[\'大黄\', \'两朴\', \'消两\', \'甘草\', \'连翘\', \'栀子\', \'黄芩\', \'薄荷\', \'柴胡\', \'槟榔\']');
INSERT INTO `prescript` VALUES (56954, '40202', '加味二陈汤', '陈皮1钱，半夏1钱，白茯苓1钱，白术1钱，苍术1钱，益智仁（盐水炒）1钱，炙草5分，升麻4分，柴胡7分。', '加生姜为引，水煎服。', '《万氏女科》卷一', '白浊。', '-', '-', '柴胡', '[\'陈皮\', \'半夏\', \'白茯苓\', \'白术\', \'苍术\', \'益智仁\', \'盐水\', \'草分\', \'升麻\', \'柴胡\']');
INSERT INTO `prescript` VALUES (56955, '40171', '加减小柴胡汤', '净柴胡1两（去芦），人参3钱（去芦），半夏（汤洗7次切作片子）2钱半，甘草（炙）2钱半，防风（去芦）3钱。', '上(口父)咀。每服5钱。水1盏，加生姜5片，大枣1枚，同煎至7分，去滓温服，不拘时候。热甚大便秘涩，加大黄。', '《普济方》卷三六九', '小儿伤风伤寒，疮疹阴阳不和，寒热往来，口苦舌干，及气喘咳嗽。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'人参\', \'半夏\', \'洗次\', \'切作\', \'片子\', \'钱半\', \'甘草\', \'防风\', \'芦钱\']');
INSERT INTO `prescript` VALUES (56956, '40149', '防风汤', '防风（去叉）1两，柴胡（去苗）1两，黄连（去须）1两，当归（炙）1两，枳壳（去瓤，麸炒）1两，大黄（锉，熬）1两，天雄（炮裂，去皮脐）1两，地骨皮1两，桑根白皮（锉，炒）1两半，羌活（去芦头）1两半，芎1两半，石膏（捶碎）1两，旋覆花半两，桂（去粗皮）半两，菊花半两。', '每服5钱匕，以水1盏半，加生姜半分（切），煎取8分，去滓温服。', '《圣济总录》卷十六', '风头痛。', '-', '-', '柴胡', '[\'防风\', \'柴胡\', \'黄连\', \'当归\', \'枳壳\', \'大黄\', \'两天\', \'雄炮裂\', \'去皮\', \'地骨皮\', \'两桑根\', \'白皮\', \'两半\', \'羌活\', \'芦头\', \'两半\', \'两半\', \'石膏\', \'旋覆花\', \'半两\', \'桂去\', \'粗皮\', \'半两\', \'菊花\', \'半两\']');
INSERT INTO `prescript` VALUES (56957, '40118', '加减柴胡汤', '柴胡（去芦）8钱，半夏（姜制）2钱半，黄芩（去心）3钱，人参（去芦）3钱，甘草（炙）3钱，生熟地黄共半两。', '上(口父)咀。每服2钱，水1盏半，加生姜2片，枣1枚，煎至7分，去滓温服。大人倍加，煎服。', '《普济方》卷一三四引《经验良方》', '伤寒鼻衄。', '-', '-', '柴胡', '[\'柴胡\', \'芦钱\', \'半夏\', \'制钱\', \'黄芩\', \'心钱\', \'人参\', \'芦钱\', \'甘草\', \'炙钱生\', \'熟地黄\', \'半两\']');
INSERT INTO `prescript` VALUES (56958, '40087', '加减乌金散', '厚朴1钱5分，柴胡1钱5分，黄芩1钱5分，麻黄2钱，陈皮1钱5分，当归1钱5分，川芎1钱5分，桔梗1钱5分，茯苓1钱5分，桂枝1钱，苍术1钱，白芷1钱，枳壳1钱，羌活2钱，草果2钱，半夏2钱，甘草9分，白芍药1钱5分，熟地黄1钱5分。', '每服用水1钟半，加生姜3片，葱3茎，煎至1钟，不拘时服。有汗，多当归、川芎、白芍药、熟地黄；有胀，多厚朴、陈皮；有热，多柴胡、黄芩；有寒，多苍术、草果桂枝；有痰，多半夏、桔梗、茯苓；有头痛，多川芎、白芷、羌活；有泻，去枳壳、甘草；有余血块在腹，作潮热疼痛，加三棱、莪术，多用延胡索、八角、茴香；遍身痛，加羌活、独活；寒热往来，加黄芩、柴胡。', '《准绳·女科》卷五', '产后寒热似疟。三阴疟有锗杂之邪者。', '-', '-', '柴胡', '[\'厚朴\', \'钱分\', \'柴胡\', \'钱分\', \'黄芩\', \'钱分\', \'麻黄\', \'陈皮\', \'钱分\', \'当归\', \'钱分\', \'川芎\', \'钱分\', \'桔梗\', \'钱分\', \'茯苓\', \'钱分\', \'桂枝\', \'苍术\', \'白芷\', \'枳壳\', \'羌活\', \'草果\', \'半夏\', \'甘草\', \'分白\', \'芍药\', \'钱分\', \'熟地黄\', \'钱分\']');
INSERT INTO `prescript` VALUES (56959, '40034', '分瘀汤', '大黄3钱，车前子3钱，丹皮5钱，当归1两，枳壳1钱，柴胡1钱。', '水煎服。', '《辨证录》卷七', '痢久不止，日夜数十行，下如清涕，内有紫黑血丝，食渐减少，脉沉细弦促。', '-', '-', '柴胡', '[\'大黄\', \'车前子\', \'丹皮\', \'当归\', \'枳壳\', \'柴胡\']');
INSERT INTO `prescript` VALUES (56960, '40032', '加味补中益气汤', '人参1钱，黄耆（蜜炙）1钱，白术1钱，白芍药（酒炒）1钱，甘草（炙）1钱，陈皮1钱，当归1钱，升麻5分，柴胡5分，砂仁5分。', '水煎服。', '《济阳纲目》卷七十三', '劳倦饮食损伤元气，或过服寒凉消导之药，致清气下陷，肚腹大痛。', '-', '-', '柴胡', '[\'人参\', \'黄耆\', \'蜜炙钱\', \'白术\', \'白芍\', \'药酒\', \'甘草\', \'陈皮\', \'当归\', \'升麻\', \'柴胡\', \'砂仁\']');
INSERT INTO `prescript` VALUES (56961, '39973', '加减补肝祛疟汤', '赤色鲜首乌1两（打碎），酒炒白芍1两，当归1两，柴胡1钱，青皮1钱5分，草蔻仁1钱（研），半夏2钱，炙甘草5分。', '煨姜3钱、大枣3枚为引。如寒甚，加肉桂8分（或桂枝1钱5分）；如热甚，加青蒿3钱；如小便不利。加车前子3钱，赤苓3钱；恶心，加灶心土3钱；如饱闷，加槟榔1钱5分。', '《慈航集》卷下', '足厥阴肝经之疟，初病令人腰痛，少腹胀满，小便不利如癃状，小便数，心气恐惧，气不足，腹中悒悒，先寒颤，后变热，面色苍白，善太息，甚者欲如死状，或头痛而渴，此疟由少阳胆经而传入厥阴肝经，两胁必胀。', '-', '-', '柴胡', '[\'赤色\', \'首乌\', \'打碎\', \'白芍\', \'当归\', \'柴胡\', \'青皮\', \'钱分\', \'草蔻仁\', \'钱研\', \'半夏\', \'甘草\']');
INSERT INTO `prescript` VALUES (56962, '39971', '半夏汤', '半夏（汤洗去滑，焙干）半两，槟榔半两，柴胡（去苗）1两，桔梗（炒）1两，人参1两，赤茯苓（去黑皮）1两，白术1两，陈橘皮（去白）3分。', '每服5钱匕，水1盏半，加生姜1分（拍碎），煎至1盏，去滓，空腹分温2服。', '《圣济总录》卷八十八', '虚劳。寒热进退，痰饮不消，四肢拘急，手足时冷。', '-', '-', '柴胡', '[\'半夏\', \'汤洗\', \'焙干\', \'半两\', \'槟榔\', \'半两\', \'柴胡\', \'桔梗\', \'人参\', \'两赤\', \'茯苓\', \'黑皮\', \'白术\', \'两陈\', \'橘皮\', \'白分\']');
INSERT INTO `prescript` VALUES (56963, '39930', '加味四君子汤', '人参3钱，白术5钱，茯苓3钱，甘草1分，柴胡1钱，枳壳5分。', '水煎服。', '《辨证录》卷九', '忍饥受饿，腹中空虚，时遇天气不正，时寒时热，遂至胸膈闷塞，宛如结胸者。', '-', '-', '柴胡', '[\'人参\', \'白术\', \'茯苓\', \'甘草\', \'柴胡\', \'枳壳\']');
INSERT INTO `prescript` VALUES (56964, '39929', '加味逍遥散', '柴胡2钱，当归2钱，白术1钱，甘草1钱，茯苓3钱，陈皮1钱，白芍3钱，炒栀子1钱，羌活5分。', '水煎服。', '《辨证录》卷五', '春温。春月伤风4-5日，身热恶风，头项强，胁下满，手足温，口渴。', '-', '-', '柴胡', '[\'柴胡\', \'当归\', \'白术\', \'甘草\', \'茯苓\', \'陈皮\', \'白芍\', \'栀子\', \'羌活\']');
