INSERT INTO `prescript` VALUES (56983, '39729', '开壅汤', '红花3钱，当归尾2钱，牛膝2钱，桃仁14个，柴胡2钱，丹皮3钱，大黄1钱，香附1钱，郁金3钱，天花粉2钱，延胡索1钱。', '水煎服。', '《辨证录》卷三', '通经泻肝。主月经不通3月，忽然眼目红肿疼痛如刺，肝脉大而又大，或弦而滑。', '-', '-', '柴胡', '[\'花钱\', \'当归\', \'尾钱\', \'牛膝\', \'桃仁\', \'柴胡\', \'丹皮\', \'大黄\', \'香附\', \'钱郁\', \'金钱\', \'天花粉\', \'延胡索\']');
