INSERT INTO `prescript` VALUES (56986, '39657', '化坚汤', '升麻1钱，葛5分，漏芦，牡丹皮3钱，当归3钱，生熟地黄3钱，连翘1钱，黄耆1钱，芍药3钱，桂3钱，柴胡8钱，黍粘1钱，羌活1钱，防风5分，独活5分，昆布、三棱、广术、人参、黄连、陈皮。', '方中自昆布以下用量原缺。腹胀，加朴；气不顺，加木香；便秘，加大黄。', '《脉因证治》卷下', '泻火散结。主瘰疬，瘿瘤。', '-', '-', '柴胡', '[\'升麻\', \'钱葛分\', \'漏芦\', \'牡丹皮\', \'当归\', \'钱生\', \'熟地黄\', \'连翘\', \'黄耆\', \'芍药\', \'钱桂钱\', \'柴胡\', \'羌活\', \'防风\', \'独活\', \'昆布\', \'三棱\', \'广术\', \'人参\', \'黄连\', \'陈皮\']');
