INSERT INTO `prescript` VALUES (56988, '39617', '半夏橘皮汤', '半夏（汤洗10遍，切，焙）1两，陈橘皮（汤浸，去白，焙）1两，杏仁（去皮尖双仁，麸炒，别研）1两，麻黄（去根节）1两1分，赤茯芩（去黑皮）1两1分，柴胡（去苗）1两1分，生姜（切，焙）半两，甘草（炙，锉）半两。', '每服3钱匕，水1盏，煎至6分，去滓温服，不拘时候。', '《圣济总录》卷六十五', '脾咳。', '-', '-', '柴胡', '[\'半夏\', \'汤洗\', \'遍切\', \'橘皮\', \'汤浸\', \'杏仁\', \'皮尖\', \'双仁麸\', \'炒别\', \'研两\', \'麻黄\', \'根节\', \'两分\', \'茯芩\', \'黑皮\', \'两分\', \'柴胡\', \'去苗\', \'两分\', \'生姜\', \'切焙\', \'半两\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (56989, '39568', '加减肥气丸', '柴胡半两，厚朴半两，人参半两，干姜半两，川乌3钱，巴豆霜3钱，肉桂2钱，黄连1两，川椒5分，甘草5分。', '初服2丸，1日加1丸，2日加2丸，渐加至大便微溏，再从2丸加服，空心淡醋汤送下。秋冬，干生姜半钱，加厚朴1倍，减黄连1半。', '《准绳·类方》卷二', '肝之积在左胁下，如覆杯，有头足，久不愈，令人咳逆，(疒皆)疟连年不已，其脉弦而细。', '-', '-', '柴胡', '[\'柴胡\', \'半两\', \'厚朴\', \'半两\', \'人参\', \'半两\', \'干姜\', \'半两\', \'川乌\', \'巴豆霜\', \'肉桂\', \'黄连\', \'两川椒分\', \'甘草\']');
INSERT INTO `prescript` VALUES (56990, '39527', '加味小柴胡汤', '柴胡1钱5分，人参5分，黄芩7分，半夏5分，甘草（炒）3分，山栀，牡丹皮。', '柴胡栀子散（《景岳全书》卷五十六）。', '《保婴撮要》卷十三', '肝胆风热，发为疮疡，耳前后肿，或结核焮痛，发热恶寒，或寒热往来，或潮热晡热，口苦耳聋，或胸胁作痛，或月经不调。', '-', '-', '柴胡', '[\'柴胡\', \'钱分\', \'人参\', \'黄芩\', \'半夏\', \'甘草\', \'分山\', \'牡丹皮\']');
INSERT INTO `prescript` VALUES (56991, '39489', '龙胆汤', '龙胆草（微炒）2钱，钓藤皮2钱，柴胡2钱，北梗2钱，芍药2钱，川芎2钱，茯苓2钱，甘草（炙）2钱，人参1钱，大黄2钱半（湿纸裹煨）。', '每服2钱，井水煎服。仍以红纱袋盛夜明砂与儿佩带。', '《直指小儿》卷三', '小儿鬾病。', '-', '-', '柴胡', '[\'龙胆草\', \'藤皮\', \'柴胡\', \'钱北\', \'芍药\', \'川芎\', \'茯苓\', \'甘草\', \'人参\', \'大黄\', \'钱半湿纸\']');
INSERT INTO `prescript` VALUES (56992, '39488', '加减柴芩汤', '柴胡8分，黄芩3钱，半夏（姜制）2钱，猪苓2钱，苍术1钱5分，青皮2钱，厚朴（姜炒）8分，槟榔1钱，草果1钱，乌梅2钱，甘草8分，泽泻2钱。', '加生姜、大枣，水煎服。', '《寿世保元》卷三', '诸疟，寒热交作，阴阳不分，口干发渴，小便赤涩，或作吐泻。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'半夏\', \'制钱\', \'猪苓\', \'苍术\', \'钱分\', \'青皮\', \'厚朴\', \'炒分\', \'槟榔\', \'草果\', \'乌梅\', \'甘草\', \'泽泻\']');
INSERT INTO `prescript` VALUES (56993, '39469', '加味柴胡汤', '柴胡1钱，半夏5分，黄芩1钱，陈皮3分，白芍2钱，防风5分，荆芥1钱，甘草5分。', '水煎服。外用鸡子清和香油调青黛末涂之。', '《外科真铨》卷下', '水激丹，乃足少阳胆经风火所致，初生两胁，虚肿红热，证轻者。', '-', '-', '柴胡', '[\'柴胡\', \'半夏\', \'黄芩\', \'陈皮分\', \'白芍\', \'防风\', \'荆芥\', \'甘草\']');
INSERT INTO `prescript` VALUES (56994, '39447', '回天大补膏', '人参6两，白术4两，白茯苓3两，当归4两，白芍4两，川芎2两，生地1斤，熟地1斤，天冬5两，麦冬5两，知母3两，八制香附8两，红花1两，山药2两，自制龟胶4两，清阿胶4两，鳖胶4两，玄参2两，丹皮3两，柴胡3两，人乳2碗，牛乳半斤，羊乳半斤，梨汁1碗，柿霜3两。', '-', '《陈素庵妇科补解》卷一', '虚损痨瘵之血枯。血枯则月水断绝，其外症畏寒发热，肌肉消瘦，皮肤干涩，爪甲青而不润，饮食减少，大便溏泄，小便痛而数，口干，咽燥，渐成劳瘵。脉侯左寸、左右尺必微而涩，右关必沉弦，左关必虚细。', '-', '-', '柴胡', '[\'人参\', \'白术\', \'白茯苓\', \'当归\', \'白芍\', \'川芎\', \'生地\', \'熟地\', \'天冬\', \'麦冬\', \'知母\', \'两八制\', \'香附\', \'红花\', \'山药\', \'自制\', \'龟胶\', \'两清\', \'阿胶\', \'玄参\', \'丹皮\', \'柴胡\', \'两人\', \'乳碗\', \'牛乳\', \'半斤\', \'羊乳\', \'半斤\', \'梨汁\', \'柿霜\']');
INSERT INTO `prescript` VALUES (56995, '39424', '产后救生丸', '百草霜4两，川芎5钱，丹参1两，炮姜2两，明天麻1两，飞罗面2两，茯神2两，柴胡5钱，熟地1两，当归2两，阿胶珠1两，麦冬1两，广木香1两，凤眼草1两，京墨5钱，远志2两，红花饼5钱。', '每服1丸，空心姜汤送下。', '《全国中药成药处方集》（沈阳方）', '化滞生新，行瘀止痛。主产后恶露不尽，败血上冲，神昏谵语，不省人事，暴脱下血，脐腹疼痛。', '孕妇忌服。', '-', '柴胡', '[\'百草霜\', \'川芎\', \'丹参\', \'两炮\', \'两明\', \'天麻\', \'两飞罗面\', \'茯神\', \'柴胡\', \'熟地\', \'当归\', \'阿胶\', \'麦冬\', \'两广\', \'木香\', \'凤眼\', \'两京\', \'墨钱\', \'远志\', \'红花\']');
INSERT INTO `prescript` VALUES (56996, '39389', '二九还元丹', '胡麻8两，苦参8两，荆芥8两，防风2两，羌活2两，升麻2两，独活2两，风藤4两，木通4两，黄柏4两，当归4两，白芷4两，柴胡3两，僵蚕1两5钱，蝉壳1两，川芎1两，蒺藜2两5钱，大风子12两。', '每服50丸，温酒送下，1日3次。', '《解围元薮》卷三', '风疠危笃恶证。', '避风，戒色。', '-', '柴胡', '[\'胡麻\', \'苦参\', \'荆芥\', \'防风\', \'羌活\', \'升麻\', \'独活\', \'两风藤\', \'木通\', \'黄柏\', \'当归\', \'白芷\', \'柴胡\', \'僵蚕\', \'川芎\', \'蒺藜\', \'大风子\']');
INSERT INTO `prescript` VALUES (56997, '39367', '地骨皮汤', '柴胡3钱，地骨皮3钱。', '水煎服。', '《本草纲目》卷三十六引《兰室秘藏》', '膀胱移热于小肠，上为口糜，生疮溃烂，心胃壅热，水谷不下。', '-', '-', '柴胡', '[\'柴胡\', \'地骨皮\']');
INSERT INTO `prescript` VALUES (56998, '39345', '加减小柴胡汤', '鳖血柴胡1钱，光桃仁3钱，归尾1钱半，粉丹皮2钱，酒炒黄芩1钱，杜红花1钱，生地2钱，益元散3钱（包煎）。', '-', '《重订通俗伤寒论》', '妇人中风7-8日，经水适断，热入血室，寒热如疟，发作有时者。', '-', '-', '柴胡', '[\'柴胡\', \'钱光\', \'桃仁\', \'钱归尾\', \'半粉\', \'丹皮\', \'钱酒\', \'黄芩\', \'杜红\', \'花钱\', \'生地\', \'钱益\', \'钱包\']');
INSERT INTO `prescript` VALUES (56999, '39332', '加减拨云散', '防风6分，荆芥6分，蝉蜕8分，车前子1钱，木贼8分，柴胡6分，黄芩8分，青葙子8分，赤芍1钱，决明8分，甘草4分。', '老生姜1薄片为引。', '《程松崖先生眼科》', '眼睛黑珠有白点及成块，轻者为云，厚者为眵。', '-', '-', '柴胡', '[\'防风\', \'荆芥\', \'蝉蜕\', \'车前子\', \'木贼\', \'柴胡\', \'黄芩\', \'青葙子\', \'赤芍\', \'决明\', \'甘草\']');
INSERT INTO `prescript` VALUES (57000, '39315', '加减小柴胡汤', '柴胡1钱，半夏2钱，当归8钱，秦艽1钱5分，青皮1钱5分，草蔻仁1钱（研），独活1钱5分，炒枳壳1钱5分，甘草5分，煨姜3钱，大枣3个。', '如恶心呕吐，加藿香3钱；胸口饱胀，加槟榔1钱5分，炒山楂3钱；口干，加花粉2钱；热重不退，加炒黄芩1钱5分，赤芩2钱。', '《慈航集》卷下', '劳倦受寒停滞而为劳疟，初病恶寒甚热亦甚，周身骨节酸痛。', '-', '-', '柴胡', '[\'柴胡\', \'半夏\', \'当归\', \'秦艽\', \'钱分\', \'青皮\', \'钱分\', \'草蔻仁\', \'钱研\', \'独活\', \'钱分\', \'枳壳\', \'钱分\', \'甘草\', \'姜钱\', \'大枣\']');
INSERT INTO `prescript` VALUES (57001, '39298', '二柴胡饮', '陈皮1钱半，半夏2钱，细辛1-2钱，厚朴1钱半，生姜3-7片，柴胡1钱半或2-3钱，甘草8分。', '水1钟半，煎7-8分，温服。如邪甚者，可加羌活、白芷、防风、紫苏之属，择而用之；如头痛不止者，加川芎1-2钱；如多湿，加苍术；如阴寒气胜，必加麻黄1-2钱，或兼桂枝。', '《景岳全书》卷五十一', '四时外感，或其人元气充实，脏气素平无火，或时逢寒胜之令，本无内热等证者。', '-', '-', '柴胡', '[\'陈皮\', \'半夏\', \'细辛\', \'厚朴\', \'钱半\', \'生姜\', \'柴胡\', \'钱半\', \'甘草\']');
INSERT INTO `prescript` VALUES (57002, '39281', '白芷胃风汤', '白芷2钱5分，升麻2钱5分，葛根8分，苍术（米泔炒）8分，炙草1钱5分，当归1钱5分，草豆蔻4分，柴胡4分，黄柏（炒）4分，藁本4分，羌活4分，蔓荆子3分，白僵蚕3分，麻黄（去节）7分。', '水煎服。', '《外科枢要》卷四', '手足阳明经气虚风热，面目麻木，或牙关紧急，眼目瞤动。', '-', '-', '柴胡', '[\'白芷\', \'钱分\', \'升麻\', \'钱分\', \'葛根\', \'苍术\', \'分炙草\', \'钱分\', \'当归\', \'钱分\', \'草豆蔻\', \'柴胡\', \'黄柏\', \'炒分\', \'藁本\', \'羌活\', \'蔓荆子\', \'白僵蚕\', \'麻黄\', \'节分\']');
INSERT INTO `prescript` VALUES (57003, '39266', '白术散', '白术1两，人参1两（去芦头），葛根1两，赤茯苓1两，陈橘皮1两（汤浸，去白瓤，焙），枇杷叶（拭去毛，炙微黄）半两，枳壳（麸炒微黄，去瓤）半两，黄耆（锉）半两，柴胡（去苗）半两，麦门冬（去心）半两，甘草（炙微赤）半两，半夏（汤洗7遍去滑）半两。', '每服3钱，以水1中盏，加生姜半两，煎至6分，去滓温服，不拘时候。', '《圣惠》卷七十四', '妊娠心胸痰逆，烦闷，头重目眩，憎寒，恶闻食气，四肢无力。', '-', '-', '柴胡', '[\'白术\', \'人参\', \'两去\', \'芦头\', \'葛根\', \'两赤\', \'茯苓\', \'两陈\', \'橘皮\', \'两汤\', \'浸去\', \'枇杷叶\', \'拭去\', \'微黄\', \'半两\', \'枳壳\', \'微黄\', \'半两\', \'黄耆\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'麦门冬\', \'去心\', \'半两\', \'甘草\', \'炙微\', \'半两\', \'半夏\', \'洗遍\', \'半两\']');
INSERT INTO `prescript` VALUES (57004, '39240', '回生丹', '金银花8两，玄参3两，蒲公英3两，川芎1两，甘草5钱，花粉3钱，柴胡3钱。', '水煎服。头面，加附子1钱；身之前后左右，加当归2两，甘菊1两，附子3分；四肢，加白术2两，茯苓1两，附子5分。', '《外科集腋》卷五', '无名肿毒。', '-', '-', '柴胡', '[\'金银花\', \'玄参\', \'蒲公英\', \'川芎\', \'甘草\', \'花粉\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57005, '39232', '加味香连散', '白术4两（米泔汤拌炒），黄芩3两，黄连1两（土炒），砂仁2两5钱，厚朴3两（炒），薄荷2两，白茯苓3两，白芍3两（酒炒），木香2两，陈皮2两，甘草2两，山楂肉4两，红花1两，扁豆3两（炒），柴胡2两，车前子3两，当归3两（隔纸烘干另磨，或晒干）。', '男妇大人每服2钱或3钱，3-5岁者，每服1钱或2钱，乳孩每服5-6分。一切痢疾，俱用白滚汤化下；水泻用生姜汤化下。此方药味平和，须宜多服，以愈为度。', '《幼科直言》卷四', '痢疾初起，毒气深重，米谷汤水不能进，而成噤口者；并治一切红白痢疾。', '-', '-', '柴胡', '[\'白术\', \'两米\', \'泔汤\', \'黄芩\', \'黄连\', \'两土\', \'砂仁\', \'厚朴\', \'薄荷\', \'白茯苓\', \'白芍\', \'两酒\', \'木香\', \'陈皮\', \'甘草\', \'山楂\', \'红花\', \'扁豆\', \'柴胡\', \'车前子\', \'当归\', \'两隔纸\', \'烘干\', \'另磨\', \'晒干\']');
