INSERT INTO `prescript` VALUES (57045, '38284', '加减三黄汤', '石膏3钱，黄芩1钱，黄连1钱，黄柏1钱，炒栀子1钱5分，柴胡1钱，夏枯草5钱，天花粉2钱，赤芍3钱。', '水煎服。', '《洞天奥旨》卷十三', '眼丹胞。胃火沸腾，上炽于目，肉轮上生胞，红肿而作脓者。', '-', '-', '柴胡', '[\'石膏\', \'黄芩\', \'黄连\', \'黄柏\', \'栀子\', \'钱分\', \'柴胡\', \'夏枯草\', \'天花粉\', \'赤芍\']');
INSERT INTO `prescript` VALUES (57046, '38270', '加味逍遥散', '大当归（酒洗）1钱，白芍药（酒炒）1钱，白茯神（去皮）1钱，白术（土炒）1钱，北柴胡（炒）1钱，牡丹皮1钱，苏薄荷3分，甘草3分，川黄连3分（用吴茱萸煎汤拌炒）。', '上(口父)咀。水煎服。', '《一草亭》', '妇人郁怒伤肝，眼目赤涩昏暗，及血虚发热，口干自汗，月经不调，腹痛。', '-', '-', '柴胡', '[\'当归\', \'洗钱\', \'白芍\', \'药酒\', \'茯神\', \'去皮\', \'白术\', \'钱北\', \'柴胡\', \'牡丹皮\', \'钱苏\', \'薄荷\', \'甘草\', \'分川\', \'黄连\', \'吴茱萸\']');
INSERT INTO `prescript` VALUES (57047, '38238', '决明汤', '决明子1两，升麻1两，枳实（炙）1两，柴胡1两，黄芩1两，芍药1两，栀子14枚，竹叶1升，车前草4升，甘草1两（炙）。', '决明子汤（《圣济总录》卷一○八）。', '《外台》卷二十一引《广济方》', '客热冲眼，赤痛泪出。', '忌海藻，菘莱。', '-', '柴胡', '[\'决明子\', \'升麻\', \'枳实\', \'柴胡\', \'黄芩\', \'芍药\', \'栀子\', \'竹叶\', \'车前草\', \'甘草\']');
INSERT INTO `prescript` VALUES (57048, '38220', '八风汤', '天雄5两，当归5两，人参5两，附子4两，防风4两，天门冬4两，蜀椒4两，独活4两，乌头3钱，秦艽3钱，细辛3钱，白术3钱，干姜3钱，山茱萸半两，五味子半两，桔梗半两，香白芷半两，柴胡半两，莽草半两。', '每服2钱，温酒调下，1日3次。以身中觉如针刺者，药行也。', '《女科百问》卷下', '中风，迷惑如醉，狂言惊悸，恍惚见鬼。', '-', '-', '柴胡', '[\'天雄\', \'当归\', \'人参\', \'附子\', \'防风\', \'天门冬\', \'两蜀椒\', \'独活\', \'乌头\', \'秦艽\', \'细辛\', \'白术\', \'干姜\', \'山茱萸\', \'半两\', \'五味子\', \'半两\', \'桔梗\', \'半两\', \'白芷\', \'半两\', \'柴胡\', \'半两\', \'莽草\', \'半两\']');
INSERT INTO `prescript` VALUES (57049, '38216', '加减逍遥散', '柴胡1钱5分，炙甘草1钱，茯苓3钱，白术2钱，当归2钱，白芍3钱，丹皮1钱5分，黑山栀1钱5分，煅牡蛎1钱5分，薄荷3分，广陈皮1钱5分，半夏2钱，白芥子2钱。', '-', '《疬科全书》', '妇人情志不逐，忧郁内伤，阴火上炎，而致生疬凝结不消者。', '-', '-', '柴胡', '[\'柴胡\', \'钱分\', \'甘草\', \'茯苓\', \'白术\', \'当归\', \'白芍\', \'丹皮\', \'钱分\', \'黑山\', \'栀钱分\', \'牡蛎\', \'钱分\', \'薄荷\', \'分广\', \'陈皮\', \'钱分\', \'半夏\', \'白芥子\']');
INSERT INTO `prescript` VALUES (57050, '38207', '地骨皮散', '地骨皮2分，赤芍药1两，桑根白皮1两，茅根1两（锉），甘草1两（炙微赤，锉），柴胡2两（去苗）。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷三十一', '骨蒸，羸瘦少力，燥热，背膊酸痛，小便赤黄，口舌干燥烦渴。', '-', '-', '柴胡', '[\'地骨皮\', \'分赤\', \'芍药\', \'两桑根\', \'白皮\', \'两茅根\', \'甘草\', \'两炙微\', \'柴胡\', \'两去\']');
INSERT INTO `prescript` VALUES (57051, '38163', '六和汤', '陈皮1两（去白），青皮（去白）3两，柴胡3两，净香附3两，苏叶3两，甘草1两半。', '上(口父)咀。水煎服。', '《普济方》卷三九○', '疟疾寒多热少，食积疳热。', '-', '-', '柴胡', '[\'陈皮\', \'两去\', \'青皮\', \'柴胡\', \'两净\', \'香附\', \'两苏叶\', \'甘草\', \'两半\']');
INSERT INTO `prescript` VALUES (57052, '38083', '加味益气汤', '人参（无参者，以淮山药3钱代之，或以时下生条参3钱代之）1钱，当归1钱，甘草（炙）1钱，白术1钱半，陈皮8分，川芎6分，黄耆（蜜炙）2钱，升麻（蜜炒）3分，柴胡（酒炒）3分，石菖蒲6分。', '生姜、大枣为引，水煎服。', '《会约》卷六', '劳苦太过，气虚耳聋，或耳鸣眩运，倦怠。', '-', '-', '柴胡', '[\'人参\', \'参者\', \'以淮\', \'山药\', \'钱代\', \'时下\', \'生条\', \'参钱代\', \'当归\', \'甘草\', \'白术\', \'钱半\', \'陈皮分\', \'川芎\', \'分黄耆\', \'蜜炙钱\', \'升麻\', \'炒分\', \'柴胡\', \'炒分\', \'石菖蒲\']');
INSERT INTO `prescript` VALUES (57053, '38080', '化骨至神丹', '当归9钱，金银花9钱，白芍5钱，茵陈3钱，龙胆草3钱，白术3钱，柴胡1钱，生甘草3钱。', '水煎服。', '《石室秘录》卷四', '多骨疽。', '-', '-', '柴胡', '[\'当归\', \'金银\', \'花钱\', \'白芍\', \'茵陈\', \'龙胆草\', \'白术\', \'柴胡\', \'生甘草\']');
INSERT INTO `prescript` VALUES (57054, '38076', '加减钱氏白术散', '人参半钱，白术半钱，白茯半钱，甘草（炙）半钱，枳壳（炒）半钱，藿香1钱，干葛2钱，木香3分，五味子3分，柴胡3分。', '上作1服。水煎服。', '《便览》卷二', '消渴不能食。', '-', '-', '柴胡', '[\'参半\', \'白术\', \'半钱白\', \'茯半钱\', \'甘草\', \'炙半钱\', \'枳壳\', \'炒半钱\', \'藿香\', \'干葛钱\', \'木香\', \'五味子\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57055, '38072', '六合汤', '当归1两，大黄1两，川芎1两，熟地黄1两，白芍1两，柴胡1两（一方加桂半两）。', '3岁1钱，水半盏，煎服，不拘时候。', '《水类钤方》卷二十', '小儿血热，每日巳午间发热，遇晚则凉。', '-', '-', '柴胡', '[\'当归\', \'大黄\', \'川芎\', \'熟地黄\', \'白芍\', \'柴胡\', \'一方\', \'加桂\', \'半两\']');
INSERT INTO `prescript` VALUES (57056, '38061', '夺命救生散', '桑白皮1两，白茯苓1两，杏仁1两，枳壳1两，人参1两，桃仁1两，陈皮（焙）1两，秦艽（净）1两，白芷1两，麻黄（去节）两半，柴胡两半，甘草（炙）半两，附子（炮）半两，桂心（生）半两，槟榔半两，肉豆蔻3分，当归（洗，焙）2两，麝3钱。', '每服2钱，温酒调下。不胜酒，水1盏，桃、柳枝各3寸，乌梅半个，煎7分，不拘时候服。', '《幼幼新书》卷二十', '大小传尸，肺痿骨蒸，酒色食气，诸劳。', '-', '-', '柴胡', '[\'桑白皮\', \'白茯苓\', \'杏仁\', \'枳壳\', \'人参\', \'桃仁\', \'陈皮\', \'秦艽\', \'白芷\', \'麻黄\', \'去节\', \'两半\', \'柴胡\', \'两半\', \'甘草\', \'半两\', \'附子\', \'半两\', \'桂心生\', \'半两\', \'槟榔\', \'半两\', \'肉豆蔻\', \'当归\']');
INSERT INTO `prescript` VALUES (57057, '38053', '开门散', '白芍5钱，白术5钱，茯苓3钱，陈皮1钱，当归5钱，柴胡3钱，苏叶1钱，牛膝3钱，车前子3钱，炒栀子3钱，天花粉3钱。', '水煎1碗，缓缓呷之。', '《辨证录》卷五', '关格。食至胃而吐，欲大小便而不能出，眼睛红赤，目珠暴露而胁胀满，气逆拂抑，求一通气而不可得，属肝气过郁者。', '-', '-', '柴胡', '[\'白芍\', \'白术\', \'茯苓\', \'陈皮\', \'当归\', \'柴胡\', \'苏叶\', \'牛膝\', \'车前子\', \'栀子\', \'天花粉\']');
INSERT INTO `prescript` VALUES (57058, '38041', '地骨皮散', '地骨皮半两，茯苓半两，柴胡1两，黄芩1两，生地黄1两，知母1两，石膏2两。', '加生姜，水煎服。如自汗已，多加知母。', '《云歧子脉诀》', '阳毒浑身壮热，自汗。', '-', '-', '柴胡', '[\'地骨皮\', \'半两\', \'茯苓\', \'半两\', \'柴胡\', \'黄芩\', \'生地黄\', \'知母\', \'石膏\']');
INSERT INTO `prescript` VALUES (57059, '37965', '加减逍遥散', '当归（酒洗）1钱，白芍（酒炒）1钱，白术（土炒）1钱，白茯1钱，柴胡1钱，甘草（炙）5分。', '加煨姜1片，薄荷少许，水煎服。如发热盛，加地骨皮、知母；如手擅掉，加防风、荆芥、薄荷；如咳嗽，加五味子、紫菀；如气恼胸膈痞闷，加枳实、青皮、香附；如吐痰，加半夏、贝母、瓜蒌仁；如饮食不消，加山楂、神曲；如发渴，加麦门冬、天花粉；如胸中作热，加黄连、栀子；如心慌心跳，加酸枣仁、远志肉；如久泻，加干姜炒黑；如遍身痛，加羌活、防风、川芎以利关节；如吐血，加生地、阿胶、牡丹皮；如自汗，加黄耆、酸枣仁；如左腿血块，加三棱、莪术、桃仁、红花；如右腹气块，加木香、槟榔；如怒气伤肝，眼目昏花，加龙胆草，黄连、栀子、白豆蔻', '《古今医鉴》卷十一', '肝脾血虚发热，或潮热，或自汗盗汗，或头痛目涩，或怔忡不宁，颊赤口干，或月经不调，或肚腹作痛，或小腹重坠，水道涩痛，或肿痛出脓，内热作渴。', '-', '-', '柴胡', '[\'当归\', \'洗钱\', \'白芍\', \'白术\', \'茯钱\', \'柴胡\', \'甘草\', \'炙分\']');
INSERT INTO `prescript` VALUES (57060, '37937', '地骨皮散', '地骨皮1两，柴胡1两（去苗），琥珀3两（细研），赤芍药半两，土瓜根半两，木通半两（锉），黄芩半两，青蒿子半两，当归3分（锉，微炒），川大黄1两（锉，微炒），牡丹半两，甘草1分（炙微赤，锉）。', '每服3钱，以水1中盏，加生姜半分，煎至6分，去滓，食前温服。', '《圣惠》卷七十二', '室女月水不通，心神烦热，四肢疼痛，不思饮食。', '-', '-', '柴胡', '[\'地骨皮\', \'柴胡\', \'两去\', \'琥珀\', \'两细\', \'研赤\', \'芍药\', \'半两\', \'土瓜根\', \'半两\', \'木通\', \'半两\', \'黄芩\', \'半两\', \'青蒿\', \'半两\', \'当归\', \'炒川\', \'大黄\', \'牡丹\', \'半两\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (57061, '37936', '二十四味建中汤', '黄耆（蜜炙）2两，官桂2两，秦艽2两，肉豆蔻2两，煨柴胡2两，荆芥2两，白芷2两，川芎2两，鳖甲（醋炙）2两，桔梗2两，当归1两，莪术（炮）1两，麦门冬（去心）1两，白芍药1两，人参（去芦）1两，茯苓1两，甘草（炙）1两，木香1两，酸枣仁（炒）1两，海桐皮1两，枳壳（去瓤，煨）1两，干地黄1两，沉香半两，槟榔半两。', '每2钱半，水1盏，加生姜3片，乌梅2个，煎至7分，温服。如觉脏腑冷，即空心热服，小便多，即食后、临卧时服。', '《简易方》引《卫生家宝》（见《医方类聚》卷一五○）', '虚劳，体倦骨疼，羸瘦少力，心忪胸满，痞闷不食；妇人血气风劳，月水不调，不孕者。', '-', '-', '柴胡', '[\'黄耆\', \'官桂\', \'秦艽\', \'肉豆蔻\', \'柴胡\', \'荆芥\', \'白芷\', \'川芎\', \'鳖甲\', \'桔梗\', \'当归\', \'莪术\', \'麦门冬\', \'去心\', \'两白\', \'芍药\', \'人参\', \'去芦\', \'茯苓\', \'甘草\', \'木香\', \'酸枣仁\', \'海桐皮\', \'枳壳\', \'两干\', \'地黄\', \'沉香\', \'半两\', \'槟榔\', \'半两\']');
INSERT INTO `prescript` VALUES (57062, '37905', '龙胆汤', '龙胆半两，枳壳（去瓤，麸炒）半两，柴胡（去苗）半两，栀子仁半两，知母（切，焙）半两，地骨皮半两，木通（锉）半两，芍药半两，甘草（炙）半两，羚羊角（镑）半两，麦门冬（去心，焙）半两，升麻半两。', '每服5钱匕，用水1盏半，煎至1盏，去滓，不拘时候温服。', '《圣济总录》卷二十八', '伤寒发黄烦热，皮肉皆黄，小便赤不利。', '-', '-', '柴胡', '[\'龙胆\', \'半两\', \'枳壳\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'栀子\', \'半两\', \'知母\', \'切焙\', \'半两\', \'地骨皮\', \'半两\', \'木通\', \'半两\', \'芍药\', \'半两\', \'甘草\', \'半两\', \'羚羊角\', \'半两\', \'麦门冬\', \'半两\', \'升麻\', \'半两\']');
INSERT INTO `prescript` VALUES (57063, '37889', '加减逍遥散', '柴胡1钱半，白芍3钱，茯苓3钱，白术2钱，甘草1钱，山栀3钱，蕲艾3钱。', '水煎，温服。心烦躁甚者，加黄芩2钱；出血多者，加乌贼骨8钱，生地炭3钱。', '《中医妇科治疗学》', '平肝解郁以安胎。主妊娠胎动不安，或腹痛下血，兼精神抑郁，心烦善怒，肋胁胀痛，时有潮热，嗳气食少，或呕苦吐酸，脉弦而滑。', '-', '-', '柴胡', '[\'柴胡\', \'钱半\', \'白芍\', \'茯苓\', \'白术\', \'甘草\', \'钱山\', \'栀钱\', \'艾钱\']');
INSERT INTO `prescript` VALUES (57064, '37879', '布膏药', '生地3钱，当归3钱，首乌3钱，川芎3钱，川断3钱，红花3钱，加皮3钱，川草乌3钱，茅术3钱，良姜3钱，官桂3钱，香附3钱，乌药3钱，枳壳3钱，陈皮3钱，柴胡3钱，白芷3钱，羌独活3钱，灵仙3钱，麻黄3钱，莪术3钱，三棱3钱，寄奴3钱，荆芥3钱，防风3钱，赤芍3钱，青皮3钱，桃红3钱，川军3钱，牙皂3钱，藁本3钱，连翘3钱，南星3钱，山奈3钱，姜半夏3钱，海风藤3钱，甘松3钱，细料方：麝香1钱，附子2钱，冰片5分，洋樟3钱，木香3钱，肉桂1钱，乳没药3钱，细辛3钱，阿魏3钱，八角3钱，茴香3钱（共研末）。', '筋骨疼痛，腰腿酸软，四肢无力，贴两膏盲及肾俞；男子艰嗣，梦遗精滑，贴命门；妇女漏下半产，白带，贴子宫穴；左瘫右痪，手足麻木，贴肩井、曲池、环跳；跌打损伤，贴痛处；鹤膝风，贴膝眼；赤白痢疾，贴丹田；漏肩风，贴肩井；胁肋气痛，贴期门、章门；大、小疟疾，贴肺俞；心腹痛、呕吐，贴中脘；症瘕痞癖，贴痛处、气海；哮喘咳嗽，贴肺俞，中脘；木肾疝气，贴丹田、肾俞；瘀血作痛，贴丹田、气海；腰背疼痛、偏正头风，贴太阳、风门。', '《青囊秘传》', '男子艰嗣，梦遗精滑，妇人半产漏下，白带及跌打损伤，遍身筋骨疼痛，腰脚酸痛，足膝无力，左瘫右痪，水泻痢疾，手足麻痹，腰胁气痛，哮喘咳嗽，症瘕痞癖，心腹肚痛，呕吐，木肾疝气，偏正头风，漏肩鹤膝，疟疾，瘀血作痛。', '-', '-', '柴胡', '[\'生地\', \'当归\', \'首乌\', \'川芎\', \'钱川断\', \'花钱\', \'加皮\', \'钱川\', \'草乌\', \'钱茅术\', \'钱良\', \'姜钱\', \'官桂钱\', \'香附\', \'乌药\', \'枳壳\', \'陈皮\', \'柴胡\', \'白芷\', \'独活\', \'钱灵仙\', \'麻黄\', \'莪术\', \'三棱\', \'奴钱\', \'荆芥\', \'防风\', \'赤芍\', \'青皮\', \'桃红\', \'川军\', \'钱牙皂\', \'藁本\', \'连翘\', \'南星\', \'钱山奈钱\', \'半夏\', \'海风藤\', \'甘松\', \'细料方\', \'麝香\', \'附子\', \'冰片\', \'分洋\', \'木香\', \'肉桂\', \'钱乳\', \'没药\', \'细辛\', \'阿魏\', \'八角钱\', \'茴香\', \'共研\']');
INSERT INTO `prescript` VALUES (57065, '37871', '地骨皮散', '地骨皮1两，川升麻1两，玄参1两，甘草（炙微赤，锉）1两，防风（去芦头）1两，黄芩1两，赤茯苓2两，羌活3分，桑根白皮2两（锉），决明子2两半，石膏2两，柴胡2两半（去苗）。', '每服4钱，以水1中盏，加生姜半分，淡竹叶2-7片，黑豆50粒，煎至6分，去滓，食后温服。', '《圣惠》卷三十二', '肝壅毒气上攻，眼睛赤涩疼痛，心躁体热。', '-', '-', '柴胡', '[\'地骨皮\', \'两川\', \'升麻\', \'玄参\', \'甘草\', \'炙微\', \'防风\', \'芦头\', \'黄芩\', \'两赤\', \'茯苓\', \'羌活\', \'分桑根\', \'白皮\', \'决明子\', \'两半\', \'石膏\', \'柴胡\', \'两半\', \'去苗\']');
