INSERT INTO `prescript` VALUES (57067, '37790', '加味和中益气汤', '人参钱半，白术钱半，陈皮钱半，柴胡钱半，黄芩钱半，半夏1钱，升麻5分，当归1钱，川芎1钱，黄耆1钱，枳实1钱，甘草5分。', '水2钟，加生姜3片，煎至8分，食远服。', '《摄生众妙方》卷五', '泄泻，少进饮食。', '-', '-', '柴胡', '[\'人参\', \'钱半\', \'白术\', \'钱半\', \'陈皮\', \'柴胡\', \'钱半\', \'黄芩\', \'钱半\', \'半夏\', \'升麻\', \'当归\', \'川芎\', \'黄耆\', \'枳实\', \'甘草\']');
INSERT INTO `prescript` VALUES (57068, '37760', '地骨皮散', '地骨皮1两，枳壳1两（麸炒微黄，去瓤），川大黄1两（锉碎，微炒），赤芍药半两，柴胡1两（去苗），鳖甲1两（涂醋炙令黄，去裙襕），麦门冬1两（去心，焙），甘草半两（炙微赤，锉）。', '每服5钱，以水1大盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十七', '热病七日，遍身疼痛，壮热不解。', '-', '-', '柴胡', '[\'地骨皮\', \'枳壳\', \'两麸\', \'微黄\', \'大黄\', \'芍药\', \'半两\', \'柴胡\', \'两去\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'麦门冬\', \'两去\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (57069, '37739', '开元固气丸', '西党参4两，上绵耆（炙）4两，焦白术4两，川楝肉（盐酒炒）4两，蛀青皮4两，当归身（酒炒）4两，小茴香（盐水炒）3两，上官桂3两，赤芍3两，白芍3两，软柴胡（醋炒）1两5钱，肥升麻1两5钱，大熟地5两，炙甘草2两，新会皮2两，青木香2两，橘核2两。', '上各为末，炼蜜为丸。每服4钱，淡酒送下。', '《古方汇精》卷一', '各种疝气。', '-', '-', '柴胡', '[\'党参\', \'两上\', \'绵耆\', \'两焦\', \'白术\', \'川楝\', \'青皮\', \'当归\', \'身酒\', \'小茴香\', \'盐水\', \'上官\', \'桂两\', \'赤芍\', \'白芍\', \'两软\', \'柴胡\', \'升麻\', \'熟地\', \'甘草\', \'两新会\', \'青木香\', \'橘核\']');
INSERT INTO `prescript` VALUES (57070, '37705', '加减补中益气汤', '补中益气汤加柴胡8分，升麻1钱（蜜炒），白芍2钱（酒炒），桂枝8分，酸枣仁2钱（炒），熟附8分，麻黄根8分，浮小麦3钱，倍加黄耆。', '-', '《寿世保元》卷二', '伤寒误投攻击发表之药过多，发得表虚，上气喘急，口干不食，肢体昏沉，冷汗大出，以致亡阳。', '-', '-', '柴胡', '[\'补中\', \'益气汤\', \'柴胡\', \'升麻\', \'钱蜜\', \'白芍\', \'钱酒\', \'桂枝\', \'酸枣仁\', \'炒熟\', \'附分\', \'麻黄根\', \'浮小麦\', \'倍加\', \'黄耆\']');
INSERT INTO `prescript` VALUES (57071, '37683', '加减十全汤', '川芎1两，川当归（去芦，酒浸）1两，白芍药1两，熟干地黄（酒浸）1两，半夏（汤泡7次，焙）1两，秦艽（去芦）1两，人参（去芦）1两，白术（炒）1两，金钗石斛（酒浸）1两，甘草（炙）1两，鹿角胶（锉，麸炒成珠）1两，白茯苓（去皮）1两，黄耆（蜜炙）1两，肉桂（去粗皮，不见火）2两，银州柴胡（去芦）2两。', '上(口父)咀。每服3大钱，水1盏半，加生姜5片，枣子1枚，入饧1块，煎7分，去滓热服，不拘时候。', '《魏氏家藏方》卷四', '调营卫，壮力，退热，收虚汗，美饮食，悦颜色。主诸虚百损。', '-', '-', '柴胡', '[\'川芎\', \'两川\', \'当归\', \'芦酒\', \'两白\', \'芍药\', \'两熟\', \'干地\', \'黄酒\', \'半夏\', \'汤泡次\', \'秦艽\', \'去芦\', \'人参\', \'去芦\', \'白术\', \'金钗\', \'石斛\', \'酒浸\', \'甘草\', \'鹿角胶\', \'成珠\', \'白茯苓\', \'去皮\', \'两黄耆\', \'肉桂\', \'粗皮\', \'不见\', \'火两\', \'银州\', \'柴胡\', \'去芦\']');
INSERT INTO `prescript` VALUES (57072, '37667', '半夏十味汤', '半夏5两（熊州者，汤洗去滑，汁尽），干姜3两，吴茱萸2两，桂心1两，白术3两，细辛3两，柴胡3两，牡丹皮3两，大黄5两，芒硝2两。', '以水1斗，煮取3升，去滓，纳芒消，搅令消尽，分温3服，每服如人行10里久。若服1服利后，须伺候将息，勿更进汤药，但研好粟米作汁饮，细细与之。如觉利伤多，可以醉饭止，稠醉浆粥亦得。', '《外台》卷三引许仁则方', '天行病。服生芦根等8味饮子饮之，诸状不歇，渐不下食，心腹结硬，不得手近，有时触着，痛不可忍，既是热病，体气合热，骨肉疼痛，脉合洪数，口合苦爽，食合呕逆，体气反凉，脉反沉细，饭食反下，反不知痛恼，大小便秘塞，心上如石，痛不可近，视唇急鼻张，手眼寻绎，狂言妄语，此由热极，将息酷冷，饮食寝寐，唯冷是求，热结在心，无因通泄。但加身体黄，眼白睛色如黄柏，此是急黄。', '忌羊肉、饧、生葱、生菜、桃李、雀肉、胡荽等。', '-', '柴胡', '[\'半夏\', \'两熊州者\', \'滑汁\', \'干姜\', \'吴茱萸\', \'两桂心\', \'白术\', \'细辛\', \'柴胡\', \'牡丹皮\', \'大黄\', \'芒硝\']');
INSERT INTO `prescript` VALUES (57073, '37665', '地骨皮散', '地骨皮1两，柴胡1两（去苗），白茯苓半两，桑根白皮3分（锉），五加皮半两，人参半两（去芦头），黄耆3分（锉），甘草半两（炙微赤，锉），桂心半两，白芍药半两，前胡3分（去芦头），枳壳3分（麸炒微黄，去瓤）。', '每服3钱，以水1中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷七十', '妇人血风，气体虚，发歇寒热。', '-', '-', '柴胡', '[\'地骨皮\', \'柴胡\', \'两去\', \'白茯苓\', \'半两\', \'桑根\', \'白皮\', \'五加皮\', \'半两\', \'人参\', \'半两\', \'芦头\', \'黄耆\', \'甘草\', \'半两\', \'炙微\', \'桂心\', \'半两\', \'芍药\', \'半两\', \'胡分\', \'芦头\', \'枳壳\', \'分麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (57074, '37572', '白茯苓散', '白茯苓半两，人参半两（去芦头），柴胡半两（去苗），草豆蔻1分（去皮），半夏半两（汤洗7遍去滑），枇杷叶半两（拭去毛，炙令微黄），厚朴半两（去粗皮，涂生姜汁，炙令微黄）。', '每服5钱，以水1大盏，加生姜半分，大枣2个，煎至5分，去滓，食前温服。', '《圣惠》卷十八', '热病后，脾胃气虚，冷痰滞，不思饮食。', '-', '-', '柴胡', '[\'白茯苓\', \'半两\', \'人参\', \'半两\', \'芦头\', \'柴胡\', \'半两\', \'去苗\', \'草豆蔻\', \'去皮\', \'半夏\', \'半两\', \'洗遍\', \'枇杷叶\', \'半两\', \'拭去\', \'毛炙令\', \'微黄\', \'厚朴\', \'半两\', \'粗皮\', \'涂生\', \'姜汁\', \'炙令\', \'微黄\']');
