INSERT INTO `prescript` VALUES (57076, '37518', '二术救痹饮', '白术5钱，白芍5钱，茯神5钱，陈皮1钱，肉桂1钱，柴胡1钱，枳壳5分，远志3钱，白芥子3钱，苍术3钱。', '水煎服。', '《辨证录》卷二', '肝痹，气血不足，风寒湿邪乘之，肝气常逆，胸膈引痛，睡卧多惊，饮食不思，吞酸作呕，筋脉拘急。', '-', '-', '柴胡', '[\'白术\', \'白芍\', \'茯神\', \'陈皮\', \'肉桂\', \'柴胡\', \'枳壳\', \'远志\', \'白芥子\', \'苍术\']');
INSERT INTO `prescript` VALUES (57077, '37502', '地骨皮汤', '地骨皮1两，芍药1两半，桑根白皮（锉）1两半，茅根1两半，甘草（炙，锉）1两，柴胡（去苗）1两半，石膏（碎）3两半。', '每用5钱匕，水1盏半，煎至1盏，去滓，分2次温服，空心、食后各1次。', '《圣济总录》卷九十三', '骨蒸羸瘦少力，或热或寒，背膊疼痛，口干，小便赤黄。', '-', '-', '柴胡', '[\'地骨皮\', \'芍药\', \'两半\', \'桑根\', \'白皮\', \'两半\', \'茅根\', \'两半\', \'甘草\', \'柴胡\', \'去苗\', \'两半\', \'石膏\', \'两半\']');
