INSERT INTO `prescript` VALUES (57086, '37297', '地骨皮散', '人参1两半，地骨皮1两半，柴胡1两半，黄耆1两半，生地黄1两半，白茯苓半两，知母1两，石膏2两。', '每服1两，水2盏，加生姜7片，煎至7分，去滓，细细温服。', '《云岐子脉诀》', '肺痈，疮疡，气虚内热者；脏中积冷，荣中有热，脉举之有余，按之不足，阳有余阴不足者；蒸潮热，自汗，咳吐腥秽稠痰。', '-', '-', '柴胡', '[\'人参\', \'两半\', \'地骨皮\', \'两半\', \'柴胡\', \'两半\', \'黄耆\', \'两半\', \'生地黄\', \'两半\', \'白茯苓\', \'半两\', \'知母\', \'石膏\']');
INSERT INTO `prescript` VALUES (57087, '37237', '加味茯苓汤', '苍术2钱，白术3钱，茯苓1钱，甘草（炙）5分，猪苓8分，泽泻1钱，升麻5分，肉桂7分，柴胡6分，黄芩1钱，生草5分，川归1钱，白芍7分。', '上(口父)咀。水2钟，煎1钟，温服。', '《杏苑》卷四', '补中健脾，疏利湿热。主水泄注下，日夜无度，小便短少，口渴咽干，腹中疼痛或变成赤白痢疾。', '-', '-', '柴胡', '[\'苍术\', \'白术\', \'茯苓\', \'甘草\', \'炙分\', \'猪苓\', \'泽泻\', \'升麻\', \'肉桂\', \'柴胡\', \'黄芩\', \'钱生\', \'草分川\', \'白芍\']');
