INSERT INTO `prescript` VALUES (57089, '37177', '半夏汤', '半夏（汤洗7遍，焙）半两，赤茯苓（去黑皮）3两，人参3两，前胡（去苗）3两，甘草（炙，锉）1分，桂（去粗皮）3分，柴胡（去苗）半两。', '每服5钱匕，水2盏，加生姜5片，大枣3个（擘破），用煎至1盏，去滓温服，不拘时候。', '《圣济总录》卷六十一', '胸痹短气。', '-', '-', '柴胡', '[\'半夏\', \'洗遍\', \'半两\', \'茯苓\', \'黑皮\', \'人参\', \'两前\', \'胡去\', \'甘草\', \'分桂去\', \'粗皮分\', \'柴胡\', \'去苗\', \'半两\']');
INSERT INTO `prescript` VALUES (57090, '37150', '加减小柴胡汤', '柴胡4钱，人参3钱，竹茹3钱，伏龙肝6钱，黄芩4钱，生姜1两（切片），陈皮3钱，甘草5钱。', '水煎，温服。', '《医学探骊集》卷三', '伤寒2-3日，胃腑为寒热所困，饮食入口，少顷即吐者。', '-', '-', '柴胡', '[\'柴胡\', \'人参\', \'竹茹\', \'伏龙肝\', \'黄芩\', \'生姜\', \'切片\', \'陈皮\', \'甘草\']');
INSERT INTO `prescript` VALUES (57091, '37142', '苍术复煎散', '红花1分，黄柏3分，柴胡5分，藁本5分，泽泻5分，白术5分，升麻5分，羌活1钱，苍术4两。', '上（口父）咀。水2碗，先煎苍术汤2大盏，复煎前项药至1大盏。稍热空心服。取微汗为效。', '《兰室秘藏》卷中', '寒湿相合，脑右痛，恶寒。项筋脊骨强，肩背胛眼痛，膝膑痛，无力，行步沉重。风湿热痛。', '忌酒、湿面。', '-', '柴胡', '[\'红花\', \'黄柏\', \'柴胡\', \'藁本\', \'泽泻\', \'白术\', \'升麻\', \'羌活\', \'苍术\']');
INSERT INTO `prescript` VALUES (57092, '37059', '地黄丸', '生干地黄2两，柴胡（去苗，净洗）半两，秦艽（净洗，去芦）半两，黄芩半两，赤芍药1两。', '赤芍地黄丸（《医学入门》卷八）、抑阴丸（《古今医鉴》卷十一）、断欲丸（《寿世保元》卷七）。本方方名，《妇人良方》引作“生地黄丸”，《准绳·类方》引作“抑阴地黄丸”。', '《本事》卷十', '尼师寡妇，独阴无阳，欲男子而不可得．以致恶风体倦，乍寒乍热，面赤心烦，或时自汗，厥阴脉弦长而上出鱼际。', '-', '-', '柴胡', '[\'生干\', \'地黄\', \'柴胡\', \'苗净\', \'半两\', \'秦艽\', \'净洗\', \'半两\', \'黄芩\', \'半两\', \'芍药\']');
INSERT INTO `prescript` VALUES (57093, '37048', '加味龙胆泻肝汤', '龙胆草2钱，当归2钱，生地3钱，泽泻2钱，木通3钱，苡仁3钱，柴胡1钱，黄芩3钱，栀子3钱，莲须2钱，赤芍2钱，甘草1钱。', '水煎服。阴道有热感，去当归、柴胡，加贯仲3钱，阴道红肿，小便困难，去当归、柴胡、莲须，加黄连1钱、琥珀1钱；湿甚，舌苔厚腻，去生地。', '《中医妇科治疗学》', '平肝清热解郁。主带下浅红色，似血非血，胁胀或痛，口苦尿黄，舌红苔黄，脉弦数。', '-', '-', '柴胡', '[\'龙胆草\', \'当归\', \'生地\', \'泽泻\', \'木通\', \'柴胡\', \'黄芩\', \'栀子\', \'莲须\', \'赤芍\', \'甘草\']');
INSERT INTO `prescript` VALUES (57094, '37023', '白凤丹', '白丝毛乌骨雄鸡1只（先以黄耆末1两，当归末1两，甘草未5钱，3味和米粉7合，匀作7分，调成小块，鸡食之，约有6-7日，吊死不出血，去毛肠不用），当归身（酒洗）3两，川芎2两，白芍（酒炒）3两，怀生地黄（酒洗）5两，山药3两，鹿角霜4两，天门冬（去心）1两，人参2两，丹参（水洗净）2两，山茱萸（酒蒸，去核）2两，木瓜1两半，胡黄连1两，知母（去毛，酒炒）3两，小茴（酒炒）2两，麦门冬（去心）2两，怀牛膝（去芦，酒洗）2两，秦艽（去芦）2两，银柴胡2两，鳖甲（醋炙）1两，生甘草1两。', '每服100丸，空心淡盐汤送下。', '《寿世保元》卷七', '妇女五劳七伤，骨蒸，五心烦热，心虚惊怕，经水来时，或前或后，或淡白，或紫色，时常注带下；或因烦劳、性气恼怒、产后失调，致赤白带渗，及夜卧身体上下疼痛，及午后神疲，腰腿酸软，或心嘈，又时饱闷，及梦寐不清，或冲任二脉结，症瘕隐隐。', '-', '-', '柴胡', '[\'白丝毛\', \'乌骨\', \'雄鸡\', \'先以\', \'黄耆\', \'当归\', \'甘草\', \'未钱味\', \'米粉\', \'合匀\', \'作分\', \'调成\', \'小块\', \'鸡食\', \'之约\', \'吊死\', \'出血\', \'毛肠\', \'不用\', \'当归\', \'身酒\', \'川芎\', \'白芍\', \'两怀\', \'生地黄\', \'酒洗\', \'山药\', \'鹿角霜\', \'天门冬\', \'人参\', \'丹参\', \'洗净\', \'山茱萸\', \'酒蒸\', \'去核\', \'木瓜\', \'两半\', \'胡黄连\', \'知母\', \'毛酒\', \'两小茴酒\', \'麦门冬\', \'怀牛膝\', \'芦酒\', \'秦艽\', \'去芦\', \'银柴胡\', \'鳖甲\', \'生甘草\']');
INSERT INTO `prescript` VALUES (57095, '37006', '半夏厚朴汤', '红花5厘，苏木5厘，吴茱萸1分，干生姜1分，黄连1分，木香2分，青皮2分，肉桂3分，苍术3分，白茯苓3分，泽泻3分，柴胡3分，陈皮3分，生黄芩3分，草豆蔻仁3分，生甘草3分，京三棱4分，当归梢4分，猪苓4分，升麻4分，神曲6分，厚朴8分，半夏1钱，桃仁7个，昆布少许。', '服广茂溃坚汤二服之后，中满减半，止有积不消，再服此药。渴，加葛根3分。', '《兰室秘藏》卷上', '消胀化积。主中满腹胀，内有积聚，坚硬如石，其形如盘，令人不能坐卧，大小便涩滞，上喘气促，面色萎黄，通身虚肿。', '-', '-', '柴胡', '[\'红花\', \'苏木\', \'吴茱萸\', \'分干\', \'生姜\', \'黄连\', \'木香\', \'青皮\', \'肉桂\', \'苍术\', \'白茯苓\', \'泽泻\', \'柴胡\', \'陈皮\', \'分生\', \'黄芩\', \'草豆蔻\', \'仁分\', \'生甘草\', \'分京\', \'三棱\', \'当归\', \'猪苓\', \'升麻\', \'神曲\', \'厚朴\', \'半夏\', \'桃仁\', \'昆布\', \'少许\']');
