INSERT INTO `prescript` VALUES (57101, '36910', '半夏汤', '半夏（汤洗7遍，焙干）1两半，桑根白皮（炙，锉）1两半，细辛（去苗叶）1两半，前胡（去芦头）1两半，桔梗（炒）1两，甘草（炙，锉）1两，贝母（去心）1两，柴胡（去苗）1两，人参1两，诃黎勒（微煨，去核）1两，白术1两。', '每服3钱匕，水1盏，加大枣3个（擘破），生姜半分（拍碎），煎至7分，去滓温服，食后、夜卧各1次。', '《圣济总录》卷七十一', '肺积，息贲咳嗽。', '-', '-', '柴胡', '[\'半夏\', \'洗遍\', \'焙干\', \'两半\', \'桑根\', \'白皮\', \'两半\', \'细辛\', \'苗叶\', \'两半\', \'胡去\', \'芦头\', \'两半\', \'桔梗\', \'甘草\', \'贝母\', \'柴胡\', \'人参\', \'两诃黎勒微\', \'去核\', \'白术\']');
INSERT INTO `prescript` VALUES (57102, '36894', '半夏汤', '半夏（汤洗7遍，焙）3分，人参3分，柴胡（去苗）3分，麦门冬（去心，焙）3分，赤茯苓（去黑皮）半两，竹茹半两，桂（去粗皮）半两，芦根（锉）半两，甘草（炙，锉）1分。', '每服5钱匕，水1盏半，加生姜5片，同煎至8分，去滓温服。', '《圣济总录》卷六十三', '上焦壅热，食饮不下，呕吐，两胁痛。', '-', '-', '柴胡', '[\'半夏\', \'洗遍\', \'焙分\', \'人参\', \'柴胡\', \'苗分\', \'麦门冬\', \'心焙分\', \'茯苓\', \'黑皮\', \'半两\', \'竹茹\', \'半两\', \'桂去\', \'粗皮\', \'半两\', \'芦根\', \'半两\', \'甘草\']');
