INSERT INTO `prescript` VALUES (57104, '36814', '加味四物汤', '大熟地1两（九蒸），白芍5钱（酒炒），当归5钱（酒洗），川芎3钱（酒洗），白术5钱（土炒），粉丹皮3钱，元胡1钱（酒炒），甘草1钱，柴胡1钱。', '水煎服。', '《傅青主女科》卷上', '补肝之血，通郁散风。主妇人经水忽来忽断，时疼时止，寒热往来者。', '-', '-', '柴胡', '[\'熟地\', \'两九蒸\', \'白芍\', \'钱酒\', \'当归\', \'钱酒\', \'川芎\', \'钱酒\', \'白术\', \'钱土\', \'炒粉\', \'丹皮\', \'钱元\', \'胡钱\', \'甘草\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57105, '36790', '加减广茂溃坚汤', '厚朴4分，黄芩5分，益智2分，草蔻仁2分，升麻2分，红花2分，甘草2分，当归5分，黄连5分，广茂3分，陈皮3分，柴胡3分，泽泻3分，神曲3分，白术1钱，茯苓1钱，半夏7分，吴萸1分，青皮3分，姜3片。', '-', '《方症会要》卷二', '中满腹有积块，坚硬如石，坐卧不宁，二便涩赤，上气喘促，通身虚肿。', '-', '-', '柴胡', '[\'厚朴\', \'黄芩\', \'益智\', \'分草蔻\', \'仁分\', \'升麻\', \'红花\', \'甘草\', \'当归\', \'黄连\', \'分广茂分\', \'陈皮分\', \'柴胡\', \'泽泻\', \'神曲\', \'白术\', \'茯苓\', \'半夏\', \'吴萸\', \'青皮\', \'姜片\']');
INSERT INTO `prescript` VALUES (57106, '36749', '白带丸', '乌贼骨1两，山药2两，芡实2两，炒黄柏5钱，醋柴胡4两，白芍1两，续断5钱，香附4钱，白果仁1两，车前子5钱，牡蛎1两，赤石脂5钱。', '每服3-4钱。', '《全国中药成药处方集》（重庆方）', '赤白带下，经水不调或先或后，头晕眼花，四肢无力，腰胸闷，骨蒸内热，饮食减少。', '忌食生冷。', '-', '柴胡', '[\'乌贼骨\', \'山药\', \'芡实\', \'黄柏\', \'柴胡\', \'白芍\', \'续断\', \'香附\', \'钱白\', \'果仁\', \'车前子\', \'牡蛎\', \'赤石脂\']');
