INSERT INTO `prescript` VALUES (57109, '36675', '回生夺命神丹', '羌活3两，白蒺藜3两，藁本3两，天南星3两，防风3两，川续断3两，苍术3两，五加皮3两，独活3两，海风藤3两，白芷3两，大腹皮3两，乌药3两，仙灵脾3两，防已3两，皂角刺3两，川乌3两，明天麻3两，桔梗3两，北细辛3两，柴胡3两，蔓荆子3两，黄芩3两，川萆薢3两，薄荷3两，甘松3两，白芍3两，大黄3两，红花7两，玄参7两，草乌4两，苦参2斤（上用阴阳水各1大桶浸，春5、夏3、秋7、冬9日，煎100沸去滓，加炼蜜1斤，煎成膏如漆），白术2两，胡麻2两，人参（酒浸，蒸熟晒干）2两，砂仁2两，礞石（硝煅）2两，', '年深久远者，每服10丸；4-5年者，每服4-5丸；1-2年者，每服1-2丸，须麻姑酒磨下。随汗愈。', '《疡医大全》卷二十八', '三十六疯，十四癞。', '-', '-', '柴胡', '[\'羌活\', \'两白\', \'蒺藜\', \'藁本\', \'天南星\', \'防风\', \'两川\', \'续断\', \'苍术\', \'五加皮\', \'独活\', \'海风藤\', \'白芷\', \'大腹皮\', \'乌药\', \'仙灵脾\', \'两防\', \'皂角刺\', \'川乌\', \'两明\', \'天麻\', \'桔梗\', \'两北\', \'细辛\', \'柴胡\', \'蔓荆子\', \'黄芩\', \'两川\', \'薄荷\', \'两甘松\', \'白芍\', \'大黄\', \'红花\', \'玄参\', \'草乌\', \'苦参\', \'阴阳水\', \'大桶\', \'春夏秋冬\', \'沸去\', \'加炼\', \'蜜斤\', \'煎成\', \'膏如漆\', \'白术\', \'胡麻\', \'人参酒\', \'蒸熟\', \'晒干\', \'砂仁\', \'两礞\', \'石硝\']');
INSERT INTO `prescript` VALUES (57110, '36664', '加减小柴胡合龙胆泻肝汤', '柴胡1钱，人参5分，青皮5分，车前5分，龙胆草4分，栀子4分，半夏7分，黄芩7分，甘草3分，白芍1钱，归梢6分。', '加生姜1片，水煎服。仍服芦荟丸3次。', '《方症会要》卷三', '肝火盛，胁痛。', '-', '-', '柴胡', '[\'柴胡\', \'人参\', \'青皮\', \'分车\', \'前分\', \'龙胆草\', \'栀子\', \'半夏\', \'黄芩\', \'甘草\', \'白芍\', \'归梢分\']');
