INSERT INTO `prescript` VALUES (57112, '36636', '加味二陈汤', '陈皮1钱，半夏1钱半（姜泡），茯苓1钱半（盐水炒），白术1钱，桔梗1钱，石菖蒲7分，黄柏2分，知母3分，栀子（炒黑）1钱半，升麻1钱（酒炒），柴胡1钱（酒炒），甘草1钱。', '加生姜，水煎服。', '《古今医鉴》卷八', '遗精。', '-', '-', '柴胡', '[\'陈皮\', \'半夏\', \'钱半\', \'姜泡\', \'茯苓\', \'钱半\', \'盐水\', \'白术\', \'桔梗\', \'石菖蒲\', \'黄柏\', \'知母\', \'栀子\', \'黑钱\', \'升麻\', \'钱酒\', \'柴胡\', \'钱酒\', \'甘草\']');
INSERT INTO `prescript` VALUES (57113, '36615', '开郁顺气丸', '柴胡2两，青皮1两5钱，榔片1两，香附1两，木香5钱，枳壳5钱，酒芍5钱，山栀5钱，黄芩5钱，姜夏5钱，川芎5钱，神曲5钱，紫补5钱，砂仁5钱，广皮5钱，苍术5钱，乌药5钱，茯苓5钱，盔沉5钱，当归5钱，甘草5钱，桔梗8钱，莱菔3钱。', '每服1丸，早晚空心白开水送下。', '《全国中药成药处方集》(沈阳方)', '开郁养血，消食顺气，和胃健脾。主胸膈胀满，两胁攻痛，饮食不消，胃脘胀痛，症瘕痞块，肠痛肠肿，红白痢疾。', '孕妇忌服。', '-', '柴胡', '[\'柴胡\', \'青皮\', \'榔片\', \'香附\', \'木香\', \'枳壳\', \'钱酒\', \'芍钱山\', \'栀钱\', \'黄芩\', \'姜夏钱\', \'川芎\', \'神曲\', \'紫补\', \'砂仁\', \'钱广皮\', \'苍术\', \'乌药\', \'茯苓\', \'钱盔\', \'沉钱\', \'当归\', \'甘草\', \'桔梗\', \'莱菔\']');
