INSERT INTO `prescript` VALUES (57116, '36514', '龙荟锭', '柴胡1两，龙胆草1两，黄芩1两，青皮1两，胆星1两，芦荟1两，黄连1两，青黛1两，大黄1两，木通1两，菖蒲1两，皂角1两，细辛1两，全蝎3个，陈小粉（炒黑）5两。', '临用醋磨敷耳1周。有脓日久不干者，加枯矾、雄黄、轻粉、海螵蛸末。', '《理瀹》', '耳鸣耳聋，并治耳痛及一切肝火。', '-', '-', '柴胡', '[\'柴胡\', \'龙胆草\', \'黄芩\', \'青皮\', \'两胆星\', \'芦荟\', \'黄连\', \'两青黛两\', \'大黄\', \'木通\', \'菖蒲\', \'两皂角\', \'细辛\', \'全蝎\', \'小粉\']');
