INSERT INTO `prescript` VALUES (57118, '36451', '加味白术散', '人参8分，白术8分，茯苓8分，甘草（炙）8分，藿香8分，干葛1钱，木香4分，枳壳（麸炒）4分，五味子4分，柴胡4分。', '水煎，食远温服。', '《医统》卷五十二', '中消，消谷善饥。', '-', '-', '柴胡', '[\'人参\', \'白术\', \'茯苓\', \'甘草\', \'炙分\', \'藿香\', \'分干葛钱\', \'木香\', \'枳壳\', \'炒分\', \'五味子\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57119, '36441', '补元散热饮', '人参5分，黄耆5分，白术5分，柴胡1钱2分，黄芩1钱2分，甘草1钱2分，白芍1钱2分，车前子1钱2分，当归1钱2分。', '加灯心30茎，水煎服。', '《玉案》卷三', '元气虚弱，口干发热，小便短赤。', '-', '-', '柴胡', '[\'人参\', \'分黄\', \'耆分\', \'白术\', \'柴胡\', \'钱分\', \'黄芩\', \'钱分\', \'甘草\', \'钱分\', \'白芍\', \'钱分\', \'车前子\', \'钱分\', \'当归\', \'钱分\']');
INSERT INTO `prescript` VALUES (57120, '36425', '安神复元汤', '黄耆（蜜炙）1钱5分，人参1钱5分，当归（酒洗）1钱5分，柴胡1钱，升麻5分，黄连（酒炒）1钱，黄芩（酒炒）1钱，黄柏（酒炒）3钱，知母1钱，防风1钱，蔓荆子7分，麦门冬1钱，茯神1钱，酸枣（炒）1钱5分，川芎1钱，甘草5分，甘枸杞子1钱5分。', '上锉1剂，加龙眼肉3枚，水煎服。', '《寿世保元》卷六', '思虑烦心而神散，精脱于下，真阴不上泥丸，而气不聚，耳鸣耳重听，及耳内痒。', '-', '-', '柴胡', '[\'黄耆\', \'蜜炙钱分\', \'人参\', \'钱分\', \'当归\', \'洗钱\', \'柴胡\', \'升麻\', \'黄连\', \'黄芩\', \'黄柏\', \'知母\', \'防风\', \'蔓荆子\', \'麦门冬\', \'茯神\', \'酸枣\', \'炒钱分\', \'川芎\', \'甘草\', \'分甘\', \'枸杞子\', \'钱分\']');
INSERT INTO `prescript` VALUES (57121, '36400', '防风汤', '防风（去叉）1两半，柴胡（去苗）1两半，白芷1两半，桔梗（炒）1两半，木通（锉）1两半，当归（切，焙）1两半，羌活（去芦头）1两，麻黄（去根节，煎，掠去沫，焙）1两，附子（炮裂，去皮脐）1两，甘草（炙，锉）1两。', '每服5钱匕，水1盏半，煎至8分，去滓温服，食后、临卧各1次。如欲出汗，空心并2服，后投以热姜稀粥，盖复取汗，慎外风。', '《圣济总录》卷一二九', '风毒中人，留血脉不散，与荣卫相搏，结成风疽。身体烦热，昏冒肿痛。', '-', '-', '柴胡', '[\'防风\', \'两半\', \'柴胡\', \'去苗\', \'两半\', \'白芷\', \'两半\', \'桔梗\', \'两半\', \'木通\', \'两半\', \'当归\', \'切焙\', \'两半\', \'羌活\', \'芦头\', \'麻黄\', \'根节\', \'掠去\', \'附子\', \'炮裂\', \'去皮\', \'甘草\']');
INSERT INTO `prescript` VALUES (57122, '36383', '龙胆泻肝汤', '龙胆草3钱，黄芩2钱，山栀子2钱，木通1钱，车前1钱，银柴胡1钱，甘草1钱，当归2钱，生地黄5钱。', '水煎，去滓，下黄蜜3钱，和匀，温服。如伏邪未尽，加蝉蜕7枚，僵蚕2钱。', '《羊毛温证论》', '温邪病退，余毒留于肝肾，胁痛耳聋，口苦咽干，筋痿阴汗，阴囊肿痛，白浊便血，忽寒忽热。', '-', '-', '柴胡', '[\'龙胆草\', \'黄芩\', \'钱山\', \'栀子\', \'木通\', \'钱车\', \'银柴胡\', \'甘草\', \'当归\', \'生地黄\']');
INSERT INTO `prescript` VALUES (57123, '36319', '八风九州岛岛岛岛汤', '麻黄4两（去节），甘草（炙）3两，干姜3两，附子（炮）3两，防风3两，独活3两，石膏（绵裹）2两，茯苓2两，白术2两，芎2两，柴胡2两，当归2两，人参2两，杏仁40枚（去皮尖两仁），细辛2两。', '上切。以水1斗，清酒5升，渍3夜。煮取4升，分为3服，1日令尽。若病人羸瘦者。用水煎服。药讫厚覆，当汗出微微，去上衣，汗解，以粉粉之。', '《外台》卷十四引《古今录验》', '男子、妇人寒冷不自爱护，当风解衣，汗出卧冷湿地，半身不遂，手足苦冷，或不遂，或俯仰屈伸难，周身淫淫痹，四肢不收，状如风狂，饮食损少。', '忌生菜、海藻、菘菜、酢、桃、李、猪肉、雀肉。', '-', '柴胡', '[\'麻黄\', \'两去\', \'甘草\', \'干姜\', \'附子\', \'防风\', \'独活\', \'石膏\', \'绵裹\', \'茯苓\', \'白术\', \'两芎\', \'柴胡\', \'当归\', \'人参\', \'杏仁\', \'皮尖\', \'两仁\', \'细辛\']');
INSERT INTO `prescript` VALUES (57124, '36291', '加味温胆汤', '枳实（麸炒）8两4钱，半夏（汤泡7次）8两4钱，竹茹8两4钱，橘红11两3钱，白茯苓6两3钱，甘草（炙）4两1钱，香附1斤半，人参6两3钱，柴胡6两3钱，麦门冬6两3钱，桔梗6两3钱。', '参胡温胆汤（《杂病源流犀烛》卷六）。', '《袖珍》卷一', '心胆虚怯，触事易惊，梦寐不祥，异象感惑，遂致心惊胆慑，气郁生涎，涎与气搏，变生诸证，或短气悸乏，或复自汗，四肢浮肿，饮食无味，心虚烦闷，坐卧不安。', '-', '-', '柴胡', '[\'枳实\', \'半夏\', \'汤泡次\', \'竹茹\', \'橘红\', \'白茯苓\', \'甘草\', \'香附\', \'斤半\', \'人参\', \'柴胡\', \'麦门冬\', \'桔梗\']');
INSERT INTO `prescript` VALUES (57125, '36228', '加味逍遥散', '当归1钱，芍药（酒炒）1钱，白术1钱，茯苓1钱，甘草（炙）1钱，柴胡1钱，牡丹皮5分，山栀子（炒）5分，钩藤钩5分。', '上(口父)咀，作1服。加生姜3片，薄荷少许，水煎，食远或临卧服。', '《济阳纲目》卷四十五', '肝火亡血，手足瘈疭，及血虚有热。遍身瘙痒。', '-', '-', '柴胡', '[\'当归\', \'芍药\', \'白术\', \'茯苓\', \'甘草\', \'柴胡\', \'牡丹皮\', \'分山\', \'栀子\', \'炒分\', \'钩藤\']');
INSERT INTO `prescript` VALUES (57126, '36220', '加减小柴胡汤', '柴胡钱半，半夏1钱，人参（弱者用之）1钱，甘草1钱，白芍1钱，当归钱半，黄芩钱半。', '水煎，另用百草霜（松柴烧者不用）、血余（即头发，烧灰存性）、蒲黄（炒黑）各3分，研细末，以上汤药调服。或加阿胶。', '《会约》卷四', '伤寒邪热乘肝鼻衄。', '-', '-', '柴胡', '[\'柴胡\', \'钱半\', \'半夏\', \'人参\', \'弱者\', \'甘草\', \'白芍\', \'当归\', \'钱半\', \'黄芩\', \'钱半\']');
INSERT INTO `prescript` VALUES (57127, '36217', '加减小柴胡汤', '柴胡（去苗）1两，黄芩1两，地骨皮半两，人参半两，知母半两，半夏（制）半两，茯苓半两，炙甘草3钱，白芍药8钱。', '上(口父)咀。每服1两，加生姜，水煎服。', '《云歧子脉诀》卷三', '心中恍惚、多悸惊、血虚烦热。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'地骨皮\', \'半两\', \'人参\', \'半两\', \'知母\', \'半两\', \'半夏\', \'半两\', \'茯苓\', \'半两\', \'甘草\', \'钱白\', \'芍药\']');
INSERT INTO `prescript` VALUES (57128, '36203', '加味升麻汤', '升麻5钱，玄参5钱，柴胡5钱，黄芩5钱，干葛4钱，赤芍4钱，独活1钱，甘草2钱。', '每锉3-4钱，水煎服。', '《古今医鉴》卷十四', '预防麻疹。主小儿麻疹初起。', '-', '-', '柴胡', '[\'升麻\', \'玄参\', \'柴胡\', \'黄芩\', \'干葛钱\', \'赤芍\', \'独活\', \'甘草\']');
INSERT INTO `prescript` VALUES (57129, '36202', '加减清经散', '熟地黄5钱，白芍3钱，黄芩3钱，地骨皮4钱，益母草3钱，万年灰3钱，郁金3钱，柴胡3钱，青蒿2钱。', '水煎服。', '《医学探骊集》卷六', '妇女血热，经水先期。', '-', '-', '柴胡', '[\'熟地黄\', \'白芍\', \'黄芩\', \'地骨皮\', \'益母草\', \'万年\', \'灰钱郁\', \'金钱\', \'柴胡\', \'青蒿\']');
INSERT INTO `prescript` VALUES (57130, '36200', '加减柴胡汤', '柴胡1钱，黄芩1钱，知母1钱，炙甘草1钱，茯苓5钱，枳壳5分，神曲5分，萝卜子3钱。', '水煎服。', '《辨证录》卷五', '伤风发潮热，大便溏，小便利，胸膈满，为春温之热留于阳明者。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'知母\', \'甘草\', \'茯苓\', \'枳壳\', \'神曲\', \'萝卜\', \'子钱\']');
INSERT INTO `prescript` VALUES (57131, '36185', '二参汤', '玄参1两，紫参1两，白药1两，大黄（锉，炒）1两，山栀子（去皮）1两，地骨皮（洗，焙）1两，甘草（炙，锉）1两，柴胡（去苗）1两，桑根白皮（锉，炒）1两，防风（去叉）1两。', '每服3钱匕，水1盏，煎至7分，食后去滓温服。', '《圣济总录》卷一二二', '心肺蕴热，咽喉闭塞不通。', '-', '-', '柴胡', '[\'玄参\', \'两紫参\', \'白药\', \'大黄\', \'两山\', \'栀子\', \'去皮\', \'地骨皮\', \'甘草\', \'柴胡\', \'去苗\', \'两桑根\', \'白皮\', \'防风\']');
INSERT INTO `prescript` VALUES (57132, '36175', '老鸡丸', '胡黄连1两，银柴胡1两，人参1两，黄耆1两，熟地1两，川芎1两，远志1两，肉苁蓉1两，秦艽1两，甘草1两，当归1两，山药5钱，白术5钱，五味5钱，天冬1两2钱，麦冬1两2钱。', '每服80-90丸，空心米饮送下。', '《灵验良方汇编》卷下', '妇人下元气虚，五心烦热，食少，子宫冷，赤白带下，经水不调。', '-', '-', '柴胡', '[\'胡黄连\', \'银柴胡\', \'人参\', \'两黄耆\', \'熟地\', \'川芎\', \'远志\', \'肉苁蓉\', \'秦艽\', \'甘草\', \'当归\', \'山药\', \'白术\', \'五味\', \'天冬\', \'麦冬\']');
INSERT INTO `prescript` VALUES (57133, '36148', '白玉锭', '炉甘石（装入银锅，上盖瓦片，火煅起金花，钳出，浸入童便内，片时去童便，又以黄连煎汁，将石飞1-2次，至次日去连水，纸封晒干，再用群药取汁入内）1两，白当归5分，川芎5分，蔓荆子5分，草决明5分，密蒙花5分，柴胡5分，羌活5分，防风5分，薄荷5分，白芷5分，南苍术5分，枯芩5分，木贼5分，尖槟榔5分，蝉蜕5分，芥穗5分，甘草5分，白菊花5分（煎汁，澄1夜，入甘石内，搅数10次，纸糊碗口晒干，为丹头）。', '炼蜜法：用新竹截筒，将白蜜装入内，湿豆腐皮封口，再加盐面封固，布裹，麻绳扎紧，煮一昼夜，取出调药。', '《眼科秘书》卷下', '时行热眼，或暴发赤肿疼痛、隐涩羞明；兼治牙疼，耳底流脓出血，蝎蜇、蛇咬、蜂叮、口疮。', '-', '-', '柴胡', '[\'炉甘石\', \'装入\', \'银锅\', \'瓦片\', \'金花\', \'钳出\', \'浸入\', \'童便\', \'片时\', \'童便\', \'黄连\', \'石飞\', \'次日\', \'连水纸\', \'晒干\', \'再用\', \'群药\', \'取汁入\', \'两白\', \'当归\', \'川芎\', \'蔓荆子\', \'草决明\', \'密蒙花\', \'柴胡\', \'羌活\', \'防风\', \'薄荷\', \'白芷\', \'分南\', \'苍术\', \'分枯芩分\', \'木贼\', \'分尖\', \'槟榔\', \'蝉蜕\', \'分芥穗分\', \'甘草\', \'白菊花\', \'夜入\', \'甘石\', \'数次\', \'纸糊\', \'碗口\', \'晒干\', \'为丹头\']');
INSERT INTO `prescript` VALUES (57134, '36140', '六和汤', '人参1钱，知母1钱，草果1钱，贝母1钱，乌梅1钱，白芷1钱，槟榔1钱，柴胡1钱（用酒拌），常山2钱。', '加生姜3片，大枣1个，水煎服。', '《丹溪心法》卷二', '疟疾。', '-', '-', '柴胡', '[\'人参\', \'知母\', \'草果\', \'贝母\', \'乌梅\', \'白芷\', \'槟榔\', \'柴胡\', \'钱用\', \'常山\']');
INSERT INTO `prescript` VALUES (57135, '36139', '龙胆丸', '龙胆1两，熊胆（研）1分，马牙消（研）半两，朴消（研）半两，山栀子（去皮）半两，玄参半两，人参半两，枳壳（去瓤，麸炒）半两，柴胡（去苗）半两，当归（切，焙）半两，陈橘皮（去白，焙）半两，麝香（研）半钱，沉香半两，甘草（炙，锉）1两，赤茯苓（去黑皮）1两。', '1岁1丸，温水化下。', '《圣济总录》卷一七七', '小儿骨热生风。', '-', '-', '柴胡', '[\'龙胆\', \'熊胆\', \'研分\', \'马牙\', \'消研\', \'半两\', \'朴消研\', \'半两\', \'栀子\', \'去皮\', \'半两\', \'玄参\', \'半两\', \'人参\', \'半两\', \'枳壳\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'当归\', \'切焙\', \'半两\', \'橘皮\', \'半两\', \'麝香\', \'研半钱\', \'沉香\', \'半两\', \'甘草\', \'茯苓\', \'黑皮\']');
INSERT INTO `prescript` VALUES (57136, '36134', '六奇汤', '柴胡（去苗）半两，厚朴（去粗皮，生姜汁炙，锉）半两，枳壳（去瓤，麸炒）半两，白术半两，京三棱（醋浸，炮，锉）1两，白茯苓（去黑皮）1两。', '每服3钱匕，水1盏，加生姜半分(切碎)，同煎至8分，去滓，空心温服。', '《圣济总录》卷八十九', '虚劳羸瘦，日久不瘥；心腹痞满，不思饮食。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'半两\', \'厚朴\', \'粗皮生\', \'姜汁\', \'半两\', \'枳壳\', \'半两\', \'白术\', \'半两\', \'三棱\', \'浸炮\', \'白茯苓\', \'黑皮\']');
INSERT INTO `prescript` VALUES (57137, '36098', '白术散', '白术2两，人参（去芦）1两，白茯苓（去黑皮）1两，黄耆1两，山药3分，百合3分（去心），甘草（炙）半两，前胡（去芦）1分，柴胡（去芦）1分。', '方中山药用量原缺。本方改为丸剂，名“白术丸”（见《中国医学大辞典》）。', '《百一》卷六', '行营卫，顺气止血，进食退热。主吐血、咯血,脾肺气虚。', '忌食热面、煎炙、海味、猪、鸡一切发风之物；酒不宜，饮食不宜饱。', '-', '柴胡', '[\'白术\', \'人参\', \'去芦\', \'白茯苓\', \'黑皮\', \'两黄耆\', \'山药\', \'百合\', \'分去\', \'甘草\', \'半两\', \'胡去\', \'芦分\', \'柴胡\', \'芦分\']');
INSERT INTO `prescript` VALUES (57138, '36091', '加减乌金散', '柴胡8钱，桂枝两半，黄芩两半（酒炒），厚朴两半，白芍两半（酒炒），半夏两半（制），甘草5钱，生姜10片，葱白10枚。', '每服3钱，水煎，去滓温服。', '《医略六书》卷三十', '产后寒伤腠理，热遏胸中，寒热往来，胸满呕恶，脉数弦滞者。', '-', '-', '柴胡', '[\'柴胡\', \'桂枝\', \'两半\', \'黄芩\', \'两半\', \'厚朴\', \'两半\', \'白芍\', \'两半\', \'半夏\', \'两半\', \'甘草\', \'生姜\', \'葱白\']');
INSERT INTO `prescript` VALUES (57139, '36084', '加减八物柴胡汤', '人参3钱，茯苓1钱，白芍1钱，地黄1钱，知母1钱，麦冬1钱，柴胡1钱，炙甘草5分。', '《医钞类编》有归身、淡竹叶。如有汗，加丹皮、淡竹叶；如热甚，加炒黑干姜1钱。', '《女科秘要》卷四', '经闭不行。血闭不行，骨蒸潮热，脉虚者。', '-', '-', '柴胡', '[\'人参\', \'茯苓\', \'白芍\', \'地黄\', \'知母\', \'麦冬\', \'柴胡\', \'甘草\']');
INSERT INTO `prescript` VALUES (57140, '36077', '加减逍遥散', '茯苓5钱，白芍（酒炒）5钱，甘草（生用）5钱，柴胡1钱，茵陈3钱，陈皮1钱，桅子3钱（炒）。', '水煎服。', '《傅青主女科》卷上', '妇人青带。带下色青，甚则如绿豆汁，稠粘不断，其气腥臭。', '-', '-', '柴胡', '[\'茯苓\', \'白芍\', \'甘草\', \'用钱\', \'柴胡\', \'茵陈\', \'陈皮\', \'桅子\']');
INSERT INTO `prescript` VALUES (57141, '36075', '加减丹栀逍遥散', '白芍3钱，柴胡2钱，茯苓3钱，白术3钱，丹皮2钱，山栀3钱，甘草1钱，焦艾3钱，益母草4钱。', '水煎服。血色深红，量多如泉涌者，加泡参、乌贼骨各1两；出血有热感心烦躁者，加生地5钱。', '《中医妇科治疗学》', '行气解郁。主崩漏由于肝气郁结，暴崩下血，或淋漓不止，色紫兼有血块，少腹胀痛，兼见精神抑郁，胸胁胀满，脉弦数。', '-', '-', '柴胡', '[\'白芍\', \'柴胡\', \'茯苓\', \'白术\', \'丹皮\', \'钱山\', \'栀钱\', \'甘草\', \'焦艾钱\', \'益母草\']');
INSERT INTO `prescript` VALUES (57142, '36044', '加减柴陈汤', '柴胡根1钱，黄芩钱半，炙甘草5分，当归身3钱，青蒿根2钱，赤茯苓3钱，制半夏钱半，陈皮1钱，生姜1片。', '无汗，加羌活1钱，防风1钱，川芎1钱；汗多，加生牡蛎3钱；寒重，加桂枝8分，干姜5分，甚则加制附子，去生姜、黄芩；热重，加竹叶30片。生石膏5钱，或玄参、知母之类；气虚，加人参1钱、黄耆2钱，冬白术钱半；阴亏，加大生地4钱，或麦冬之类；实者，加槟榔1钱，厚朴8分，草果仁5分；便秘，加大黄3钱、玄明粉2钱；溲秘，加车前子3钱，木通1钱；久疟，加鳖甲3钱、怀牛膝3钱；痰疟，加酒炒常山3钱；食疟，加麦芽2钱，神曲2钱，或谷芽、山楂之类；春，加防风1钱，苏梗1钱；夏，加香薷1钱，川黄连8分；秋，加青皮1钱；冬，加', '《医略十三篇》卷九', '痎疟。', '-', '-', '柴胡', '[\'柴胡\', \'根钱\', \'黄芩\', \'钱半\', \'甘草\', \'当归\', \'身钱\', \'青蒿\', \'根钱\', \'茯苓\', \'钱制\', \'半夏\', \'钱半\', \'陈皮\', \'生姜\']');
INSERT INTO `prescript` VALUES (57143, '35942', '加减保阴煎', '生地2钱，黄芩1钱，白芍（酒炒）1钱5分，柴胡1钱5分，丹皮1钱，甘草1钱或加地骨皮2钱。', '-', '《不知医必要》卷四', '凉血。主因伤寒劳役，怒气而发热，适遇经行，以致热入血室，或血不止，或血不行，令人昼则明了安静，夜则（讠占）语如见鬼神。', '-', '-', '柴胡', '[\'生地\', \'黄芩\', \'白芍\', \'钱分\', \'柴胡\', \'钱分\', \'丹皮\', \'甘草\', \'或加\', \'地骨皮\']');
INSERT INTO `prescript` VALUES (57144, '35941', '龙胆泻肝丸', '龙胆草2两，柴胡4钱，当归8钱，泽泻8钱，木通6钱，蝉蜕3钱，车前子5钱，栀子7钱，黄芩7钱，生地1两，甘草4钱。', '每服3钱。', '《眼科临证笔记》', '蟹睛症（虹膜凸出）、鱼子石榴症（滤泡性结膜炎）。', '-', '-', '柴胡', '[\'龙胆草\', \'柴胡\', \'当归\', \'泽泻\', \'木通\', \'蝉蜕\', \'车前子\', \'栀子\', \'黄芩\', \'生地\', \'甘草\']');
INSERT INTO `prescript` VALUES (57145, '35873', '加味补中益气汤', '人参1钱，黄耆1钱，当归1钱，白术1钱，陈皮5分，甘草5分，升麻5分，柴胡5分，防风5分，白芷5分，川芎5分。', '加生姜、大枣，水煎服。', '《医统》卷五十五', '中气不足，卫气不舒，致患搔痒。', '-', '-', '柴胡', '[\'人参\', \'黄耆\', \'当归\', \'白术\', \'陈皮分\', \'甘草\', \'升麻\', \'柴胡\', \'防风\', \'白芷\', \'川芎\']');
INSERT INTO `prescript` VALUES (57146, '35853', '加味托里散', '人参1钱，黄耆（盐水拌，炒）1钱，当归（酒拌）1钱，川芎1钱，麦门冬（去心）1钱，知母（酒拌，炒）1钱，黄柏（酒拌，炒）1钱，芍药（炒）1钱，金银花1钱，柴胡1钱，甘草（水1钟，浸透，以慢火炙）1钱。', '上作1剂，用水2钟，煎8分，食前服。', '《外科经验方》', '悬痈不消不溃。', '-', '-', '柴胡', '[\'人参\', \'黄耆\', \'盐水\', \'当归\', \'酒拌\', \'川芎\', \'麦门冬\', \'心钱\', \'知母\', \'酒拌\', \'黄柏\', \'酒拌\', \'芍药\', \'金银\', \'花钱\', \'柴胡\', \'甘草\', \'水钟\', \'浸透\', \'慢火\']');
INSERT INTO `prescript` VALUES (57147, '35835', '补中益气汤', '黄耆2两，人参1钱，炙草8分，半夏1两，炒白芍5钱，独活5钱，防风5钱，炒白术3钱，茯苓3钱，泽泻3钱，柴胡3钱，连翘2钱，羌活1钱半。', '加生姜3片、大枣2枚，水煎服。', '《梅氏验方新编》卷六', '损伤后气虚感邪，脓出不止，疮口白肉突出者。', '-', '-', '柴胡', '[\'黄耆\', \'人参\', \'草分\', \'半夏\', \'白芍\', \'独活\', \'防风\', \'白术\', \'茯苓\', \'泽泻\', \'柴胡\', \'连翘\', \'羌活\', \'钱半\']');
INSERT INTO `prescript` VALUES (57148, '35831', '补中益气汤', '黄耆1钱5分，甘草（炙）1钱，人参1钱，当归1钱，白术1钱，升麻3分，柴胡3分，陈皮3分，麦门冬6分，五味子（炒）5分。', '水2钟，加生姜3片，大枣2枚，煎1钟，空心热服。', '《外科正宗》卷一', '疮疡元气不足，四肢倦怠，口干发热，饮食无味，或饮食失节，或劳倦身热，脉洪大而无力，或头痛而恶寒，或声高而喘，身热而烦。', '-', '-', '柴胡', '[\'黄耆\', \'钱分\', \'甘草\', \'人参\', \'当归\', \'白术\', \'升麻\', \'柴胡\', \'陈皮分\', \'麦门冬\', \'五味子\', \'炒分\']');
INSERT INTO `prescript` VALUES (57149, '35821', '白鲜皮散', '白鲜皮2两，黄连1两半（去须），土瓜根1两半，芍药1两半，大青1两半，栀子仁1两半，茵陈蒿1两半，栝楼根1两半，柴胡（去苗）1两半，芒消3两半（研入），贝珠30枚（烧赤，研入），黄芩（去黑心）1两，大黄3两。', '每服3钱匕，煎茅根汁调下，空腹顿服。取利；至晚不利再服，服了以少葱豉粥投之。', '《圣济总录》卷六十', '诸黄。皮肉如金色，小便赤黑，口干烦渴。', '-', '-', '柴胡', '[\'白鲜皮\', \'黄连\', \'两半\', \'土瓜根\', \'两半\', \'芍药\', \'两半\', \'大青\', \'两半\', \'栀子\', \'两半\', \'茵陈蒿\', \'两半\', \'栝楼\', \'两半\', \'柴胡\', \'去苗\', \'两半\', \'芒消\', \'两半\', \'研入\', \'贝珠\', \'研入\', \'黄芩\', \'黑心\', \'大黄\']');
INSERT INTO `prescript` VALUES (57150, '35763', '加味小柴胡汤', '小柴胡汤加竹茹2钱，生姜重用3钱。', '水煎服。', '《会约》卷十', '少阳证，耳聋胁痛，干呕，潮热。', '-', '-', '柴胡', '[\'小柴胡\', \'汤加\', \'竹茹\', \'生姜\', \'重用\']');
INSERT INTO `prescript` VALUES (57151, '35743', '开闭丹', '黄耆1两，当归5钱，肉桂5分，甘草5分，菖蒲1钱，远志1钱，柴胡1钱，香附1钱，天花粉2钱。', '水煎服。', '《辨证录》卷三', '耳痛之后虽愈而耳鸣如故，以手按其耳则其鸣少息，证属阳虚气闭者。', '-', '-', '柴胡', '[\'黄耆\', \'当归\', \'肉桂\', \'甘草\', \'菖蒲\', \'远志\', \'柴胡\', \'香附\', \'天花粉\']');
INSERT INTO `prescript` VALUES (57152, '35733', '灵感丸', '柴胡（去苗）半两，防风（去又）半两，紫菀（去苗土）半两，当归（切，焙）半两，人参半两，赤茯苓（去黑皮）半两，干姜（炮裂）半两，桔梗（炒）半两，菖蒲半两，乌头（炮裂，去皮脐）半两，厚朴（去粗皮，生姜汁炙，锉）半两，大黄半两，吴茱萸（汤洗，焙干）半两，皂荚（去皮子、酥炙）半两，蜀椒（去目并闭口，炒出汗）半两，陈橘皮（去白，炒）半两，郁李仁（别研）半两，黄连（去须，炒）半两，巴豆半两（去油，研）。', '每服5丸，空心酒饮送下，取微利为度。如风冷气人，长服此药最佳。又宜夜服。', '《圣济总录》卷九十一', '虚劳积聚，腹胁坚满；男子、妇人一切风劳冷气，头旋眼疼，手脚（？？）痹；血风劳气，攻击五脏四肢，筋脉掉动，面上习习似虫行；遍生疮癣；心膈烦闷，腹痛虚鸣，腰疼膝冷，手足或冷或热；诸气刺痛，呕逆醋心，肠胃秘涩，肺气发动，耳复虚鸣，脚膝无力；仍治妇人病，冷血劳气，发损面黄，气刺心腹，骨筋酸痛，经脉不调，经年逾月，或下过多不定；兼治冷热诸痢，脚气水肿等。', '-', '-', '柴胡', '[\'柴胡\', \'去苗\', \'半两\', \'防风\', \'半两\', \'紫菀\', \'苗土\', \'半两\', \'当归\', \'切焙\', \'半两\', \'人参\', \'半两\', \'茯苓\', \'黑皮\', \'半两\', \'干姜\', \'炮裂\', \'半两\', \'桔梗\', \'半两\', \'菖蒲\', \'半两\', \'乌头\', \'炮裂\', \'去皮\', \'半两\', \'厚朴\', \'粗皮生\', \'姜汁\', \'半两\', \'大黄\', \'半两\', \'吴茱萸\', \'汤洗\', \'焙干\', \'半两\', \'皂荚\', \'皮子\', \'半两\', \'蜀椒\', \'闭口\', \'出汗\', \'半两\', \'橘皮\', \'半两\', \'郁李仁\', \'别研\', \'半两\', \'黄连\', \'半两\', \'巴豆\', \'半两\', \'油研\']');
INSERT INTO `prescript` VALUES (57153, '35687', '加味逍遥散', '柴胡2钱，白芍5钱，当归3钱，陈皮5钱，甘草1钱，白术3钱，茯神3钱，人参1钱，川芎1钱，瓜蒌3钱，半夏3钱。', '水煎服。', '《外科证治全书》卷三', '乳悬。肝气不舒，痰气郁结，乳内忽大如桃，不觉痛痒，色亦不赤，身体发热，形渐瘦损。', '-', '-', '柴胡', '[\'柴胡\', \'白芍\', \'当归\', \'陈皮\', \'甘草\', \'白术\', \'茯神\', \'人参\', \'川芎\', \'钱瓜\', \'半夏\']');
