INSERT INTO `prescript` VALUES (57161, '35565', '开邪散', '白术5钱，茯苓5钱，前胡1钱，柴胡1钱，甘草5分，猪苓2钱，人参1钱，青皮1钱，枳壳1钱，白豆蔻3分，山楂1钱，半夏1钱。', '水煎服。', '《辨证录》卷八', '健脾胃之土，散太阳之邪，消痰化食。主发疟先腰痛头疼且重，寒从背起，先寒后热，热如火炽，热止汗出，不能即干，遍身骨节无不酸痛，小便短赤，证属太阳膀胱经之疟者。', '-', '-', '柴胡', '[\'白术\', \'茯苓\', \'钱前\', \'胡钱\', \'柴胡\', \'甘草\', \'猪苓\', \'人参\', \'青皮\', \'枳壳\', \'白豆蔻\', \'山楂\', \'半夏\']');
INSERT INTO `prescript` VALUES (57162, '35531', '加味逍遥散', '柴胡6钱（盐水炒），白芍2两（炒），白术1两半（制），当归3两，茯苓1两，炙草4钱，山栀2两（炒），丹皮1两半，蛤壳3两（生研）。', '白雷丸3钱，煎汤调下3钱。', '《医略六书》卷二十六', '阴痒，脉弦虚数。', '-', '-', '柴胡', '[\'柴胡\', \'盐水\', \'白芍\', \'白术\', \'两半\', \'当归\', \'茯苓\', \'两炙草\', \'钱山\', \'丹皮\', \'两半\', \'蛤壳\', \'两生研\']');
INSERT INTO `prescript` VALUES (57163, '35480', '冬葵子散', '冬葵子（炒）1两，柴胡（去苗）1两，桑根白皮（锉）1两，赤茯苓1两，赤芍药3分，当归3分（锉，微炒）。', '冬葵子汤（《医方考》卷六）。', '《圣惠》卷七十四', '妊娠胎不安，小便淋涩，小腹疼痛。', '-', '-', '柴胡', '[\'冬葵子\', \'柴胡\', \'去苗\', \'两桑根\', \'白皮\', \'茯苓\', \'两赤\', \'芍药\', \'当归\']');
INSERT INTO `prescript` VALUES (57164, '35376', '龙胆汤', '龙胆（去根）1分，冬葵子1分，萎蕤1分，大青1分，柴胡（去苗）1分，赤茯苓（去黑皮）半两，甘草（炙）半两。', '每服1钱匕，以水半盏，煎至3分，去滓，分3次服，如人行10里已来1服。', '《圣济总录》卷一六八', '小儿生40-50日，服药下后，身体壮热如火，伤寒兼腹满，头面丹肿，此皆内有伏热。', '-', '-', '柴胡', '[\'龙胆\', \'根分\', \'冬葵子\', \'萎蕤\', \'分大青分\', \'柴胡\', \'苗分\', \'茯苓\', \'黑皮\', \'半两\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (57165, '35338', '加味逍遥散', '软柴胡5分，白芍药1钱半（酒炒），冬白术1钱半（炒），当归身2钱，白茯苓2钱（去木），粉甘草5分，钩藤钩5钱，忍冬藤3钱，山栀、丹皮。', '水煎，去滓温服。', '《医略六书》卷十八', '女子血虚火旺，经闭潮热；男子阴虚木旺，脉弦虚数者。', '-', '-', '柴胡', '[\'柴胡\', \'分白\', \'芍药\', \'钱半酒\', \'炒冬\', \'白术\', \'钱半\', \'当归\', \'身钱\', \'白茯苓\', \'木粉\', \'甘草\', \'钩藤\', \'忍冬藤\', \'钱山\', \'丹皮\']');
INSERT INTO `prescript` VALUES (57166, '35334', '加味四逆散', '川柴胡8分，炒枳实1钱，生白芍1钱，清炙草8分，干姜5分（拌捣北5味3分），桂枝尖5分，浙茯苓4钱，干薤白5枚（烧酒洗，捣），淡附片5分。', '用水2碗，煎成1碗，去滓温服。', '《重订通俗伤寒论》', '达郁通阳。主伤寒邪传少阴，火为水遏，阳气内郁，不得外达，水气上冲而下注，致四肢厥逆，干咳心悸，便泄溺涩，腹痛下重，舌苔白而底绛，脉左沉弦而滑，右弦急。', '-', '-', '柴胡', '[\'柴胡\', \'枳实\', \'钱生\', \'白芍\', \'清炙草分\', \'干姜\', \'分拌\', \'北味分\', \'桂枝\', \'尖分\', \'茯苓\', \'薤白\', \'烧酒\', \'捣淡\', \'附片\']');
INSERT INTO `prescript` VALUES (57167, '35267', '加味补中益气汤', '黄耆1钱，人参1钱，白术1钱，甘草（炙）1钱，当归7分，陈皮7分，升麻3分，柴胡3分，肉豆蔻5分，补骨脂5分。', '水煎服。', '《济阴纲目》卷十四', '脾肾虚寒，大便不禁。', '-', '-', '柴胡', '[\'黄耆\', \'人参\', \'白术\', \'甘草\', \'当归\', \'陈皮分\', \'升麻\', \'柴胡\', \'肉豆蔻\', \'补骨脂\']');
INSERT INTO `prescript` VALUES (57168, '35243', '加味败毒散', '黄芩1钱，半夏1钱，桔梗1钱，薄荷1钱，人参1钱，独活1钱，柴胡1钱，羌活1钱，枳壳1钱，茯苓1钱，甘草1钱，川芎1钱，前胡1钱。', '加生姜3片，水煎服。痰甚，加石膏。', '《医林绳墨大全》卷八', '咽喉风燥，干枯如毛刺，吞咽有碍。', '-', '-', '柴胡', '[\'黄芩\', \'半夏\', \'桔梗\', \'薄荷\', \'人参\', \'独活\', \'柴胡\', \'羌活\', \'枳壳\', \'茯苓\', \'甘草\', \'川芎\', \'钱前\', \'胡钱\']');
INSERT INTO `prescript` VALUES (57169, '35211', '半夏汤', '半夏半两（生姜汁淹浸1宿，切，焙），贝母（去心）1两，柴胡（去苗）1两，猪牙皂荚（炙，去皮）半两，甘草（炙）半两。', '每服3钱匕，水1盏，加生姜5片，同煎7分，去滓温服，不拘时候。', '《圣济总录》卷一六四', '产后咳嗽痰壅。', '-', '-', '柴胡', '[\'半夏\', \'半两\', \'姜汁\', \'淹浸\', \'宿切\', \'贝母\', \'柴胡\', \'猪牙皂\', \'去皮\', \'半两\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (57170, '35189', '加减补中益气汤', '人参5钱，黄耆3钱（生用），柴胡1钱，甘草1分，当归3钱（酒洗），白术5钱（土炒），茯苓1两，升麻3分，陈皮3分。', '水煎服。', '《傅青主女科》卷下', '妊娠5月，脾肺气虚，肢体倦怠，饮食无味，先两足肿，渐至头面遍身俱肿。', '-', '-', '柴胡', '[\'人参\', \'黄耆\', \'生用\', \'柴胡\', \'甘草\', \'当归\', \'钱酒\', \'白术\', \'钱土\', \'茯苓\', \'升麻\', \'陈皮分\']');
INSERT INTO `prescript` VALUES (57171, '35129', '六物汤', '嫩常山2钱半，柴胡2钱，鸡心槟榔2钱，青皮（去白）2钱，草果仁1钱半，甘草（炙）1钱半。', '每服用大软乌梅2个，好夏酒准1啜许，新水2盏，煎半，隔宿露空，以纱盖之，次早拂明服。若寒热等，加制厚朴2钱，略暖服，是日饮食皆勿用热。', '《直指》卷十二', '久疟不已，寒少热多。', '-', '-', '柴胡', '[\'常山\', \'钱半\', \'柴胡\', \'鸡心\', \'槟榔\', \'青皮\', \'白钱\', \'果仁\', \'钱半\', \'甘草\']');
INSERT INTO `prescript` VALUES (57172, '35106', '加味调经丸', '香附5斤（分5分，1斤用盐水浸，1斤用醋浸，1斤用童便浸，1斤用无灰酒浸，1斤用米泔水浸，每样春3日、夏2日、秋5日、冬10日，仍用原水煎，不犯铁器，晒干，用葱5斤，取白切细，拌香附焙干，以葱白黄香为度），当归4两，白芍4两，生地4两，青皮1两5钱，黄连3两，黄芩3两，川芎2两，杏仁2两，柴胡2两，白芷2两5钱，滑石（水飞净）5两，荆芥5两。', '每服80丸，空心白汤送下。', '《女科秘要》卷八', '妇人血热，经水先期，气旺痰火者，服本方易孕。', '-', '-', '柴胡', '[\'香附\', \'斤分\', \'分斤\', \'水浸\', \'浸斤\', \'童便\', \'浸斤\', \'无灰酒\', \'浸斤\', \'米泔水\', \'每样\', \'春日\', \'夏日\', \'秋日\', \'冬日\', \'仍用\', \'原水\', \'不犯\', \'铁器\', \'晒干\', \'用葱斤\', \'取白\', \'切细\', \'香附\', \'焙干\', \'葱白\', \'黄香\', \'为度\', \'当归\', \'白芍\', \'生地\', \'青皮\', \'黄连\', \'黄芩\', \'川芎\', \'杏仁\', \'柴胡\', \'白芷\', \'滑石\', \'水飞净\', \'荆芥\']');
INSERT INTO `prescript` VALUES (57173, '35098', '地黄丸', '生干地黄2两，地骨皮1两1分，麦门冬（去心，焙）1两1分，柴胡（去苗）1两1分，枳壳（去瓤，麸炒）1两1分，赤芍药1两1分，黄连（去须）1两1分，羚羊角（屑）1两1分，桃仁（汤浸，去皮尖双仁，炒）1两1分，百合1两1分，桔梗（炒）1两1分，郁李仁（汤浸，去皮，炒）1两，玄参1两，槟榔（锉）1两，茯神（去木）1两。', '每服20丸至30丸，煎茯苓汤送下。', '《圣济总录》卷一五○', '妇人血风劳气，头项筋急疼痛，咽喉干，脐腹痛，四肢无力，血脏经脉不调。', '-', '-', '柴胡', '[\'生干\', \'地黄\', \'地骨皮\', \'两分\', \'麦门冬\', \'两分\', \'柴胡\', \'去苗\', \'两分\', \'枳壳\', \'两分\', \'芍药\', \'两分\', \'黄连\', \'两分\', \'羚羊角\', \'两分\', \'桃仁\', \'汤浸\', \'皮尖\', \'双仁\', \'两分\', \'百合\', \'两分\', \'桔梗\', \'两分\', \'郁李仁\', \'汤浸\', \'去皮\', \'玄参\', \'槟榔\', \'茯神\']');
INSERT INTO `prescript` VALUES (57174, '35085', '白虎去石膏加首乌当归和疟饮', '知母3钱，生甘草1钱，鲜首乌8钱，当归5钱，青蒿3钱，柴胡1钱，青皮1钱5分，草寇仁1钱（研）。', '河、井水各半煎，露1宿，疟前2时服之。1服热除，2服疟去其8分，3服全愈。如热重烦躁口渴，加石膏8钱；恶心，加广藿香3钱；胸口饱闷作胀，加槟榔1钱5分，炒枳壳1钱5分。', '《慈航集》卷下', '瘁疟初病，但热不寒，邪气内藏于心，外舍分肉之间，肌肉消烁，脉弦有力者。', '-', '-', '柴胡', '[\'知母\', \'生甘草\', \'钱鲜\', \'首乌\', \'当归\', \'青蒿\', \'柴胡\', \'青皮\', \'钱分\', \'草寇\', \'仁钱研\']');
INSERT INTO `prescript` VALUES (57175, '35070', '后天和疟饮', '甜白术1钱（土炒），云苓1钱，制半夏1钱，炒枳壳6分，甘草2分，青皮5分，柴胡3分，草豆蔻8分（研）。', '疟前2时服。如恐有不止者，加入川贝母1成，知母8分，黑豆49粒；疟愈，去柴胡、青皮，加炒五谷虫1钱，陈皮3分，炒神曲5分；发烧无汗，加葛根1钱5分；有汗热重，加青蒿3钱；口渴，加花粉1钱；心热发烦，加连翘8分；痰多，加姜汁炒白僵蚕2钱；恶心，加灶心土3钱；如作泻，加炒白芍2线，车前子1钱；如大便结，加当归2钱，炒麦芽（研粉）1钱5分；如脉数洪大，实热，加炒黄芩5分；如面红赤，加酒炒川连1步。', '《慈航集》卷三', '培土和解。主小儿胎疟。', '服药后不可即吃乳食。', '-', '柴胡', '[\'白术\', \'钱土\', \'炒云苓\', \'钱制\', \'半夏\', \'枳壳\', \'甘草\', \'青皮\', \'柴胡\', \'草豆蔻\', \'分研\']');
INSERT INTO `prescript` VALUES (57176, '35017', '加味四物汤', '当归1两，地黄1两，芍药1两，川芎1两，柴胡半两，黄芩2钱半。', '《御药院方》本方用法：上为粗末。每服四钱，水一盏半，入乌梅半枚，同煎至一大盏，去滓，食后温服。', '《产乳备要》', '妇人冲任不调，脐腹疼痛，月事入时不来，及冲任太过，致使阴阳不和，或发寒热，渐减饮食，欲成劳病;冲任虚损，月水不行，肌肤发热如瘵状。', '-', '-', '柴胡', '[\'当归\', \'两地\', \'黄两\', \'芍药\', \'川芎\', \'柴胡\', \'半两\', \'黄芩\', \'钱半\']');
INSERT INTO `prescript` VALUES (57177, '35003', '加味六君子汤', '陈皮1钱，半夏1钱，苍术（米汁水浸）1钱，人参1钱，白术1钱5分，白茯苓1钱2分，炙草7分，升麻5分，柴胡5分。', '原书治上证，兼眼苍莎导痰丸。', '《万氏女科》卷一', '白带，年久不止者。白带属湿痰而兼虚者。', '-', '-', '柴胡', '[\'陈皮\', \'半夏\', \'苍术\', \'米汁\', \'水浸\', \'人参\', \'白术\', \'钱分\', \'白茯苓\', \'钱分\', \'草分\', \'升麻\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57178, '34991', '地黄汤', '熟干地黄（焙）1两，附子（炮裂，去皮脐）1两，当归（切，焙）1两，人参3分，柴胡（去苗）3分，白茯苓（去黑皮）3分，芎穷3分，肉苁蓉（切，酒洗，焙）1两，黄耆（锉）1两，芍药3分。', '每服2钱匕，水1盏，煎至7分，去滓温服，不拘时候。', '《圣济总录》卷一六三', '产后虚热不解，烦倦无力，困瘁。', '-', '-', '柴胡', '[\'熟干\', \'地黄\', \'附子\', \'炮裂\', \'去皮\', \'当归\', \'切焙\', \'人参\', \'柴胡\', \'苗分\', \'白茯苓\', \'黑皮\', \'分芎\', \'穷分\', \'肉苁蓉\', \'切酒\', \'黄耆\', \'芍药\']');
INSERT INTO `prescript` VALUES (57179, '34937', '加味四苓汤', '茯苓3钱，白术1钱半（炒黑），猪苓1钱半，柴胡梢5分，泽泻1钱半，青皮1钱半（炒），陈皮1钱半，橘核3钱（炒）。', '水煎，去滓温服。', '《医略六书》卷二十六', '女子前阴漫肿，脉弦者。', '-', '-', '柴胡', '[\'茯苓\', \'白术\', \'钱半\', \'猪苓\', \'钱半\', \'柴胡\', \'泽泻\', \'钱半\', \'青皮\', \'钱半\', \'陈皮\', \'橘核\']');
INSERT INTO `prescript` VALUES (57180, '34914', '加减阴疸汤', '人参2钱，软柴胡1钱，白芍（酒炒）8分，炙甘草8分，干姜1钱，法夏1钱，西党参（姜汁炒）3钱。', '阳亏者，加桂、附；阴虚者，加归身、熟地；微热者，加丹皮。', '《胎产秘书》卷下', '产后疸症，汗黄、眼黄，便黄如金汁者。', '忌茵陈清寒等药。', '-', '柴胡', '[\'人参\', \'钱软\', \'柴胡\', \'白芍\', \'炒分\', \'甘草\', \'干姜\', \'钱法\', \'夏钱西\', \'党参\', \'姜汁\']');
INSERT INTO `prescript` VALUES (57181, '34885', '地骨皮散', '地骨皮1两，柴胡1两（去苗），人参2两（去芦头），羚羊角屑1两，甘草1两（炙微赤，锉）。', '每服4钱，以水1中盏，煎至5分，去滓，加生地黄汁半合，不拘时候温服。', '《圣惠》卷五十五', '髓黄，体赤黄，四肢不举，肌肉战掉，鼻中出血，两脚疼闷，一手专安额上，身不壮热，爱卧冷处。', '-', '-', '柴胡', '[\'地骨皮\', \'柴胡\', \'两去\', \'人参\', \'两去\', \'芦头\', \'羚羊角\', \'甘草\', \'两炙微\']');
INSERT INTO `prescript` VALUES (57182, '34867', '决明散', '石决明8分，草决明8分，防风8分，赤芍8分，柴胡8分，白芷8分，菊花1钱2分，川芎5分，羌活5分，蒺藜5分，红花5分，当归1钱，栀子6分。', '加姜皮些须，灯心20寸，水2钟，煎8分，食远温服。', '《眼科阐微》卷三', '目珠被物撞打，疼痛，眼眶停留瘀血。', '-', '-', '柴胡', '[\'石决明\', \'草决明\', \'防风\', \'赤芍\', \'柴胡\', \'白芷\', \'菊花\', \'钱分\', \'川芎\', \'羌活\', \'蒺藜\', \'红花\', \'当归\', \'栀子\']');
INSERT INTO `prescript` VALUES (57183, '34861', '归芍汤', '当归25g，白芍25g，柴胡6g，川朴6g，枳壳6g，槟榔6g，芒消10g，丹皮10g，黄芩6g，川连6g，公英18g，没药6g，阿胶10g，三仙各10g。', '水煎服，每日1-2剂，口服或胃管注入。腹胀重，大便秘结，加大黄，重用芒消；腹腔感染重，发烧者，加金银花、连翘、白花蛇舌草。', '《急腹症方药新解》', '理气消胀，清热通便，凉血消肿。主腹腔感染所致的麻痹性肠梗阻，腹部手术后肠麻痹。', '-', '-', '柴胡', '[\'当归\', \'白芍\', \'柴胡\', \'川朴\', \'枳壳\', \'槟榔\', \'芒消\', \'丹皮\', \'黄芩\', \'川连公英\', \'没药\', \'阿胶\', \'三仙\']');
INSERT INTO `prescript` VALUES (57184, '34786', '加减清毒拨翳汤', '生地1钱5分，甘菊7分，归尾8分，川芎6分，柴胡8分，红花5分，草决明1钱（研），木贼5分，白蒺藜1钱（炒、研），黄芩7分（酒炒），牛蒡子7分（炒，研），连翘7分（去心），生甘草2分（去皮）。', '-', '《痘疹定论》卷四', '疹后患生目翳，眼赤红肿，眼皮烂者。', '-', '-', '柴胡', '[\'生地\', \'钱分\', \'甘菊\', \'分归尾分\', \'川芎\', \'柴胡\', \'红花\', \'草决明\', \'钱研\', \'木贼\', \'分白\', \'蒺藜\', \'炒研\', \'黄芩\', \'分酒\', \'牛蒡子\', \'炒研\', \'连翘\', \'分去\', \'生甘草\', \'去皮\']');
INSERT INTO `prescript` VALUES (57185, '34777', '加味柴胡汤', '柴胡2钱，黄芩2钱，牡蛎1钱，半夏1钱，枳实1钱，甘草1钱。', '加生姜、大枣，水煎服。', '《证治汇补》卷六引《良方》', '伤寒少阳证，胁痛。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'牡蛎\', \'半夏\', \'枳实\', \'甘草\']');
INSERT INTO `prescript` VALUES (57186, '34715', '人参蛤蚧散', '蛤蚧1对（蜜炙），人参（去芦头）半两，百部半两，款冬花（去梗）半两，贝母（去心）半两，紫菀茸半两，阿胶（蛤粉炒）1分，柴胡（去苗）1分，肉桂（去粗皮）1分，黄耆（蜜炙）1分，甘草（炙）1分，鳖甲（醋炙）1分，杏仁（汤浸，去皮尖）1分，半夏（生姜汁制）1分。', '本方方名，《普济方》引作“蛤蚧散”。', '《杨氏家藏方》卷十', '虚劳咳嗽咯血，潮热盗汗，不思饮食。', '肉桂虽去风寒，有热人不宜服，则当改用细辛。', '-', '柴胡', '[\'蛤蚧\', \'对蜜\', \'人参\', \'芦头\', \'半两\', \'百部\', \'半两\', \'款冬花\', \'半两\', \'贝母\', \'去心\', \'半两\', \'紫菀\', \'半两\', \'阿胶\', \'蛤粉\', \'炒分\', \'柴胡\', \'苗分\', \'肉桂\', \'粗皮分\', \'黄耆\', \'蜜炙分\', \'甘草\', \'炙分\', \'鳖甲\', \'醋炙分\', \'杏仁\', \'汤浸\', \'皮尖\', \'半夏\', \'姜汁\', \'制分\']');
INSERT INTO `prescript` VALUES (57187, '34690', '护骨散', '牛膝3钱，丹皮3钱，金钗石斛2钱，山芋2钱，熟地5钱，白术5钱，当归5钱，柴胡1钱，天花粉1钱。', '水煎服。', '《辨证录》卷九', '人有争强好斗，或赤身不顾，或流血不知，以致筋骨内伤，风入皮肤，畏寒发热，头疼胁痛。', '-', '-', '柴胡', '[\'牛膝\', \'丹皮\', \'金钗\', \'石斛\', \'山芋\', \'熟地\', \'白术\', \'当归\', \'柴胡\', \'天花粉\']');
INSERT INTO `prescript` VALUES (57188, '34631', '加味逍遥散', '当归1钱，白芍1钱，白术1钱，茯苓1钱，柴胡1钱，薄荷5分，甘草6分，丹皮7分，香附8分。', '水2钟，煎8分，食远温服。有热，加黄芩5分，生姜3片，红枣2枚。', '《外科大成》卷二', '妇人血虚，五心烦热，肢体疼痛，头目昏重，心忡颊赤，口燥咽干，发热盗汗，食少嗜卧；并室女血弱，荣卫不调，痰嗽潮热，肌体羸瘦，渐成骨蒸；及血热相搏，月水不调，寒热如疟，脐腹作痛。', '-', '-', '柴胡', '[\'当归\', \'白芍\', \'白术\', \'茯苓\', \'柴胡\', \'薄荷\', \'甘草\', \'丹皮\', \'香附\']');
INSERT INTO `prescript` VALUES (57189, '34589', '半夏散', '半夏（姜制）1两，贝母3分，柴胡半两，杏仁半两，川升麻半两，桑白皮（炙）半两，地骨皮半两，款冬花半两，麦门冬半两，马兜铃半两，青橘皮半两，甘草（炙）1分。', '每服1钱，薄荷1叶，绵裹，水1盏，加生姜1片，大枣半个，煎5分，盏盛放火上，时时温服。', '《幼幼新书》卷十六引丁时发方', '止泻润肺。主肺热咳嗽。', '忌生冷毒物。', '-', '柴胡', '[\'半夏\', \'姜制\', \'贝母\', \'柴胡\', \'半两\', \'杏仁\', \'半两\', \'升麻\', \'半两\', \'桑白皮\', \'半两\', \'地骨皮\', \'半两\', \'款冬花\', \'半两\', \'麦门冬\', \'半两\', \'马兜铃\', \'半两\', \'橘皮\', \'半两\', \'甘草\', \'炙分\']');
INSERT INTO `prescript` VALUES (57190, '34585', '伐肝补脾汤', '黄连1钱2分，芍药1钱，柴胡1钱，青皮8分，白术1钱半，人参8分，白茯苓1钱，甘草（炙）5分。', '水煎，食前服。', '《赤水玄珠》卷三', '脾胃气弱，木乘土位而口酸者。', '-', '-', '柴胡', '[\'黄连\', \'钱分\', \'芍药\', \'柴胡\', \'青皮\', \'白术\', \'钱半\', \'人参\', \'白茯苓\', \'甘草\', \'炙分\']');
INSERT INTO `prescript` VALUES (57191, '34511', '完臂汤', '当归5钱，白芍5钱，柴胡2钱，羌活2钱，半夏2钱（制），白芥子2钱，陈皮1钱，秦艽2钱，附子3分。', '水煎服。', '《外科医镜》', '两臂生痈。', '-', '-', '柴胡', '[\'当归\', \'白芍\', \'柴胡\', \'羌活\', \'半夏\', \'钱制\', \'白芥子\', \'陈皮\', \'秦艽\', \'附子\']');
INSERT INTO `prescript` VALUES (57192, '34506', '加味归脾汤', '白术（炒）1钱，人参1钱，茯苓1钱，柴胡5分，川芎5分，山栀（炒）5分，芍药（炒）5分，甘草（炒）5分，熟地黄8两，当归8两。', '水煎服。', '《医部全录》卷三九九引《薛氏医案》', '内消乳岩。主妇人乳岩初起。', '-', '-', '柴胡', '[\'白术\', \'人参\', \'茯苓\', \'柴胡\', \'川芎\', \'分山\', \'炒分\', \'芍药\', \'炒分\', \'甘草\', \'炒分\', \'熟地黄\', \'当归\']');
INSERT INTO `prescript` VALUES (57193, '34474', '风火两消汤', '白芍1两，炒栀子3钱，柴胡2钱，天花粉2钱，甘草1钱，车前子2钱，丹皮5钱。', '水煎服。', '《辨证录》卷九', '肝经内伤，感触风邪，木郁不泄，木乃生火，火郁不宣，火乃生风，动多气恼，大声骂詈，身热胸满，两胁作胀。', '-', '-', '柴胡', '[\'白芍\', \'栀子\', \'柴胡\', \'天花粉\', \'甘草\', \'车前子\', \'丹皮\']');
INSERT INTO `prescript` VALUES (57194, '34442', '虎骨散', '虎骨（醋炙）1两，猴孙骨（醋炙）1两，自然铜（烧，醋淬）1两，骨碎补（去毛）1两，赤芍药1两，补骨脂（炒）1两，金牙（烧，醋淬）1两，苍术（切，炒）1两，当归（切，炒）1两，芎1两，牛膝（切，酒浸，焙）1两，桂（去粗皮）1两，人参1两，柴胡（去苗）1两，败龟（醋炙）1两，沉香1两。', '每服2钱匕，温酒调下，空心、日午。近夜服。', '《圣济总录》卷八十九', '虚劳，荣卫俱伤，遍身疼痛。', '-', '-', '柴胡', '[\'虎骨\', \'两猴\', \'孙骨\', \'自然铜\', \'烧醋\', \'骨碎补\', \'芍药\', \'补骨脂\', \'金牙\', \'烧醋\', \'苍术\', \'当归\', \'两芎\', \'牛膝\', \'切酒\', \'两桂去\', \'粗皮\', \'人参\', \'柴胡\', \'去苗\', \'两败\', \'沉香\']');
INSERT INTO `prescript` VALUES (57195, '34441', '白鲜皮汤', '白鲜皮1两，款冬花1两，柴胡（去苗）1两，车前子1两，枳壳（去瓤，麸炒）1两，黄芩（去黑心）1两，甘草（炙）半两，百合2两，菊花1两半，蔓荆实（炒）各1两半。', '每服5钱匕，以水1盏半，煎至8分，去滓，食后温服，临卧再服。', '《圣济总录》卷一一一', '目肤翳遮睛，及瞳人上有物如蝇翅状，令人视物不明。', '-', '-', '柴胡', '[\'白鲜皮\', \'款冬花\', \'柴胡\', \'车前子\', \'枳壳\', \'黄芩\', \'黑心\', \'甘草\', \'半两\', \'百合\', \'菊花\', \'两半\', \'荆实\', \'两半\']');
INSERT INTO `prescript` VALUES (57196, '34389', '八贤散', '当归1两，白芍药1两，白茯苓1两，甘草1两，川芎半两，桂半两，柴胡半两，熟地黄1两。', '上(口父)咀。三岁1钱，水半盏，煎至3分，去滓，不拘时服。', '《普济方》卷三九五', '小儿泄泻，发热，手足梢疼。', '-', '-', '柴胡', '[\'当归\', \'两白\', \'芍药\', \'白茯苓\', \'甘草\', \'川芎\', \'半两\', \'半两\', \'柴胡\', \'半两\', \'熟地黄\']');
INSERT INTO `prescript` VALUES (57197, '34387', '归身生地汤', '归身5钱（生），生地5钱，知母1钱，黄芩1钱，柴胡2钱，羌活2钱，葛根2钱，浮萍1钱半。', '若热势上冲，咽喉肿痛，热郁于胃，大渴引饮，呕吐不食，胸腹胀痛，坚硬拒按，加酒浸大黄3钱下之。', '《医门八法》卷四', '疹证欲出不出，方出骤回，泻痢，喘促，谵妄等疹初诸实证。', '-', '-', '柴胡', '[\'归身\', \'钱生\', \'生地\', \'知母\', \'黄芩\', \'柴胡\', \'羌活\', \'葛根\', \'浮萍\', \'钱半\']');
INSERT INTO `prescript` VALUES (57198, '34291', '半夏汤', '半夏（汤洗7遍，焙）1两，麦门冬（去心，焙）1两，人参1两，白茯苓（去黑皮）1两，桔梗（炒）1两，青橘皮（汤浸去白，焙）1两，柴胡（去苗）1两，防风（去叉）1两，前胡（去芦头）1两，细辛（去苗叶）1两，白芷1两，紫菀（去土）1两，款冬花1两，厚朴（去粗皮，生姜汁炙）1两半，枳壳（去瓤，麸炒）1两半。', '每服3钱匕，水1盏半，加生姜3片，煎至1盏，去滓，稍热服。', '《圣济总录》卷四十七', '胃热肠寒，冷热不匀，善食数饥，入腹胀痛。', '-', '-', '柴胡', '[\'半夏\', \'洗遍\', \'麦门冬\', \'人参\', \'白茯苓\', \'黑皮\', \'桔梗\', \'两青\', \'橘皮\', \'汤浸\', \'柴胡\', \'防风\', \'两前\', \'胡去\', \'芦头\', \'细辛\', \'苗叶\', \'白芷\', \'紫菀\', \'款冬花\', \'厚朴\', \'粗皮生\', \'姜汁\', \'两半\', \'枳壳\', \'两半\']');
INSERT INTO `prescript` VALUES (57199, '34217', '加减益气汤', '黄耆（蜜炒）1钱，人参1钱，白术（去芦）1钱，陈皮1钱，当归1钱，升麻5分，柴胡5分，木香5分，香附7分，青皮（去瓤）7分，川芎7分，桂枝3分，甘草3分。', '加生姜、大枣，水煎服。', '《寿世保元》卷五', '气虚麻木。', '-', '-', '柴胡', '[\'黄耆\', \'人参\', \'白术\', \'芦钱\', \'陈皮\', \'当归\', \'升麻\', \'柴胡\', \'木香\', \'香附\', \'青皮\', \'川芎\', \'桂枝\', \'甘草\']');
INSERT INTO `prescript` VALUES (57200, '34211', '加味清室汤', '柴胡1钱，黄芩1钱，甘草1钱，半夏1钱，白芍5分，丹皮3钱，陈皮5分。', '水煎服。', '《辨证录》卷五', '热入血室，妇人经水适来，正当伤风，发热恶寒，胸胁胀满，谵语者。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'甘草\', \'半夏\', \'白芍\', \'丹皮\', \'陈皮分\']');
INSERT INTO `prescript` VALUES (57201, '34206', '补中益气汤', '人参8分，蜜炙黄耆1钱，白术8分，当归身8分，柴胡4分，升麻4分，川芎4分，炙甘草5分，陈皮4分，生姜1片。', '水煎，温服。渴者，加麦门冬1钱，五味子9粒。', '《活幼心法》卷四', '痘疹浆足回水，至结痴还元数日，发热稍缓，头热面不甚热，手心脚心热，手背脚背不热，精神困倦，大小便利者，为虚热。', '-', '-', '柴胡', '[\'人参\', \'分蜜\', \'黄耆\', \'白术\', \'当归\', \'身分\', \'柴胡\', \'升麻\', \'川芎\', \'甘草\', \'陈皮分\', \'生姜\']');
INSERT INTO `prescript` VALUES (57202, '34198', '半夏汤', '当归2两，防风2两，黄耆2两，柴胡半斤，细辛1两，麻黄（去节）1两，人参1两，杏仁50粒，桂心3两，半夏1升（洗），大枣20枚，生姜5两，黄芩1两。', '以水1斗，先煮麻黄1沸，去上沫，更入水1升及诸药，煮取5升，分为5服，日3夜2。', '《外台》卷十引《古今录验》', '上气，五脏闭塞，不得饮食，胸中胁下支胀，乍去乍来，虚气结于心中，伏气住胃管，唇干口燥，肢体动摇，手足疼冷，梦寐若见人怖惧。', '忌羊肉、生葱、生菜、饧等。', '-', '柴胡', '[\'当归\', \'防风\', \'两黄耆\', \'柴胡\', \'半斤\', \'细辛\', \'麻黄\', \'去节\', \'人参\', \'杏仁\', \'粒桂心\', \'半夏\', \'升洗\', \'大枣\', \'生姜\', \'黄芩\']');
INSERT INTO `prescript` VALUES (57203, '34174', '加减当归龙荟丸', '归身1钱，人参1钱，炙甘草1钱，柴胡1钱，川芎1钱，青皮7分，芦荟7分，木香7分，胆草（酒洗）5分，栀仁5分，半夏（大者）3个（一本有黄芩、陈皮）。', '每服25丸，寅、卯时竹叶汤送下。', '《幼科发挥》卷二', '平疳止搐。主小儿疟疾发搐，随又成疳，汗出便泄，心下跳，腹中鸣，面色(白光)白，囟陷发疏，体渐羸瘦者。', '-', '-', '柴胡', '[\'归身\', \'人参\', \'甘草\', \'柴胡\', \'川芎\', \'青皮\', \'芦荟\', \'木香\', \'分胆\', \'草酒\', \'洗分\', \'栀仁分\', \'半夏\', \'大者\', \'一本\', \'黄芩\', \'陈皮\']');
INSERT INTO `prescript` VALUES (57204, '34127', '龙胆草散', '龙胆草1两，大青1两，柴胡1两，枳实1两（麸炒令黄），栝楼1两，黄芩1两，栀子仁1两，茵陈1两，川大黄1两（微炒），甘草半两（微炒）。', '每服5钱，以水1大盏，煎至5分，去滓，不拘时候温服。', '《医方类聚》卷五十四引《神巧万全方》', '伤寒壮热，骨节烦疼，连心两肋气账急硬痛，不能食，变为黄。', '-', '-', '柴胡', '[\'龙胆草\', \'两大青\', \'柴胡\', \'枳实\', \'两麸\', \'炒令\', \'栝楼\', \'黄芩\', \'栀子\', \'茵陈\', \'两川\', \'大黄\', \'两微\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (57205, '34099', '防风散', '防风1两，蜀椒（去目、闭口者，汗）1两，麦门冬（去心）1两，天雄（炮，去皮）5分，附子（炮，去皮）5分，人参5分，当归5分，五味子3两，干姜3两，乌头（炮，去皮）3两，细辛3两，白术3两，柴胡半两，山茱萸半两，莽草半两，麻黄（去节）半两，桔梗半两，白芷半两。', '每服方寸匕，酒送下，日3次。不知稍增，以知为度。', '《千金翼》卷十六', '风所为，猝起眩冒不知人，四肢不知痛处，不能行走，或身体偏枯不遂，口吐涎沫出，手足拘急。', '-', '-', '柴胡', '[\'防风\', \'两蜀椒\', \'闭口\', \'麦门冬\', \'去心\', \'两天\', \'雄炮\', \'去皮\', \'附子\', \'去皮\', \'人参\', \'当归\', \'五味子\', \'干姜\', \'乌头\', \'去皮\', \'细辛\', \'白术\', \'柴胡\', \'半两\', \'山茱萸\', \'半两\', \'莽草\', \'半两\', \'麻黄\', \'去节\', \'半两\', \'桔梗\', \'半两\', \'白芷\', \'半两\']');
INSERT INTO `prescript` VALUES (57206, '34094', '加减补中益气汤', '黄芩2钱，柴胡3分，陈皮8分，茯苓2钱，升麻3分，枸杞1钱，川芎8分，炙甘草5分，白术1钱，归身1钱。', '-', '《程松崖眼科》', '气虚，眼胞下坠，视物不明，目无红肿疼痛者。', '-', '-', '柴胡', '[\'黄芩\', \'柴胡\', \'陈皮分\', \'茯苓\', \'升麻\', \'枸杞\', \'川芎\', \'甘草\', \'白术\', \'钱归身\']');
INSERT INTO `prescript` VALUES (57207, '34083', '加味地黄汤', '熟地黄3钱，薄荷6分，泽泻1钱，山萸肉1钱5分，白茯苓1钱，柴胡6分，牡丹皮1钱，山药1钱5分。', '水3钟煎，空心服。其乳母亦当如方服药，亦有功益。', '《幼科直言》卷四', '小儿黄疸，而人虚体瘦，兼骨蒸劳热者。', '乳孩此症，多因吃乳母热乳而成，然必须戒乳，吃药方效，以米汤薄粥代之。同时须戒鱼腥、酒、面、猪肉、鲤鱼、羊肉、野鸡等物。', '-', '柴胡', '[\'熟地黄\', \'薄荷\', \'泽泻\', \'钱山萸\', \'钱分\', \'白茯苓\', \'柴胡\', \'牡丹皮\', \'山药\', \'钱分\']');
INSERT INTO `prescript` VALUES (57208, '34064', '加减小柴胡汤', '人参（去芦），北柴胡（去芦）4两，黄芩（去心）1两半，甘草（炙）1两半，半夏（泡7次，切）1两1分。', '上(口父)咀。每服5钱匕，水1盏半，加生姜4片，大枣3枚，煮取8分，去滓温服，不拘时候。咳嗽者，去人参、大枣、生姜，加北五味子2两，干姜半两；若胁下满痛者，去枣，每服加煅过牡蛎半钱；若腹痛者，去黄芩，加芍药1两半；妇人经脉适行，或适断，寒热如疟，昼日明了，暮则谵语，如见鬼状，此为热入血室，每服加生干地黄1两；若心下悸，小便不利者，去黄芩加赤茯苓2两；若不渴，外有微热者，去人参，加桂枝1两半；若胸中烦而不呕者，去半夏、人参，加栝楼实1枚，用4分之1；若渴者，去半夏，更加人参3分，栝楼根2两。', '《医方类聚》卷五十六引《管见大全良方》', '伤寒3-4日，传少阳胆经，胸胁痛，耳聋，口苦，舌干，往来寒热而呕者。', '-', '-', '柴胡', '[\'人参\', \'芦北\', \'柴胡\', \'去芦\', \'黄芩\', \'去心\', \'两半\', \'甘草\', \'两半\', \'半夏\', \'泡次切\', \'两分\']');
INSERT INTO `prescript` VALUES (57209, '34013', '加味补中益气汤', '黄耆（蜜炙）1钱，人参1钱，白术1钱，陈皮1钱，当归1钱，升麻5分，柴胡5分，木香5分，香附8分，青皮（去瓤）8分，川芎8分，甘草3分，桂枝少许。', '加生姜、大枣，水煎服。', '《济阳纲目》卷八十二', '浑身麻，属气虚者。', '-', '-', '柴胡', '[\'黄耆\', \'蜜炙钱\', \'人参\', \'白术\', \'陈皮\', \'当归\', \'升麻\', \'柴胡\', \'木香\', \'香附\', \'青皮\', \'川芎\', \'甘草\', \'桂枝\', \'少许\']');
INSERT INTO `prescript` VALUES (57210, '33988', '加减八珍汤', '人参1钱，当归3钱，白芍2钱，生甘草1钱，茯苓3钱，白术5钱，黄耆3钱，熟地5钱，生地5钱，柴胡1钱，川芎8分，天花粉2钱。', '水煎服。先用6剂；去柴胡，加北五味子10粒，再服6剂。有火者，加黄芩2钱。', '《洞天奥旨》卷十', '疥疮脓窠。', '-', '-', '柴胡', '[\'人参\', \'当归\', \'白芍\', \'生甘草\', \'茯苓\', \'白术\', \'黄耆\', \'熟地\', \'生地\', \'柴胡\', \'川芎\', \'天花粉\']');
