INSERT INTO `prescript` VALUES (57215, '33885', '加味地黄汤', '熟地8两，山茱萸4两，山药4两，丹皮3两，泽泻3两，柴胡1两，麦冬3两，当归3两，白芍3两，肉桂1两，菖蒲5钱，茯苓3两。', '每服5钱，早、晚空腹滚水送下。1料即愈。', '《洞天奥旨》卷十', '鹅掌风，足癣。', '-', '-', '柴胡', '[\'熟地\', \'山茱萸\', \'山药\', \'丹皮\', \'泽泻\', \'柴胡\', \'麦冬\', \'当归\', \'白芍\', \'肉桂\', \'菖蒲\', \'茯苓\']');
INSERT INTO `prescript` VALUES (57216, '33808', '加减清脾饮', '柴胡1钱5分，川朴1钱，秦艽1钱5分，青蒿6分，姜夏2钱，草果仁1钱5分，灵仙1钱5分，青皮1钱，赤苓3钱，通草1钱，老姜2片。', '-', '《镐京直指》', '夏秋感受风湿而为暑疟，午后微寒而热，或1日轻而1日重，其寒热界限模糊者。', '-', '-', '柴胡', '[\'柴胡\', \'分川朴\', \'秦艽\', \'钱分\', \'青蒿\', \'姜夏钱\', \'果仁\', \'钱分\', \'灵仙\', \'钱分\', \'青皮\', \'赤苓\', \'通草\', \'姜片\']');
INSERT INTO `prescript` VALUES (57217, '33801', '龙胆散', '龙胆1分（去芦头），钩藤1分，柴胡1分（去苗），黄芩1分，桔梗1分（去芦头），赤芍药1分，茯神1分，甘草1分（炙微赤，锉），蜣螂2枚（去翅足，微炒），川大黄1两（锉碎，微炒），人参1分（去芦头），当归1分（锉，微炒）。', '龙胆汤（《圣济总录》卷一六七）。', '《圣惠》卷八十八', '小儿百病，变蒸，客忤，惊痫，壮热不解。', '-', '-', '柴胡', '[\'龙胆\', \'分去\', \'芦头\', \'钩藤\', \'柴胡\', \'分去\', \'黄芩\', \'桔梗\', \'分去\', \'芦头\', \'芍药\', \'茯神\', \'甘草\', \'分炙微\', \'蜣螂\', \'足微\', \'炒川\', \'大黄\', \'人参\', \'分去\', \'芦头\', \'当归\']');
INSERT INTO `prescript` VALUES (57218, '33764', '附子防风散', '白术1两，白茯苓3分，柴胡1两半（去苗），五味子1两，干姜3分（炮裂，切），甘草3分（炙微赤，切），附子3分（炮裂，去皮脐），桂心半两，防风3分（去芦头）。', '防子防风汤（《医统》卷十四）。《得效》有川芎三分。', '《活人书》卷十七', '伤寒阴痓，闭目合面，手足厥逆，筋脉拘急，汗出不止。', '-', '-', '柴胡', '[\'白术\', \'白茯苓\', \'柴胡\', \'两半\', \'去苗\', \'五味子\', \'干姜\', \'分炮\', \'裂切\', \'甘草\', \'分炙微\', \'赤切\', \'附子\', \'分炮裂\', \'去皮\', \'脐桂心\', \'半两\', \'防风\', \'分去\', \'芦头\']');
INSERT INTO `prescript` VALUES (57219, '33751', '加味治中汤', '人参1钱半，白术（陈土炒）2钱半，白芍药（醋炒）1钱5分，甘草（炙）1钱，青皮（去瓤，麸炒）7分，陈皮（去自）1钱，干姜（炒黑）1钱，苍术（麸炒）1钱半，升麻5分，柴胡5分，防风5分，白茯苓1钱。', '加生姜3片，加大枣2枚，水2钟，煎1钟，食前服。久泻虚寒，加熟附1钱。', '《医便》卷二', '春月肝木乘脾，腹痛久泻不止。', '-', '-', '柴胡', '[\'人参\', \'钱半\', \'白术\', \'陈土\', \'半白\', \'芍药\', \'钱分\', \'甘草\', \'青皮\', \'炒分\', \'陈皮\', \'自钱\', \'干姜\', \'黑钱\', \'苍术\', \'升麻\', \'柴胡\', \'防风\', \'白茯苓\']');
INSERT INTO `prescript` VALUES (57220, '33750', '妇科得生丹', '益母草320两，白芍80两，当归80两，羌活30两，木香30两，柴胡30两。', '每服1丸，温开水送下。', '《北京市中成方选集》引黄毓息方', '调经化瘀，解郁和肝。主气滞胸满，经血不调，血瘀腹痛，四肢倦怠。', '孕妇忌服。', '-', '柴胡', '[\'益母草\', \'白芍\', \'当归\', \'羌活\', \'木香\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57221, '33736', '加味四君汤', '人参1钱，白术（蜜炙）1钱，茯苓1钱，枳壳（麸炒）1钱，柴胡1钱，黄芩1钱，山栀仁（炒）1钱，甘草5分。', '加生姜3片，葱白3茎，水煎服。', '《叶氏女科》卷二', '子悬。妊娠四五月，胃热而致胎气不和，逆上心胸，胀满疼痛不安者。', '-', '-', '柴胡', '[\'人参\', \'白术\', \'蜜炙钱\', \'茯苓\', \'枳壳\', \'柴胡\', \'黄芩\', \'钱山\', \'栀仁\', \'甘草\']');
INSERT INTO `prescript` VALUES (57222, '33644', '加味益气汤', '黄耆（蜜炒）1钱5分，人参1钱，白术（去芦，炒）2钱，陈皮8分，当归1钱，柴胡5分，升麻5分，茵陈4分，苍术（米泔浸，炒）4分，栀子（炒）4分，猪苓4分，赤茯苓1钱，泽泻4分，黄连4分，滑石4分，甘草（炙）4分。', '加生姜，水煎服。以六味地黄丸加苍术、白术、茵陈、黄柏各2两，蜜为丸，相兼而进之。', '《寿世保元》卷三', '五疸，遍身发黄如栀子水染，病延日久，身体黑瘦，四肢沉困，憎寒发热，不思饮食。', '-', '-', '柴胡', '[\'黄耆\', \'钱分\', \'人参\', \'白术\', \'去芦\', \'陈皮分\', \'当归\', \'柴胡\', \'升麻\', \'茵陈\', \'苍术\', \'泔浸\', \'炒分\', \'栀子\', \'炒分\', \'猪苓\', \'分赤\', \'茯苓\', \'泽泻\', \'黄连\', \'滑石\', \'甘草\', \'炙分\']');
INSERT INTO `prescript` VALUES (57223, '33642', '当归汤', '当归（切，焙）2两，防风（去叉）2两，黄耆（细锉）2两，柴胡（去苗）8两，细辛（去苗叶）1两，麻黄（去根节，煮1-2沸，掠去沫，控干）1两，人参1两，杏仁（去皮尖双仁，炒黄）50个，桂（去粗皮）3两，半夏（汤浸去滑7遍）5两，黄芩（去黑心）1两。', '每服4钱匕，水1盏，加生姜7片，大枣2个（擘破），同煎至6分，去滓温服，日3夜2，不拘时候。', '《圣济总录》卷十九', '肺痹上气，闭塞胸中，胁下支满，乍作乍止，不得饮食，唇干口燥，手足冷痛。&#039;', '-', '-', '柴胡', '[\'当归\', \'切焙\', \'防风\', \'黄耆\', \'柴胡\', \'细辛\', \'苗叶\', \'麻黄\', \'根节\', \'掠去\', \'控干\', \'人参\', \'杏仁\', \'皮尖\', \'双仁\', \'黄个\', \'桂去\', \'粗皮\', \'半夏\', \'汤浸\', \'滑遍\', \'黄芩\', \'黑心\']');
INSERT INTO `prescript` VALUES (57224, '33605', '龙胆散', '龙胆2分（去芦头），甘草3分（炙微赤，锉），牡蛎1两（烧为粉），麦门冬3分（去心），柴胡3两（去苗），川升麻3分，犀角屑3分。', '龙胆汤（《圣济总录》卷六十）。', '《圣惠》卷五十五', '劳黄，额上汗出，手足中热，四肢烦疼，薄暮寒热，小便自利。', '-', '-', '柴胡', '[\'龙胆\', \'分去\', \'芦头\', \'甘草\', \'分炙微\', \'牡蛎\', \'两烧\', \'麦门冬\', \'分去\', \'柴胡\', \'两去\', \'苗川\', \'升麻\', \'犀角\']');
INSERT INTO `prescript` VALUES (57225, '33604', '加味地骨皮散', '地骨皮（鲜者）3钱，桑白皮（鲜者）2钱，麦门冬2钱，银柴胡1钱，赤芍药1钱，干葛1钱，甘草5分，生犀屑5分。', '水煎，调大小无比散5-7分服。', '《准绳·幼科》卷六', '疹出发热不退，饮食不进；亦治喘急不止。', '-', '-', '柴胡', '[\'地骨皮\', \'鲜者\', \'桑白皮\', \'鲜者\', \'麦门冬\', \'银柴胡\', \'芍药\', \'干葛钱\', \'甘草\', \'分生\', \'犀屑分\']');
INSERT INTO `prescript` VALUES (57226, '33599', '甘露饮', '犀角1钱，生甘草1钱，生地1钱，银柴胡1钱，枳壳1钱，麦门冬1钱，知母1钱，枇杷叶1钱，黄芩1钱，钗石斛1钱，茵陈1钱。', '用淡竹叶7片，灯心10很为引，水煎服。', '《疡医大全》卷十四', '茧唇。', '-', '-', '柴胡', '[\'犀角\', \'生甘草\', \'生地\', \'银柴胡\', \'枳壳\', \'麦门冬\', \'知母\', \'枇杷叶\', \'黄芩\', \'钱钗\', \'石斛\', \'茵陈\']');
INSERT INTO `prescript` VALUES (57227, '33592', '加减当归龙荟丸', '当归（酒洗）1两，龙胆草（酒洗）1两，栀子仁（炒）1两，黄芩1两，青皮1两，大黄（酒蒸）5钱，芦荟5钱，柴胡5钱，木香2钱半，牛胆南星3钱，麝香5分。', '每服20丸，生姜汤送下，日3次。一七后用针砂酒以通其气。方用针砂1两，穿山甲1钱拌针砂，养1昼夜，去山甲，将针砂用酒1碗浸3-4日，含酒口内，外用磁石1块，绵裹塞耳中即通。', '《济阳纲目》卷一○三', '聪耳泻火。主耳病。', '戒暴怒、色欲。', '-', '柴胡', '[\'当归\', \'酒洗\', \'龙胆草\', \'酒洗\', \'栀子\', \'黄芩\', \'青皮\', \'两大\', \'黄酒\', \'蒸钱\', \'芦荟\', \'柴胡\', \'木香\', \'钱半牛\', \'胆南星\', \'麝香\']');
INSERT INTO `prescript` VALUES (57228, '33552', '龙胆散', '龙胆半两（去芦头），麦门冬半两（去心，焙），知母半两，人参半两（去芦头），甘草1分（生，锉），柴胡半两（去苗）。', '每服2钱，磨犀角温水调下，不拘时候。', '《圣惠》卷十一', '伤寒心脾热，唇干舌肿，口内生疮。', '-', '-', '柴胡', '[\'龙胆\', \'半两\', \'芦头\', \'麦门冬\', \'半两\', \'知母\', \'半两\', \'人参\', \'半两\', \'芦头\', \'甘草\', \'分生\', \'柴胡\', \'半两\', \'去苗\']');
INSERT INTO `prescript` VALUES (57229, '33542', '加减当归龙荟丸', '当归（酒洗，晒干）1钱，人参1钱，川芎1钱，胆草（酒浸）5分，山栀仁5分，青皮7分，荟芦7分，甘草1钱，柴胡1钱，半夏（大者）3个（泡7次，切，生姜自然汁浸1时，又以白矾水洗之）。', '每服25丸，寅、卯时淡竹叶汤送下。', '《育婴秘诀》卷四', '平肝补脾。主小儿暑疟惊痫。', '-', '-', '柴胡', '[\'当归\', \'酒洗\', \'晒干\', \'人参\', \'川芎\', \'钱胆\', \'草酒\', \'浸分山\', \'栀仁分\', \'青皮\', \'分荟\', \'芦分\', \'甘草\', \'柴胡\', \'半夏\', \'大者\', \'泡次切\', \'生姜\', \'自然\', \'浸时\', \'白矾\', \'水洗\']');
INSERT INTO `prescript` VALUES (57230, '33536', '半夏散', '半夏2两（汤洗7遍去滑），人参1两（去芦头），柴胡2两（去苗），黄芩1两，甘草1两（炙微赤，锉），栝楼根2两。', '每服4钱，以水1中盏，加生姜半分，大枣3个，煎至6分，去滓温服，不拘时候。', '《圣惠》卷十四', '伤寒10余日不解，往来寒热，发如疟，胸膈满闷。', '-', '-', '柴胡', '[\'半夏\', \'两汤\', \'洗遍\', \'人参\', \'两去\', \'芦头\', \'柴胡\', \'两去\', \'黄芩\', \'甘草\', \'两炙微\', \'栝楼\', \'根两\']');
INSERT INTO `prescript` VALUES (57231, '33514', '安胎膏', '老母鸡1只（缉死，勿经水，拔尽毛，竹刀破击肠杂，入粳米、糯米半碗，银针穿线缝好，麻油4斤熬听用），生地4两，川芎（酒洗）2两，当归（酒洗）2两，杜仲（炒）2两，续断（炒）2两，白术2两，黄芩2两，制香附2两，淮山药2两，党参1两，黄耆1两，熟地1两，酒白芍1两，麦冬1两，知母1两，苍术1两，陈皮1两，枳壳1两，半夏（姜汁炒透则不碍胎）1两，羌活1两，防风1两，白芷1两，柴胡（炒）1两，苏子（或梗）1两，藿香1两，黑山栀1两，泽泻1两，甘草（生炙各半）1两，砂仁1两，南薄荷5钱，北细辛5钱，葱白1-2斤，益', '本方保为主，治症次之，治以上诸症，宜辨证配合内服药物。', '《理瀹》', '保胎。主妇人胎前诸症。凡感受风寒暑湿，或妊娠之初，头目昏晕，肢体沉重，憎闻食气，好食酸咸，恶心呕吐，或心烦躁闷，或咳嗽，或痢，或泻，或寒热往来；或胎中有水，面目身体脚膝肿胀，足指出水；或痰迷发搐；或胎气不和，逆上痛胀；或胎气壅塞，小便淋痛；或肾虚腰痛，或带下腰酸；或胎漏，或胎动下血；热病护胎；孕妇转脬；或小便不通，大便不通，一切闪挫。', '-', '-', '柴胡', '[\'老母鸡\', \'只缉死\', \'经水拔\', \'毛竹\', \'破击\', \'肠杂入\', \'粳米\', \'糯米\', \'半碗\', \'银针\', \'穿线\', \'缝好\', \'麻油\', \'生地\', \'川芎\', \'酒洗\', \'当归\', \'酒洗\', \'杜仲\', \'续断\', \'白术\', \'黄芩\', \'两制\', \'香附\', \'两淮\', \'山药\', \'党参\', \'两黄耆\', \'熟地\', \'两酒\', \'白芍\', \'麦冬\', \'知母\', \'苍术\', \'陈皮\', \'枳壳\', \'半夏\', \'姜汁\', \'透则\', \'不碍\', \'羌活\', \'防风\', \'白芷\', \'柴胡\', \'苏子\', \'藿香\', \'黑山\', \'栀两\', \'泽泻\', \'甘草\', \'生炙\', \'半两\', \'砂仁\', \'两南\', \'薄荷\', \'钱北\', \'细辛\', \'葱白\', \'斤益\']');
INSERT INTO `prescript` VALUES (57232, '33456', '补母汤', '当归1钱，茯苓1钱，桔梗1钱，柴胡1钱，木香1钱，芍药1钱，莪术3钱，藿香3钱，芎3钱，人参3钱，黄耆3钱，肉桂3钱，桂心3钱，熏陆3钱，沉香3钱，乳香3钱，熟地黄3钱，丁子3钱，石膏3钱，滑石3钱，大黄3钱，升麻3钱，缩砂3钱，槟榔3钱，黄芩3钱，甘草3钱，安息香3钱。', '水煎服。', '《名家方选》', '产前产后，或金疮打扑，凡从血症变出者。', '-', '-', '柴胡', '[\'当归\', \'茯苓\', \'桔梗\', \'柴胡\', \'木香\', \'芍药\', \'莪术\', \'藿香\', \'人参\', \'黄耆\', \'肉桂\', \'钱桂心\', \'熏陆\', \'沉香\', \'乳香\', \'熟地黄\', \'钱丁子\', \'石膏\', \'滑石\', \'大黄\', \'升麻\', \'钱缩\', \'槟榔\', \'黄芩\', \'甘草\', \'安息香\']');
INSERT INTO `prescript` VALUES (57233, '33431', '龙胆汤', '龙胆1两，黄连（去须）1两，木通（锉）1两，柴胡（去苗）1两，麦门冬（去心，焙）1两，人参1两，陈橘皮（去白，焙）半两，黄芩（去黑心）半两。', '每服3钱匕，以水1盏，煎取7分，去滓，食后温服，日2次。', '《圣济总录》卷四十七', '胃热，善食而瘦。', '-', '-', '柴胡', '[\'龙胆\', \'黄连\', \'木通\', \'柴胡\', \'麦门冬\', \'人参\', \'两陈\', \'橘皮\', \'半两\', \'黄芩\', \'黑心\', \'半两\']');
INSERT INTO `prescript` VALUES (57234, '33415', '半夏散', '半夏（汤洗7遍去滑）1两，柴胡（去苗）1两，黄耆（锉）1两，赤芍药1两，人参（去芦头）1两，桂心1两，陈橘皮（汤浸，去白瓤，焙）1两，大腹皮（锉）1两。', '每服5钱，以水1大盏，加生姜半分，大枣3个，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十五', '时气，腹胁虚胀，心膈壅滞，呕逆不能食。', '-', '-', '柴胡', '[\'半夏\', \'洗遍\', \'柴胡\', \'黄耆\', \'芍药\', \'人参\', \'芦头\', \'两桂心\', \'橘皮\', \'汤浸\', \'大腹皮\']');
INSERT INTO `prescript` VALUES (57235, '33408', '安土散', '白术1两，茯苓5钱，车前子3钱，薏仁5钱，赤小豆1钱，通草1钱，柴胡5分，石斛3钱。', '水煎服。', '《辨证录》卷七', '太阴痉症。', '-', '-', '柴胡', '[\'白术\', \'茯苓\', \'车前子\', \'薏仁\', \'赤小豆\', \'通草\', \'柴胡\', \'石斛\']');
INSERT INTO `prescript` VALUES (57236, '33403', '加味补中益气汤', '黄耆1钱，人参1钱，甘草（炙）1钱，白术1钱，当归1钱，陈皮1钱，升麻3分，柴胡3分，生地黄8分，天花粉8分。', '作1服。水煎服。', '《济阴纲目》卷二', '饮食劳倦，损伤脾胃，气弱体倦，发热作渴，饮食减少，而不生血者。', '-', '-', '柴胡', '[\'黄耆\', \'人参\', \'甘草\', \'白术\', \'当归\', \'陈皮\', \'升麻\', \'柴胡\', \'生地黄\', \'天花粉\']');
INSERT INTO `prescript` VALUES (57237, '33386', '决明汤', '决明子（微炒）3分，地骨皮3分，玄参3分，黄连（去须）3分，桔梗（炒）3分，柴胡（去苗）3分，茯神（去木）3分，山栀子仁半两，羚羊角屑1两。', '本方方名，《普济方》引作\"决明散\"。', '《圣济总录》卷一一一', '眼生肤翳，遮覆瞳仁。', '-', '-', '柴胡', '[\'决明子\', \'炒分\', \'地骨皮\', \'玄参\', \'黄连\', \'桔梗\', \'炒分\', \'柴胡\', \'苗分\', \'茯神\', \'木分山\', \'栀子\', \'半两\', \'羚羊角\']');
INSERT INTO `prescript` VALUES (57238, '33385', '净府散', '柴胡1钱，黄芩8分，半夏（姜汁浸，炒）8分，人参2分，白术（去芦）7分，白茯苓（去皮）1钱，猪苓7分，泽泻1钱，三棱（煨）1钱，莪术（煨）1钱，山楂肉1钱，胡黄连3分，甘草3分。', '加生姜、大枣煎服。', '《古今医鉴》卷十三', '小儿腹中癖块，发热口干，小便赤。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'半夏\', \'姜汁\', \'炒分\', \'人参\', \'白术\', \'芦分\', \'白茯苓\', \'去皮\', \'猪苓\', \'泽泻\', \'三棱\', \'莪术\', \'山楂\', \'胡黄连\', \'甘草\']');
INSERT INTO `prescript` VALUES (57239, '33370', '加味当归饮子', '当归5钱，生地黄5钱，升麻5钱，防风2钱半，荆芥穗2钱，何首乌2钱，白芍药3钱，柴胡3钱，川芎3钱，羌活3钱，黄耆3钱，红花1钱，苏木1钱，甘草1钱。', '加味当归饮（《疡科选粹》卷二）。', '《普济方》卷二七二', '诸疮痛痒。', '-', '-', '柴胡', '[\'当归\', \'生地黄\', \'升麻\', \'防风\', \'钱半\', \'荆芥\', \'何首乌\', \'钱白\', \'芍药\', \'柴胡\', \'川芎\', \'羌活\', \'黄耆\', \'花钱\', \'苏木\', \'甘草\']');
INSERT INTO `prescript` VALUES (57240, '33344', '加味败毒散', '前胡（去芦）1两，柴胡（银州者，去苗）1两，人参1两，甘草（冬服用炙，夏月不用）1两，羌活1两，独活1两，桔梗1两，茯苓（去皮）1两，枳壳（汤浸，去瓤，麸炒令香）1两，川芎1两，半夏（汤洗7次）、苍术（米泔浸炒）各等分。', '每服2钱，水1盏，入生姜、薄荷，同煎至8分，去滓温服。如觉着风，即并热进2-3服。微汗出立愈。热甚者，加大黄。', '《普济方》卷一○五引《余居士选奇方》', '风气上攻头目，咽燥舌涩，心胸烦满，痰涎不利，头旋目眩；兼解伤寒阳证，脚气，踝上赤肿疼痛，寒热如疟，自汗恶风，或无汗恶风。', '-', '-', '柴胡', '[\'胡去\', \'芦两\', \'柴胡\', \'银州\', \'人参\', \'甘草\', \'服用\', \'炙夏月\', \'不用\', \'羌活\', \'独活\', \'桔梗\', \'茯苓\', \'去皮\', \'枳壳\', \'汤浸\', \'令香\', \'川芎\', \'半夏\', \'洗次\', \'苍术\', \'泔浸\']');
INSERT INTO `prescript` VALUES (57241, '33334', '加减八物汤', '人参3钱，白茯苓3钱，熟地3钱，小茴3钱，白术4钱，川芎4钱，当归5钱，白芍5钱，香附5钱，甘草2钱，黄芩2钱，柴胡2钱。', '每服7钱，加生姜3片，水煎服。腹痛，加元胡索、干漆、枳壳各3钱；恶心，加良姜、砂仁各2钱；麻痹，加肉桂1钱；咳嗽，加五味、款冬、杏仁。', '《何氏济生论》卷八', '产后浮肿，气急潮热。', '-', '-', '柴胡', '[\'人参\', \'白茯苓\', \'熟地\', \'钱小茴钱\', \'白术\', \'川芎\', \'当归\', \'白芍\', \'香附\', \'甘草\', \'黄芩\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57242, '33323', '龙虎双降散', '大黄6两，天花粉6两，元参10两，麦冬5两（去心），滑石5两，银柴胡2两，荆芥2两，丹参2两，白芍3两，石膏3两。', '每服8钱，地浆煎服。', '《古方汇精》卷一', '时行瘟疫，失心癫狂，一切火热蕴结重症。', '-', '-', '柴胡', '[\'大黄\', \'两天\', \'花粉\', \'两元\', \'参两\', \'麦冬\', \'两去\', \'滑石\', \'银柴胡\', \'荆芥\', \'丹参\', \'白芍\', \'石膏\']');
INSERT INTO `prescript` VALUES (57243, '33286', '补阳汤', '肉桂1钱（去皮），知母（炒）3钱，当归身（酒洗）3钱，生地黄（酒洗）3钱，白茯苓3钱，泽泻3钱，陈皮3钱，白芍药5钱，防风5钱，黄耆1两，人参1两，白术1两，羌活1两，独活1两，熟地黄1两，甘草1两，柴胡2两。', '上(口父)咀。每服5钱，水2盏，煎至1大盏，去滓，空心服。', '《兰室秘藏》卷上', '视正反斜症及内障。', '若大寒大风，过于劳役，饮食不调，精神不足，或气弱，俱不得服。', '-', '柴胡', '[\'肉桂\', \'去皮\', \'知母\', \'当归\', \'身酒\', \'洗钱\', \'生地黄\', \'洗钱\', \'白茯苓\', \'泽泻\', \'陈皮\', \'芍药\', \'防风\', \'黄耆\', \'人参\', \'白术\', \'羌活\', \'独活\', \'熟地黄\', \'甘草\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57244, '33258', '加减当归饮子', '防风1两半，当归1两半，柴胡1两半，芍药1两，生地黄1两半，黄芩1两，人参1两，黄连5钱，甘草1两2钱，滑石6两，大黄1两半。', '上(口父)咀。每服5钱，水2盏，煎至1盏，去滓，食后通口服。', '《袖珍》卷二引《圣惠》', '肩忽疼痛。', '-', '-', '柴胡', '[\'防风\', \'两半\', \'当归\', \'两半\', \'柴胡\', \'两半\', \'芍药\', \'生地黄\', \'两半\', \'黄芩\', \'人参\', \'黄连\', \'甘草\', \'滑石\', \'大黄\', \'两半\']');
INSERT INTO `prescript` VALUES (57245, '33249', '加减拨云散', '羌活2两2钱半，甘菊1两9钱，木贼1两1钱半，白蒺藜1两1钱半，防风1两1钱，柴胡1两1钱，苍术1两1钱，枳壳1两1钱，川芎1两1钱，甘草1两1钱，荆芥1两，薄荷1两，蝉壳7钱半，石决明（煅制）4钱，密蒙花4钱。', '每服2钱，食后薄荷汤调下。', '《东医宝鉴·外形篇》卷一引《医林》', '诸般眼病。', '-', '-', '柴胡', '[\'羌活\', \'甘菊\', \'木贼\', \'半白\', \'蒺藜\', \'防风\', \'柴胡\', \'苍术\', \'枳壳\', \'川芎\', \'甘草\', \'荆芥\', \'薄荷\', \'两蝉壳\', \'石决明\', \'制钱\', \'密蒙花\']');
INSERT INTO `prescript` VALUES (57246, '33217', '补中益气汤', '黄耆半钱，人参（去芦）3钱，甘草半钱，红花1分，白芍药3分（秋冬之月未有，只用白术3分，代之），葛根半钱，当归身2分（酒洗，焙干），橘皮（不去白）2-3分，升麻2-3分，柴胡6分或3分，黄柏（酒洗，去皮）1-2分，黄芩2-3分，生甘草梢3分。', '上(口父)咀，作1服。水2盏，量气弱气盛加减水盏大小，去滓，食远稍热服，伤重者不2服。病劳复热甚者，黄耆用1钱；如觉胸中热者，去黄柏；如觉胸中不热者，去生甘草梢；腹中痛，加白芍药半分、甘草3分；恶寒冷痛，加去皮中桂1-2分（即桂心）；恶热喜寒而痛者，于已加白芍药、甘草二味中，更加生黄芩1-2分，治时热也；如天凉时恶热而痛，于已加白芍药、甘草、黄芩中，更少加桂1分；如腹痛在天气寒冷时，则去芍药，以其味酸而寒故也，别加热药用之；天气寒时腹痛，加益智仁2-3分，或加半夏半钱、生姜3片，忌用芍药；头痛加蔓荆子2分', '《普济方》卷二十四引《内外伤辨》', '补元气，泻心火。主饮食劳倦所伤，气高身热，烦喘短气，鼻息不调，嗜卧困倦少言，皆为热伤元气耗神。其初肌肤间必大热燥闷，心烦而渴，久后则不渴，头痛大作，四肢疼痛，表虚不任风寒，目不欲开。', '-', '-', '柴胡', '[\'黄耆\', \'半钱\', \'人参\', \'芦钱\', \'甘草\', \'半钱\', \'红花\', \'分白\', \'芍药\', \'秋冬\', \'未有\', \'只用\', \'白术\', \'分代\', \'葛根\', \'半钱\', \'当归\', \'身分\', \'酒洗\', \'焙干\', \'橘皮\', \'不去\', \'升麻\', \'柴胡\', \'黄柏\', \'酒洗\', \'去皮\', \'黄芩\', \'生甘草\']');
INSERT INTO `prescript` VALUES (57247, '33149', '加减补中益气汤', '黄耆3钱，党参3钱，白术2钱，陈皮2钱，升麻1钱，柴胡1钱，阿胶（冲化）2钱，焦艾2钱，甘草1钱。', '水煎服。大便溏薄，胃纳不佳，加砂仁2钱，扁豆4钱。', '《中医妇科治疗学》', '补气安胎。主平素体质不强，妊娠4-5月，忽然腰酸腹胀，或有下坠感，精神疲乏，胎动不安，阴道有少许出血，脉滑无力，属气虚下陷者。', '-', '-', '柴胡', '[\'黄耆\', \'党参\', \'白术\', \'陈皮\', \'升麻\', \'柴胡\', \'阿胶\', \'化钱\', \'焦艾钱\', \'甘草\']');
INSERT INTO `prescript` VALUES (57248, '33145', '防葵丸', '防葵3分（锉碎），柴胡3分（去苗），赤茯苓（去黑皮）3分，桂半两（去粗皮），木香半两，鳖甲1两半（去裙襕，醋蘸慢火炙令黄色），槟榔（锉）1两半，桔梗1两（炒），郁李仁1两（汤浸，去皮尖，别研如膏），大黄（锉碎，微炒）1两1分，当归半两（切，焙），京三棱半两（炮，锉），五味子半两。', '方中赤茯苓，原书卷七十三作赤芍药。', '《圣济总录》卷七十二', '症癖气块，胁肋满，腹胀不能饮食，腹痛。', '-', '-', '柴胡', '[\'防葵\', \'柴胡\', \'分去\', \'茯苓\', \'黑皮\', \'分桂\', \'半两\', \'粗皮\', \'木香\', \'半两\', \'鳖甲\', \'两半\', \'慢火\', \'炙令\', \'黄色\', \'槟榔\', \'两半\', \'桔梗\', \'郁李仁\', \'两汤\', \'浸去\', \'皮尖\', \'别研\', \'如膏\', \'大黄\', \'两分\', \'当归\', \'半两\', \'切焙京\', \'三棱\', \'半两\', \'五味子\', \'半两\']');
INSERT INTO `prescript` VALUES (57249, '33112', '夺命散', '桃仁21枚（去皮尖，麸炒），甘草半两，人参半两，鳖甲1两，知母1两，天灵盖（醋浸1宿，酥炙）1两，青蒿、柴胡、阿魏（4枣子大），葱白1握。', '秤2钱，用量便2盏，煎1盏，露1夜，至5更3点暖服半盏。服了衣被盖卧，天明又暖半盏服之，扶病人强行5-7步，3日勿洗手面指头，候生毛为验，每日早晨先饮白汤投之。', '《医统》卷四十六', '传尸劳。', '-', '-', '柴胡', '[\'桃仁\', \'皮尖\', \'甘草\', \'半两\', \'人参\', \'半两\', \'鳖甲\', \'知母\', \'天灵盖\', \'浸宿\', \'青蒿\', \'柴胡\', \'阿魏\', \'枣子\', \'葱白\']');
INSERT INTO `prescript` VALUES (57250, '33081', '加减泻肝汤', '胆草3分，栀子1钱，黄芩1钱，泽泻1钱，柴胡7分，车前2钱，木通6分，生地1钱2分，甘草6分。', '-', '《外科真诠》卷上', '缠腰火丹，累累如珠，色赤形如云片，上起风粟作痒，发热，属肝心二经风火者。', '-', '-', '柴胡', '[\'胆草分\', \'栀子\', \'黄芩\', \'泽泻\', \'柴胡\', \'分车\', \'木通\', \'生地\', \'钱分\', \'甘草\']');
INSERT INTO `prescript` VALUES (57251, '33077', '龙胆丸', '龙胆草1两，柴胡1两，黄芩1两，鳖甲（醋炙）1两，桃仁（去皮尖）半两，山栀子半两，陈皮半两，当归（酒洗）半两，大黄半两，甘草（炙）半两。', '每服20丸，空心白汤送下。如小儿，减丸数服之。', '《医统》卷四十六', '解肌骨之热，散滞毒。主积热劳瘦不食，热壅疮肿。', '-', '-', '柴胡', '[\'龙胆草\', \'柴胡\', \'黄芩\', \'鳖甲\', \'桃仁\', \'皮尖\', \'半两\', \'栀子\', \'半两\', \'陈皮\', \'半两\', \'当归\', \'酒洗\', \'半两\', \'大黄\', \'半两\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (57252, '32983', '百合汤', '百合1两，知母（焙）1两，鳖甲（去裙襕，醋炙）1两，柴胡（去苗）1两，葛根（锉）1两，桑根白皮（锉）1两。', '每服5钱匕，水1盏半，煎至8分，去滓，入生地黄汁1合，搅匀，食后温服，日2次。', '《圣济总录》卷三十一', '伤寒后四肢烦热，骨节疼痛。', '-', '-', '柴胡', '[\'百合\', \'知母\', \'鳖甲\', \'柴胡\', \'葛根\', \'两桑根\', \'白皮\']');
INSERT INTO `prescript` VALUES (57253, '32960', '半夏散', '半夏3分（汤浸7遍去滑），柴胡1两（去苗），黄芩半两，赤芍药3分，甘草1分（炙微赤，锉），桂心半两，陈橘皮3分（汤浸，去白瓤，焙），大腹皮3分（锉）。', '每服5钱，以水1大盏，加生姜半分，煎去5分，去滓温服，不拘时候。', '《圣惠》卷十八', '热病，腹胃虚胀，心膈壅滞，呕哕不能食。', '-', '-', '柴胡', '[\'半夏\', \'分汤\', \'浸遍\', \'柴胡\', \'两去\', \'黄芩\', \'半两\', \'芍药\', \'甘草\', \'分炙微\', \'桂心\', \'半两\', \'橘皮\', \'分汤\', \'浸去\', \'大腹皮\']');
INSERT INTO `prescript` VALUES (57254, '32903', '加减逍遥散', '柴胡5钱，白芍两半（酒炒），鳖甲3两（醋炒），生地5两，茯苓3两，白术两半（炒），木香1两，米仁5两（炒），智仁3两（盐水炒），生姜2片。', '熟地、砂仁1钱煎汤，煎3钱，去滓温服。', '《医略六书》卷三十', '腹胀潮热，脉弦虚数者。', '-', '-', '柴胡', '[\'柴胡\', \'白芍\', \'两半\', \'鳖甲\', \'两醋\', \'生地\', \'茯苓\', \'白术\', \'两半\', \'木香\', \'两米\', \'炒智仁\', \'盐水\', \'生姜\']');
INSERT INTO `prescript` VALUES (57255, '32894', '归术降胞汤', '当归2两，白术2两，柴胡1钱，牛膝3钱，丹皮3钱，红花5钱，荆芥3钱，益母草5钱。', '水煎服。', '《辨证录》卷十二', '妇人气结不行，产数日而胎不下，服催生药皆不效者。', '-', '-', '柴胡', '[\'当归\', \'白术\', \'柴胡\', \'牛膝\', \'丹皮\', \'钱红\', \'花钱\', \'荆芥\', \'益母草\']');
INSERT INTO `prescript` VALUES (57256, '32810', '补阴益气煎', '生地5钱，人参3钱，山药3钱（炒），阿胶3钱（蛤粉炒），白芍1钱半（炒），炙草1钱半，柴胡5分，茯神1钱半（去木），黄耆3钱（蜜炙）。', '水煎，去滓温服。', '《医略六书》卷十八', '气阴两亏，不能摄火而火不归经，或下血，或潮热，脉软数者。', '-', '-', '柴胡', '[\'生地\', \'人参\', \'山药\', \'阿胶\', \'蛤粉\', \'白芍\', \'钱半\', \'炒炙草\', \'柴胡\', \'茯神\', \'钱半去\', \'黄耆\', \'钱蜜\']');
INSERT INTO `prescript` VALUES (57257, '32793', '地血散', '茜草1钱，当归1钱，白芍1钱，乌梅1钱，柴胡1钱，知母1钱。', '加生姜3片，水煎，温服。', '《扁鹊心书·神方》', '妇人心血间有热，饮食不减，起居如常．但发烦热。', '-', '-', '柴胡', '[\'茜草\', \'当归\', \'白芍\', \'乌梅\', \'柴胡\', \'知母\']');
INSERT INTO `prescript` VALUES (57258, '32767', '龙脑鸡苏丸', '鸡苏叶（龙脑薄荷是也），黄耆2两，麦门冬（去心）4两，甘草1两半，黄连1两，干地黄6两（为末），人参2两，木通2两，新蒲黄2两，阿胶（炒焦）2两，柴胡（银州鼠尾红色者）2两（锉，同木通沸汤半升，浸1日夜，绞取汁）。', '《兰台轨范》有黄芩，无黄耆。', '《元戎》卷五', '除劳解热，下气散郁，清神爽气，润肺开心，益志滋肝，补肾，令人身强体轻，耳目聪明，利膈，化热痰，去膀胱中积热。主肺热咳血，心热惊悸，脾胃热口甘吐血，肝胆热气出口苦，肾热神志不定，上而酒毒、膈热、消渴，下而血痢、五淋、血崩。', '-', '-', '柴胡', '[\'鸡苏叶\', \'龙脑\', \'薄荷\', \'黄耆\', \'麦门冬\', \'甘草\', \'两半\', \'黄连\', \'两干\', \'地黄\', \'两为\', \'人参\', \'木通\', \'两新\', \'蒲黄\', \'阿胶\', \'炒焦\', \'柴胡\', \'银州\', \'鼠尾\', \'红色\', \'木通\', \'沸汤\', \'半升\', \'日夜\', \'绞取\']');
INSERT INTO `prescript` VALUES (57259, '32756', '加减正元丹', '香附1斤（同艾2两，醋浸2宿，分作4分，1分用盐水炒，1分酥炙，1分童便浸炒，1分和乳瓦上炒），当归身（酒洗）5两，川芎2两，白芍药8两（酒浸，切片，半生半炒），生地6两（酒洗），阿胶4两（蛤粉炒成珠，无则鹿角胶代之），枳壳3两（江西者良，半生半炒），艾2两（用浸香附醋打糊饼，晒干），青蒿子3两，山茱萸肉3两，银柴胡1两，五味子3两，鳖甲（醋炙如法）4两。', '每服4钱，空腹淡醋汤吞下。如经后期，去青蒿子、银柴胡、鳖甲。', '《广笔记》卷二', '妇人月经不调，无子。', '忌白莱菔。如经调后，觉经不行，恐有妊娠，即勿服。', '-', '柴胡', '[\'香附\', \'同艾\', \'两醋\', \'浸宿\', \'分作分\', \'分用\', \'盐水\', \'炒分\', \'炙分\', \'童便\', \'炒分\', \'乳瓦上\', \'当归\', \'身酒\', \'川芎\', \'两白\', \'芍药\', \'两酒\', \'切片\', \'半生\', \'生地\', \'两酒\', \'阿胶\', \'蛤粉\', \'炒成\', \'珠无则\', \'鹿角胶\', \'代之\', \'枳壳\', \'江西\', \'半生\', \'两用\', \'香附\', \'打糊饼\', \'晒干\', \'青蒿\', \'山茱萸\', \'银柴胡\', \'五味子\', \'鳖甲\', \'如法\']');
INSERT INTO `prescript` VALUES (57260, '32697', '凤麟膏', '荆芥1钱，薄荷1钱，玄参1钱，防风1钱，白芷1钱，蕤仁1钱，白芍1钱，栀子1钱，干葛1钱，连翘1钱，当归1钱，黄芩1钱，柴胡1钱，升麻1钱，车前子1钱，生地1钱，木贼1钱，细辛1钱，枸杞1钱，桑白皮1钱，夏枯草1钱，黄连1钱，菊花1钱，石决明1钱，蒺藜1钱，白术1钱，香附米1钱，黄柏1钱。', '本方用法中的“龙砂”、“虎液”，均出原着。参阅“龙砂”、“虎液”条。', '《眼科阐微》卷四', '降火，拨云去翳。主', '-', '-', '柴胡', '[\'荆芥\', \'薄荷\', \'玄参\', \'防风\', \'白芷\', \'蕤仁\', \'白芍\', \'栀子\', \'干葛钱\', \'连翘\', \'当归\', \'黄芩\', \'柴胡\', \'升麻\', \'车前子\', \'生地\', \'木贼\', \'细辛\', \'枸杞\', \'桑白皮\', \'夏枯草\', \'黄连\', \'菊花\', \'石决明\', \'蒺藜\', \'白术\', \'香附\', \'黄柏\']');
INSERT INTO `prescript` VALUES (57261, '32696', '地骨皮汤', '地骨皮（去土）半两，人参半两，甘草（炙，锉）半两，柴胡（去苗）半两，葛根（锉）半两，麦门冬（去心，焙）半两。', '每服3钱匕，水1盏，加竹叶20片，生姜2片，同煎至7分，去滓，食后温服。', '《圣济总录》卷十二', '风热毒气，身体烦热，头目不利，口干舌涩，夜卧不安。兼解利伤寒汗后，余热烦躁。', '-', '-', '柴胡', '[\'地骨皮\', \'半两\', \'人参\', \'半两\', \'甘草\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'葛根\', \'半两\', \'麦门冬\', \'半两\']');
INSERT INTO `prescript` VALUES (57262, '32685', '佛手散', '当归1两，川芎1两，黄耆1两，北柴胡1分，前胡1分。', '上（口父？？）咀。每服3钱，水1大盏，桃、柳枝各3寸，枣子、乌梅各1个，生姜3片，煎至6分，去滓温服。如有痰，去乌梅。', '《妇人良方》卷二十一', '产后血虚劳倦，盗汗，多困少力，咳嗽有痰。', '-', '-', '柴胡', '[\'当归\', \'川芎\', \'两黄耆\', \'两北\', \'柴胡\', \'分前\', \'胡分\']');
INSERT INTO `prescript` VALUES (57263, '32673', '两宜汤', '茯苓5钱，白术1两，薏仁5钱，柴胡5分，龙胆草1钱，茵陈1钱，郁李仁5分。', '水煎服。2剂轻，4剂又轻，10剂全愈。', '《辨证录》卷七', '心惊胆战，面目俱黄，小水不利，皮肤瘦削。', '-', '-', '柴胡', '[\'茯苓\', \'白术\', \'薏仁\', \'柴胡\', \'龙胆草\', \'茵陈\', \'郁李仁\']');
INSERT INTO `prescript` VALUES (57264, '32672', '耳聋左慈丸', '熟地4两，山萸肉（炙）2两，茯苓1两5钱，山药2两，丹皮1两5钱，泽泻1两5钱，磁石3两，柴胡1两1钱。', '耳鸣丸（《全国中药成药处方集》（南京方）、柴磁地黄丸《全国中药成药处方集》武汉方）。', '《饲鹤亭集方》', '滋阴清热，益气平肝。主肾水不足，虚火上升，头眩目晕，耳聋耳鸣。', '-', '-', '柴胡', '[\'熟地\', \'两山萸\', \'茯苓\', \'山药\', \'丹皮\', \'泽泻\', \'磁石\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57265, '32628', '补中益气汤', '黄耆1钱，人参1钱，白术1钱，当归1钱，炙草5分，陈皮5分，升麻3分，柴胡3分，麻黄根1钱，浮麦1钱。', '尺脉虚大，加黄柏、知母、熟地；夹风邪，加挂枝5分，白芍1钱。', '《嵩屋尊生》卷八', '内伤气虚自汗。', '-', '-', '柴胡', '[\'黄耆\', \'人参\', \'白术\', \'当归\', \'草分\', \'陈皮分\', \'升麻\', \'柴胡\', \'麻黄根\', \'浮麦\']');
INSERT INTO `prescript` VALUES (57266, '32616', '加味达原饮', '槟榔2钱，川朴2钱（捣），草果1钱（炒，研），知母1钱，黄芩1钱（生），白芍1钱（生），甘草1钱，柴胡2钱，羌活2钱，葛根2钱。', '原书本方治表症头痛加生姜三片，大枣二个为引。', '《医门八法》卷二', '瘟疫盛行之年，偶感风寒，触动瘟疫者，以及并无疫疠之年，重感风寒，全似瘟疫者，初感风寒、瘟疫，致表症头痛，兼憎寒发热等表证。', '-', '-', '柴胡', '[\'槟榔\', \'钱川朴\', \'草果\', \'炒研\', \'知母\', \'黄芩\', \'钱生\', \'白芍\', \'生甘草\', \'柴胡\', \'羌活\', \'葛根\']');
INSERT INTO `prescript` VALUES (57267, '32530', '加味左金汤', '黄连5分，吴萸2分，瓦楞子3钱（煅，研），荜澄茄1钱，蒺藜3分，郁金2钱，青皮1钱，柴胡1钱（醋炒），延胡索1钱，木香5分，广皮1钱，砂仁1钱，佛手5分。', '-', '《医醇剩义》卷四', '肝气郁结，气火俱升，上犯胃经，痛连胁肋。', '-', '-', '柴胡', '[\'黄连\', \'吴萸\', \'瓦楞子\', \'煅研\', \'荜澄茄\', \'蒺藜\', \'分郁\', \'金钱\', \'青皮\', \'柴胡\', \'延胡索\', \'木香\', \'分广皮\', \'砂仁\', \'佛手\']');
INSERT INTO `prescript` VALUES (57268, '32522', '地骨皮汤', '地骨皮半两，知母（焙）半两，柴胡（去苗）半两，枳壳（去瓤，麸炒）半两，鳖甲（去裙襕，醋炙）3分，赤茯苓（去黑皮）3分，虎头骨1两（酒涂炙焦）。', '每服以水3盏．煎取1盏，加地黄汁1合，更煎3-5沸，去滓，分2次温服，食后良久再服，1日1帖。', '《圣济总录》卷三十七', '寒热往来，久成痨瘵。', '-', '-', '柴胡', '[\'地骨皮\', \'半两\', \'知母\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'枳壳\', \'半两\', \'鳖甲\', \'炙分\', \'茯苓\', \'黑皮\', \'分虎\', \'头骨\', \'两酒\', \'涂炙焦\']');
INSERT INTO `prescript` VALUES (57269, '32509', '当归龙胆汤', '防风1钱5分，石膏1钱5分，柴胡2钱，羌活2钱，五味子2钱，升麻2钱，甘草3钱，酒黄连3钱，黄耆3钱，酒黄芩（炒）5钱，酒黄柏（炒）5钱，当归身（酒洗）5钱，草龙胆（酒洗）5钱，芍药5钱。', '羌活退翳散（《审视瑶函》卷三）。本方方名，《保婴撮要》引作“羌活退翳汤”。', '《兰室秘藏》卷上', '眼中白翳。', '-', '-', '柴胡', '[\'防风\', \'钱分\', \'石膏\', \'钱分\', \'柴胡\', \'羌活\', \'五味子\', \'升麻\', \'甘草\', \'钱酒\', \'黄连\', \'黄耆\', \'黄芩\', \'黄柏\', \'当归\', \'身酒\', \'洗钱\', \'龙胆\', \'洗钱\', \'芍药\']');
INSERT INTO `prescript` VALUES (57270, '32503', '半夏散', '半夏2两（汤洗7遍去滑），葛根1两（锉），白术1两，人参1两（去芦头），柴胡2两（去苗），陈橘皮1两（汤浸，去白瓤，焙），厚朴1两（去粗皮，涂生姜汁炙令香熟），黄芩1两，甘草1两（炙微赤，锉）。', '每服4钱，以水1中盏，加大枣3个，生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷九', '伤寒9日不解，往来寒热，状如温疟，胸膈满闷，时有痰逆不止。', '-', '-', '柴胡', '[\'半夏\', \'两汤\', \'洗遍\', \'葛根\', \'白术\', \'人参\', \'两去\', \'芦头\', \'柴胡\', \'两去\', \'橘皮\', \'两汤\', \'浸去\', \'厚朴\', \'两去\', \'粗皮\', \'涂生\', \'姜汁\', \'炙令\', \'香熟\', \'黄芩\', \'甘草\', \'两炙微\']');
INSERT INTO `prescript` VALUES (57271, '32483', '加料舒肝丸', '香附4两，当归1两，枳实8钱，醋柴胡8钱，沉香5钱，毛橘1两，川芎8钱，郁金5钱，醋青皮1两，琥珀5钱，木香5钱，元胡8钱，油桂5钱，老蔻5钱，红花5钱，油朴1两，乌药8钱，白芍8钱，枳壳8钱，块苓1两。', '每服1丸，白水送下。', '《全国中药成药处方集》（吉林方）', '平肝顺气。主肝瘀气滞。', '忌食腥冷。', '-', '柴胡', '[\'香附\', \'当归\', \'枳实\', \'柴胡\', \'沉香\', \'毛橘\', \'川芎\', \'钱郁\', \'金钱\', \'青皮\', \'琥珀\', \'木香\', \'钱元\', \'胡钱\', \'油桂钱\', \'老蔻\', \'花钱\', \'油朴\', \'乌药\', \'白芍\', \'枳壳\', \'钱块苓\']');
INSERT INTO `prescript` VALUES (57272, '32477', '贝母汤', '贝母1两（去心，姜制半日，焙），黄芩（生，去皮）1两，干姜（生，）1两，陈皮（去白）1两，五味子（拣）1两，桑白皮（洗净，蜜炙黄）半两，半夏（汤浸7次）半两，柴胡（去苗，净洗）半两，桂心（不见火）半两，木香1分，甘草1分（炙）。', '每服5钱，水1盏半，加杏仁7个(去皮尖，碎之)生姜7片，同煎至7分，去滓热服。', '《本事》卷三', '诸嗽久不愈。', '-', '-', '柴胡', '[\'贝母\', \'两去\', \'姜制\', \'半日\', \'黄芩\', \'去皮\', \'干姜\', \'生两\', \'陈皮\', \'五味子\', \'桑白皮\', \'洗净\', \'蜜炙黄\', \'半两\', \'半夏\', \'浸次\', \'半两\', \'柴胡\', \'苗净\', \'半两\', \'桂心\', \'不见\', \'半两\', \'木香\', \'甘草\']');
INSERT INTO `prescript` VALUES (57273, '32386', '加减补中和疟饮', '人参1钱，甜于术5钱，黄耆3钱（蜜炙），当归3钱，炙甘草5分，柴胡8分（炒），青皮1钱5分，草蔻仁1钱（研），干姜1钱（蜜炙），益母草3钱。', '加大枣3枚，河、井水各半煎，露1宿，疟前2时温服，二煎接服更妙。如热多，加青蒿3钱；如恶心，加灶心土5钱；如腹痛作泻，加煨广木香1钱5分、酒炒元胡索2钱；如胸口不宽，加炒枳壳1钱5分。', '《慈航集》卷下', '产后疟证，寒多热少。', '-', '-', '柴胡', '[\'人参\', \'钱甜\', \'于术\', \'黄耆\', \'钱蜜\', \'当归\', \'甘草\', \'柴胡\', \'青皮\', \'钱分\', \'草蔻仁\', \'钱研\', \'干姜\', \'钱蜜\', \'益母草\']');
INSERT INTO `prescript` VALUES (57274, '32321', '加减四物汤', '熟地黄3钱，当归3钱，川芎1钱，白芍2钱，山栀仁（炒）1钱，柴胡1钱，牡丹皮1钱。', '水煎服。', '《叶氏女科》卷三', '产后恶露不止，怒火伤肝而血不藏者；或产后血虚火燥之大便闭结。', '-', '-', '柴胡', '[\'熟地黄\', \'当归\', \'川芎\', \'白芍\', \'钱山\', \'栀仁\', \'柴胡\', \'牡丹皮\']');
INSERT INTO `prescript` VALUES (57275, '32309', '当归饮子', '当归2两，柴胡2两，人参1两，半夏7钱半，白芍药1两半，黄芩1两，甘草半两。', '每服4钱，水1盏半，加生姜3片，大枣1枚，煎8分，去滓服，不拘时候。', '《普济方》卷三五五', '妇人血虚劳倦，五心烦热，肢体疼痛，头目昏重，心忪颊赤，口燥咽干，发搦盗汗，减食嗜卧；及血热相搏，月水不调，脐腹胀满疼痛，寒热如疟；又疗室女血弱，阴虚荣卫不和，痰嗽潮热，肌体羸瘦，渐成骨蒸。', '-', '-', '柴胡', '[\'当归\', \'柴胡\', \'人参\', \'半夏\', \'半白\', \'芍药\', \'两半\', \'黄芩\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (57276, '32294', '加味小柴胡汤', '小柴胡5钱加官桂1钱，草果1钱，槟榔1钱。', '上(口父)咀。每服水2盏，加生姜3片，大枣1枚同煎，去滓，午后温服；滓再煎，不拘时候。夏月，加香薷1钱。', '《普济方》卷一九七引《德生堂方》', '表里不分，发为疟疾。', '-', '-', '柴胡', '[\'小柴胡\', \'加官\', \'桂钱\', \'草果\', \'槟榔\']');
INSERT INTO `prescript` VALUES (57277, '32283', '龙胆汤', '龙胆6铢，钩藤皮6铢，柴胡6铢，黄芩6铢，桔梗6铢，芍药6铢，茯苓（一方作茯神）6铢，甘草6铢，蜣螂2枚，大黄1两。', '龙胆散（《圣惠》卷八十二）。《直指小儿》引本方用法：为末。每服一钱，北枣煎服；或加防风、麦门冬以导心热，黄芩减半用。', '《千金》卷五', '清热舒利。主婴儿发热，呕吐下利，舌上生疮，毛发不泽，脐风，惊痫；婴儿出腹，血脉盛实，寒热温壮，四肢惊掣，发热，大吐 (口见) 者。若已能进哺，中食实不消，壮热及变蒸不解，中客人鬼气，并诸惊痫；胎惊，月内气盛发热；及妇人有儿，未能行时，复有孕，使儿饮此乳，亦作此病；噤口，舌上生疮如黍米状，吮乳不得，啼声渐小，因胎热所致者。', '-', '-', '柴胡', '[\'龙胆\', \'钩藤\', \'皮铢\', \'柴胡\', \'黄芩\', \'桔梗\', \'芍药\', \'茯苓\', \'一方\', \'茯神\', \'甘草\', \'蜣螂\', \'大黄\']');
INSERT INTO `prescript` VALUES (57278, '32268', '经效散', '黄芩3钱，当归3钱，芍药3钱，大黄2钱，犀角、粉草、白芷、柴胡。', '上(口父)咀。水煎服。', '《良朋汇集》卷五', '偷针撞刺，昧目飞尘。', '-', '-', '柴胡', '[\'黄芩\', \'当归\', \'芍药\', \'大黄\', \'犀角\', \'粉草\', \'白芷\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57279, '32251', '车前子饮', '车前子1两，甘菊花1两，天雄（炮裂，去皮脐）1两，当归（炙、锉）1两，京三棱（煨、锉）1两，黄连（去须）1两，熟干地黄（焙）1两半，桔梗（锉、炒）1两半，延胡索1两半，萆薢1两半，柴胡（去苗）1两半，赤芍药1两半，赤石脂（研）1两半，石膏（推碎）3两，桂（去粗皮）半两。', '每服5钱匕，以水1盏半，加生姜1枣大(切)，煎取8分，去滓温服，不拘时候。', '《圣济总录》卷一五一', '妇人经水不调，头眩睛疼，恶心，减食。', '-', '-', '柴胡', '[\'车前子\', \'甘菊花\', \'两天\', \'雄炮裂\', \'去皮\', \'当归\', \'两京\', \'三棱\', \'黄连\', \'两熟\', \'地黄\', \'两半\', \'桔梗\', \'两半\', \'延胡索\', \'两半\', \'两半\', \'柴胡\', \'去苗\', \'两半\', \'芍药\', \'两半\', \'赤石脂\', \'两半\', \'石膏\', \'推碎\', \'两桂去\', \'粗皮\', \'半两\']');
INSERT INTO `prescript` VALUES (57280, '32235', '决明子散', '决明子1两，川升麻1两，枳壳（麸炒微黄，去瓤）1两，柴胡（去苗）1两，栀子仁1两，车前子1两，细辛1两，防风（去芦头）1两，黄连（去须）3分，甘草半两（炙微赤，锉）。', '每服3钱，以水1中盏，煎至6分，去滓，食后温服。', '《圣惠》卷三十二', '客热冲眼，赤多泪出，生疮。', '忌炙煿热酒面。', '-', '柴胡', '[\'决明子\', \'两川\', \'升麻\', \'枳壳\', \'微黄\', \'柴胡\', \'栀子\', \'车前子\', \'细辛\', \'防风\', \'芦头\', \'黄连\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (57281, '32227', '龙胆泻肝汤', '龙胆草1钱，柴胡1钱，泽泻1钱，木通5分，车前子5分，赤茯苓5分，生地黄5分，当归5分，酒拌山栀仁5分，黄芩5分，甘草5分。', '水煎，空心服。', '《东医宝鉴》卷四引《入门》', '肝脏湿热，男子阴挺肿胀，妇人阴挺疮疡，或阴茎湿痒，出脓水，此因酒得之。', '-', '-', '柴胡', '[\'龙胆草\', \'柴胡\', \'泽泻\', \'木通\', \'车前子\', \'分赤\', \'茯苓\', \'生地黄\', \'当归\', \'分酒\', \'栀仁分\', \'黄芩\', \'甘草\']');
INSERT INTO `prescript` VALUES (57282, '32213', '白鲜皮散', '白鲜皮1两，黄芩1两，柴胡1两，大青1两，麦门冬1两，栀子仁1两，甘草（炙微赤，锉）1两，羚羊角屑半两，川大黄1两（锉碎，微炒）。', '每服5钱，以水1大盏，煎至5分，去滓温服，不拘时候。', '《圣惠》卷十五', '时气6日，热毒不退，心胸烦躁，大小肠秘涩，不得眠卧。', '-', '-', '柴胡', '[\'白鲜皮\', \'黄芩\', \'柴胡\', \'两大青\', \'麦门冬\', \'栀子\', \'甘草\', \'炙微\', \'羚羊角\', \'半两\', \'大黄\']');
INSERT INTO `prescript` VALUES (57283, '32211', '加减完带汤', '泡参4钱，白芍2钱，苍术2钱，茵陈3钱，甘草1钱，荆芥1钱，柴胡8分，栀子2钱，黄柏2钱，黄连1钱。', '水煎，温服。阴道痒者，加蛇床子、银花各2钱。', '《中医妇科治疗学》', '清热渗湿。主带下色青，质粘稠，且有臭气，面色苍黄，头胀眩重，精神疲惫，胸闷胁痛，不思饮食，舌淡红，苔黄腻，脉象弦数。', '-', '-', '柴胡', '[\'参钱\', \'白芍\', \'苍术\', \'茵陈\', \'甘草\', \'荆芥\', \'柴胡\', \'栀子\', \'黄柏\', \'黄连\']');
INSERT INTO `prescript` VALUES (57284, '32164', '龙虎丹', '黑牵牛（爁）2两，藿香叶（生）2两，天麻（去苗）2两，牛膝（去苗，酒浸，切，焙，微炒）2两，硫黄（结沙）2两，天竺黄（生研）2两，细辛（去苗，洗）2两，半夏（汤洗7次，生姜汁制）2两，附子（炮，去皮脐）2两，何首乌（去粗皮）2两，羌活（去苗，洗焙）2两，独活（去苗）2两，柴胡（去苗）2两，川芎（洗）2两，桔梗（生）2两，寒水石（烧通赤，研飞）1斤，茴香（淘去土，焙）5两，甘松（洗去土，焙）5两，肉桂（去粗皮）5两，五灵脂（生）5两，白芷（生）5两，菊花（去土）5两，川乌（炮，去皮脐）5两，白僵蚕（去丝嘴，', '每服1丸，用薄荷酒嚼下，日1次，重即2次。产后惊风，乱道见物，朱砂酒磨下；产后身多虚肿，血风，频增昏沉，身如针刺，发随梳落，面黄心逆，并煎当归酒嚼下，日2次；若治伤寒，炒葱、豉，酒嚼下1-2服，盖覆出汗立愈；小儿惊风，薄荷酒化下少许；大人急风，口噤失音等，薄荷酒化灌之。常服茶、酒任下，不拘时候。', '《局方》卷一', '丈夫妇人新得、久患急、缓风，半身不遂，手脚筋衰，及风毒攻注，遍身疮疥，头风多饶白屑，毒风面上生疮，刺风状如针刺，痫风急倒作声，顽风不认痛痒，疬风颈生斑驳，暗风头旋眼黑，隳风面生赤点，肝风鼻闷眼瞤，偏风口眼斜，节风肢节断续，脾风心多呕逆，酒风行步不前，肺风鼻塞项疼，胆风令人不睡，气风肉似虫行，肾风耳内蝉鸣，阴间湿痒。', '-', '-', '柴胡', '[\'牵牛\', \'藿香\', \'叶生\', \'天麻\', \'牛膝\', \'苗酒\', \'浸切\', \'硫黄\', \'结沙\', \'天竺黄\', \'生研\', \'细辛\', \'苗洗\', \'半夏\', \'汤洗\', \'次生\', \'姜汁\', \'制两\', \'附子\', \'去皮\', \'何首乌\', \'粗皮\', \'羌活\', \'苗洗\', \'独活\', \'柴胡\', \'川芎\', \'桔梗\', \'生两\', \'寒水石\', \'烧通\', \'研飞斤\', \'茴香\', \'淘去\', \'土焙\', \'两甘松\', \'肉桂\', \'粗皮\', \'五灵脂\', \'生两\', \'白芷\', \'生两\', \'菊花\', \'川乌\', \'去皮\', \'白僵蚕\']');
INSERT INTO `prescript` VALUES (57285, '32128', '利胆汤', '柴胡5钱，茵陈5钱，郁金5钱，黄芩5钱，白芍5钱，大黄（后下）5钱，金银花1两，大青叶1两，金钱草1两，芒消（冲服）3钱，木香4钱。', '-', '《新急腹症学》引青岛台西医院方', '清热利胆。主胆道蛔虫病恢复期。', '-', '-', '柴胡', '[\'柴胡\', \'茵陈\', \'钱郁\', \'金钱\', \'黄芩\', \'白芍\', \'大黄\', \'后下\', \'金银花\', \'大青叶\', \'金钱草\', \'两芒\', \'冲服\', \'木香\']');
INSERT INTO `prescript` VALUES (57286, '32115', '红花当归散', '柴胡3钱，陈皮3钱，当归6钱，川芎5钱，赤芍药5钱，熟地5钱，小茴香2钱，枳壳2钱，三棱2钱，干漆2钱，玄胡索3钱，厚朴3钱，香附3钱，黄芩3钱，白术3钱，红花3钱，甘草1钱5分。', '《郑氏家传女科万金方》有牛膝、生姜，无干漆。遍身疼，加羌活3钱；泄泻，加肉蔻、粟壳各2钱；咳嗽，加杏仁、五味、桔梗、苏叶各3钱，气急亦然。', '《女科万金方》', '妇人32-33岁，连年生育，败血过多，血虚脾胃弱，盛热，以致经水不匀，或1月、或40日、或2月，不时肚痛，腹中结块，饮食少进，困倦，潮热往来，恶心烦躁。', '-', '-', '柴胡', '[\'柴胡\', \'陈皮\', \'当归\', \'川芎\', \'芍药\', \'熟地\', \'小茴香\', \'枳壳\', \'三棱\', \'干漆\', \'钱玄\', \'胡索\', \'厚朴\', \'香附\', \'黄芩\', \'白术\', \'钱红\', \'花钱\', \'甘草\', \'钱分\']');
INSERT INTO `prescript` VALUES (57287, '32088', '加减补中益气汤', '人参1钱半，黄耆3钱（蜜炙），白术3钱（制），升麻5分，当归3钱，柴胡5分，白芍1钱半（炒），龙骨3钱（煅），牡蛎3钱（煅），熟地5钱。', '水煎，去滓温服。', '《医略六书》卷二十六', '阴中挺出，脉软者。', '-', '-', '柴胡', '[\'人参\', \'钱半\', \'黄耆\', \'钱蜜\', \'白术\', \'钱制\', \'升麻\', \'当归\', \'柴胡\', \'白芍\', \'钱半\', \'龙骨\', \'牡蛎\', \'熟地\']');
INSERT INTO `prescript` VALUES (57288, '32074', '白术散', '白术1两，白芷半两，鳖甲（去裙襕，醋炙令焦）半两，苍术（米泔浸1宿，锉，焙）半两，防风（去叉）半两，厚朴（去粗皮，生姜汁炙，锉）半两，桂（去粗皮）半两，人参半两，陈橘皮（去白，焙）半两，干姜（炮）半两，高良姜（炮）半两，吴茱萸（汤洗3遍，焙干）1两，柴胡（去苗）1两，蜀椒（去合口并目，炒出汗）1两，芎藭1两，白茯苓（去黑皮）1两，白芜荑1两，缩砂（去皮）1两，附子2枚（炮裂，去皮脐），沉香（锉）1分，丁香1分，当归（炙，锉）1分，木香1分。', '每服5钱匕，用猪肝3两，批开，入葱白，盐各少许，掺药在内，湿纸要，慢火煨香熟为度，空心，食前米饮嚼下。', '《圣济总录》卷八十七', '冷劳，大便滑泄，食饮不美，有盗汗。', '-', '-', '柴胡', '[\'白术\', \'白芷\', \'半两\', \'鳖甲\', \'炙令\', \'半两\', \'苍术\', \'浸宿\', \'半两\', \'防风\', \'半两\', \'厚朴\', \'粗皮生\', \'姜汁\', \'半两\', \'桂去\', \'粗皮\', \'半两\', \'人参\', \'半两\', \'橘皮\', \'半两\', \'干姜\', \'半两\', \'高良姜\', \'半两\', \'吴茱萸\', \'洗遍\', \'焙干\', \'柴胡\', \'去苗\', \'两蜀椒\', \'合口\', \'并目\', \'出汗\', \'白茯苓\', \'黑皮\', \'两白\', \'两缩\', \'去皮\', \'附子\', \'炮裂\', \'去皮\', \'沉香\', \'锉分\', \'丁香\', \'当归\', \'木香\']');
INSERT INTO `prescript` VALUES (57289, '32045', '加味补中益气汤', '人参（淮山药炒黄3-5钱代之亦可），黄耆（蜜炒）2钱，白术1钱5分，当归1钱5分，熟地1钱5分，白芍（酒炒）1钱，陈皮1钱，甘草（炙）8分，升麻（蜜炒）3分，柴胡（酒炒）3分。', '方中人参用量原缺。', '《会约》卷十四', '元气虚损，不时漏血，历年不止者。', '-', '-', '柴胡', '[\'人参\', \'山药\', \'钱代\', \'亦可\', \'黄耆\', \'白术\', \'钱分\', \'当归\', \'钱分\', \'熟地\', \'钱分\', \'白芍\', \'陈皮\', \'甘草\', \'炙分\', \'升麻\', \'炒分\', \'柴胡\', \'炒分\']');
INSERT INTO `prescript` VALUES (57290, '32040', '木香蛤蚧丸', '蛤蚧1对（尾全者，洗净，酥炙），生鳖甲7钱半（去裙，醋炙焦），白茯苓7钱半，川芎7钱半，当归7钱半，北五味子7钱半，牛膝7钱半，绵黄耆半两，柴胡半两，知母半两，贝母半两（去心），鸡心槟榔半两，明阿胶半两（炒酥），巴戟半两（酒浸，去心），桃仁半两（酒浸，去皮，焙），肉豆蔻3个（生），木香3钱，秦艽3钱（洗），羌活3钱，破故纸3钱（炒），生发3钱（纸燃火烧存性）。', '每服1丸，煎枣汤空心嚼下。', '《直指》卷九', '劳瘵久嗽声干，骨痿瘦瘁。', '-', '-', '柴胡', '[\'蛤蚧\', \'对尾\', \'全者\', \'洗净\', \'酥炙生\', \'鳖甲\', \'钱半去\', \'白茯苓\', \'钱半\', \'川芎\', \'钱半\', \'当归\', \'钱半北\', \'五味子\', \'钱半\', \'牛膝\', \'钱半绵\', \'黄耆\', \'半两\', \'柴胡\', \'半两\', \'知母\', \'半两\', \'贝母\', \'半两\', \'去心\', \'鸡心\', \'槟榔\', \'半两\', \'阿胶\', \'半两\', \'巴戟\', \'半两\', \'酒浸\', \'桃仁\', \'半两\', \'酒浸\', \'去皮\', \'肉豆蔻\', \'个生\', \'木香\', \'秦艽\', \'羌活\', \'钱破\', \'纸钱\', \'生发\', \'钱纸\', \'火烧\', \'存性\']');
INSERT INTO `prescript` VALUES (57291, '32026', '加减黄耆建中汤', '白术3钱，白茯苓3钱，桔梗3钱，人参3钱半，秦艽5钱，北柴胡（去芦）5钱，防风5钱，白芍药5钱，甘草5钱，当归（去尾）5钱，泽泻5钱，生干地黄5钱，熟地5钱，黄地5钱，骨皮肉5钱，豆蔻（煨）5钱，槟榔5钱，缩砂仁5钱，猪苓4钱，黄耆1两。', '每服3钱，水1盏半煎，温服，不拘时候。老人更加黄耆1两，或为末，蜜汤调服，临期斟酌。', '《得效》卷九', '男子妇人，五劳骨蒸者。', '-', '-', '柴胡', '[\'白术\', \'白茯苓\', \'桔梗\', \'人参\', \'钱半\', \'秦艽\', \'钱北\', \'柴胡\', \'芦钱\', \'防风\', \'钱白\', \'芍药\', \'甘草\', \'当归\', \'泽泻\', \'钱生\', \'地黄\', \'熟地\', \'黄地\', \'皮肉\', \'豆蔻\', \'槟榔\', \'钱缩\', \'砂仁\', \'猪苓\', \'黄耆\']');
INSERT INTO `prescript` VALUES (57292, '32021', '加味补中益气汤', '黄耆6钱，白术6钱，广皮2钱，升麻2钱，柴胡2钱，泡参2钱，秦归2钱，乌贼骨2两，茜草根（炒炭）4钱。', '水煎服。', '《中医妇科治疗学》', '补气摄血。主崩中或漏下不止，色淡红，精神疲惫，气短自汗。', '-', '-', '柴胡', '[\'黄耆\', \'白术\', \'钱广皮\', \'升麻\', \'柴胡\', \'钱泡\', \'参钱\', \'秦归钱\', \'乌贼骨\', \'茜草\', \'炒炭\']');
INSERT INTO `prescript` VALUES (57293, '31987', '两消丹', '柴胡2钱，丹皮5钱，鳖甲3钱，山楂肉1钱，枳壳5分，炒栀子2钱，甘草1钱，白芍5钱，当归3钱，桃仁10粒。', '水煎服。1剂而痛轻，2剂谵语止，腹亦安然，杳无寒热之苦矣。', '《辨证录》卷一', '冬月妇人伤寒，发热至6-7日，昼则了了，夜则谵语，如见鬼状，按其腹则大痛欲死。', '-', '-', '柴胡', '[\'柴胡\', \'丹皮\', \'鳖甲\', \'山楂\', \'枳壳\', \'栀子\', \'甘草\', \'白芍\', \'当归\', \'桃仁\']');
INSERT INTO `prescript` VALUES (57294, '31978', '加减归脾汤', '人参3钱，当归3钱，茯苓3钱，白术3钱，白芍3钱，甘草5分，半夏5分，川芎2钱，白豆蔻1粒，柴胡1钱，远志1钱，枣仁1钱，麦冬5钱。', '水煎服。', '《辨证录》卷九', '劳倦伤脾，色白神怯，秋间发热头痛，吐泻食少，两目喜闭，喉哑昏昧，粥饮有碍，手常揾住阴囊。', '-', '-', '柴胡', '[\'人参\', \'当归\', \'茯苓\', \'白术\', \'白芍\', \'甘草\', \'半夏\', \'川芎\', \'白豆蔻\', \'柴胡\', \'远志\', \'枣仁\', \'麦冬\']');
INSERT INTO `prescript` VALUES (57295, '31973', '地黄养血汤', '熟地1钱2分，归身1钱，柴胡5分，茯苓1钱，白芍1钱，蔓荆8分，枣仁1钱，丹皮1钱，炙草5分，远志肉1钱2分，川芎1钱，黄耆1钱2分，升麻3分。', '-', '《陈素庵妇科补解》卷一', '血虚气陷，经行发热，兼头重目暗。', '-', '-', '柴胡', '[\'熟地\', \'钱分\', \'归身\', \'柴胡\', \'茯苓\', \'白芍\', \'荆分\', \'枣仁\', \'丹皮\', \'草分\', \'远志\', \'肉钱分\', \'川芎\', \'黄耆\', \'钱分\', \'升麻\']');
INSERT INTO `prescript` VALUES (57296, '31967', '当归汤', '当归（切，焙）半两，柴胡（去芦头）3分，黄芩（去黑心）半两，细辛（去苗叶）3分，大黄（锉，炒）1两，升麻半两，五味子半两，紫菀（去苗土）1两，牛黄（研）1分，杏仁20个（汤浸，退去皮尖双仁，炒，别研）。', '2-3岁儿，每服1钱匕，以水7分，煎至5分，去滓温服，日3次。', '《圣济总录》卷一六八', '小儿挟实温壮，惕惕微惊。', '-', '-', '柴胡', '[\'当归\', \'切焙\', \'半两\', \'柴胡\', \'芦头分\', \'黄芩\', \'黑心\', \'半两\', \'细辛\', \'苗叶分\', \'大黄\', \'升麻\', \'半两\', \'五味子\', \'半两\', \'紫菀\', \'苗土\', \'牛黄\', \'研分\', \'杏仁\', \'退去\', \'皮尖\', \'双仁\', \'别研\']');
INSERT INTO `prescript` VALUES (57297, '31953', '防风立效散', '柴胡1两，升麻1两，牛蒡子1两（炒），全蝎1两，石膏1两（生用），干葛1两，赤芍药1两，甘草1两，北防风半两，郁金半两，薄荷叶半两，半夏半两（泡），赤茯苓半两，北细辛半两，川芎半两，羌活半两，桔梗2两，荆芥2两。', '每服4大钱，水1盏半，葱头1个，煎至1盏，食后卧时温服，滓合煎服。候发散后，又相间服《局方》解毒雄黄丸。', '《朱氏集验方》卷九', '咽喉病。', '忌酒、毒物。', '-', '柴胡', '[\'柴胡\', \'升麻\', \'牛蒡子\', \'全蝎\', \'石膏\', \'两生用\', \'干葛\', \'芍药\', \'甘草\', \'两北\', \'防风\', \'半两\', \'郁金\', \'半两\', \'薄荷叶\', \'半两\', \'半夏\', \'半两\', \'茯苓\', \'半两\', \'细辛\', \'半两\', \'川芎\', \'半两\', \'羌活\', \'半两\', \'桔梗\', \'荆芥\']');
INSERT INTO `prescript` VALUES (57298, '31950', '加味阴痛四物汤', '生地5钱，柴胡5分（梢），白芍钱半（炒），川芎1钱，当归3钱，龙胆草钱半（酒炒），山栀钱半（炒），丹皮钱半。', '水煎，去滓温服。', '《医略六书》卷二十六', '阴肿痛，脉数涩弦者。', '-', '-', '柴胡', '[\'生地\', \'柴胡\', \'分梢\', \'白芍\', \'钱半\', \'川芎\', \'当归\', \'龙胆草\', \'钱半酒\', \'炒山\', \'栀钱\', \'丹皮\', \'钱半\']');
INSERT INTO `prescript` VALUES (57299, '31883', '决明子汤', '决明子（炒）3分，柴胡（去苗）3分，黄连（去须）3分，防风（去叉）3分，升麻3分，苦竹叶3分，甘草（炙，锉）半两，菊花半两，细辛（去苗叶）1分。', '每服5钱匕，水1盏半，煎至8分，去滓，食后温服。', '《圣济总录》卷一○二', '肝脏实热，目眦生赤肉涩痛。', '-', '-', '柴胡', '[\'决明子\', \'炒分\', \'柴胡\', \'苗分\', \'黄连\', \'防风\', \'去叉分\', \'升麻\', \'苦竹叶\', \'甘草\', \'半两\', \'菊花\', \'半两\', \'细辛\', \'苗叶分\']');
INSERT INTO `prescript` VALUES (57300, '31849', '加减补中益气汤', '人参2钱，白术2钱，黄耆（炙）7分，柴胡7分，炙草5分，归身1钱，白芍1钱，川芎1钱，陈皮1钱，神曲（炒）5分，麦芽（炒）5分。', '生姜、大枣为引。更宜服前参术大补丸、乌鸡丸。以经行为度。', '《万氏女科》卷一', '固脾胃损伤，血枯经闭不行者。', '-', '-', '柴胡', '[\'人参\', \'白术\', \'黄耆\', \'炙分\', \'柴胡\', \'分炙草\', \'分归身\', \'白芍\', \'川芎\', \'陈皮\', \'神曲\', \'炒分\', \'麦芽\', \'炒分\']');
INSERT INTO `prescript` VALUES (57301, '31810', '化痒汤', '炒栀子3钱，甘草2钱，天花粉3钱，白芍4钱，柴胡3钱。', '水煎服。', '《石室秘录》卷四', '内火郁结而不散，致胃肠中作痒，而无法搔扒者。', '-', '-', '柴胡', '[\'栀子\', \'甘草\', \'天花粉\', \'白芍\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57302, '31778', '沉香汤', '沉香3分，槟榔（锉）3分，甘草（炙）3分，鳖甲（九肋者，去裙襕，醋炙）1两半，木香1两，当归（切，焙）1两，柴胡（去苗）1两，人参1两，白茯苓（去黑皮）1两，桂（去粗皮）1两，青橘皮（汤浸，去白，焙）1两，陈橘皮（汤浸，去白，焙）1两，生地黄1两。', '每服3钱匕，水1盏，加生姜1枣大（拍碎），同煎至7分，去滓温服，空心，日晚各1次。', '《圣济总录》卷一五一', '室女荣卫凝涩，月水不利，或时头目昏闷，肢体拘急，五心虚烦，饮食进退，多困少力。', '-', '-', '柴胡', '[\'沉香\', \'槟榔\', \'锉分\', \'甘草\', \'炙分\', \'鳖甲\', \'九肋者\', \'两半\', \'木香\', \'当归\', \'切焙\', \'柴胡\', \'人参\', \'白茯苓\', \'黑皮\', \'两桂去\', \'粗皮\', \'两青\', \'橘皮\', \'汤浸\', \'橘皮\', \'汤浸\', \'生地黄\']');
INSERT INTO `prescript` VALUES (57303, '31765', '回风外解汤', '柴胡1钱，薄荷1钱，前胡1钱，桔梗1钱，枳壳1钱，葛根2钱，豆豉3钱，广皮1钱，茯苓2钱，白术1钱，姜皮6分，荷叶1角。', '-', '《医醇剩义》卷四', '感风下利，身热脉微弦。', '-', '-', '柴胡', '[\'柴胡\', \'薄荷\', \'钱前\', \'胡钱\', \'桔梗\', \'枳壳\', \'葛根\', \'豆豉\', \'钱广皮\', \'茯苓\', \'白术\', \'姜皮分\', \'荷叶\']');
INSERT INTO `prescript` VALUES (57304, '31691', '加减羌活桂枝汤', '羌活8分，桂枝1钱5分，紫苏1钱5分，厚朴1钱5分，柴胡8分，草蔻仁1钱（研），青皮1钱5分，甘草5分。', '煨姜2钱，大枣5个为引。如周身痛，加独活1钱5分；如恶心，加广藿香3钱；如腹胀饱闷，加槟榔1钱5分、炒枳壳1钱5分。', '《慈航集》卷下', '寒疟。因暑月贪凉洗浴，感寒而得，阴邪所伏，无汗恶寒，周身挛痛，面惨色寒，先寒后热者。', '-', '-', '柴胡', '[\'羌活\', \'桂枝\', \'钱分\', \'紫苏\', \'钱分\', \'厚朴\', \'钱分\', \'柴胡\', \'分草蔻仁\', \'钱研\', \'青皮\', \'钱分\', \'甘草\']');
INSERT INTO `prescript` VALUES (57305, '31689', '败毒散', '生地黄1钱5分，丹皮7分，柴胡7分，桔梗8分，薄荷5分，连翘8分（去心），牛蒡子8分（炒，研），黄柏5分（蜜水炒），天花粉8分，黄芩7分（酒炒），黑参8分，赤芍5分，金银花8分，甘草3分（生，去皮）。', '《麻科活人》有射干，赤芍，无白芍。', '《痘疹定论》卷四', '清胃利咽。主疹后口臭、口疮、唇烂，兼咽喉疼痛。', '-', '-', '柴胡', '[\'生地黄\', \'钱分\', \'丹皮\', \'柴胡\', \'桔梗\', \'薄荷\', \'连翘\', \'分去\', \'牛蒡子\', \'炒研\', \'黄柏\', \'分蜜水\', \'天花粉\', \'黄芩\', \'分酒\', \'参分\', \'赤芍\', \'金银花\', \'甘草\', \'去皮\']');
INSERT INTO `prescript` VALUES (57306, '31686', '观音救苦甘露饮', '观音柳1枝（5钱），滑石3两，炒谷芽3两，焦神曲3两，苍术2两（泔水浸），云茯苓2两，柴胡1两5钱，川厚朴1两（姜汁炒），黄芩1两，枳壳1两，葛根1两，苏叶1两，姜半夏1两，陈皮1两（盐水炒），芍药1两，楂肉1两，乌药1两，香附5钱，木香5钱，甘草5钱，陈茶叶2斤（安化茶或六安茶之陈者佳）。', '每次3钱，用阴阳水煎服。', '《霍乱吐泻方论》', '避邪逐恶，祛风清热，疏滞和中。主感冒时邪，瘟疫疟疾，伏暑停食，霍乱吐泻，头痛腹胀，口渴心烦，脾胃不调，吞酸嗳腐，一切不服水土。', '-', '-', '柴胡', '[\'观音\', \'柳枝\', \'滑石\', \'谷芽\', \'两焦\', \'神曲\', \'苍术\', \'泔水\', \'浸云\', \'茯苓\', \'柴胡\', \'两钱川\', \'厚朴\', \'姜汁\', \'黄芩\', \'枳壳\', \'葛根\', \'两苏叶\', \'半夏\', \'陈皮\', \'盐水\', \'芍药\', \'两楂\', \'乌药\', \'香附\', \'木香\', \'甘草\', \'茶叶\', \'安化\', \'六安\', \'陈者\']');
INSERT INTO `prescript` VALUES (57307, '31653', '当归汤', '当归（切，焙）1两，麦门冬（去心，焙）1两，芎1两，赤茯苓（去黑皮）1两，甘草（炙）1两，大蓟（去根）3分，柴胡（去苗）3分。', '每服3钱匕，水1盏，加生姜5片，大枣3个，煎至8分，去滓，稍热服，不拘时候。', '《圣济总录》卷一五五', '妊娠心腹气攻疼痛。', '-', '-', '柴胡', '[\'当归\', \'切焙\', \'麦门冬\', \'两芎\', \'两赤\', \'茯苓\', \'黑皮\', \'甘草\', \'大蓟\', \'根分\', \'柴胡\', \'苗分\']');
INSERT INTO `prescript` VALUES (57308, '31632', '肝肾两舒汤', '熟地1两，玄参1两，茯苓3钱，白芍1两，柴胡1钱，当归5钱，甘草1钱，炒栀子1钱，丹皮3钱。', '水煎服。', '《辨证录》卷六', '舒肝解火，补肾济水。主肝火郁结不伸，闷烦躁急，吐痰黄块。', '-', '-', '柴胡', '[\'熟地\', \'玄参\', \'茯苓\', \'白芍\', \'柴胡\', \'当归\', \'甘草\', \'栀子\', \'丹皮\']');
INSERT INTO `prescript` VALUES (57309, '31595', '加减补中益气汤', '人参2钱，黄耆（蜜炙）1钱5分，当归身1钱5分，麦冬1钱5分，陈皮（去白）5分，炙甘草5分，柴胡5分，白术（去芦，去皮）6分，北五味子（大颗者，研碎）9粒。', '加生姜1片，好胶枣1枚，洗净同煎。', '《医林绳墨大全》卷四', '内伤挟外感，发汗后体虚甚者。', '-', '-', '柴胡', '[\'人参\', \'黄耆\', \'蜜炙钱分\', \'当归\', \'身钱分\', \'麦冬\', \'钱分\', \'陈皮\', \'白分\', \'甘草\', \'柴胡\', \'白术\', \'去芦\', \'去皮\', \'分北\', \'五味子\', \'大颗\', \'研碎\']');
INSERT INTO `prescript` VALUES (57310, '31582', '诃黎勒散', '诃黎勒皮1两，桂心3分，木香3分，枳壳3分（麸炒微黄，去瓤），赤芍药3分，柴胡3分（去苗），槟榔1两，川大黄1两（锉碎，微炒）。', '本方方名，据剂型，当作“诃黎勒丸”。', '《圣惠》卷四十五', '湿脚气。腹中妨闷，不能饮食，羸瘦。', '-', '-', '柴胡', '[\'黎勒皮\', \'两桂心分\', \'木香\', \'枳壳\', \'分麸\', \'微黄\', \'芍药\', \'柴胡\', \'分去\', \'槟榔\', \'两川\', \'大黄\']');
INSERT INTO `prescript` VALUES (57311, '31580', '化老汤', '人参3分，白术1钱，生地2钱，款冬花3分，白芥子3钱，白芍3钱，地骨皮3钱，柴胡4分，甘草1钱，麦冬5钱。', '水煎服。', '《辨证录》卷四', '老痰之病，咳嗽长年不愈，吐痰色黄，结成顽块，凝滞喉间，肺气不清，用尽气力，始得出于口者。', '-', '-', '柴胡', '[\'人参\', \'白术\', \'生地\', \'款冬花\', \'白芥子\', \'白芍\', \'地骨皮\', \'柴胡\', \'甘草\', \'麦冬\']');
INSERT INTO `prescript` VALUES (57312, '31513', '加味补中益气汤', '人参3钱，黄耆5钱，白术1钱，当归3钱，柴胡8分，升麻4分，生草1钱，陈皮1钱，金银花1两。', '水煎服。纵色者，加熟地1两，山萸肉4钱；动怒者，加白芍药1两，当归2钱，丹皮3钱，熟地5钱。', '《洞天奥旨》卷十二', '脐漏疮。', '-', '-', '柴胡', '[\'人参\', \'黄耆\', \'白术\', \'当归\', \'柴胡\', \'升麻\', \'分生\', \'草钱\', \'陈皮\', \'金银花\']');
INSERT INTO `prescript` VALUES (57313, '31460', '加减升麻汤', '升麻1钱，防风6分，桔梗5钱，川芎5钱，陈皮5钱，牛蒡8分，连翘8分，山楂8分，柴胡8分，蝉退4分，赤芍6分，甘草3分，木通5分。', '-', '《种痘新书》卷四', '痘疮初热，外感风寒，憎寒壮热，咳嗽流涕，体性旺者。', '-', '-', '柴胡', '[\'升麻\', \'防风\', \'桔梗\', \'川芎\', \'陈皮\', \'牛蒡\', \'连翘\', \'山楂\', \'柴胡\', \'分蝉\', \'退分\', \'赤芍\', \'甘草\', \'木通\']');
INSERT INTO `prescript` VALUES (57314, '31434', '加味小柴胡汤', '柴胡2钱，黄芩2钱，人参1钱半，半夏1钱半，牡蛎粉1钱，枳壳（麸炒）1钱，甘草1钱。', '上作1服。水2钟，加生姜3片，红枣2枚，煎1钟，食远服。', '《奇效良方》卷二十八', '伤寒胁痛，少阳、厥阴热疟。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'人参\', \'钱半\', \'半夏\', \'钱半\', \'牡蛎\', \'粉钱\', \'枳壳\', \'甘草\']');
INSERT INTO `prescript` VALUES (57315, '31428', '加味六一顺气汤', '白僵蚕（酒炒）3钱，蝉蜕10个，大黄（酒浸）4钱，芒消2钱5分，柴胡2钱，黄连1钱，黄芩1钱，白芍1钱，甘草（生）1钱，厚朴1钱5分，枳实2钱。', '六一顺气汤（《医方简义》卷二）。', '《寒温条辨》卷四', '少阴、厥阴病，口燥咽干，怕热消渴，谵语神昏，大便燥实，胸腹满硬，或热结旁流，绕脐疼痛，厥逆脉沉者。温病发痉者。', '-', '-', '柴胡', '[\'白僵蚕\', \'蝉蜕\', \'黄酒\', \'浸钱芒\', \'消钱分\', \'柴胡\', \'黄连\', \'黄芩\', \'白芍\', \'甘草\', \'生钱\', \'厚朴\', \'钱分\', \'枳实\']');
INSERT INTO `prescript` VALUES (57316, '31397', '连翘散', '连翘3分，漏芦3分，知母3分，木通1两（锉），桂心3分，黄芩3分，柴胡1两（去苗），玄参3分，川大黄2两（锉碎，微炒），川朴消2两，甘草3分（炙微赤，锉）。', '每服4钱，以水1中盏，煎至6分，去滓，每于食前温服。以利为度。', '《圣惠》卷六十六', '肝膈热毒盛，攻项腋，生瘰疬，肿痛，心神烦闷，背胛急疼，四肢不利。', '-', '-', '柴胡', '[\'连翘\', \'漏芦\', \'知母\', \'木通\', \'桂心分\', \'黄芩\', \'柴胡\', \'两去\', \'玄参\', \'分川\', \'大黄\', \'碎微\', \'炒川朴\', \'消两\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (57317, '31391', '加味左金丸', '柴胡3g，枳实6g，白芍9g，元胡9g，川楝子9g，青黛9g，竹茹9g，香附9g，黄连18g，吴萸3g，甘草6g。', '每服3g，日3次。', '《中医内科临床治疗学》引《冰玉堂经验方》', '疏肝清热，降逆止顺。主呃逆连声，因情志不舒而诱发，气冲引胁，脘闷纳呆，两胁胀痛，咽中不利，肠鸣矢气，舌苔薄白，脉弦。', '-', '-', '柴胡', '[\'柴胡\', \'枳实\', \'白芍\', \'川楝子\', \'青黛\', \'竹茹\', \'香附\', \'黄连\', \'吴萸\', \'甘草\']');
INSERT INTO `prescript` VALUES (57318, '31388', '加减开邪散', '甜白术5钱（土炒），云苓5钱，制半夏3钱，青皮1钱5分，枳壳1钱（炒），柴胡1钱，山楂3钱（炒），草蔻仁1钱（研）。', '煨姜3钱，大枣3枚为引。体虚脉弱者，加人参1钱，好肉桂心8分，另顿对服，贫人无力者，以上党参1两代之；恶心，加灶心土5钱；作泻，加炒白芍5钱；内热，小便短赤，加青蒿3钱；遍身骨节酸痛，加秦艽1钱5分；手足冷，青紫色，加桂枝2钱。', '《慈航集》卷下', '疟疾，足太阳膀胱之疟与手太阳小肠合证，初病令人腰痛头重，寒从背起，先寒后热，熇熇暍暍，热止汗出，遍身骨节酸痛，小便短赤。', '-', '-', '柴胡', '[\'白术\', \'钱土\', \'炒云苓\', \'钱制\', \'半夏\', \'青皮\', \'钱分\', \'枳壳\', \'柴胡\', \'山楂\', \'草蔻仁\', \'钱研\']');
INSERT INTO `prescript` VALUES (57319, '31383', '地黄芎穷丸', '熟干地黄（焙）1两，芎穷3分，白茯苓（去黑皮）半两，人参3分，当归（切，焙）3分，柴胡（去苗）半两，刺蓟半两，桑寄生（焙干）半两，厚朴（去粗皮，涂生姜汁炙）1两，龙骨3分，阿胶（炒沸）3分，白石脂3分，黄耆（锉）半两，甘草（炙，锉）1分。', '每服30丸，粥饮送下，日3次，不拘时候。', '《圣济总录》卷一五五', '和气养胎。主妊娠气血虚弱，令胎不长。', '-', '-', '柴胡', '[\'熟干\', \'地黄\', \'两芎\', \'穷分\', \'白茯苓\', \'黑皮\', \'半两\', \'人参\', \'当归\', \'切焙分\', \'柴胡\', \'去苗\', \'半两\', \'半两\', \'桑寄生\', \'焙干\', \'半两\', \'厚朴\', \'粗皮\', \'涂生\', \'姜汁\', \'龙骨\', \'阿胶\', \'炒沸分\', \'白石\', \'脂分\', \'黄耆\', \'半两\', \'甘草\']');
INSERT INTO `prescript` VALUES (57320, '31361', '肺脾双解饮', '人参1钱，麦冬3钱，茯苓3钱，柴胡1钱，神曲5分，车前子1钱，甘草1钱，薏仁5钱。', '水煎服。', '《石室秘录》卷三', '肺脾气衰，人或咳嗽不已，吐泻不已。', '-', '-', '柴胡', '[\'人参\', \'麦冬\', \'茯苓\', \'柴胡\', \'神曲\', \'车前子\', \'甘草\', \'薏仁\']');
INSERT INTO `prescript` VALUES (57321, '31350', '陈橘皮丸', '陈橘皮1两（汤浸，去白瓤，焙），厚朴3分（去粗皮，涂生姜汁炙令香熟），神曲1两（微炒），木香半两，槟榔3分，人参半两（去芦头），桂心半两，柴胡3分（去苗），白术3分，诃黎勒3分（煨，用皮），白豆蔻3分（去皮），高良姜半两（锉），白茯苓3分，沉香3分，枳实3分（麸炒令微黄）。', '每服30丸，生姜、大枣汤送下，不拘时候。', '《圣惠》卷二十八', '气劳，脾胃乏弱，饮食不消，四肢羸瘦。', '-', '-', '柴胡', '[\'橘皮\', \'两汤\', \'浸去\', \'厚朴\', \'分去\', \'粗皮\', \'涂生\', \'姜汁\', \'炙令\', \'香熟\', \'神曲\', \'两微\', \'木香\', \'半两\', \'槟榔\', \'人参\', \'半两\', \'芦头\', \'桂心\', \'半两\', \'柴胡\', \'分去\', \'白术\', \'分诃黎勒分\', \'白豆蔻\', \'去皮\', \'高良姜\', \'半两\', \'白茯苓\', \'沉香\', \'枳实\', \'分麸\', \'炒令\', \'微黄\']');
INSERT INTO `prescript` VALUES (57322, '31302', '半夏散', '半夏半两（汤洗7遍去滑），细辛3分，桔梗半两（去芦头），杏仁3分（汤浸，去皮尖双仁，麸炒微黄），陈橘皮1两（汤浸，去白瓤，焙），麻黄3分（去根节），桂心2两，前胡半两（去芦头），枳壳半两（麸炒微黄，去瓤），紫菀半两（洗去苗土），桑根白皮半两（锉），贝母半两（煨令微黄），柴胡半两（去苗），甘草1分（炙微赤，锉），木通半两（锉），诃黎勒皮半两。', '每服4钱，以水1中盏，加生姜半分，大枣3个，煎至6分，去滓稍热服，不拘时候。', '《圣惠》卷六', '肺脏外伤风冷，声嘶言不能出，胸膈气滞。', '忌生冷、热面。', '-', '柴胡', '[\'半夏\', \'半两\', \'洗遍\', \'细辛\', \'桔梗\', \'半两\', \'芦头\', \'杏仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'橘皮\', \'两汤\', \'浸去\', \'麻黄\', \'分去\', \'根节\', \'桂心\', \'两前\', \'半两\', \'芦头\', \'枳壳\', \'半两\', \'微黄\', \'紫菀\', \'半两\', \'苗土\', \'桑根\', \'白皮\', \'半两\', \'贝母\', \'半两\', \'微黄\', \'柴胡\', \'半两\', \'去苗\', \'甘草\', \'分炙微\', \'木通\', \'半两\', \'锉诃黎勒皮\', \'半两\']');
INSERT INTO `prescript` VALUES (57323, '31299', '苍术升麻汤', '苍术1钱半，半夏1钱，厚朴7分，陈皮7分，枳实7分，桔梗7分，川芎7分，升麻7分，柴胡7分，木通7分，黄连5分，黄芩5分，木香5分，甘草5分，生姜3片。', '-', '《杂病源流犀烛》卷二十', '瘴疫。岭南春秋时月，山岚雾瘴之毒中于人，发为寒热温疟者。', '-', '-', '柴胡', '[\'苍术\', \'钱半\', \'半夏\', \'厚朴\', \'陈皮分\', \'枳实\', \'桔梗\', \'川芎\', \'升麻\', \'柴胡\', \'木通\', \'黄连\', \'黄芩\', \'木香\', \'甘草\', \'生姜\']');
INSERT INTO `prescript` VALUES (57324, '31218', '当归汤', '当归（切，焙）1两，黄耆（锉）1两，牛膝（酒浸，切，焙）1两，枳壳（去瓤，麸炒）1两，芎1两，羌活（去芦头）1两，人参1两，附子（炮裂，去皮脐）1两，芍药1两，木香1两，槟榔（锉）1两，桔梗（锉，炒）1两，牡丹（去心）1两，沉香（锉）1两，甘草（炙，锉）1两，地骨皮1两，半夏（生姜汁浸，炒）1两，桂（去粗皮）1两半，蓬莪（？？）（煨，锉）1两半，陈橘皮（汤浸，去白，炒）1两半，柴胡（去苗）1两半，熟干地黄（焙）1两半，荆芥穗1两半，鳖甲（去裙襕，醋炙）2两。', '每服3钱匕，水1盏，加生姜3片，乌梅1个，同煎至7分，去滓温服。', '《圣济总录》卷一五○', '妇人血风，身体百节疼痛，乍寒乍热，经脉不利，日渐羸瘦。', '-', '-', '柴胡', '[\'当归\', \'切焙\', \'黄耆\', \'牛膝\', \'浸切\', \'枳壳\', \'两芎\', \'羌活\', \'芦头\', \'人参\', \'附子\', \'炮裂\', \'去皮\', \'芍药\', \'木香\', \'槟榔\', \'桔梗\', \'牡丹\', \'沉香\', \'甘草\', \'地骨皮\', \'半夏\', \'姜汁\', \'两桂去\', \'粗皮\', \'两半\', \'两半\', \'橘皮\', \'汤浸\', \'两半\', \'柴胡\', \'去苗\', \'两半\', \'熟干\', \'地黄\', \'两半\', \'荆芥\', \'两半\', \'鳖甲\']');
INSERT INTO `prescript` VALUES (57325, '31202', '半夏散', '半夏1两（汤浸7遍去滑），白术2两，赤茯苓1两，鳖甲1两（涂醋炙令黄，去裙襕），杏仁1两（汤浸，去皮尖双仁，麸炒微黄），陈橘皮2两（汤浸，去白瓤，焙），赤芍药1两，柴胡1两（去苗），大腹皮2两（锉），枳壳1两（麸炒微黄，去瓤），木香1两，诃黎勒1两半（煨，用皮）。', '每服4钱，以水1中盏，加生姜半分，大枣3个，煎至6分，去滓，食前温服。', '《圣惠》卷二十六', '脾劳实，四肢不举，五脏不调，胀满气急。', '忌饴糖、苋菜。', '-', '柴胡', '[\'半夏\', \'两汤\', \'浸遍\', \'白术\', \'两赤\', \'茯苓\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'杏仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'橘皮\', \'两汤\', \'浸去\', \'芍药\', \'柴胡\', \'两去\', \'大腹皮\', \'枳壳\', \'两麸\', \'微黄\', \'木香\', \'两诃黎勒\', \'两半\']');
INSERT INTO `prescript` VALUES (57326, '31091', '当归芍药和疟汤', '全当归1两（酒洗），白芍1两（酒炒），益母草3钱，炮差1钱5分，青皮1钱5分，柴胡8分（炒），草蔻仁1钱（研），炙甘草5分。', '河、井水各半煎，露1宿，早服。3服全愈。如腹痛积瘀，恶露未清，加炒山楂3钱，酒炒元胡索3钱；如恶心，加灶心土5钱；如头痛，加川芎2钱；如大便结，加桃仁泥3钱，酒洗红花3钱；如作泻，加车前子3钱；如胸口饱胀，加炒枳壳1钱5分；加热盛，加青蒿3钱；如寒多，加入煨姜3钱、大枣3枚。', '《慈航集》卷下', '补血和解。主产后疟疾，夹痰夹滞，寒热不止。', '-', '-', '柴胡', '[\'当归\', \'两酒\', \'白芍\', \'两酒\', \'益母草\', \'钱炮\', \'差钱\', \'青皮\', \'钱分\', \'柴胡\', \'草蔻仁\', \'研炙\', \'甘草\']');
INSERT INTO `prescript` VALUES (57327, '31046', '栀子乌梅汤', '栀子半两，黄芩半两，甘草半两（炙微赤），柴胡1两，乌梅肉14个（微炒用）。', '本方方名，《医学纲目》引作“乌梅汤”。', '《活人书》卷十八', '伤寒后，虚烦不得眠，心中懊憹。', '-', '-', '柴胡', '[\'栀子\', \'半两\', \'黄芩\', \'半两\', \'甘草\', \'半两\', \'炙微\', \'柴胡\', \'乌梅\', \'肉个\']');
INSERT INTO `prescript` VALUES (57328, '31032', '半夏散', '半夏半两（汤洗7遍去滑），知母半两，桔梗半两（去芦头），黄耆1两（锉），柴胡2两（去苗），鳖甲1两（涂醋炙令黄，去裙襕），人参半两（去芦头），赤茯苓半两，秦艽半两（去苗），麦门冬半两（去心），赤芍药半两，甘草1分（炙微赤，锉），乌梅肉半两，大腹皮3分（锉）。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷七十', '妇人热劳，烦渴口干，体瘦无力，四肢疼痛，或时寒热，痰逆不欲饮食。', '-', '-', '柴胡', '[\'半夏\', \'半两\', \'洗遍\', \'知母\', \'半两\', \'桔梗\', \'半两\', \'芦头\', \'黄耆\', \'柴胡\', \'两去\', \'鳖甲\', \'两涂\', \'炙令\', \'黄去\', \'人参\', \'半两\', \'芦头\', \'茯苓\', \'半两\', \'秦艽\', \'半两\', \'去苗\', \'麦门冬\', \'半两\', \'芍药\', \'半两\', \'甘草\', \'分炙微\', \'乌梅\', \'半两\', \'大腹皮\']');
INSERT INTO `prescript` VALUES (57329, '30912', '加减逍遥散', '柴胡2钱，白芍5钱，白术3钱，当归3钱，生地3钱，甘草1钱，炒栀子1钱，半夏1钱，青皮5分。', '水煎服。', '《辨证录》卷二', '怒后吐痰，胸满作痛，服四物、二陈之汤，加芩、连、枳壳之类，杳无一应，更加祛风之味，反致半身不遂，筋渐挛缩，四肢痿软，日晡益甚，内热口干，形体倦怠，属郁怒未解，肝气未舒者。', '-', '-', '柴胡', '[\'柴胡\', \'白芍\', \'白术\', \'当归\', \'生地\', \'甘草\', \'栀子\', \'半夏\', \'青皮\']');
INSERT INTO `prescript` VALUES (57330, '30873', '当归活血汤', '当归8分，赤芍药3分，甘草3分，红花3分，桂心3分，干姜3分，枳壳3分，生地黄1钱，人参8分，柴胡8分，桃仁泥3分。', '方中诸药用量原缺，据《伤寒六书纂要辩疑》补。《张氏医通》有茯苓，无人参。服3贴后，去桃仁、红花、干姜、桂，加白术、茯苓。', '《伤寒六书》卷三', '伤寒挟血，无头痛、无恶寒，止身热发渴，小便利，大便黑，语言无伦，神志昏沉，如见鬼祟。', '-', '-', '柴胡', '[\'当归\', \'分赤\', \'芍药\', \'甘草\', \'红花\', \'分桂心分\', \'干姜\', \'枳壳\', \'生地黄\', \'人参\', \'柴胡\', \'桃仁\', \'泥分\']');
INSERT INTO `prescript` VALUES (57331, '30853', '当归汤', '当归（切，焙）1分，赤芍药1分，鳖甲（醋炙，去裙襕）1分，柴胡（去苗）1分，大黄（锉，炒）1分，甘草（炙）1分，桃仁（去皮尖双仁，麸炒）半两。', '5-6岁儿，每服半钱匕，水半盏，煎至3分，去滓温服，日3次。', '《圣济总录》卷一七四', '小儿疟疾。', '-', '-', '柴胡', '[\'当归\', \'切焙分\', \'芍药\', \'鳖甲\', \'柴胡\', \'苗分\', \'大黄\', \'炒分\', \'甘草\', \'炙分\', \'桃仁\', \'皮尖\', \'双仁麸\', \'半两\']');
INSERT INTO `prescript` VALUES (57332, '30851', '红雨丹', '柴胡4钱，黄芩3钱，石膏3钱，甘草3钱，丹皮3钱，生姜3钱，元参3钱，芍药3钱。', '水煎，热服。覆衣，饮热稀粥，取微汗。', '《四圣悬枢》卷一', '3日少阳温病，胸胁疼痛．耳聋，舌苦，咽干作渴者。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'石膏\', \'甘草\', \'丹皮\', \'生姜\', \'钱元\', \'参钱\', \'芍药\']');
INSERT INTO `prescript` VALUES (57333, '30824', '合阴汤', '柴胡8分，茯苓5钱，甘草5分，天花粉1钱，枳壳3分，神曲5分，白芍3钱。', '水煎服。', '《辨证录》卷一', '冬月伤寒至九日而泻利不已。', '-', '-', '柴胡', '[\'柴胡\', \'茯苓\', \'甘草\', \'天花粉\', \'枳壳\', \'神曲\', \'白芍\']');
INSERT INTO `prescript` VALUES (57334, '30742', '百解散', '柴胡1两，前胡1两，羌活1两，独活1两，桔梗1两，枳壳1两，人参1两，茯苓1两，甘草1两，川芎1两，陈皮1两，杏仁1两。', '每服2钱，水1盏，加生姜3片，煎至5分，去滓温服。加薄荷3叶煎为佳。', '《鸡峰》卷五', '风寒咳嗽，鼻塞声重，风痰头痛，呕逆寒热。', '-', '-', '柴胡', '[\'柴胡\', \'两前\', \'胡两\', \'羌活\', \'独活\', \'桔梗\', \'枳壳\', \'人参\', \'茯苓\', \'甘草\', \'川芎\', \'陈皮\', \'杏仁\']');
INSERT INTO `prescript` VALUES (57335, '30740', '补阳消疟丹', '人参5钱，鳖甲1两，白术1两，茯苓1两，当归7钱，白芍7钱，柴胡1钱，枳壳1钱，槟榔1钱。', '水煎服。', '《石室秘录》卷一', '疟疾忽为下痢，欲发汗则身已亡阴，欲祛邪则下已便浊物者。', '-', '-', '柴胡', '[\'人参\', \'鳖甲\', \'白术\', \'茯苓\', \'当归\', \'白芍\', \'柴胡\', \'枳壳\', \'槟榔\']');
INSERT INTO `prescript` VALUES (57336, '30721', '白术除湿汤', '白术1两，生地黄（炒）7钱，地骨皮7钱，泽泻7钱，知母7钱，赤茯苓5钱，人参5钱，炙甘草5钱，柴胡5钱。', '每服5钱，水2盏，煎至1盏，去滓，食远温服。如小便快利，减茯苓、泽泻1半；如有刺痛，1料药中加当归身（酒洗）7钱。', '《兰室秘藏》卷下', '午后发热，背恶风，四肢沉重，小便或多或少、黄色；又治汗后发热。', '-', '-', '柴胡', '[\'白术\', \'生地黄\', \'地骨皮\', \'泽泻\', \'知母\', \'茯苓\', \'人参\', \'甘草\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57337, '30666', '当归龙荟丸', '当归1两，龙胆草1两，柴胡1两，青黛5钱，胆星5钱，大黄5钱，芦荟5钱，麝香5分，栀子1两，酒黄芩1两，酒黄连1两，黄柏1两，木香2钱5分。', '每服20丸，生姜汤送下。', '《准绳·幼科》卷三', '常服宣通血气，调顺阴阳。主小儿肝胆风热，耳中鸣，出青脓，名曰震耳，大便秘，小便黄。', '-', '-', '柴胡', '[\'当归\', \'龙胆草\', \'柴胡\', \'两青黛钱\', \'胆星\', \'大黄\', \'芦荟\', \'麝香\', \'栀子\', \'两酒\', \'黄芩\', \'两酒\', \'黄连\', \'黄柏\', \'木香\', \'钱分\']');
INSERT INTO `prescript` VALUES (57338, '30658', '加减败毒散', '防风1钱5分，荆芥2钱，羌活2钱，独活2钱，前胡2钱，升麻5分，干葛1钱，赤芍2钱，桔梗8分，川芎1钱5分，白芷2钱，薄荷8分，牛蒡子3钱，甘草8分，柴胡8分。', '加生姜、葱，水煎，热服出汗。', '《寿世保元》卷二', '天行时疫，头面肿大，咽喉不利，舌干口燥，憎寒壮热。四时瘟疫，皆可通用。', '-', '-', '柴胡', '[\'防风\', \'钱分\', \'荆芥\', \'羌活\', \'独活\', \'钱前\', \'胡钱\', \'升麻\', \'分干葛钱\', \'赤芍\', \'桔梗\', \'川芎\', \'钱分\', \'白芷\', \'薄荷\', \'牛蒡子\', \'甘草\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57339, '30654', '鬼箭丸', '鬼箭羽1两，川芒消1两，柴胡1两（去苗），水蛭1分（炒微黄），虻虫1分（炒令微黄，去翅足），川大黄3分（锉，微炒），赤茯苓3分，干漆半两（捣碎，炒令烟出），川椒1分（去目及闭口者，微炒去汗），甜葶苈1两（隔纸炒令紫色），杏仁3分（汤浸，去皮尖双仁，麸炒微黄），桃仁3分（汤浸，去皮尖双仁，麸炒微黄），牡丹3分。', '每服20丸，食前以温酒送下。', '《圣惠》卷七十二', '妇人脉不通，手足心热，腹满喘急，不欲睡卧，心神烦闷。', '-', '-', '柴胡', '[\'鬼箭羽\', \'两川芒\', \'消两\', \'柴胡\', \'两去\', \'水蛭\', \'微黄\', \'炒令\', \'微黄\', \'足川\', \'大黄\', \'茯苓\', \'干漆\', \'半两\', \'捣碎\', \'炒令烟\', \'出川椒\', \'分去\', \'目及\', \'闭口\', \'隔纸\', \'炒令\', \'紫色\', \'杏仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'桃仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'牡丹\']');
INSERT INTO `prescript` VALUES (57340, '30589', '鸡胫丸', '天门冬1两，麦门冬（各去心）1两，秦艽1两，雄黄1两，雌黄1两，瓜蒌根1两，黄芩1两，柴胡（茸）1两，枯白矾1两，防风1两，桂1两，茯苓1两，桑白皮1两，雄鸡胫1对（炙），雌鸡胫1对（炙）。', '每服20丸，米饮粥送下，1日2次，不拘时候。', '《医方类聚》卷二六○引《施圆端效方》', '常服少病儿肥。主小儿惊疳黄瘦，咬牙，肌热盗汗，食积肚大，脚细，长大不能行。', '-', '-', '柴胡', '[\'天门冬\', \'麦门冬\', \'各去\', \'秦艽\', \'雄黄\', \'雌黄\', \'两瓜\', \'黄芩\', \'柴胡\', \'白矾\', \'防风\', \'两桂\', \'茯苓\', \'桑白皮\', \'雄鸡\']');
INSERT INTO `prescript` VALUES (57341, '30564', '灵犀饮', '犀角（镑屑）半两，胡黄连半两，茯苓（去皮）1两，人参（去芦）1两，川芎1两，秦艽1两，甘草1两，羌活1两，柴胡1两，桔梗1两，地骨皮1两。', '灵犀散（《普济方》卷三八四）。', '《婴童百问》卷六', '小儿骨蒸潮热，盗汗，咳嗽，不食多渴，面黄肌瘦，腹急气粗。', '-', '-', '柴胡', '[\'犀角\', \'半两\', \'胡黄连\', \'半两\', \'茯苓\', \'去皮\', \'人参\', \'去芦\', \'川芎\', \'秦艽\', \'甘草\', \'羌活\', \'柴胡\', \'桔梗\', \'地骨皮\']');
INSERT INTO `prescript` VALUES (57342, '30544', '补真丸', '厚朴（去粗皮）4两，苍术（净刮去黑皮）4两（二味用大枣1斤半、生姜2斤，细切，同入大锅，以浆水煮1日，耗更添之，慢火泣尽水脉，焙干用），陈橘皮（汤浸，去瓤）2两，鳖甲1两（小便、酒、醋各1升，同煮1日了，更将汁涂炙了，焙干），石斛（去根）2两，丁香2两，肉苁蓉（酒浸、切，焙）2两，木香2两，巴戟天（去心）2两，当归（切焙）2两，草豆蔻（去皮）2两，诃子皮2两，肉桂2两，五味子2两，槟榔（锉）2两，山茱萸2两，杜仲（去粗皮，炙，锉）2两，破故纸（炒）2两，人参1两，黄耆（锉）3两，附子（炮裂，去皮脐）1两，', '《普济方》有吴茱萸五钱。', '《传家秘宝》卷下', '脾元脏虚冷，四肢无力，吃食不得，心腹满胀，或时下痢盗汗，冷劳玄癖。', '-', '-', '柴胡', '[\'厚朴\', \'粗皮\', \'苍术\', \'刮去\', \'黑皮\', \'二味\', \'大枣\', \'斤半\', \'生姜\', \'细切\', \'同入\', \'大锅\', \'浆水\', \'日耗\', \'更添\', \'慢火\', \'尽水脉\', \'焙干\', \'橘皮\', \'汤浸\', \'鳖甲\', \'小便\', \'酒醋\', \'各升同\', \'焙干\', \'石斛\', \'丁香\', \'肉苁蓉\', \'浸切\', \'木香\', \'巴戟天\', \'当归\', \'切焙\', \'草豆蔻\', \'去皮\', \'诃子\', \'肉桂\', \'五味子\', \'槟榔\', \'山茱萸\', \'杜仲\', \'粗皮\', \'两破\', \'故纸\', \'人参\', \'两黄耆\', \'附子\', \'炮裂\', \'去皮\']');
INSERT INTO `prescript` VALUES (57343, '30505', '龙车散', '柴胡1钱，甘草1钱，白芍5分，茯苓5钱，车前子3钱，龙胆草5分。', '水煎服。', '《辨证录》卷七', '少阳痉病，感湿热之气，又感风邪，颈项强直，一目或左右视，手足搐搦。', '-', '-', '柴胡', '[\'柴胡\', \'甘草\', \'白芍\', \'茯苓\', \'车前子\', \'龙胆草\']');
INSERT INTO `prescript` VALUES (57344, '30467', '决明散', '决明子半两，子芩半两，柴胡半两（去苗），川大黄半两（锉，微炒），川升麻半两，栀子仁半两，羚羊角屑半两，甘草（炙微赤，锉）半两，石膏1两。', '决明汤（《圣济总录》卷一八一）。', '《圣惠》卷八十九', '膈中有热，赤眼涩痛不开。', '-', '-', '柴胡', '[\'决明子\', \'半两\', \'子芩\', \'半两\', \'柴胡\', \'半两\', \'苗川\', \'大黄\', \'半两\', \'锉微\', \'炒川\', \'升麻\', \'半两\', \'栀子\', \'半两\', \'羚羊角\', \'半两\', \'甘草\', \'炙微\', \'半两\', \'石膏\']');
INSERT INTO `prescript` VALUES (57345, '30440', '补阴散邪汤', '熟地1两，何首乌5钱，当归5钱，地骨皮3钱，丹皮3钱，天花粉2钱，神曲2钱，人参1钱，柴胡1钱，砂仁1粒。', '水煎服。', '《辨证录》卷五', '春温之症，肾肝阴虚，满身疼痛，夜间发热，日间则冷。', '-', '-', '柴胡', '[\'熟地\', \'何首乌\', \'当归\', \'地骨皮\', \'丹皮\', \'天花粉\', \'神曲\', \'人参\', \'柴胡\', \'砂仁\']');
INSERT INTO `prescript` VALUES (57346, '30431', '补益紫金丸', '青蒿2两，柴胡（去苗）2两，芍药1两，五加皮1两，续断1两，石斛（去根）1两，黄耆1两，羌活（去芦头）1两，以上锉，以无灰酒2升，童便2升浸，置日中晒3日，逐日转动，日足漉出焙干，捣罗为末，其浸药之酒，留熬后药：当归（切，焙）1两，牛膝（酒浸，切，焙）1两，桃仁（去皮尖，麸炒）1两半，肉苁蓉（酒浸，切，焙）1两半，地黄汁3升，以上五味，除地黄汁外，捣罗为末，同地黄汁并入前浸药酒内，慢火熬，时时搅转，令膏凝，即住火。芎1两，人参1两，白茯苓（去黑皮）1两，桂（去粗皮）1两，附子（炮裂，去皮脐）1两，蛇床子', '每日30丸，空心、日午温酒送下。', '《圣济总录》卷一八五', '除百病，肌肤充实，颜色红润，进饮食，壮筋骨，暖血海，黑髭发。主形气衰惫，积气上攻，心膈不利，身体羸瘦，饮食无味；妇人屡经产育，血海冷惫，腰腹气痛。', '-', '-', '柴胡', '[\'青蒿\', \'柴胡\', \'芍药\', \'五加皮\', \'续断\', \'石斛\', \'黄耆\', \'羌活\', \'芦头\', \'以上\', \'无灰酒\', \'童便\', \'升浸\', \'逐日\', \'转动\', \'日足漉出\', \'焙干\', \'捣罗为\', \'浸药\', \'当归\', \'切焙\', \'牛膝\', \'浸切\', \'桃仁\', \'皮尖\', \'两半\', \'肉苁蓉\', \'浸切\', \'两半\', \'地黄\', \'汁升\', \'以上\', \'五味\', \'地黄\', \'汁外\', \'捣罗为\', \'末同\', \'地黄\', \'并入\', \'前浸\', \'药酒\', \'慢火\', \'时时\', \'转令膏\', \'凝即\', \'火芎\', \'人参\', \'白茯苓\', \'黑皮\', \'两桂去\', \'粗皮\', \'附子\', \'炮裂\', \'去皮\', \'蛇床子\']');
INSERT INTO `prescript` VALUES (57347, '30419', '加味补中益气汤', '人参（随便），黄耆（蜜炒）2钱，白术钱半，当归钱半，炙草8分，陈皮8分，柴胡（酒炒）3分，升麻（蜜炒）3分，杜仲钱半，续断钱半，淮山药（炒）钱半，百合2钱，五味子15粒。', '胎至七月，肺经养之，前此或堕，肺经受伤，后孕宜预于六月调补之，必须大剂，不可间断，保过七月，则无虞也。如肺虚有热，或口渴溺赤，或咳嗽喉燥，加麦冬1-2钱；如脾寒泄泻，加炮干姜1钱；如气滞而胀，加腹皮8分，或加枳壳7分。', '《会约》卷十四', '堕胎。', '-', '-', '柴胡', '[\'人参\', \'随便\', \'黄耆\', \'白术\', \'钱半\', \'当归\', \'钱半炙草分\', \'陈皮分\', \'柴胡\', \'炒分\', \'升麻\', \'炒分\', \'杜仲\', \'钱半\', \'续断\', \'钱半淮\', \'山药\', \'百合\', \'五味子\']');
INSERT INTO `prescript` VALUES (57348, '30416', '阿魏丸', '阿魏（细研）1两，当归（切，炒）1两，芜荑仁（炒）1两，雌黄（研）半两，猪牙皂荚（去皮子，酥炙）半两，麝香（研）3分，蓬莪术（煨，锉）3分，柴胡（去苗）2两，白槟榔（锉）2两。', '每服50丸，五更温酒送下。仍饮令醉，以青绢被盖之，睡觉汗出通身，必有细虫在被间，日光内看，急须烧之，或泻下恶物并虫等是效。', '《圣济总录》卷九十三', '传尸骨蒸，女人血气，月候经年不通，痰嗽黄瘦，四肢羸弱，盗汗骨蒸，或时憎寒，饮食减少。', '-', '-', '柴胡', '[\'阿魏\', \'细研\', \'当归\', \'两芜\', \'雌黄\', \'半两\', \'猪牙皂\', \'皮子\', \'半两\', \'麝香\', \'研分蓬\', \'莪术\', \'柴胡\', \'去苗\', \'两白\', \'槟榔\']');
INSERT INTO `prescript` VALUES (57349, '30359', '抗过敏膏', '乌梅5两，防风3两，柴胡3两，生甘草3两，五味子（醋制）3两，白鲜皮5两，苦杏仁3两。', '每服5钱，热开水冲服，1日2次。', '《北京市中成药规范》第二册', '清热去湿，散风止痒。主风热蕴结脾湿风毒引起的风湿疙瘩时起时伏，周身刺痒，怕冷发热，骨节酸痛，过敏性皮肤病，荨麻疹。', '-', '-', '柴胡', '[\'乌梅\', \'防风\', \'柴胡\', \'生甘草\', \'五味子\', \'醋制\', \'白鲜皮\', \'苦杏仁\']');
INSERT INTO `prescript` VALUES (57350, '30351', '火郁汤', '山栀1钱，柴胡1钱，干葛1钱，抚芎1钱，白芍1钱，连翘1钱，地骨皮1钱，甘草3分。', '上锉1剂。水煎服。', '《回春》卷二', '火郁症。', '-', '-', '柴胡', '[\'山栀钱\', \'柴胡\', \'干葛钱\', \'白芍\', \'连翘\', \'地骨皮\', \'甘草\']');
INSERT INTO `prescript` VALUES (57351, '30327', '地骨皮丸', '地骨皮3分，柴胡1两（去苗），子芩1两，生干地黄1两，麻黄根1两，麦门冬半两（去心，焙），犀角屑半两，知母1两，川升麻1两，牡蛎粉半两，人参1两（去芦头），赤茯苓1两，甘草半两（炙微赤，锉）。', '每服30丸，煎淡竹叶汤送下，不拘时候。', '《圣惠》卷四', '心脏壅滞，或时烦热，频多汗出。', '-', '-', '柴胡', '[\'地骨皮\', \'柴胡\', \'两去\', \'苗子\', \'两生\', \'地黄\', \'麻黄根\', \'麦门冬\', \'半两\', \'犀角\', \'半两\', \'知母\', \'两川\', \'升麻\', \'牡蛎\', \'半两\', \'人参\', \'两去\', \'芦头\', \'茯苓\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (57352, '30317', '加味十全大补汤', '人参（无者，山药3钱代之），白术钱半，茯苓1钱2分，炙草1钱2分，当归1钱2分，白芍1钱2分，川芎1钱2分，熟地2钱，黄耆（蜜炙）2钱，肉桂1钱，升麻（盐炒）3分，柴胡（酒炒）3分，天麻钱半，白附子（如竹节者真）8分。', '水煎，温服。', '《会约》卷六', '头上冷而畏风，或痛或不痛，属后天气血之不足者。', '-', '-', '柴胡', '[\'人参\', \'无者\', \'山药\', \'钱代\', \'白术\', \'钱半\', \'茯苓\', \'分炙草\', \'钱分\', \'当归\', \'钱分\', \'白芍\', \'钱分\', \'川芎\', \'钱分\', \'熟地\', \'黄耆\', \'蜜炙钱\', \'肉桂\', \'升麻\', \'炒分\', \'柴胡\', \'炒分\', \'天麻\', \'钱半\', \'白附子\', \'竹节\', \'真分\']');
INSERT INTO `prescript` VALUES (57353, '30254', '防耆分湿汤', '黄耆5钱，白术5钱，茯苓5钱，薏仁5钱，防风1钱，柴胡1钱，天花粉1钱，桂枝3分，麻黄5分。', '水煎服。', '《辨证录》卷三', '气虚而痰结于经络皮肤，遍身生块而痛。', '-', '-', '柴胡', '[\'黄耆\', \'白术\', \'茯苓\', \'薏仁\', \'防风\', \'柴胡\', \'天花粉\', \'桂枝\', \'麻黄\']');
INSERT INTO `prescript` VALUES (57354, '30233', '固胎饮', '人参2钱，黄耆2钱，白术2钱，条芩2钱，当归1钱，川芎7分，芍药7分，熟地黄5分，陈皮5分，黄连、升麻、柴胡，糯米81粒。', '本方方名，据剂型，当作“固胎丸”。方中黄连、升麻、柴胡用量原缺。', '《医统》卷八十五', '妊娠气血俱虚，3个月内惯要坠胎。', '-', '-', '柴胡', '[\'人参\', \'黄耆\', \'白术\', \'钱条\', \'当归\', \'川芎\', \'芍药\', \'熟地黄\', \'陈皮分\', \'黄连\', \'升麻\', \'柴胡\', \'糯米\']');
INSERT INTO `prescript` VALUES (57355, '30223', '补阴助阳汤', '玄参1两，麦冬1两，熟地1两，人参2钱，白芥子5钱，柴胡1钱，白芍1两，当归1两，白术1两，茯苓5钱，菖蒲1钱。', '水煎服。1剂而昏迷苏，再剂而痰涎化，3剂而厥逆回，则可生也，否则不可救矣。', '《辨证录》卷五', '阴厥。', '-', '-', '柴胡', '[\'玄参\', \'麦冬\', \'熟地\', \'人参\', \'白芥子\', \'柴胡\', \'白芍\', \'当归\', \'白术\', \'茯苓\', \'菖蒲\']');
INSERT INTO `prescript` VALUES (57356, '30129', '补阴辟邪汤', '熟地半两，山茱萸4钱，当归3钱，白芍3钱，鳖甲5钱，柴胡3钱，白芥子3钱，陈皮1钱，生何首乌3钱，茯苓5钱，北五味1钱，麦冬3钱。', '水煎服。', '《石室秘录》卷二', '阴气甚虚，夜发寒热。', '-', '-', '柴胡', '[\'熟地\', \'半两\', \'山茱萸\', \'当归\', \'白芍\', \'鳖甲\', \'柴胡\', \'白芥子\', \'陈皮\', \'钱生\', \'何首乌\', \'茯苓\', \'钱北\', \'五味\', \'麦冬\']');
INSERT INTO `prescript` VALUES (57357, '30105', '护内汤', '白术3钱，茯苓3钱，麦芽1钱，山楂5粒，甘草1钱，柴胡1钱，半夏1钱，枳壳5分，神曲8分，肉桂2分。', '水煎服。', '《辨证录》卷九', '消食，祛逐外邪。主人有好食肥甘烹炙之物，遂至积于胸胃久而不化，少遇风邪，便觉气塞不通。', '-', '-', '柴胡', '[\'白术\', \'茯苓\', \'麦芽\', \'山楂\', \'甘草\', \'柴胡\', \'半夏\', \'枳壳\', \'神曲\', \'肉桂\']');
INSERT INTO `prescript` VALUES (57358, '30088', '百部散', '百部1两，赤茯苓2两，百合1两，桑根白皮1两（锉），木通1两（锉），甘草半两（炙微赤，锉），柴胡1两（去苗），枳壳1两（麸炒微黄，去瓤），赤芍药3分，郁李仁3分（汤浸去皮，微炒）。', '百部汤（《圣济总录》卷六十五）。', '《圣惠》卷四十六', '肺气暴热咳嗽，气满喘急；又治骨蒸劳，烦热，肩背疼痛，四肢乏力，咳嗽。', '-', '-', '柴胡', '[\'百部\', \'两赤\', \'茯苓\', \'百合\', \'两桑根\', \'白皮\', \'木通\', \'甘草\', \'半两\', \'炙微\', \'柴胡\', \'两去\', \'枳壳\', \'两麸\', \'微黄\', \'芍药\', \'郁李仁\', \'分汤\', \'去皮\']');
INSERT INTO `prescript` VALUES (57359, '30087', '连翘汤', '连翘2两，柴胡半两，当归半两，生地黄半两，赤芍药半两，黄芩1两，大黄3钱。', '上（口父）咀。每服1两，水煎服。', '《云歧子脉诀》', '主脉浮，客脉洪，浮洪相合，热结于胸中者。', '-', '-', '柴胡', '[\'连翘\', \'柴胡\', \'半两\', \'当归\', \'半两\', \'生地黄\', \'半两\', \'芍药\', \'半两\', \'黄芩\', \'大黄\']');
