INSERT INTO `prescript` VALUES (57375, '29647', '防风汤', '防风（去叉）1两，独活（去芦头）1两，羌活（去芦头）1两，柴胡（去苗）1两，白术1两，甘草（炙）1两，麻黄（去节）1两，芎2两，荆芥穗半两，菊花半两。', '每服3钱匕，以水1盏，煎去7分，去滓，食后温服。', '《圣济总录》卷十三', '劳风壅滞，多痰逆头昏。', '-', '-', '柴胡', '[\'防风\', \'独活\', \'芦头\', \'羌活\', \'芦头\', \'柴胡\', \'白术\', \'甘草\', \'麻黄\', \'去节\', \'两芎\', \'荆芥\', \'半两\', \'菊花\', \'半两\']');
INSERT INTO `prescript` VALUES (57376, '29622', '附子汤', '附子（炮裂，去皮脐）半两，白茯苓（去黑皮）1两，人参1两，细辛（去苗叶）1两，柴胡（去苗）1两，陈橘皮（去白，焙）1两，甘草（炙，锉）半两，厚朴（去粗皮，生姜汁炙）半两，莎草根（去须）半两，黄耆（炙，锉）半两，赤芍药半两。', '每服2钱匕，水1盏，加生姜5片，大枣2枚（擘），同煎至6分，去滓温服，不拘时候。', '《圣济总录》卷二十一', '伤寒憎寒壮热，头痛膈闷，四肢疼倦。', '-', '-', '柴胡', '[\'附子\', \'炮裂\', \'去皮\', \'半两\', \'白茯苓\', \'黑皮\', \'人参\', \'细辛\', \'苗叶\', \'柴胡\', \'橘皮\', \'甘草\', \'半两\', \'厚朴\', \'粗皮生\', \'姜汁\', \'半两\', \'莎草\', \'根去\', \'半两\', \'黄耆\', \'半两\', \'芍药\', \'半两\']');
INSERT INTO `prescript` VALUES (57377, '29503', '补中益气汤', '黄耆1钱，甘草（炙）5分，人参（去芦）3分，升麻3分，柴胡3分，橘皮3分，当归身（酒洗）3分，白术3分。', '医王汤（《伤寒论今释》卷七引《方函口诀》）。①《小儿痘疹》有生姜、大枣。②本方改为丸剂，名“补中益气丸”（见《中药成方配本》苏州方）；本方改为片剂，名“补中益气片”（见《天津市中成药规范》）。手扪之肌表热，服补中益气汤1-2服后，若再烦乱，腹中或周身有刺痛，皆血涩不足，加当归身5分或1钱；如精神短少，加人参5分，五味子20个；头痛，加蔓荆子3分，痛甚，加川芎5分；顶痛脑痛，加藁本5分，细辛3分；如头痛有痰，沉重懒倦者，乃太阴痰厥头痛，加半夏5分，生姜3分；耳鸣，目黄，颊颔肿，颈、肩、臑、肘、臂外后廉痛，面', '《内外伤辨》卷中', '补中益气，升阳举陷。主脾胃气虚，发热，自汗出，渴喜温饮，少气懒言，体倦肢软，面色（白光）白，大便稀溏，脉洪而虚，舌质淡，苔薄白。或气虚下陷，脱肛，子宫下垂，久泻，久痢，久疟等，以及清阳下陷诸证。', '下元虚者禁用。', '-', '柴胡', '[\'黄耆\', \'甘草\', \'炙分\', \'人参\', \'芦分\', \'升麻\', \'柴胡\', \'橘皮\', \'当归\', \'身酒\', \'洗分\', \'白术\']');
INSERT INTO `prescript` VALUES (57378, '29466', '龙胆泻肝汤', '龙胆草8分，升麻3分，柴胡3分，羌活根1钱，酒黄柏1钱，防风根2钱，麻黄根2钱，苍术5分，猪苓3分，泽泻3分，藁本2分，红花2分，当归2分，黄芩5分，炙甘草3分。', '上(口父)咀，作1服。水2盏煎，稍热服。', '《医统》卷六十', '尿黄，臊臭淋沥，两丸如水，汗浸两胯，阴头亦冷。', '忌酒。面。', '-', '柴胡', '[\'龙胆草\', \'升麻\', \'柴胡\', \'羌活\', \'根钱\', \'黄柏\', \'防风\', \'根钱\', \'麻黄根\', \'苍术\', \'猪苓\', \'泽泻\', \'藁本\', \'红花\', \'当归\', \'黄芩\', \'甘草\']');
INSERT INTO `prescript` VALUES (57379, '29460', '加减补中益气汤', '人参1钱半（去芦），黄耆1钱半（蜜炙），白术1钱，当归1钱（酒洗），草（炙）7分，陈皮8分，升麻5分，柴胡5分，加半夏1钱2分，黄柏8分，茯神1钱2分，枣仁1钱2分，贝母1钱2分，甘枸杞1钱2分。', '加生姜3片，大枣1枚，水2钟，煎8分，食远服。或加黄柏5分。如身大热，只1服，气和微汗而愈。夏月神短，加麦门冬、五味子；口干，加葛根；身刺痛乃少血，加当归；头痛，加川芎、蔓荆子，头顶痛，加藁本、细辛，诸头痛并用此四味；有痰加半夏、生姜；咳嗽，春加川芎、佛耳草，夏加黄芩、麦门冬、五味子，秋加黄芩、麻黄、金佛草，冬加款冬花、马兜铃；久嗽，乃肺中伏火，去参、耆；饮食不下，乃胃中有寒，或气滞，春加青皮、陈皮、木香，冬加益智仁、草豆蔻仁，夏加芩、连，秋加槟榔、砂仁；心下痞，加枳实、黄连、白芍药；腹胀，加枳实、木香、', '《医便》卷二', '饮食劳力，读书刻苦，勤政伤神，饥饱失时，症类疟状，发热头疼恶寒，身强体痛，苦劳极复感风寒，则头疼如破，全似外感伤寒之症，误表伤正者。', '-', '-', '柴胡', '[\'人参\', \'钱半去\', \'芦黄耆\', \'半蜜\', \'白术\', \'当归\', \'钱酒\', \'草炙分\', \'陈皮分\', \'升麻\', \'柴胡\', \'分加\', \'半夏\', \'钱分\', \'黄柏\', \'茯神\', \'钱分\', \'枣仁\', \'钱分\', \'贝母\', \'钱分\', \'枸杞\', \'钱分\']');
INSERT INTO `prescript` VALUES (57380, '29451', '龙胆泻肝汤', '龙胆草（酒炒）1钱，天冬1钱，麦冬1钱，甘草1钱，黄连1钱，黄芩1钱半，柴胡1钱半，山栀1钱，知母1钱，五味3分。', '水煎，热服。', '《会约》卷四', '筋疝。', '-', '-', '柴胡', '[\'龙胆草\', \'天冬\', \'麦冬\', \'甘草\', \'黄连\', \'黄芩\', \'钱半\', \'柴胡\', \'半山\', \'栀钱\', \'知母\', \'五味\']');
INSERT INTO `prescript` VALUES (57381, '29412', '地骨皮汤', '地骨皮1两，知母（焙）1两，柴胡（去苗）1两，当归（切，焙）1两，秦艽（去苗土）1两，鳖甲（醋炙，去裙襕）1两，甘草（炙，锉）1两，枳壳（去瓤，麸炒）1两。', '每服2钱匕，水1盏，加乌梅、生姜、桃柳枝、小麦各少许，煎至7分，去滓温服。', '《圣济总录》卷九十三', '虚劳骨蒸，烦热发渴。', '-', '-', '柴胡', '[\'地骨皮\', \'知母\', \'柴胡\', \'当归\', \'切焙\', \'秦艽\', \'苗土\', \'鳖甲\', \'甘草\', \'枳壳\']');
INSERT INTO `prescript` VALUES (57382, '29402', '安胎饮', '白术1钱5分，陈皮4分（去白），甘草4分，人参1钱，川芎8分，当归2钱，生地1钱5分，柴胡4分，半夏6分（姜制），升麻4分。', '加生姜为引，空心屡服。盐汤探吐，则气升尿下行。', '《胎产指南》卷一', '孕妇脾胃气虚，胎压尿胞而脐腹作胀，或小便淋闭。', '-', '-', '柴胡', '[\'白术\', \'钱分\', \'陈皮分\', \'甘草\', \'人参\', \'川芎\', \'当归\', \'生地\', \'钱分\', \'柴胡\', \'半夏\', \'分姜制\', \'升麻\']');
INSERT INTO `prescript` VALUES (57383, '29393', '加减补中益气汤', '人参3钱，黄耆（蜜炙）1钱，白术（蜜炙）1钱，白芍（酒炒）1钱，当归身（酒洗）1钱，川芎1钱，陈皮1钱，柴胡7分，白芷7分，茯苓7分，黄柏（酒炒）7分，知母（酒炒）7分，生地黄7分，炙甘草5分。', '加生姜3片，大枣2枚，水2钟，煎7分，食前服。', '《叶氏女科》', '崩漏经乱，经用四物汤、十灰丸、地黄汤，崩漏既止，里热已除，宜补气血者。', '-', '-', '柴胡', '[\'人参\', \'黄耆\', \'蜜炙钱\', \'白术\', \'蜜炙钱\', \'白芍\', \'当归\', \'身酒\', \'洗钱\', \'川芎\', \'陈皮\', \'柴胡\', \'白芷\', \'茯苓\', \'黄柏\', \'炒分\', \'知母\', \'炒分\', \'生地黄\', \'甘草\']');
INSERT INTO `prescript` VALUES (57384, '29381', '百一膏', '莨菪子1合，乌麻油1大升，乱发1拳许（灰汁净洗），黄丹4两，蜡1两，绯帛子1方寸，松脂桃许大，丁香2-7枚，曲头棘针2-7枚（破之），印成盐7枚（破），柴胡1斤（末），紫菀1斤，驴耳塞1钱匕。', '以故帛摊附肿上。', '《医方类聚》卷一七二引《千金月令》', '发背、下疮、乳痈，一切毒疮肿。', '-', '-', '柴胡', '[\'莨菪子\', \'合乌\', \'麻油\', \'大升\', \'乱发\', \'许灰汁\', \'黄丹\', \'两蜡\', \'帛子\', \'方寸\', \'松脂\', \'桃许\', \'丁香\', \'曲头\', \'棘针\', \'印成\', \'盐枚\', \'柴胡\', \'紫菀\', \'耳塞\']');
INSERT INTO `prescript` VALUES (57385, '29334', '冲和汤', '甘草2钱半（炙），黄芩1钱半，柴胡（去梗）半两，人参（去芦）半两，半夏（汤泡7次）半两。', '每服3钱，水1盏半，生姜5片，大枣2枚，煎至7分，去滓温服。寒多，加桂；热多，增人参；渴，加干葛。', '《魏氏家藏方》卷一', '暑疟，寒热多痰。', '-', '-', '柴胡', '[\'甘草\', \'钱半\', \'黄芩\', \'钱半\', \'柴胡\', \'半两\', \'人参\', \'去芦\', \'半两\', \'半夏\', \'汤泡次\', \'半两\']');
INSERT INTO `prescript` VALUES (57386, '29302', '防葵丸', '防葵1两，柴胡1两（去苗），木香3分，桃仁1两（汤浸，去皮尖双仁，麸炒微黄），鳖甲1两（涂醋炙微黄，去裙襕），桂心半两，川大黄1两（锉碎，微炒），当归半两，京三棱1两（炮，锉），赤芍药半两，槟榔1两，郁李仁1两（汤浸，去皮尖，微炒）。', '每服20丸，食前以温酒送下。', '《圣惠》卷二十八', '虚劳积聚，胁下妨满，腹胀不能食，及腹中痛。', '忌苋案、生冷、湿面。', '-', '柴胡', '[\'防葵\', \'柴胡\', \'两去\', \'苗木\', \'香分\', \'桃仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'鳖甲\', \'两涂\', \'微黄\', \'桂心\', \'半两\', \'大黄\', \'当归\', \'半两\', \'三棱\', \'两炮\', \'芍药\', \'半两\', \'槟榔\', \'郁李仁\', \'两汤\', \'浸去\', \'皮尖\']');
INSERT INTO `prescript` VALUES (57387, '29299', '补中益气汤', '黄耆3钱，当归3钱，白术3钱，广陈皮3钱，川升麻8分，人参6分，柴胡6分，甘草6分，桂枝、防风、木通、木瓜。', '方中桂枝、防风、木通、木瓜用量原缺。', '《一盘珠》卷三', '升清降浊。主体虚冒风发肿。', '-', '-', '柴胡', '[\'黄耆\', \'当归\', \'白术\', \'钱广\', \'陈皮\', \'钱川\', \'升麻\', \'人参\', \'柴胡\', \'甘草\', \'桂枝\', \'防风\', \'木通\', \'木瓜\']');
INSERT INTO `prescript` VALUES (57388, '29257', '补益煎', '生地黄4斤，生天门冬1斤，生藕1斤，生姜半斤，（以上4味锉碎，用生绢袋绞取汁），石斛（去根）1两，鹿茸（酥炙，去毛）1两，菟丝子（酒浸1宿，捣成片子，焙干）1两，牛膝（酒浸1宿，焙干）1两，黄耆（锉）1两，柴胡（去苗）1两，地骨皮1两，人参1两，白茯苓（去黑皮）1两，桂（去粗皮）1两，木香1两，附子（炮裂，去皮脐）1两。', '每日1匙，空心温酒调下。', '《圣济总录》卷八十九', '和益营卫，驻颜补气，滋润肌体。主虚劳肌瘦，腿膝少力，不思饮食，皮肤生疮。', '-', '-', '柴胡', '[\'生地黄\', \'斤生\', \'天门冬\', \'生藕斤\', \'生姜\', \'半斤\', \'以上\', \'碎用\', \'生绢袋\', \'取汁\', \'石斛\', \'鹿茸\', \'菟丝子\', \'浸宿\', \'捣成\', \'片子\', \'焙干\', \'牛膝\', \'浸宿\', \'焙干\', \'两黄耆\', \'柴胡\', \'地骨皮\', \'人参\', \'白茯苓\', \'黑皮\', \'两桂去\', \'粗皮\', \'木香\', \'附子\', \'炮裂\', \'去皮\']');
INSERT INTO `prescript` VALUES (57389, '29250', '导热汤', '当归3钱，白芍3钱，柴胡2钱，黄芩1钱，丹皮3钱，甘草1钱，天花粉1钱。', '水煎服。', '《辨证录》卷五', '引血归经，导火外泄。主春月伤风，热入血室，下血谵语，头汗出，似狂非狂。', '-', '-', '柴胡', '[\'当归\', \'白芍\', \'柴胡\', \'黄芩\', \'丹皮\', \'甘草\', \'天花粉\']');
INSERT INTO `prescript` VALUES (57390, '29234', '君子汤', '陈皮2钱，茯苓2钱，枳实2钱，川芎2钱，赤芍2钱，苏叶2钱，槟榔2钱，桔梗2钱，白术2钱，半夏2钱，当归3钱，香附3钱，厚朴3钱，甘草1钱，红花1钱，黄连（酒炒）1钱，柴胡1钱，砂仁1钱5分。', '上分8帖。加生姜3片，酒、水各半煎，空心服。如嗽，加五味子、杏仁各2钱；口渴潮热，加竹沥2匙。', '《女科旨要》卷一', '妇人经后潮热，误食生冷，聚成痰饮，腹心胀满，气升上隔，饮食不思，腹结块成瘼。', '-', '-', '柴胡', '[\'陈皮\', \'茯苓\', \'枳实\', \'川芎\', \'赤芍\', \'苏叶\', \'槟榔\', \'桔梗\', \'白术\', \'半夏\', \'当归\', \'香附\', \'厚朴\', \'甘草\', \'钱红\', \'花钱\', \'黄连\', \'柴胡\', \'砂仁\', \'钱分\']');
INSERT INTO `prescript` VALUES (57391, '29217', '龙胆泻毒汤', '柴胡1钱，龙胆草1钱，山栀1钱，大黄1钱5分，黄连1钱5分，滑石1钱5分，木通1钱5分，甘草5分。', '加灯心30茎，水煎，食前服。', '《玉案》卷六', '一切下疳。', '-', '-', '柴胡', '[\'柴胡\', \'龙胆草\', \'钱山\', \'栀钱\', \'大黄\', \'钱分\', \'黄连\', \'钱分\', \'滑石\', \'钱分\', \'木通\', \'钱分\', \'甘草\']');
INSERT INTO `prescript` VALUES (57392, '29183', '两宜汤', '人参2钱，茯苓5钱，白术5钱，甘草1钱，泽泻1钱，黄连1钱，肉桂3分，陈皮5分，天花粉2钱，柴胡3分。', '水煎服。2剂愈。', '《辨证录》卷四', '口渴饮水忽然呃逆者。', '-', '-', '柴胡', '[\'人参\', \'茯苓\', \'白术\', \'甘草\', \'泽泻\', \'黄连\', \'肉桂\', \'陈皮分\', \'天花粉\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57393, '29136', '复方大柴胡汤', '柴胡12g，黄芩9g，枳壳9g，川楝子9g，大黄9g，玄胡10g，白芍10g，蒲公英15g，木香6g，丹参6g，甘草6g。', '-', '《医学资料选编》', '和解表里，清泻热结。主溃疡病穿孔缓解后腹腔感染，腹部有压痛，肠鸣，便燥，身热，脉数，舌苔黄。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'枳壳\', \'川楝子\', \'大黄\', \'玄胡\', \'白芍\', \'蒲公英\', \'木香\', \'丹参\', \'甘草\']');
INSERT INTO `prescript` VALUES (57394, '29114', '地骨散', '地骨皮半两，黄芩半两，人参（去芦头）半两，黄耆（锉）半两，葳蕤半两，麦门冬（去心）半两，甘草（炙微赤，锉）半两，赤芍药半两，柴胡1两（去苗）。', '每服4钱，以水1中盏，加生差半分，淡竹叶2-7片，煎至6分，去滓温服，不拘时候。', '《圣惠》卷七十四', '妊娠烦躁，体热疼痛，口干食少。', '-', '-', '柴胡', '[\'地骨皮\', \'半两\', \'黄芩\', \'半两\', \'人参\', \'芦头\', \'半两\', \'黄耆\', \'半两\', \'葳蕤\', \'半两\', \'麦门冬\', \'去心\', \'半两\', \'甘草\', \'炙微\', \'半两\', \'芍药\', \'半两\', \'柴胡\', \'两去\']');
INSERT INTO `prescript` VALUES (57395, '29089', '两舒散', '白芍5钱，柴胡1钱，茯苓3钱，陈皮5分，甘草5分，车前子1钱，六曲5分。', '水煎服。', '《石室秘录》卷一', '肝郁克脾，吞酸，泄泻。', '-', '-', '柴胡', '[\'白芍\', \'柴胡\', \'茯苓\', \'陈皮分\', \'甘草\', \'车前子\', \'六曲\']');
INSERT INTO `prescript` VALUES (57396, '29081', '厚朴汤', '厚朴（去粗皮，用生姜汁涂炙）1两，槟榔（锉）1两，食茱萸1两，芍药1两，柴胡（去苗）1两，当归（切，焙）1两，郁李仁（汤浸，去皮，炒）3分。', '每服5钱匕，水1盏半，煎至1盏，去滓温服，空心、日午、夜卧各1服。', '《圣济总录》卷五十六', '九种心痛。', '-', '-', '柴胡', '[\'厚朴\', \'粗皮\', \'用生\', \'姜汁\', \'槟榔\', \'食茱萸\', \'芍药\', \'柴胡\', \'当归\', \'切焙\', \'郁李仁\', \'汤浸\', \'去皮\', \'炒分\']');
INSERT INTO `prescript` VALUES (57397, '29048', '赤芍药散', '牡丹皮1两，白茯苓1两，赤芍药1两，吴白芷1两，甘草1两，柴胡3两（去芦）。', '每服2钱，水一钟，加生姜3片，大枣1个，煎至7分，食后温服。', '《博济》卷四', '妇人气血不和，心胸烦闷，不思饮食，四肢少力，头目昏眩，身体疼痛。', '-', '-', '柴胡', '[\'牡丹皮\', \'白茯苓\', \'两赤\', \'芍药\', \'两吴\', \'白芷\', \'甘草\', \'柴胡\', \'两去\']');
INSERT INTO `prescript` VALUES (57398, '29008', '地骨皮散', '地骨皮2两，柴胡（去苗）1两。', '每服2钱匕，用麦门冬（去心）煎汤调下，不拘时候。', '《圣济总录》卷八十七', '热劳。', '-', '-', '柴胡', '[\'地骨皮\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57399, '28975', '扶解调肝汤', '泡参5钱，白术3钱，炒白芍3钱，阿胶珠2钱，茯神3钱，软柴胡2钱，甘草1钱。', '水煎，食远温服。血量过多，甚至兼有血块者，加乌鲗骨1两，茜草根、蒲黄炭各2钱。', '《中医妇科治疗学》', '扶气养血舒郁。主产后血崩，头晕目眩，精神抑郁，嗳气太息，心烦善怒，胸闷，两胁胀痛，血色淡红，食欲减退，大便不调或溏薄不畅，舌苔薄白，脉弦，重按无力。', '-', '-', '柴胡', '[\'参钱\', \'白术\', \'白芍\', \'阿胶\', \'珠钱\', \'茯神\', \'钱软\', \'柴胡\', \'甘草\']');
INSERT INTO `prescript` VALUES (57400, '28965', '补肝膏', '鳖甲全个（先熬去滓），党参2两，生地2两，熟地2两，杞子2两，五味子2两，当归2两，萸肉2两，黄耆1两，白术1两，白芍1两，川芎1两，醋香附1两，山药1两，枣仁1两，灵脂1两，柴胡7钱，丹皮7钱，黑山栀7钱，龙胆草7钱，瓜蒌7钱，黄芩7钱，茯苓7钱，木通7钱，羌活7钱，防风7钱，泽泻7钱，生甘草7钱，黄连、续断、吴萸、陈皮、半夏、红花5钱，薄荷2钱，官桂2钱，乌梅5个。', '油丹熬，牛胶2-3搅，贴患处。', '《理瀹》', '肝虚气血为病，或有隐痛者。', '-', '-', '柴胡', '[\'鳖甲\', \'全个\', \'党参\', \'生地\', \'熟地\', \'两杞子\', \'五味子\', \'当归\', \'两萸\', \'黄耆\', \'白术\', \'白芍\', \'川芎\', \'两醋\', \'香附\', \'山药\', \'枣仁\', \'两灵脂\', \'柴胡\', \'丹皮\', \'黑山\', \'栀钱\', \'龙胆草\', \'钱瓜\', \'黄芩\', \'茯苓\', \'木通\', \'羌活\', \'防风\', \'泽泻\', \'生甘草\', \'连续\', \'吴萸\', \'陈皮\', \'半夏\', \'花钱\', \'薄荷\', \'官桂钱\', \'乌梅\']');
INSERT INTO `prescript` VALUES (57401, '28958', '当归柴胡汤', '柴胡5钱，半夏2钱（以生姜1钱同捣），当归1钱，甘草5分。', '加生姜、大枣，以水2盏，煎至8分，热服。取汗微微即止。', '《扁鹊心书·神方》', '伤寒头痛，发热恶寒，肢节痛，吐逆。', '-', '-', '柴胡', '[\'柴胡\', \'半夏\', \'钱以\', \'生姜\', \'钱同\', \'当归\', \'甘草\']');
INSERT INTO `prescript` VALUES (57402, '28913', '龙胆泻肝汤', '龙胆草5分（酒炒），人参5分，天冬5分（去心），甘草5分，黄连5分（炒），栀子5分（炒），知母5分，黄芩7分，柴胡1钱，五味子3分。', '水煎、温服。', '《竹林女科证治》卷三', '暴怒伤肝而动火，产户不闭者。', '-', '-', '柴胡', '[\'龙胆草\', \'分酒\', \'人参\', \'天冬\', \'分去\', \'甘草\', \'黄连\', \'栀子\', \'知母\', \'黄芩\', \'柴胡\', \'五味子\']');
INSERT INTO `prescript` VALUES (57403, '28832', '龙脑鸡苏丸', '柴胡（要真银州者）2两（锉，同木通以沸汤大半升浸1-2宿，绞汁后入膏），木通2两（锉，同柴胡浸），阿胶2两（炒微燥），蒲黄2两（真者，微炒），人参2两，麦门冬（汤洗，去心，焙干）4两，黄耆（去芦）1两，鸡苏（净叶）1斤（即龙脑薄荷也），甘草（炙）1两半，生干地黄末6两（后入膏）。', '鸡苏丸（《普济方》卷五十八引《如宜方》）。', '《局方》卷六', '除烦解劳，消谷下气，清热理血，散胸中郁热，凉上膈，解酒毒，聪耳明目，开心益智。主肺热咳嗽，鼻衄吐血，血崩下血，血淋，热淋，劳淋，气淋，消渴，惊悸，胃热口臭，肺热喉腥，脾疸口甜，胆疸口苦。', '-', '-', '柴胡', '[\'柴胡\', \'银州\', \'木通\', \'沸汤\', \'大半\', \'升浸\', \'宿绞汁\', \'入膏\', \'木通\', \'柴胡\', \'阿胶\', \'微燥\', \'蒲黄\', \'两真者\', \'人参\', \'麦门冬汤\', \'洗去\', \'焙干\', \'两黄耆\', \'芦两鸡\', \'苏净\', \'叶斤\', \'龙脑\', \'薄荷\', \'甘草\', \'半生\', \'地黄\', \'两后\', \'入膏\']');
INSERT INTO `prescript` VALUES (57404, '28823', '安土汤', '白芍1两，白术1两，柴胡1钱，茯苓3钱，甘草1钱，苍术2钱，神曲2钱，炮姜1钱。', '水煎服。', '《辨证录》卷十', '肝气旺盛，肝木g脾土，土气不能伸，而肠中自鸣，终日不已，嗳气吞酸，无有休歇。', '-', '-', '柴胡', '[\'白芍\', \'白术\', \'柴胡\', \'茯苓\', \'甘草\', \'苍术\', \'神曲\', \'钱炮\', \'姜钱\']');
INSERT INTO `prescript` VALUES (57405, '28798', '麦门冬汤', '麦门冬（去心，焙）2两，甘草（炙，锉）2两，白茯苓（去黑皮）1两，羌活（去芦头）1两，旋覆花1两，玄参1两，白术1两，芍药1两，柴胡（去苗）1两，人参1两，升麻1两，当归（切，焙）1两，桑根白皮（锉）1两，胡黄连1分，熟干地黄（焙）1两半，木香半两。', '每服3钱匕，水1盏，入甘草1寸，同煎至8分，去滓温服，不拘时候。', '《圣济总录》卷四十七', '胃热肠寒，善食数饥，少腹胀痛。', '-', '-', '柴胡', '[\'麦门冬\', \'甘草\', \'白茯苓\', \'黑皮\', \'羌活\', \'芦头\', \'旋覆花\', \'玄参\', \'白术\', \'芍药\', \'柴胡\', \'人参\', \'升麻\', \'当归\', \'切焙\', \'两桑根\', \'白皮\', \'胡黄连\', \'分熟\', \'地黄\', \'两半\', \'木香\', \'半两\']');
INSERT INTO `prescript` VALUES (57406, '28779', '连翘散', '连翘半两，白药子3分，丹参半两，山栀子仁半两，柴胡（去苗）半两，甘草（炙，锉）1分，恶实半两，黄柏（去粗皮，蜜炙）半两。', '每服2钱匕，食后用蜜水调下，日2次。', '《圣济总录》卷三十', '伤寒热毒未解，咽喉壅塞，口内生疮。', '-', '-', '柴胡', '[\'连翘\', \'半两\', \'白药子\', \'丹参\', \'半两\', \'栀子\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'甘草\', \'分恶实\', \'半两\', \'黄柏\', \'粗皮蜜\', \'半两\']');
INSERT INTO `prescript` VALUES (57407, '28773', '补夜丹', '熟地1两，白芍5钱，鳖甲3钱，当归3钱，生何首乌3钱，丹皮3钱，地骨皮3钱，茯苓5钱，麦冬5钱，贝母3钱，柴胡1钱。', '水煎服。', '《辨证录》卷五', '春温之症，满身疼痛，夜间发热，日间则冷。', '-', '-', '柴胡', '[\'熟地\', \'白芍\', \'鳖甲\', \'当归\', \'钱生\', \'何首乌\', \'丹皮\', \'地骨皮\', \'茯苓\', \'麦冬\', \'贝母\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57408, '28735', '灵应饮', '茯神2钱，小柴胡2钱，人参2钱，生地2钱，银柴胡2钱，黄芩2钱，知母1钱，麦门冬1钱。', '加大枣5枚，水煎，临服加童便1杯。', '《玉案》卷四', '潮热。', '-', '-', '柴胡', '[\'茯神\', \'小柴胡\', \'人参\', \'生地\', \'银柴胡\', \'黄芩\', \'知母\', \'麦门冬\']');
INSERT INTO `prescript` VALUES (57409, '28732', '白术汤', '白术1钱（土炒），人参1钱，茯苓1钱，牛膝1钱，黄柏1钱，苍术7分（泔制），当归1钱，白芍药1钱（酒炒），木瓜1钱，柴胡5分，广皮1钱。', '加大枣2枚，生姜1片，水煎服。', '《医彻》卷二', '脚气，气血亏损，足胫疼痛。', '-', '-', '柴胡', '[\'白术\', \'钱土\', \'人参\', \'茯苓\', \'牛膝\', \'黄柏\', \'苍术\', \'分泔制\', \'当归\', \'钱白\', \'芍药\', \'钱酒\', \'木瓜\', \'柴胡\', \'分广皮\']');
INSERT INTO `prescript` VALUES (57410, '28700', '妇女养血丸', '当归5两，香附（醋炙）3两，川芎1两，肉桂2两，木香（煨）1两，熟地黄3两，白芍（酒炒）3两，砂仁1两，山药3两，川贝母2两，阿胶珠1两，茯苓3两，炮姜1两，党参2两，黄耆（炙）2两，续断2两，白术（麸炒）2两，知母2两，甘草1两，地骨皮1两，艾叶炭2两，杜仲炭1两，柴胡（醋制）1两。', '每服1钱，日服2-3次，温开水送服。', '《中药制剂手册》', '益气养血，调经止痛。主妇女血亏，月经愆期，时来时止，血枯色淡，腹痛腰酸，精神倦怠，日晡潮热，咳嗽自汗。', '-', '-', '柴胡', '[\'当归\', \'香附\', \'川芎\', \'肉桂\', \'木香\', \'熟地黄\', \'白芍\', \'砂仁\', \'山药\', \'川贝母\', \'阿胶\', \'茯苓\', \'两炮\', \'党参\', \'两黄耆\', \'续断\', \'白术\', \'知母\', \'甘草\', \'地骨皮\', \'艾叶\', \'杜仲\', \'柴胡\', \'醋制\']');
INSERT INTO `prescript` VALUES (57411, '28682', '当归四逆汤', '附子1钱半（炮），官桂1钱半，白芍1钱半（酒炒），柴胡5分，当归3钱，吴茱1钱（醋炒），楝子2钱（酒炒），小茴3钱（醋炒），泽泻1钱半。', '水煎，去滓温服。', '《医略六书》卷二十四', '阳虚寒疝，脉紧细者。', '-', '-', '柴胡', '[\'附子\', \'钱半炮\', \'官桂钱\', \'白芍\', \'钱半酒\', \'柴胡\', \'当归\', \'吴茱钱\', \'炒楝子\', \'小茴钱\', \'泽泻\', \'钱半\']');
INSERT INTO `prescript` VALUES (57412, '28662', '当归煎丸', '当归（切，焙）半两，附子（去皮脐，生用）半两，没药（研）1分，硇砂（研）1分，血竭1分，禹余粮（煅赤，醋淬7遍）半两，延胡索半两。以上捣研为末，用酒3升调匀，于石器内慢火熬成膏，和下药：柴胡（去苗）1两，鳖甲（去裙襕，醋炙黄）1两，人参1两，生干地黄（焙）1两，芍药1两，磁石（煅，醋淬7遍）1两，牡丹皮1两，木香1分，泽兰半两。', '每服20丸，空心、食前淡醋汤送下。', '《圣济总录》卷一五三', '妇人血积血癖，脐腹（？？）痛，心膈满闷，四肢烦疼，口苦舌干，饮食减少，渐成劳瘦。', '-', '-', '柴胡', '[\'当归\', \'切焙\', \'半两\', \'附子\', \'去皮\', \'脐生用\', \'半两\', \'没药\', \'研分\', \'硇砂\', \'研分\', \'血竭\', \'禹余粮\', \'淬遍\', \'半两\', \'延胡索\', \'半两\', \'以上\', \'研为\', \'末用\', \'酒升\', \'调匀\', \'石器\', \'慢火\', \'成膏\', \'下药\', \'柴胡\', \'鳖甲\', \'人参\', \'两生\', \'地黄\', \'芍药\', \'磁石\', \'淬遍\', \'牡丹皮\', \'木香\', \'泽兰\', \'半两\']');
INSERT INTO `prescript` VALUES (57413, '28645', '地骨皮丸', '地骨皮半两，白芷半两，升麻半两，防风（去叉）半两，赤芍药半两，柴胡（去苗）1两，生干地黄（焙）1两半，大黄（锉，炒）半两，黄芩（去黑心）半两，枳壳（去瓤，麸炒）半两，芎穷半两，知母（焙）半两，萎蕤半两，槟榔（锉）半两，细辛（去苗叶）半两，甘菊花半两，藁本（去苗土）半两，牵牛子（炒）半两，马牙消（研）半两，犀角屑半两，胡黄连1两，甘草（炙）1两。', '每眼30丸，食后、夜卧熟水送下。以利为度。', '《圣济总录》卷一二○', '风袭齿龈，肿痛有血。', '-', '-', '柴胡', '[\'地骨皮\', \'半两\', \'白芷\', \'半两\', \'升麻\', \'半两\', \'防风\', \'半两\', \'芍药\', \'半两\', \'柴胡\', \'去苗\', \'两生\', \'地黄\', \'两半\', \'大黄\', \'半两\', \'黄芩\', \'黑心\', \'半两\', \'枳壳\', \'半两\', \'半两\', \'知母\', \'半两\', \'萎蕤\', \'半两\', \'槟榔\', \'半两\', \'细辛\', \'苗叶\', \'半两\', \'甘菊花\', \'半两\', \'藁本\', \'苗土\', \'半两\', \'牵牛子\', \'半两\', \'马牙\', \'消研\', \'半两\', \'犀角\', \'半两\', \'胡黄连\', \'甘草\']');
INSERT INTO `prescript` VALUES (57414, '28583', '参耆退热汤', '黄柏1钱，人参1钱，白术1钱，当归8分，橘皮8分，川芎8分，甘草（炙）5分，升麻5分，柴胡5分，甘葛5分，羌活5分，独活5分。', '本方名“参耆退热汤”，但方中无黄耆，疑脱。', '《杏苑》卷三', '劳役身热，骨疼腰痛，头重自汗。', '-', '-', '柴胡', '[\'黄柏\', \'人参\', \'白术\', \'当归\', \'橘皮\', \'川芎\', \'甘草\', \'炙分\', \'升麻\', \'柴胡\', \'分甘葛分\', \'羌活\', \'独活\']');
INSERT INTO `prescript` VALUES (57415, '28577', '白术散', '白术1两，当归3分（锉，微炒），柴胡1两（去苗），桂心半两，青橘皮3两（汤浸，去白瓤，焙），桔梗半两（去芦头），甘草半两（炙微赤，锉）。', '每服4钱，以水1中盏，加生姜半分，大枣2个，煎至6分，去滓稍热服，不拘时候。', '《圣惠》卷三十八', '乳石发动，体颤寒热，心腹痛噤，不能饮食。', '-', '-', '柴胡', '[\'白术\', \'当归\', \'柴胡\', \'两去\', \'苗桂心\', \'半两\', \'橘皮\', \'两汤\', \'浸去\', \'桔梗\', \'半两\', \'芦头\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (57416, '28573', '肥儿丸', '银柴胡1斤，厚朴1斤，使君肉1斤，麦芽1斤，青皮1斤，三棱1斤，芜荑1斤，莪术1斤，枳壳1斤，莱菔子1斤，神曲1斤，甘草1斤，白术6斤，黄连8两，胡黄连8两，槟榔8两，全蝎8两，芦荟2两，干蟾2斤，黄芩2斤，茯苓2斤，鸡内金2斤，陈皮2斤，山楂2斤，阿魏4两。', '每服20粒，1日2次，温开水送下。', '《全国中药成药处方集》（承德方）', '消积杀虫。主小儿食积，虫积，消化不良，面黄肌瘦。', '忌食油腻、生冷。', '-', '柴胡', '[\'银柴胡\', \'厚朴\', \'使君\', \'肉斤\', \'麦芽\', \'青皮\', \'三棱\', \'芜荑斤\', \'莪术\', \'枳壳\', \'莱菔子\', \'神曲\', \'甘草\', \'白术\', \'黄连\', \'胡黄连\', \'槟榔\', \'全蝎\', \'芦荟\', \'两干\', \'蟾斤\', \'黄芩\', \'茯苓\', \'鸡内金\', \'陈皮斤\', \'山楂\', \'阿魏\']');
INSERT INTO `prescript` VALUES (57417, '28567', '当归补血汤', '薄荷5分，羌活5分，茺蔚子1钱，柴胡8分，蒺藜1钱，菊花8分，防风8分，甘草4分，生地黄2钱，当归1钱5分，白芍药1钱，川芎8分。', '以水2盏，煎至1盏，去滓，食后温服。', '《金鉴》卷七十八', '经行去血过多，肝经虚损，眼目涩痛，头痛眩晕，肿涩难开，生翳于黑睛上，或如粟米，或花翳白陷。', '-', '-', '柴胡', '[\'薄荷\', \'羌活\', \'茺蔚子\', \'柴胡\', \'蒺藜\', \'菊花\', \'防风\', \'甘草\', \'生地黄\', \'当归\', \'分白\', \'芍药\', \'川芎\']');
INSERT INTO `prescript` VALUES (57418, '28565', '地骨皮散', '柴胡1两（去苗），地骨皮半两，木香半两，麻黄（去根节）半两，甘草（炙微赤，锉）半两，川升麻半两，栝楼根半两，人参（去芦头）半两，赤茯苓半两，木通（锉）半两。', '每服4钱，以水1中盏，煎至6分，去滓温服，不拘时候。', '《圣惠》卷十一', '阳毒伤寒，头昏身重，咽喉唇干，腮赤，狂言欲走，心胸胀满，呕逆不下饮食，面色斑斑如锦文。', '-', '-', '柴胡', '[\'柴胡\', \'两去\', \'地骨皮\', \'半两\', \'木香\', \'半两\', \'麻黄\', \'根节\', \'半两\', \'甘草\', \'炙微\', \'半两\', \'升麻\', \'半两\', \'栝楼\', \'半两\', \'人参\', \'芦头\', \'半两\', \'茯苓\', \'半两\', \'木通\', \'半两\']');
