INSERT INTO `prescript` VALUES (57420, '28493', '和中丸', '台白术2两，云茯苓2两，生甘草5钱，奎白芍2两，制首乌2两，银柴胡2两，知母2两，淮山药2两，地骨皮2两，使君子2两，生香附2两，木香1两5钱，川朴2两，陈广皮2两，油青皮2两，南楂肉2两，花粉2两，枳实2两，莱菔子2两，缩砂仁1两5钱，淡黄芩4两，柴胡4两5钱，木通2两，车前子2两，台乌药2两，泽泻2两。', '老年及小儿服1丸，多则2丸，饭后服。夏月，加香薷2两、六一散2两。', '《青囊秘传》', '和中理气，消痰祛湿。主', '-', '-', '柴胡', '[\'白术\', \'两云\', \'茯苓\', \'生甘草\', \'钱奎\', \'白芍\', \'两制\', \'首乌\', \'银柴胡\', \'知母\', \'两淮\', \'山药\', \'地骨皮\', \'使君子\', \'两生\', \'香附\', \'木香\', \'两钱川朴\', \'陈广皮\', \'两油\', \'青皮\', \'两南\', \'花粉\', \'枳实\', \'莱菔子\', \'两缩\', \'砂仁\', \'黄芩\', \'柴胡\', \'木通\', \'车前子\', \'两台\', \'乌药\', \'泽泻\']');
INSERT INTO `prescript` VALUES (57421, '28463', '两治汤', '生地3钱，人参3钱，白术5钱，茯苓3钱，甘草1钱，半夏1钱，川芎1钱，柴胡1钱，黄耆1两，当归5钱。', '水煎服。', '《辨证录》卷九', '筋酸背痛，足重腹饥，以至感冒风邪，遍身皆痛，身发寒热。', '-', '-', '柴胡', '[\'生地\', \'人参\', \'白术\', \'茯苓\', \'甘草\', \'半夏\', \'川芎\', \'柴胡\', \'黄耆\', \'当归\']');
INSERT INTO `prescript` VALUES (57422, '28440', '附子汤', '附子（炮裂，去皮脐）1两半，干姜（炮）1两半，甘草（炙）1两半，防风（去叉）1两半，独活（去芦头）1两半，石膏（碎）1两，白茯苓（去黑皮）1两，白术1两，芎1两，柴胡（去苗）1两，当归（酒浸，切，焙）1两，人参1两，杏仁（去皮尖双仁，炒研）20枚，细辛（去苗叶）1两。', '每服5钱匕，水、酒共1盏半，煎至1盏，去滓温服，日3次。人羸弱者，只用水煎服。', '《圣济总录》卷七', '风曳，手足不随，身体不能俯仰。', '-', '-', '柴胡', '[\'附子\', \'炮裂\', \'去皮\', \'两半\', \'干姜\', \'两半\', \'甘草\', \'两半\', \'防风\', \'两半\', \'独活\', \'芦头\', \'两半\', \'石膏\', \'白茯苓\', \'黑皮\', \'白术\', \'两芎\', \'柴胡\', \'当归\', \'浸切\', \'人参\', \'杏仁\', \'皮尖\', \'双仁\', \'研枚\', \'细辛\', \'苗叶\']');
INSERT INTO `prescript` VALUES (57423, '28427', '冲和汤', '柴胡1钱，黄耆1钱，当归（酒洗）1钱，半夏（煮）1钱，人参1钱，升麻8分，橘皮8分，甘草（炙）7分，黄柏（酒洗）5分，芍药5分，黄芩（酒洗）4分。', '上锉散。水煎，热服。', '《杏苑》卷三', '半身不遂，语言謇涩，心神昏愦，烦躁自汗，表虚恶风，如洒冰雪，口不知味，鼻不闻香臭，闻木音则惊怖，小便频多，大便结燥，痰嗽嗌干，疼痛不利。', '-', '-', '柴胡', '[\'柴胡\', \'黄耆\', \'当归\', \'洗钱\', \'半夏\', \'人参\', \'升麻\', \'橘皮\', \'甘草\', \'炙分\', \'黄柏\', \'洗分\', \'芍药\', \'黄芩\', \'洗分\']');
INSERT INTO `prescript` VALUES (57424, '28422', '妇女调经膏', '益母草1两，延胡索1两，穿山甲1两，香附2两，南红花1两，巴豆1两5钱，川芎1两，丹皮5钱，柴胡2两，生地3两，干姜1两，苍术1两，吴茱萸1两，透骨草1两，木香5钱，荆芥2两，小茴香2两，蕲艾1两，边桂5钱，薄荷1两，防风2两。', '将膏摊于布上，微火化开，贴于丹田穴，大小酌用，临时用姜片擦净。', '《全国中药成药处方集》（济南方）', '经血不调，阴寒肚疼，赤白带下。', '-', '-', '柴胡', '[\'益母草\', \'延胡索\', \'穿山甲\', \'香附\', \'两南\', \'红花\', \'巴豆\', \'川芎\', \'丹皮\', \'柴胡\', \'生地\', \'干姜\', \'苍术\', \'吴茱萸\', \'透骨草\', \'木香\', \'荆芥\', \'小茴香\', \'两边\', \'桂钱\', \'薄荷\', \'防风\']');
INSERT INTO `prescript` VALUES (57425, '28416', '和气八物汤', '人参3钱，茯苓3钱，熟地3钱，小茴3钱，白术4钱，川芎4钱，甘草1钱，黄芩1钱，柴胡1钱，枳壳1钱，当归6钱，白芍6钱，香附6钱。', '上分4帖。加生姜3片，灯心1团，水煎，空心热服。如肚痛，加延胡、干漆各3钱；呕吐恶心，加良姜、砂仁各3钱；手足麻痹，加肉桂1钱5分；咳嗽，加杏仁、五昧、款冬花各2钱。', '《女科旨要》卷一', '妇女十七八岁，脾胃虚弱，气血不行，经脉不通，或阻半月，或阻百日半年，颜色青黄，饮食不思，寒热头痛，目晕，肚中结块，烦闷，呕吐，膨胀。', '-', '-', '柴胡', '[\'人参\', \'茯苓\', \'熟地\', \'钱小茴钱\', \'白术\', \'川芎\', \'甘草\', \'黄芩\', \'柴胡\', \'枳壳\', \'当归\', \'白芍\', \'香附\']');
INSERT INTO `prescript` VALUES (57426, '28411', '妇科金丹', '当归40两，杭芍40两，白术（炒）40两，柴胡40两，阿胶（炒珠）24两，蛇床子24两，吴萸（炙）20两，椿根皮（炒）16两，海螵蛸16两，艾炭16两，黄芩12两，益母草16两，灵仙16两，藁本20两，秦艽22两，茯苓16两，牡蛎（煅）8两，木瓜16两，益智仁16两，香附（炙）32两，远志（炙）12两，黄耆16两，甘草8两，补骨脂（炒）16两，青皮（炒）12两，黑郁金16两，法半夏12两，使君子16两，白芷8两，羌活8两，九菖蒲24两，川牛膝8两，川芎16两，杜仲炭24两，苍术（炒）12两，川续断16两，', '每日晚临睡时服1丸，温开水送一下。', '《北京市中药成方选集》', '调经养血，舒郁止痛，健脾养胃。主经血不调，经期不准，行经腹痛，两胁胀满，赤白带下。', '-', '-', '柴胡', '[\'当归\', \'两杭芍\', \'白术\', \'柴胡\', \'阿胶\', \'炒珠\', \'蛇床子\', \'吴萸\', \'两椿根\', \'海螵蛸\', \'两艾炭\', \'黄芩\', \'益母草\', \'两灵仙\', \'藁本\', \'秦艽\', \'茯苓\', \'牡蛎\', \'木瓜\', \'益智仁\', \'香附\', \'远志\', \'黄耆\', \'甘草\', \'补骨脂\', \'青皮\', \'郁金\', \'两法\', \'半夏\', \'使君子\', \'白芷\', \'羌活\', \'菖蒲\', \'川牛膝\', \'川芎\', \'杜仲\', \'苍术\', \'两川\', \'续断\']');
INSERT INTO `prescript` VALUES (57427, '28405', '甘遂丸', '甘遂（微炒黄色）1两，芫花（醋炒黄色）1两，桃仁（汤去皮尖双仁，炒黄别研）1两，芎藭1两，当归（切，焙）1两，柴胡（去苗）1两，蜀椒（去闭口及目，炒出汗）1两，吴茱萸（汤淘7遍，焙干，炒）1两，厚朴（去粗皮1两，姜汁炙）1两，桂（去粗皮）1两。', '每服10丸，空腹煎生姜汤送下，日2次。', '《圣济总录》卷七十三', '痃气。', '-', '-', '柴胡', '[\'甘遂微\', \'黄色\', \'芫花\', \'黄色\', \'桃仁\', \'汤去\', \'皮尖\', \'双仁\', \'黄别\', \'研两\', \'当归\', \'切焙\', \'柴胡\', \'去苗\', \'两蜀椒\', \'闭口\', \'及目\', \'出汗\', \'吴茱萸\', \'淘遍\', \'焙干\', \'厚朴\', \'粗皮\', \'姜汁\', \'两桂去\', \'粗皮\']');
INSERT INTO `prescript` VALUES (57428, '28399', '安息香汤', '安息香1钱，麝香（研）1钱，天灵盖（酥炙黄）3两，秦艽（去苗土）1两，鳖甲（去裙襕，炙令黄）1两，当归（切，焙）1两，柴胡（去苗）1两。', '每服4钱，童便1盏半，葱白5寸，桃柳枝各7寸，生姜（钱大）2片，同煎至8分，去滓，不拘时候服；夜卧时再煎，放患人床头，至五更，形于梦寐，此是药效也。', '《普济方》卷二三七', '传尸劳殗殜，喘气咳嗽，心胸满闷，渐至羸瘦。', '-', '-', '柴胡', '[\'安息香\', \'麝香\', \'研钱\', \'天灵盖\', \'秦艽\', \'苗土\', \'鳖甲\', \'炙令\', \'当归\', \'切焙\', \'柴胡\']');
