INSERT INTO `prescript` VALUES (57432, '28273', '龙脑散', '龙脑1分（细研），牛黄1分（细研），朱砂1分（细研），天竺黄半两（细研），赤芍药半两，玄参半两，犀角屑1两，羚羊角屑1两，细辛1分，甘菊花半两，车前子半两，决明子半两，胡黄连半两，柴胡半两（去苗），川升麻半两，川大黄1两（锉碎，微炒），甘草3分（炙微赤，锉）。', '每服1钱，食后煎竹叶汤调下。', '《圣惠》卷三十二', '肝脏风热，眼目赤烂肿痛。', '忌炙煿、热面、毒滑、鱼肉。', '-', '柴胡', '[\'龙脑\', \'分细研\', \'牛黄\', \'分细研\', \'朱砂\', \'分细研\', \'天竺黄\', \'半两\', \'细研\', \'芍药\', \'半两\', \'玄参\', \'半两\', \'犀角\', \'羚羊角\', \'细辛\', \'甘菊花\', \'半两\', \'车前子\', \'半两\', \'决明子\', \'半两\', \'胡黄连\', \'半两\', \'柴胡\', \'半两\', \'苗川\', \'升麻\', \'半两\', \'大黄\', \'甘草\', \'分炙微\']');
