INSERT INTO `prescript` VALUES (57440, '28006', '连翘败毒丸', '连翘40两，黄连40两，当归40两，甘草40两，柴胡24两，黄柏80两，金银花160两，防风40两，苦参40两，荆芥穗40两，黄芩40两，麻黄80两，地丁240两，白芷40两，薄荷40两，天花粉40两，赤芍40两，羌活80两，大黄240两。', '每服3钱，温开水送下。', '《北京市中药成方选集》', '清热解毒，散风消肿。主疮疡初起，红肿疼痛，憎寒发热。', '-', '-', '柴胡', '[\'连翘\', \'黄连\', \'当归\', \'甘草\', \'柴胡\', \'黄柏\', \'金银花\', \'防风\', \'苦参\', \'荆芥\', \'黄芩\', \'麻黄\', \'两地\', \'白芷\', \'薄荷\', \'两天\', \'花粉\', \'赤芍\', \'羌活\', \'大黄\']');
INSERT INTO `prescript` VALUES (57441, '27916', '龙胆泻肝汤', '龙胆草1钱，软柴胡5分，小青皮1钱半（炒），怀生地5钱，车前子3钱（炒），全当归2钱，黑山栀1钱半（炒），鲜生姜1片。', '水煎，去滓温服。', '《医略六书》卷二十一', '肝胆火逆，面肿连颐，脉数者。', '-', '-', '柴胡', '[\'龙胆草\', \'钱软\', \'柴胡\', \'分小\', \'青皮\', \'钱半\', \'炒怀\', \'生地\', \'车前子\', \'当归\', \'黑山\', \'栀钱\', \'炒鲜\', \'生姜\']');
INSERT INTO `prescript` VALUES (57442, '27905', '龙虎汤', '柴胡1钱5分，黄芩1钱5分，半夏（姜制）7分，石膏2钱5分，黄连1钱5分，黄柏1钱2分，知母1钱（去毛），山栀仁1钱，粳米1撮。', '上锉1剂。加生姜1片，大枣2枚，水煎温服。阳毒入深者，先以青布折叠数重，新汲水渍之，搭于心胸之上，须臾再易，如此3次，热势稍退，即服此药。', '《古今医鉴》卷五', '热疟火盛，舌卷焦黑，鼻如烟煤，六脉洪数弦紧。', '-', '-', '柴胡', '[\'柴胡\', \'钱分\', \'黄芩\', \'钱分\', \'半夏\', \'姜制\', \'石膏\', \'钱分\', \'黄连\', \'钱分\', \'黄柏\', \'钱分\', \'知母\', \'毛山\', \'栀仁钱\', \'粳米\']');
INSERT INTO `prescript` VALUES (57443, '27904', '地骨皮散', '地骨皮（拣择令净，干，用粗葛皮包洗过后，干称）1两，黄耆（锉，焙）1两，柴胡（去芦头，洗，锉，焙）1两，人参（锉，焙）半两，白茯苓（去黑皮，锉，焙）半两，甘草（炙，焙）半两。', '每服1钱或半钱，白汤点下。', '《玉诀》引《手集》（见《幼幼新书》卷二十六）', '进食。主小儿热疳。', '-', '-', '柴胡', '[\'地骨皮\', \'拣择\', \'令净\', \'干用\', \'粗葛\', \'皮包\', \'过后\', \'干称\', \'黄耆\', \'柴胡\', \'芦头\', \'人参\', \'半两\', \'白茯苓\', \'黑皮\', \'半两\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (57444, '27901', '当归汤', '当归身2钱，黄连（酒洗）2钱，黄芩2钱，生地黄3钱（酒洗，阴干），炙甘草3钱，柴胡1两，白芍药2钱。', '保命当归汤（《原机启微》附录）。《原机启微》附录生地黄作熟地黄。', '《洁古家珍》', '补益肾水。主肝肾阴虚，风热上攻，瞳子散大，眼黑，或生翳膜。', '-', '-', '柴胡', '[\'当归\', \'身钱\', \'黄连\', \'洗钱\', \'黄芩\', \'生地黄\', \'钱酒\', \'阴干\', \'甘草\', \'柴胡\', \'两白\', \'芍药\']');
INSERT INTO `prescript` VALUES (57445, '27885', '桂心散', '桂心半两，柴胡1两（去苗），甘草1分（炙微赤，锉），葛根1两（锉），赤茯苓半两，泽泻半两，赤芍药半两，麻黄1两（去根节）。', '每服5钱，以水1中盏，加薄荷2-7叶，煎至6分，去滓，稍热服，不拘时候。衣盖取汗，未汗再服。', '《圣惠》卷十七', '热病3日，表犹未解，壮热，头目、四肢不利。', '-', '-', '柴胡', '[\'桂心\', \'半两\', \'柴胡\', \'两去\', \'甘草\', \'分炙微\', \'葛根\', \'茯苓\', \'半两\', \'泽泻\', \'半两\', \'芍药\', \'半两\', \'麻黄\', \'两去\', \'根节\']');
INSERT INTO `prescript` VALUES (57446, '27879', '郇膏汤', '熟地1两，白芍1两，山茱萸5钱，柴胡5分，荆芥（炒黑）3钱，北五味10粒，竹沥1合。', '水煎服。2剂愈。', '《辨证录》卷三', '补心君之弱，制肾火之动。主君火衰，肾中火动，双目流血，甚至射出，妇人则经闭不行，男子则口干唇燥。', '-', '-', '柴胡', '[\'熟地\', \'白芍\', \'山茱萸\', \'柴胡\', \'荆芥\', \'黑钱\', \'五味\', \'竹沥\']');
