INSERT INTO `prescript` VALUES (57449, '27733', '附桂紫金膏', '生地1两，当归1两，干姜1两，桂枝1两，麻黄1两，白芷1两，甘草1两，苍术1两，枳壳3钱，五加皮3钱，莪术3钱，桃仁3钱，山柰3钱，川乌3钱，陈皮3钱，台乌3钱，三棱3钱，细辛3钱，首乌3钱，草乌3钱，柴胡3钱，防风3钱，寄奴3钱，牙皂3钱，川芎3钱，威灵仙3钱，羌活3钱，赤芍3钱，藁本3钱，续断3钱，独活3钱，连翘3钱，血余1团，天雄8两，小茴3钱，香附3钱，荆芥3钱，海风藤3钱。', '用时将膏药在火上烘融摊开，贴患处。', '《全国中药成药处方集》（沙市方）', '风湿风寒，劳伤瘫痪，积聚痞块，流注瘰疬，寒湿脚气，鹤膝酸痛，疝气遗精等症。', '非因寒湿致病及有发炎症状者忌贴。孕妇忌用。', '-', '柴胡', '[\'生地\', \'当归\', \'干姜\', \'桂枝\', \'麻黄\', \'白芷\', \'甘草\', \'苍术\', \'枳壳\', \'五加皮\', \'莪术\', \'桃仁\', \'钱山\', \'川乌\', \'陈皮\', \'乌钱\', \'三棱\', \'细辛\', \'首乌\', \'草乌\', \'柴胡\', \'防风\', \'钱寄奴\', \'钱牙皂\', \'川芎\', \'威灵仙\', \'羌活\', \'赤芍\', \'藁本\', \'续断\', \'独活\', \'连翘\', \'钱血余团\', \'天雄\', \'两小茴钱\', \'香附\', \'荆芥\', \'海风藤\']');
INSERT INTO `prescript` VALUES (57450, '27731', '地骨皮散', '地骨皮3分，麦门冬1两（去心），柴胡1两半（去苗），川升麻1两，赤芍药3分，甘草半两（炙微赤，锉），射干1两，石膏2两，龙胆3分（去芦头）。', '每服3钱，以水1中盏，煎至5分，去滓，加牛蒡汁1合，更煎1两沸，食后温服。', '《圣惠》卷五', '脾实热，唇肿心烦，咽喉不利，体热烦疼。', '-', '-', '柴胡', '[\'地骨皮\', \'麦门冬\', \'两去\', \'柴胡\', \'两半\', \'苗川\', \'升麻\', \'两赤\', \'芍药\', \'甘草\', \'半两\', \'炙微\', \'射干\', \'石膏\', \'龙胆\', \'分去\', \'芦头\']');
