INSERT INTO `prescript` VALUES (57453, '27681', '甘露茶', '柴胡1两，厚朴1两，防风1两，山楂1两，枳壳1两，苍术1两，神曲1两，谷芽1两，陈皮1两，川乌1两。', '柴胡改前胡亦可。', '《易简方便医书》卷一', '头痛发烧，胸痞闷，气结不舒，脾胃不和，饮食停滞，霍乱吐泻，以及四时不正之气：并疟疾，红白痢疾，暑热伤食等。', '-', '-', '柴胡', '[\'柴胡\', \'厚朴\', \'防风\', \'山楂\', \'枳壳\', \'苍术\', \'神曲\', \'两谷芽\', \'陈皮\', \'川乌\']');
INSERT INTO `prescript` VALUES (57454, '27615', '连翘散', '连翘1两，川升麻1两，汉防己1两，黄芩1两，川大黄1两（锉碎，微炒），川芒消1两，柴胡1两（去苗），赤芍药2两，甘草1两（炙微赤，锉），犀角屑1两，杏仁1两（汤浸，去皮尖双仁，麸炒微黄）。', '每服3钱，以水1中盏，煎至6分，去滓，每于食后温服。', '《圣惠》卷八十一', '产后妒乳，肿痛此热，欲结成痈。', '-', '-', '柴胡', '[\'连翘\', \'两川\', \'升麻\', \'两汉\', \'防己\', \'黄芩\', \'两川\', \'大黄\', \'碎微\', \'炒川芒\', \'消两\', \'柴胡\', \'两去\', \'芍药\', \'甘草\', \'两炙微\', \'犀角\', \'杏仁\', \'两汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\']');
INSERT INTO `prescript` VALUES (57455, '27606', '地骨皮汤', '地骨皮半两，细辛（去苗叶）半两，柴胡（去苗）1两，甘草（炙，锉）半两，人参半两，白茯苓（去黑皮）半两。', '每服3钱匕，水1盏，煎至7分，去滓温服，日3次。', '《圣济总录》卷八十九', '虚劳，肢体疼痛，头目昏眩，怠惰少力，饮食无味，心忪烦渴，口苦咽干，夜多盗汗。', '-', '-', '柴胡', '[\'地骨皮\', \'半两\', \'细辛\', \'苗叶\', \'半两\', \'柴胡\', \'甘草\', \'半两\', \'人参\', \'半两\', \'白茯苓\', \'黑皮\', \'半两\']');
