INSERT INTO `prescript` VALUES (57458, '27529', '诃黎勒散', '诃黎勒皮1两，厚朴1两（去粗皮，涂生姜汁，炙令香熟），柴胡1两（去苗），木香半两，当归半两，桂心半两，芎3分，陈橘皮3分（汤浸，去白瓤，焙），熟干地黄3分，人参3分（去芦头），牛膝1两（去苗），白芍药3分，白术3分，甘草1分（炙微赤，锉）。', '每服4钱，以水1中盏，加生姜半分，大枣2枚，煎至6分，去滓温服，不拘时候。', '《圣惠》卷七十', '妇人冷劳，气攻脾胃，腹胁妨闷，四肢不和，吃食减少，渐至虚羸。', '-', '-', '柴胡', '[\'黎勒皮\', \'厚朴\', \'两去\', \'粗皮\', \'涂生\', \'姜汁\', \'炙令\', \'香熟\', \'柴胡\', \'两去\', \'苗木\', \'半两\', \'当归\', \'半两\', \'桂心\', \'半两\', \'分陈\', \'橘皮\', \'分汤\', \'浸去\', \'地黄\', \'人参\', \'分去\', \'芦头\', \'牛膝\', \'两去\', \'苗白\', \'芍药\', \'白术\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (57459, '27440', '和疟清痢两解汤', '当归1两，白芍1两（酒炒），甘草1钱，枳壳2钱，槟榔1钱5分，陈皮2钱，柴胡8分，车前子3钱，莱菔子3钱（炒，研），草蔻仁1钱（研），煨广木香1钱5分。', '此方分量不可改动，大人全方，小儿减半。1服即轻，2服疟痢即退其半，4服全愈。如遍数多，加制军3钱；如红多，加酒炒川连3分。', '《慈航集》卷下', '疟痢。', '-', '-', '柴胡', '[\'当归\', \'白芍\', \'两酒\', \'甘草\', \'枳壳\', \'槟榔\', \'钱分\', \'陈皮\', \'柴胡\', \'车前子\', \'莱菔子\', \'研草蔻仁\', \'钱研\', \'木香\', \'钱分\']');
INSERT INTO `prescript` VALUES (57460, '27408', '安坤赞育丸', '桑寄生8两，乳香8两，蕲艾8两，熟地8两，杜仲8两，制香附8两，山茱萸8两，鳖甲8两，没药8两，琥珀8两，白芍8两，乌药8两，当归8两，红花8两，龟版8两，泽泻8两，砂仁8两，柴胡8两，广陈皮8两，远志8两，酸枣仁8两，木香2两，川芎4两，沉香4两，青毛鹿茸4两。', '每服1丸，白开水送下。', '《全国中药成药处方集》（济南方）', '妇女月经不凋，崩漏带下，腰酸腹痛，面色萎黄。', '忌气恼及辛辣生冷等物。', '-', '柴胡', '[\'桑寄生\', \'乳香\', \'熟地\', \'杜仲\', \'两制\', \'香附\', \'山茱萸\', \'鳖甲\', \'没药\', \'琥珀\', \'白芍\', \'乌药\', \'当归\', \'红花\', \'两龟版\', \'泽泻\', \'砂仁\', \'柴胡\', \'两广\', \'陈皮\', \'远志\', \'酸枣仁\', \'木香\', \'川芎\', \'沉香\', \'两青毛\', \'鹿茸\']');
INSERT INTO `prescript` VALUES (57461, '27405', '绛雪散', '木通（锉）2两，桔梗（锉，炒）2两，槟榔2两，枳壳（去瓤，麸炒）1两半，犀角（镑）1两半，柴胡（去苗）2两，升麻2两，木香2两，赤茯苓（去黑皮）2两，桑根白皮（锉）4两，山栀子仁4两，桂（去粗皮）2两，人参2两，苏枋木5两，朴消（研）1斤，丹砂（研）1两，麝香（研）1分，诃黎勒（去核）5枚。', '每服1钱或2钱，食后临卧，以冷蜜汤调下。', '《圣济总录》卷一二二', '热结喉间，连颊肿不消，心膈烦满。', '-', '-', '柴胡', '[\'木通\', \'桔梗\', \'槟榔\', \'枳壳\', \'两半\', \'犀角\', \'两半\', \'柴胡\', \'升麻\', \'木香\', \'两赤\', \'茯苓\', \'黑皮\', \'两桑根\', \'白皮\', \'两山\', \'栀子\', \'两桂去\', \'粗皮\', \'人参\', \'两苏枋\', \'两朴\', \'消研斤\', \'丹砂\', \'研两\', \'麝香\', \'研分诃黎勒\', \'去核\']');
INSERT INTO `prescript` VALUES (57462, '27389', '沉香曲', '沉香2两，木香2两，柴胡1两，厚朴1两，郁金1两，白豆蔻1两，缩砂仁1两，枳壳4两，麦芽4两，青皮4两，防风4两，葛根4两，乌药4两，前胡4两，广皮4两，桔梗4两，槟榔4两，白芷4两，谷芽4两，藿香3两，檀香3两，降香3两，羌活3两，甘草1两5钱。', '每服1块，河水煎服。', '《丸散膏丹集成》', '疏表化滞，舒肝和胃。主肝胃气滞，胸闷脘胀，腹痛，呕吐吞酸。', '-', '-', '柴胡', '[\'沉香\', \'木香\', \'柴胡\', \'厚朴\', \'郁金\', \'白豆蔻\', \'两缩\', \'砂仁\', \'枳壳\', \'麦芽\', \'青皮\', \'防风\', \'葛根\', \'乌药\', \'两前\', \'两广\', \'桔梗\', \'槟榔\', \'白芷\', \'两谷芽\', \'藿香\', \'檀香\', \'降香\', \'羌活\', \'甘草\']');
INSERT INTO `prescript` VALUES (57463, '27351', '龙胆泻肝丸', '龙胆草2两，栀子3两，胡连1两6钱，木通2两，泽泻3两，熟军1两6钱，甘草2两，车前子3两，当归3两，生地3两，柴胡1两5钱。', '每次服3钱重，白开水送下，每日早晚各1次。', '《全国中药成药处方集》（兰州方）', '泻肝胆之热，清肠胃，利便，助消化。主', '孕妇忌服。', '-', '柴胡', '[\'龙胆草\', \'栀子\', \'两胡连\', \'木通\', \'泽泻\', \'两熟军\', \'甘草\', \'车前子\', \'当归\', \'生地\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57464, '27297', '芦根汤', '芦根1两，知母（焙）1两半，栝楼根1两半，柴胡（去苗）1两半，黄芩（去黑心）1两半，甘草（炙，锉）1两半。', '每服2钱匕，水1盏，加生姜3片，煎至7分，去滓温服，不拘时候。', '《圣济总录》卷二十三', '阳毒，伤寒5-6日以上，但胸中烦热，干呕躁闷。', '-', '-', '柴胡', '[\'芦根\', \'知母\', \'两半\', \'栝楼\', \'两半\', \'柴胡\', \'去苗\', \'两半\', \'黄芩\', \'黑心\', \'两半\', \'甘草\', \'两半\']');
INSERT INTO `prescript` VALUES (57465, '27292', '和解宣化汤', '银柴胡（水炒）3g，远志3g，炙鳖甲9g，甜杏仁9g，象贝母9g，炒谷芽9g，炒麦芽9g，竹沥6g，半夏6g，紫菀6g，黄芩（酒炒）4.5g，知母（酒炒）4.5g，橘红4.5g，生薏苡仁12g。', '若咳嗽重，加紫菀6g、炙枇杷叶9g。', '方出《程门雪医案》，名见《古今名方》', '和解宣化，凉营退热。主阴虚潮热，缠绵不愈，或肺痨发热，口苦，咳嗽有痰，胃纳不香。', '-', '-', '柴胡', '[\'银柴胡\', \'远志\', \'鳖甲\', \'杏仁\', \'贝母\', \'炒谷芽\', \'麦芽\', \'竹沥\', \'半夏\', \'紫菀\', \'黄芩\', \'知母\', \'橘红\', \'薏苡仁\']');
INSERT INTO `prescript` VALUES (57466, '27273', '地骨皮汤', '地骨皮1两半，白茯苓（去黑皮）1两半，麦门冬（去心，焙）1两半，柴胡（去苗）1两半，赤芍药1两，甘草（炙令赤）1两。', '每服5钱匕，用水1盏半，煎至1盏，去滓，食后分2次温服。', '《圣济总录》卷九十三', '虚劳五蒸。', '-', '-', '柴胡', '[\'地骨皮\', \'两半\', \'白茯苓\', \'黑皮\', \'两半\', \'麦门冬\', \'两半\', \'柴胡\', \'去苗\', \'两半\', \'芍药\', \'甘草\', \'炙令\']');
INSERT INTO `prescript` VALUES (57467, '27113', '地骨皮散', '地骨皮半两，黄芩半两，人参半两，黄耆半两，葳蕤半两，麦门冬半两，甘草半两，赤芍药半两，柴胡1两。', '每服4钱，水1盏，加生姜半分，淡竹叶2-7片，煎至6分，去滓温服，不拘时候。', '《普济方》卷三四○', '妊娠烦躁，体热疼痛，口干食少。', '-', '-', '柴胡', '[\'地骨皮\', \'半两\', \'黄芩\', \'半两\', \'人参\', \'半两\', \'黄耆\', \'半两\', \'葳蕤\', \'半两\', \'麦门冬\', \'半两\', \'甘草\', \'半两\', \'芍药\', \'半两\', \'柴胡\']');
INSERT INTO `prescript` VALUES (57468, '27087', '地黄煎', '地黄汁1升半，麦门冬汁5合，生姜汁5合，天门冬汁5合，玄参1两，柴胡（去苗）1两，茯苓（去黑皮）1两，射干1两，黄牛乳1升，蜜2升，黄牛酥5两，黄耆（钞，炒）2两，桂（去粗皮）2两，人参2两，五味子（炒）2两，款冬花2两，紫菀（去苗土）2两，贝母（去心）2两，杏仁（去皮尖双仁，研）5两。', '每日饭后服1匙头，仰卧渐渐咽之，令药浸润心肺，至夜临睡时如前再服。', '《圣济总录》卷六十六', '肺气不和，上气咳嗽。', '-', '-', '柴胡', '[\'地黄\', \'升半\', \'麦门冬\', \'合生\', \'姜汁\', \'天门冬\', \'汁合\', \'玄参\', \'柴胡\', \'茯苓\', \'黑皮\', \'射干\', \'两黄\', \'牛乳\', \'升蜜升\', \'黄牛\', \'黄耆\', \'两桂去\', \'粗皮\', \'人参\', \'五味子\', \'款冬花\', \'紫菀\', \'苗土\', \'贝母\', \'杏仁\', \'皮尖\', \'双仁研\']');
INSERT INTO `prescript` VALUES (57469, '27068', '和解丸', '荆芥穗4两，羌活4两，白芷4两，葛根4两，川芎3两，天花粉3两，玄参3两，赤芍药3两，柴胡3两，黄芩2两，连翘壳2两，薄荷2两，甘草2两。', '《北京市中药成方选集》有桔梗、前胡。', '《中国医学大辞典》', '清热解表。辛散透表，疏风散寒。主风寒发热，四时瘟疫，头痛无汗，百节酸痛，口干舌苦，鼻流清水，咳逆。四时感冒，头痛发热，骨节酸痛，咳逆呕哕，四肢倦怠，寒热往来，口渴自汗。', '汗出避风，忌辛辣食品。', '-', '柴胡', '[\'荆芥\', \'羌活\', \'白芷\', \'葛根\', \'川芎\', \'两天\', \'花粉\', \'玄参\', \'两赤\', \'芍药\', \'柴胡\', \'黄芩\', \'连翘\', \'薄荷\', \'甘草\']');
INSERT INTO `prescript` VALUES (57470, '27032', '红蓝花散', '红蓝花1两，柴胡1两半（去苗），羚羊角屑1两，赤芍药1两，桑根白皮2两（锉），槟榔2两，紫苏茎叶1两，红雪2两，甘草3分（炙微赤，锉）。', '方中红雪用量原缺，据《普济方》补。', '《圣惠》卷六十九', '妇人脚气，心神烦闷。', '-', '-', '柴胡', '[\'蓝花\', \'柴胡\', \'两半\', \'去苗\', \'羚羊角\', \'芍药\', \'两桑根\', \'白皮\', \'槟榔\', \'紫苏\', \'两红雪\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (57471, '27005', '肝胃百合汤', '柴胡10g，黄芩10g，乌药10g，川楝子10g，郁金10g，百合30g，丹参15g。', '胃部灼热，加蒲公英15g；灼热喜按喜温饮，加高良姜3g；胸部痞满发胀，加九香虫3g；吐酸水，加生牡蛎30g，或瓦楞子30g；大便结，加火麻仁10g；大便色黑，加桃仁10g；脾胃虚弱，加明党参、黄耆各12g；十二指肠溃疡，加白芍12g，甘草10g。', '《夏度衡医案》', '疏肝和胃，活血化瘀。主胃和十二指肠溃疡，胃脘疼痛，嗳气吞酸，心烦口苦，脘区压痛，大便色黑，舌质淡红，苔薄微黄，脉弦细。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'乌药\', \'川楝子\', \'郁金\', \'百合\', \'丹参\']');
INSERT INTO `prescript` VALUES (57472, '27000', '冲脉饮子', '黄耆（每1两用桂1钱煎汤，将碗盛饭上蒸熟）1钱，人参1钱5分，白术1钱，生地黄（酒浸）1钱，茯苓1钱，当归身2钱，白芍（酒炒）1钱，川芎1钱，柴胡5分，青皮5分，宣木瓜4分，皂角子2钱，甘草2分。', '水煎，频服。大便不通润，加火麻仁（炒）2钱，黄连（酒炒）2钱。', '《寿世保元》卷七', '妇人年五十外，乳痈初巳，而又致穿破，不得收功者。', '-', '-', '柴胡', '[\'黄耆\', \'两用\', \'桂钱\', \'盛饭\', \'蒸熟\', \'人参\', \'钱分\', \'白术\', \'生地黄\', \'酒浸\', \'茯苓\', \'当归\', \'身钱\', \'白芍\', \'川芎\', \'柴胡\', \'青皮\', \'分宣\', \'木瓜\', \'分皂\', \'角子\', \'甘草\']');
INSERT INTO `prescript` VALUES (57473, '26959', '安胆汤', '柴胡2钱，天花粉2钱，炒栀子2钱，甘草1钱，白芍3钱，丹皮3钱。', '水煎服。', '《辨证录》卷五', '少阳春温，由春月伤风所致。', '-', '-', '柴胡', '[\'柴胡\', \'天花粉\', \'栀子\', \'甘草\', \'白芍\', \'丹皮\']');
INSERT INTO `prescript` VALUES (57474, '26957', '沉香散', '沉香半两，槟榔半两，大附子（炮，去皮脐）半两，人参（去芦）半两，茯苓（去皮）半两，当归（去芦）半两，官桂（去粗皮）半两，前胡半两，黄耆半两，枳壳（去瓤，麸炒）半两，干姜（炮）半两，柴胡（去苗）1两，雀脑穹半两，诃子（炮，去核）1两，甘草1两，五味子1两，半夏2两（用浆水煮30沸，细切小片子，焙干用之），草豆蔻3分（炮，去皮）。', '沉香汤（《圣济总录》卷八十七）。', '《博济》卷一', '散滞气。主五劳七伤，寒热无力，小便黄赤，吃食无味，心多惊悸，骨节酸疼，心胸痞闷，两胁疼痛。', '-', '-', '柴胡', '[\'沉香\', \'半两\', \'槟榔\', \'半两\', \'附子\', \'去皮\', \'半两\', \'人参\', \'去芦\', \'半两\', \'茯苓\', \'去皮\', \'半两\', \'当归\', \'去芦\', \'半两\', \'官桂去\', \'粗皮\', \'半两\', \'半两\', \'黄耆\', \'半两\', \'枳壳\', \'半两\', \'干姜\', \'半两\', \'柴胡\', \'去苗\', \'两雀脑\', \'半两\', \'诃子\', \'去核\', \'甘草\', \'五味子\', \'半夏\', \'两用\', \'浆水\', \'煮沸\', \'细切\', \'片子\', \'焙干\', \'草豆蔻\', \'分炮\', \'去皮\']');
INSERT INTO `prescript` VALUES (57475, '26955', '佛手丸', '鲜白葫芦5两（去子，蒸晒9次，另研极细如飞尘），鲜佛手5两（用银柴胡3钱煎汤拌炒，切片，蒸晒9次），鲜香橼5两（用金铃子3钱煎汤拌炒，去子蒸晒9次），地道人参1钱（另研极细如飞尘），大豆黄卷10两，炒黑枣仁5两，冬霜桑叶5两，真川贝母5两（去心），建神曲5两，建莲肉5两。', '每服1钱，计共300服左右。如肝气痛者，香附汤送下，胃气痛者，木香汤送下；脚气痛者，木瓜汤送下；膨胀病者，陈麦柴汤送下。', '《良方集腋》卷上', '肝胃气痛，脚气，膨胀。', '-', '-', '柴胡', '[\'鲜白\', \'葫芦\', \'两去子\', \'蒸晒次\', \'研极细\', \'飞尘\', \'佛手\', \'两用\', \'银柴胡\', \'切片\', \'蒸晒次\', \'香橼\', \'两用\', \'金铃子\', \'去子\', \'蒸晒次\', \'地道\', \'人参\', \'研极细\', \'飞尘\', \'大豆黄卷\', \'枣仁\', \'两冬霜\', \'桑叶\', \'两真\', \'川贝母\', \'两去\', \'心建\', \'神曲\', \'两建\', \'莲肉\']');
INSERT INTO `prescript` VALUES (57476, '26936', '赤茯苓汤', '赤茯苓1两（去黑皮），柴胡1两（去苗），枳壳1两（去瓤，麸炒），白术1两，槟榔（锉）1两，人参半两，旋覆花半两，杏仁（汤浸，去皮尖双仁，麸炒）3分，半夏（汤浸7遍去滑）3分。', '每服5钱匕，以水1盏半，加生姜半分（拍碎），煎之1盏，去滓温服，不拘时候。', '《圣济总录》卷六十三', '膈间留饮，呕逆头眩，短气多渴。', '-', '-', '柴胡', '[\'茯苓\', \'两去\', \'黑皮\', \'柴胡\', \'两去\', \'枳壳\', \'两去\', \'白术\', \'槟榔\', \'人参\', \'半两\', \'旋覆花\', \'半两\', \'杏仁\', \'汤浸\', \'皮尖\', \'双仁麸\', \'炒分\', \'半夏\', \'浸遍\', \'滑分\']');
INSERT INTO `prescript` VALUES (57477, '26918', '赤茯苓散', '赤茯苓（去黑皮）3分，当归（去苗，洗，切，焙干）3分，木香半两，桂心（去粗皮，不见火）3分，白术1两，枳壳（麸炒，去瓤）半两，赤芍药1两，柴胡（去苗，洗）半两，黄耆半两（炙），鳖甲（醋炙，去裙）3分，五味子（拣净）半两，桔梗（锉，炒）半两，芎半两，橘红半两，甘草半两（炒）。', '每服4钱，水1盏半，加生姜5片，煎至8分，去滓，通口服，不拘时候。', '《传信适用方》卷一', '阴阳俱虚，经络凝涩，气血不和，身体疼痛，背膊劳倦，手足无力。', '-', '-', '柴胡', '[\'茯苓\', \'黑皮\', \'当归\', \'去苗\', \'洗切\', \'焙干\', \'木香\', \'半两\', \'桂心\', \'粗皮\', \'不见\', \'火分\', \'白术\', \'枳壳\', \'半两\', \'芍药\', \'柴胡\', \'苗洗\', \'半两\', \'黄耆\', \'半两\', \'鳖甲\', \'裙分\', \'五味子\', \'半两\', \'桔梗\', \'半两\', \'半两\', \'橘红\', \'半两\', \'甘草\', \'半两\']');
INSERT INTO `prescript` VALUES (57478, '26844', '定痛消毒饮', '蒲公英1钱2分，紫花地丁1钱2分，当归（乳房用身，乳顶用尾）1钱，白芍（醋炒）1钱，赤芍1钱，花粉1钱，浙贝母（去心，研）1钱，皂角刺7分或5分，柴胡梢8分或1钱（乳顶肿结用之，若乳房易白芷），牡丹皮8分，广皮8分，明乳香5分，没药5分，生草3分。', '水3钟，加红枣2个（去核），灯心50寸，煎8分，临服加无灰酒小半酒杯入药，滚数滚服之，不时用槐艾水洗。', '《胎产心法》卷下', '乳顶劳或乳房吹乳成痈，并乳结之证，发热恶寒，冷汗自出，势欲破而疼痛难忍。', '-', '-', '柴胡', '[\'蒲公英\', \'钱分\', \'紫花地丁\', \'钱分\', \'当归\', \'乳房\', \'用身\', \'顶用\', \'尾钱\', \'白芍\', \'赤芍\', \'花粉\', \'浙贝母\', \'去心\', \'研钱\', \'皂角刺\', \'柴胡\', \'乳顶\', \'结用\', \'乳房\', \'白芷\', \'牡丹皮\', \'分广皮\', \'分明\', \'乳香\', \'没药\', \'分生\', \'草分\']');
INSERT INTO `prescript` VALUES (57479, '26788', '儿疟饮', '槟榔1钱，草果1钱，半夏1钱，贝母1钱，柴胡1钱，黄芩1钱，甘草3分。', '水煎服。', '《仙拈集》卷三引《集验》', '小儿疟疾，内有食积痰饮者。', '-', '-', '柴胡', '[\'槟榔\', \'草果\', \'半夏\', \'贝母\', \'柴胡\', \'黄芩\', \'甘草\']');
INSERT INTO `prescript` VALUES (57480, '26771', '赤茯苓汤', '赤茯苓2两，柴胡2两（去苗），黄芩1两，龙骨2两，川大黄3两（锉碎，微炒），人参1两（去芦头），牡蛎1两（焙为粉），桂心1两，陈橘皮1两（汤浸，去白瓤，焙），半夏1两（汤浸7遍去滑），甘草1两（炙微赤，锉）。', '茯苓汤（《普济方》卷一三一）。', '《圣惠》卷九', '伤寒九日，下之后，胸膈烦满，小便不利，谵语，一身不可转。', '-', '-', '柴胡', '[\'茯苓\', \'柴胡\', \'两去\', \'黄芩\', \'龙骨\', \'两川\', \'大黄\', \'人参\', \'两去\', \'芦头\', \'牡蛎\', \'两焙为\', \'粉桂心\', \'橘皮\', \'两汤\', \'浸去\', \'半夏\', \'两汤\', \'浸遍\', \'甘草\', \'两炙微\']');
INSERT INTO `prescript` VALUES (57481, '26761', '来苏散', '甘草（捣，焙，去滓）1两，肉桂（去皮，不近火）1两，桔梗（净，焙干）1两，防风（净洗，焙）1两，五加皮（净洗，焙）1两，赤芍药（净洗，焙）1两，荆芥穗（别轻焙）1两，柴胡（去苗，净洗，焙）1两，干葛（焙，捣筛粉，去皮）1两，白茯苓1两，麻黄1两半（去节），陈橘皮1两半（去白，焙）。', '每服2钱，加生姜3片，乌梅、大枣各1个，水1大盏，煎至7分，去滓热服，日3次。解劳倦，及不染山岚瘴气。时行伤寒，如伤寒壮热头痛，连进2-3服，稍轻调理，每日3服，并不拘时候。', '《普济方》卷二二八引《卫生家宝》', '男女五劳七伤，憎寒壮热，骨节酸痛，口舌干苔，四体骨蒸，伤寒头疼，背膊劳倦，膈胃烦壅，多睡昏沉，或时咳嗽，面无颜色，小便黄赤，妇人血风劳。', '-', '-', '柴胡', '[\'甘草\', \'肉桂\', \'去皮\', \'不近\', \'桔梗\', \'焙干\', \'防风\', \'净洗\', \'五加皮\', \'净洗\', \'芍药\', \'净洗\', \'荆芥\', \'穗别\', \'柴胡\', \'苗净\', \'干葛焙\', \'筛粉\', \'去皮\', \'白茯苓\', \'麻黄\', \'两半\', \'去节\', \'橘皮\', \'两半\']');
INSERT INTO `prescript` VALUES (57482, '26729', '当归丸', '当归（切，焙）1两半，鳖甲（去裙襕，醋炙）1两，琥珀（研）1两，芎1两，桃仁（去皮尖双仁，炒黄，研）1两，牛膝（酒浸，切，焙）1两，水蛭（糯米炒令焦）1两，虎杖半两，桂（去粗皮）半两，大黄（1半生，1半炒）半两，柴胡（去苗）半两，虻虫（炒令色黄）2分，牡丹皮2分，麝香（研）半分。', '每服20丸，空心用薄荷酒送下；乌梅汤下亦得。', '《圣济总录》卷一五一', '妇人风劳气疾，经脉不通，渐加羸瘦，不思饮食，心腹胀满，遍身疼痛。', '-', '-', '柴胡', '[\'当归\', \'切焙\', \'两半\', \'鳖甲\', \'琥珀\', \'研两芎\', \'桃仁\', \'皮尖\', \'双仁\', \'黄研\', \'牛膝\', \'浸切\', \'水蛭\', \'糯米\', \'炒令\', \'虎杖\', \'半两\', \'桂去\', \'粗皮\', \'半两\', \'大黄\', \'半生\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'令色\', \'黄分\', \'牡丹皮\', \'麝香\', \'半分\']');
INSERT INTO `prescript` VALUES (57483, '26708', '赤茯苓汤', '赤茯苓（去黑皮）3分，人参3分，前胡（去芦头）3分，桂（去粗皮）3分，半夏（汤洗7遍去滑，焙）半两，柴胡（去苗）半两，甘草（炙，锉）1分。', '每服3钱匕，水1盏，加大枣2个（擘破），生姜5片，煎取7分，去滓，食后温服。', '《圣济总录》卷六十七', '胸胁短气，妨闷不下食。', '-', '-', '柴胡', '[\'茯苓\', \'黑皮\', \'人参\', \'分前\', \'胡去\', \'芦头\', \'分桂去\', \'粗皮分\', \'半夏\', \'洗遍\', \'半两\', \'柴胡\', \'去苗\', \'半两\', \'甘草\']');
INSERT INTO `prescript` VALUES (57484, '26706', '吹云膏', '细辛1分，升麻3分，蕤仁3分，青皮4分，连翘4分，防风4分，柴胡5分，生甘草6分，当归身6分，荆芥穗1钱（微取浓汁），生地黄1钱5分，拣黄连3钱。', '点之。', '《兰室秘藏》卷上', '目中泪下，及迎风寒泣，羞明怕日，常欲闭目，喜在暗室，塞其户牖，翳膜岁久遮睛。', '-', '-', '柴胡', '[\'细辛\', \'升麻\', \'蕤仁\', \'青皮\', \'连翘\', \'防风\', \'柴胡\', \'生甘草\', \'当归\', \'身分\', \'荆芥\', \'微取\', \'浓汁\', \'生地黄\', \'分拣\', \'黄连\']');
INSERT INTO `prescript` VALUES (57485, '26681', '利火降痰汤', '黄连8分，连翘1钱5分，山栀2钱，滑石2钱，木通1钱，黄芩1钱，枳实1钱，瓜蒌1钱，霜车前1钱，钩藤4钱，柴胡6分，甘草3分。', '-', '《笔花医镜》卷三', '小儿急惊风之轻者。', '-', '-', '柴胡', '[\'黄连\', \'连翘\', \'分山\', \'栀钱\', \'滑石\', \'木通\', \'黄芩\', \'枳实\', \'钱瓜\', \'钱霜车\', \'钩藤\', \'柴胡\', \'甘草\']');
INSERT INTO `prescript` VALUES (57486, '26669', '地骨皮散', '地骨皮（洗）半两，秦艽（洗，去芦）半两，柴胡半两，枳壳（制）半两，知母（生）半两，当归半两，鳖甲（醋炙黄）半两，川芎半两，甘草（炙）1分。', '每服3钱，加桃、柳枝各7寸，生姜3片，乌梅1个，同煎，空心、临卧各1服。潮热甚，加些大黄微利之。', '《直指》卷九', '虚劳，潮热骨蒸，壮热。', '-', '-', '柴胡', '[\'地骨皮\', \'半两\', \'秦艽\', \'洗去\', \'半两\', \'柴胡\', \'半两\', \'枳壳\', \'半两\', \'知母\', \'半两\', \'当归\', \'半两\', \'鳖甲\', \'半两\', \'川芎\', \'半两\', \'甘草\', \'炙分\']');
INSERT INTO `prescript` VALUES (57487, '26663', '肥儿丸', '木香1两，胡黄连1两，使君子肉1两，黄连、槟榔、龙胆草、诃子肉、肉豆蔻（煨）、芜荑、芦荟、阿魏、银柴胡。', '方中黄连、槟榔、龙胆草、诃子肉、肉豆蔻、芜荑、芦荟、阿魏、银柴胡用量原缺。', '《便览》卷四', '杀虫去积，退热进食。主疳。', '-', '-', '柴胡', '[\'木香\', \'胡黄连\', \'使君子\', \'黄连\', \'槟榔\', \'龙胆草\', \'诃子\', \'肉豆蔻\', \'芦荟\', \'阿魏\', \'银柴胡\']');
INSERT INTO `prescript` VALUES (57488, '26656', '地黄丸', '干地黄1两6株，大黄1两6铢，茯苓18铢，当归半两，柴胡半两，杏仁半两。', '干地黄丸（《圣济总录》卷一七五）。', '《千金》卷五', '生肌肉。主小儿胃气不调，不嗜食。', '-', '-', '柴胡', '[\'地黄\', \'两株\', \'大黄\', \'两铢\', \'茯苓\', \'当归\', \'半两\', \'柴胡\', \'半两\', \'杏仁\', \'半两\']');
INSERT INTO `prescript` VALUES (57489, '26647', '补脾胃泻阴火升阳汤', '柴胡1两5钱，甘草（炙）1两，黄耆1两，苍术（泔浸，去黑皮，切作片子，晒干，锉碎，炒）1两，羌活1两，升麻8钱，人参7钱，黄芩7钱，黄连（去须，酒制）5钱（炒），石膏少许（长夏微用，过时去之，从权）。', '泻阴火升阳汤（《玉机微义》卷十）、升阳泄火汤（《准绳·类方》卷一）、升阳降火汤（《便览》卷一）。', '《脾胃论》卷上', '饮食损胃，劳倦伤脾，火邪乘之而生大热。', '服药之时宜减食，宜美食。服药讫忌语话1-2时辰许，及酒、湿面大料之类，恐大热之物，复助火邪而愈损元气也。亦忌冷水及寒凉淡渗之物及诸果，恐阳气不能生旺也。宜温食及薄滋味以助阳气。', '-', '柴胡', '[\'柴胡\', \'甘草\', \'黄耆\', \'苍术\', \'泔浸\', \'黑皮\', \'切作\', \'片子\', \'晒干\', \'羌活\', \'升麻\', \'人参\', \'黄芩\', \'黄连\', \'制钱\', \'石膏\', \'少许\', \'长夏\', \'微用\', \'过时\', \'从权\']');
INSERT INTO `prescript` VALUES (57490, '26642', '利火降痰汤', '柴胡8分，黄芩7分，陈皮6分，天竹黄1钱5分，钩藤2钱，连翘1钱，木通7分，细甘草4分。', '-', '《不知医必要》卷三', '小儿急惊，壮热痰壅，昏闷不醒，搐搦颤动。', '-', '-', '柴胡', '[\'柴胡\', \'黄芩\', \'陈皮分\', \'天竹黄\', \'钱分\', \'钩藤\', \'连翘\', \'木通\', \'分细\', \'甘草\']');
INSERT INTO `prescript` VALUES (57491, '26612', '利肾汤', '泽泻1两半，生干地黄（焙）1两半，赤茯苓（去黑皮）1两半，槟榔（锉）1两，麦门冬（去心，焙）1两，柴胡（去苗）1两，枳壳（去瓤，麸炒）1两，黄芩（去黑心）1两，牛膝（酒浸，切，焙）1两。', '每服3钱匕，水1盏，煎7分，去滓温服。', '《圣济总录》卷五十一', '肾气有余，脊脉痛，气乏不欲言。', '-', '-', '柴胡', '[\'泽泻\', \'半生\', \'地黄\', \'两半\', \'茯苓\', \'黑皮\', \'两半\', \'槟榔\', \'麦门冬\', \'柴胡\', \'枳壳\', \'黄芩\', \'黑心\', \'牛膝\', \'浸切\']');
INSERT INTO `prescript` VALUES (57492, '26586', '沉香汤', '沉香1两，柴胡（去苗）2两，秦艽（去苗土）2两，肉豆蔻（去壳）2两，白芷2两，黄耆（锉）2两，鳖甲（去裙襕，醋炙）2两，桔梗（炒）2两，桂（去粗皮）2两，当归（切，洗，焙）1两，芎1两，蓬莪术（炮）1两，麦门冬（去心，焙）1两，槟榔（锉）1两，芍药1两，人参1两，白茯苓（去黑皮）1两，海桐皮（锉）1两，枳壳（去瓤，麸炒）1两，甘草（炙）1两，熟干地黄（焙）1两，酸枣仁1两，木香1两，荆芥穗3两。', '每服3钱匕，水1盏，加生姜3片、乌梅半枚，同煎至7分，去滓温服，每日3次。', '《圣济总录》卷一五一', '室女月水不调，或多或少，或断或绝，不快不利，攻刺疼痛，四肢无力，不思饮食，多困黄瘦，胸膈痞满。', '-', '-', '柴胡', '[\'沉香\', \'柴胡\', \'秦艽\', \'苗土\', \'肉豆蔻\', \'白芷\', \'两黄耆\', \'鳖甲\', \'桔梗\', \'两桂去\', \'粗皮\', \'当归\', \'切洗\', \'两芎\', \'两蓬\', \'莪术\', \'麦门冬\', \'槟榔\', \'芍药\', \'人参\', \'白茯苓\', \'黑皮\', \'海桐皮\', \'枳壳\', \'甘草\', \'两熟\', \'地黄\', \'酸枣仁\', \'木香\', \'荆芥\']');
INSERT INTO `prescript` VALUES (57493, '26574', '八物汤', '人参3钱，白茯苓3钱，当归3钱，白芍3钱，小茴香3钱，熟地3钱，白术4钱，川芎4钱，甘草1钱，柴胡1钱，香附1钱。', '加味八珍汤（《郑氏家传女科万金方》卷二）。腹痛，加枳壳、干漆、玄胡索各3钱；呕吐恶心，加良姜、砂仁各2钱；手足麻痹，恶寒，加肉桂1钱5分。', '《女科万金方》', '补气血，扶脾胃，调经水。主室女17-18岁，经脉不通，或阻百日，或半年，颜色有异，饮食少进，寒热往来，四肢困倦，头疼目眩，腹疼恶心，烦热呕吐，腹胀，此脾胃气血虚弱，误食生冷使然。', '-', '-', '柴胡', '[\'人参\', \'白茯苓\', \'当归\', \'白芍\', \'小茴香\', \'熟地\', \'白术\', \'川芎\', \'甘草\', \'柴胡\', \'香附\']');
INSERT INTO `prescript` VALUES (57494, '26560', '百合散', '百合3分，柴胡1两（去苗），桑根白皮3分（锉），杏仁1分（汤浸，去皮尖双仁，麸炒微黄），陈橘皮3分（汤浸，去白瓤，焙），麻黄3分（去根节），赤茯苓3分，甘草半两（炙微赤，锉），紫苏茎叶1两。', '每服3钱，以水1中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷三十一', '骨蒸劳热，咳嗽损肺。', '-', '-', '柴胡', '[\'百合\', \'柴胡\', \'两去\', \'苗桑根\', \'白皮\', \'杏仁\', \'分汤\', \'浸去\', \'皮尖\', \'双仁麸\', \'微黄\', \'橘皮\', \'分汤\', \'浸去\', \'麻黄\', \'分去\', \'根节\', \'茯苓\', \'甘草\', \'半两\', \'炙微\', \'紫苏\']');
INSERT INTO `prescript` VALUES (57495, '26554', '龙胆泻火汤', '胆草2钱，升麻2钱，柴胡2钱，黄柏1钱5分，山栀1钱5分，黄芩1钱5分，青黛1钱5分，苍耳子1钱5分，羌活1钱5分，川芎1钱5分，白芷1钱5分，甘草3分，生石膏3钱（引）。', '-', '《点点经》卷四', '五阳冲头，肺气猖越天精湖，鼻流臭水，口疮破裂，咽喉肿痛，并齿痛脑崩。', '-', '-', '柴胡', '[\'胆草\', \'升麻\', \'柴胡\', \'黄柏\', \'分山\', \'栀钱分\', \'黄芩\', \'钱分\', \'青黛钱分\', \'苍耳子\', \'钱分\', \'羌活\', \'钱分\', \'川芎\', \'钱分\', \'白芷\', \'钱分\', \'甘草\', \'生石膏\', \'钱引\']');
INSERT INTO `prescript` VALUES (57496, '26485', '白虎青龙各半汤', '柴胡（取银州者，去须及头，用银刀削去黄薄皮少许，粗布拭净，锉细，勿令犯火）7钱，升麻（不经雨阳者，形色翠碧，削去皮，用黄精汁浸1宿，晒干，锉，蒸，再晒）3钱，葛根（取洁白肥嫩者，用雪水或秋露润透，切片，阴干）9钱，羌活（去头，细锉，以淫羊藿拌浥3日，晒干，去藿）5钱，防风（勿用叉头叉尾者，叉头令人发狂，叉尾发人痼疾，取肥大柔润色黄通理者锉细）5钱，甘草（取黄中通理者，去头尾尖处各45寸，仅取中节，切作寸许长，入瓷器中好酒浸蒸，从巳至午，取出晒干，锉细）7钱5分，知母（槐砧上锉细，于木臼杵捣数千下，勿犯铁器', '上以水3升5合，先煮粳米减半升，去粳米，同诸药煮取1升半，去滓，分3次服。寅卯时取初服：再煮数沸，俟病者睡熟推醒服，服毕覆盖取微似汗；2服未发前半时许服，服毕温覆，勿使寒栗大作，热亦渐减；3服发后半时许服，服毕再半时许方啜热粥饮1盏许，以充营卫，勿食他物损伤药力也。', '《痎疟论疏》', '痎疟', '-', '-', '柴胡', '[\'柴胡\', \'银州\', \'头用\', \'刀削\', \'黄薄皮\', \'少许\', \'粗布\', \'拭净\', \'细勿令\', \'犯火\', \'升麻\', \'不经雨阳者\', \'形色\', \'翠碧削\', \'去皮\', \'精汁\', \'浸宿\', \'晒干\', \'葛根\', \'洁白\', \'肥嫩\', \'雪水\', \'秋露\', \'润透\', \'切片\', \'阴干\', \'羌活\', \'头细\', \'日晒\', \'干去\', \'防风\', \'用叉头\', \'尾者\', \'令人\', \'发狂\', \'发人\', \'痼疾\', \'肥大\', \'柔润\', \'黄通理者\', \'细钱\', \'甘草\', \'黄中\', \'通理者\', \'头尾\', \'尖处\', \'各寸仅\', \'取中\', \'节切\', \'作寸\', \'许长入\', \'瓷器\', \'中好\', \'浸蒸\', \'至午\', \'取出\', \'晒干\', \'细钱分\', \'知母\', \'槐砧\', \'细于\', \'臼杵\', \'数千\', \'下勿犯\', \'铁器\']');
INSERT INTO `prescript` VALUES (57497, '26464', '苓桂柴胡汤', '茯苓3钱，甘草2钱，丹皮3钱，桂枝3钱，芍药3钱，柴胡3钱，半夏3钱。', '水煎大半杯，温服。热蒸不减，加生地、黄芩；蒸退即用干姜、附子以温水土。', '《四圣心源》卷十', '骨蒸。', '-', '-', '柴胡', '[\'茯苓\', \'甘草\', \'丹皮\', \'桂枝\', \'芍药\', \'柴胡\', \'半夏\']');
