INSERT INTO `prescript` VALUES (57499, '26433', '定坤丹', '当归12两，人参（去芦）5两，黄毛鹿茸（去毛）3两，藏红花3两，熟地4两，于术3两，汉三七2两5钱，鸡血藤2两5钱，白芍3两，枸杞子3两，阿胶（炒）2两，益母草5钱，香附（醋炙）5钱，延胡索（醋炒）5钱，柴胡5钱，茺蔚子5钱，鹿角霜5钱，五灵脂（醋炒）5钱，甘草5钱，茯苓4钱，干姜（炮）4钱，杜仲（炒）4钱，川牛膝3钱，砂仁3钱，川芎2钱，黄芩2钱，肉桂（去粗皮）2钱，乌药3钱，细辛1钱5分。', '每服1丸，温开水送下，1日2次。', '《北京市中药成方选集》', '调经理血。主妇女虚弱，经期不准，行经胀痛，腰酸带下。', '-', '-', '柴胡', '[\'当归\', \'人参\', \'去芦\', \'黄毛\', \'鹿茸\', \'藏红花\', \'熟地\', \'两于术\', \'两汉\', \'三七\', \'鸡血藤\', \'白芍\', \'枸杞子\', \'阿胶\', \'益母草\', \'香附\', \'延胡索\', \'柴胡\', \'茺蔚子\', \'鹿角霜\', \'五灵脂\', \'甘草\', \'茯苓\', \'干姜\', \'炮钱\', \'杜仲\', \'川牛膝\', \'砂仁\', \'川芎\', \'黄芩\', \'肉桂\', \'粗皮\', \'乌药\', \'细辛\', \'钱分\']');
INSERT INTO `prescript` VALUES (57500, '26404', '龙胆饮', '龙胆1分，钩藤（锉）1分，柴胡（去苗）1分，黄芩（去黑心）1分，桔梗（锉，炒）1分，赤芍药1分，茯神（去木）1分，蜣螂2枚（炙，去足头甲），大黄（蒸3度，炒）1两。', '每服2钱匕，水1小盏，煎取4分，分数服。', '《圣济总录》卷一七七', '小儿被客气忤犯，状似惊痫，但眼不上戴，耳后脉急数。', '-', '-', '柴胡', '[\'龙胆\', \'钩藤\', \'锉分\', \'柴胡\', \'苗分\', \'黄芩\', \'黑心\', \'桔梗\', \'炒分\', \'芍药\', \'茯神\', \'木分\', \'蜣螂\', \'足头\', \'大黄\', \'蒸度\']');
INSERT INTO `prescript` VALUES (57501, '26344', '苍术柴胡汤', '柴胡1钱半，知母1钱，苍术（泔制，炒黄）1钱，黄芩（酒炒）1钱，葛根1钱，陈皮1钱，半夏1钱，川芎1钱，甘草（炙）7分，生姜3片，乌梅肉1个。', '水煎，清晨服。', '《张氏医通》卷十五', '瘴疟。', '-', '-', '柴胡', '[\'柴胡\', \'钱半\', \'知母\', \'苍术\', \'泔制\', \'黄钱\', \'黄芩\', \'葛根\', \'陈皮\', \'半夏\', \'川芎\', \'甘草\', \'炙分\', \'生姜\', \'乌梅\', \'肉个\']');
INSERT INTO `prescript` VALUES (57502, '26338', '赤芍药散', '赤芍药1分，知母1分，子芩1分，人参1分（去芦头），枳壳1分（款炒微黄，去瓤），甘草1分（炙微赤，锉），石膏3分，川麻1分，柴胡半两（去苗）。', '每服1钱，以水一小盏，加青竹叶7片，煎至5分，去滓温服，不拘时候。', '《圣惠》卷八十四', '小儿伤寒挟实，壮热，憎寒头痛。', '-', '-', '柴胡', '[\'芍药\', \'知母\', \'分子\', \'人参\', \'分去\', \'芦头\', \'枳壳\', \'分款\', \'微黄\', \'甘草\', \'分炙微\', \'石膏\', \'分川\', \'麻分\', \'柴胡\', \'半两\', \'去苗\']');
INSERT INTO `prescript` VALUES (57503, '26310', '利目汤', '龙胆草2钱，茵陈3钱，白芍1两，茯苓5钱，泽泻3钱，车前子3钱，白蒺藜3钱，柴胡1钱，草决明2钱。', '水煎服。', '《辨证录》卷七', '开肝气之郁，分湿散邪。主肝疸，肝气郁，湿热团结而不散，两目尽黄，身体四肢亦现黄色，但不如眼黄之甚，气逆，手足发冷，腰以上汗出不止，腰以下无汗。', '-', '-', '柴胡', '[\'龙胆草\', \'茵陈\', \'白芍\', \'茯苓\', \'泽泻\', \'车前子\', \'钱白\', \'蒺藜\', \'柴胡\', \'草决明\']');
INSERT INTO `prescript` VALUES (57504, '26287', '地骨皮散', '地骨皮1两，半夏半两（汤浸7遍去滑），桔梗半两（去芦头），人参半两（去芦头），白茯苓半两，白术半两，陈橘皮3分（汤浸，去白瓤，焙），柴胡3分（去芦头），甘草1分（炙微赤，锉）。', '每服3钱，以水1中盏，加生姜半分，煎至6分，去滓温服，不拘时候。', '《圣惠》卷二十九', '虚劳寒热，四肢羸瘦，不欲饮食。', '-', '-', '柴胡', '[\'地骨皮\', \'半夏\', \'半两\', \'浸遍\', \'桔梗\', \'半两\', \'芦头\', \'人参\', \'半两\', \'芦头\', \'白茯苓\', \'半两\', \'白术\', \'半两\', \'橘皮\', \'分汤\', \'浸去\', \'柴胡\', \'分去\', \'芦头\', \'甘草\', \'分炙微\']');
INSERT INTO `prescript` VALUES (57505, '26269', '当归川芎散', '当归1钱，川芎1钱，柴胡1钱，白术（炒）1钱，芍药（炒）1钱，牡丹皮8分，茯苓8分，蔓荆子5分，甘草5分，山栀（炒）1钱2分。', '水煎服。若肝气不平，寒热往来，加柴胡、地骨皮；肝气实热，加柴胡、黄芩；肝脾气血虚热，加参、耆、熟地；脾虚饮食少思，倍加白术、茯苓；脾虚胸膈不利，加人参、黄耆；脾虚痰滞，胸膈不利，加半夏；肝气不顺，胸膈不利，加木香；肝虚小腹痞满，或时作痛，加熟地；脾血不足，小腹作痛，加肉桂；日晡发热，加熟地。', '《校注妇人良方》卷二十四', '手足少阳经血虚疮证，或风热耳内痒痛，生疮出水，或头目不清，寒热少食，或妇女经水不调，胸膈不利，腹胁痞痛，小便不调。', '-', '-', '柴胡', '[\'当归\', \'川芎\', \'柴胡\', \'白术\', \'芍药\', \'牡丹皮\', \'茯苓\', \'蔓荆子\', \'甘草\', \'分山\', \'钱分\']');
INSERT INTO `prescript` VALUES (57506, '26262', '卷柏阿胶散', '棕皮半斤（烧灰存性）1两，卷柏1两，人参（去芦头）1两，阿胶（炒）1两，艾叶1两，子芩1两，地榆1两，生干地黄1两，伏龙肝1两，柴胡（去苗）1两，甘草（炙）1两。', '每服2钱，糯米饮煎服。', '《传家秘宝》卷下', '吐血，咯血。', '-', '-', '柴胡', '[\'棕皮\', \'半斤\', \'烧灰\', \'存性\', \'卷柏\', \'人参\', \'芦头\', \'阿胶\', \'艾叶\', \'两子\', \'地榆\', \'两生\', \'地黄\', \'伏龙肝\', \'柴胡\', \'甘草\']');
INSERT INTO `prescript` VALUES (57507, '26256', '红蓝花散', '红蓝花1两，柴胡1两半（去苗），当归1两，生干地黄1两，赤芍药1两，鬼箭羽1两，虎杖1两，大腹皮1两（锉），麦门冬1两（去心），土瓜根1两，地骨皮1两，枳壳1两（麸炒微黄，去瓤），甘草半两（炙微赤，锉）。', '每服4钱，以水1中盏，加生姜半分，煎至6分，去滓，不拘时候温服。', '《圣惠》卷七十', '妇人热劳，羸瘦，四肢少力，经脉不通。', '-', '-', '柴胡', '[\'蓝花\', \'柴胡\', \'两半\', \'去苗\', \'当归\', \'两生\', \'地黄\', \'两赤\', \'芍药\', \'鬼箭羽\', \'虎杖\', \'大腹皮\', \'麦门冬\', \'两去\', \'心土\', \'瓜根\', \'地骨皮\', \'枳壳\', \'两麸\', \'微黄\', \'甘草\', \'半两\', \'炙微\']');
INSERT INTO `prescript` VALUES (57508, '26193', '夺命还真丹', '天麻1两半，人参1两半，木香1两半，白术1两半，菟丝子1两半，藁本（去土）1两半，川独活1两半，川芎1两半，白僵蚕（炒）1两，黄芩1两，全蝎（去毒）1两，半夏（姜制）1两，熟地黄（焙）1两，蔓荆子1两，甘草（炒）1两，桂1两，生地黄1两，地骨皮1两，薄荷叶1两，黄连（净）1两，菊花1两，防风2两，茴香（炒）2两，知母2两，杜仲（炒丝断）2两，茯苓2两，柴胡（茸）2两，桔梗2两，陈皮（去白）2两，枳壳（炒，去瓤）2两，石膏2两，当归（焙）2两，羌活3两，白芍药2两半，麻黄（去节）2两半，细辛（去叶）半两，蛤', '神效夺命还真丹（《解围元薮》卷三）。《解围元薮》本方用法：遍身筋骨痛及心气痛不省人事，热醋汤下；冷风寒湿气顿抽掣走注，叫号日夜不安，黑豆炒焦烹酒下。', '《医方类聚》卷二十四引《施圆端效方》', '一切瘫痪，中风癞病，将死不救，洗头风，惊痫吐涎，暗风血风，妇人产前产后血气不调，及诸筋骨节疼痛。', '-', '-', '柴胡', '[\'天麻\', \'两半\', \'人参\', \'两半\', \'木香\', \'两半\', \'白术\', \'两半\', \'菟丝子\', \'两半\', \'藁本\', \'两半\', \'独活\', \'两半\', \'川芎\', \'两半\', \'白僵蚕\', \'黄芩\', \'全蝎\', \'半夏\', \'姜制\', \'熟地黄\', \'蔓荆子\', \'甘草\', \'两桂\', \'生地黄\', \'地骨皮\', \'薄荷叶\', \'黄连\', \'菊花\', \'防风\', \'茴香\', \'知母\', \'杜仲\', \'炒丝断\', \'茯苓\', \'柴胡\', \'桔梗\', \'陈皮\', \'枳壳\', \'石膏\', \'当归\', \'羌活\', \'两白\', \'芍药\', \'两半\', \'麻黄\', \'去节\', \'两半\', \'细辛\', \'半两\']');
